    const [CropImageHolder, setCropImageHolder] = useState<any>(null);





    const Draw = (type: any, ctx: any, w: any, h: any, method: any) => {

        if (type === "lift") {
            ctx.filter =
                "contrast(1.25) brightness(0.98) blur(0px) saturate(130%)";
        } else if (type === "floyd") {
            ctx.filter = "contrast(1.02) brightness(0.95) saturate(164%)";
        } else if (type === "jentle") {
            ctx.filter =
                "contrast(0.82) brightness(0.83) hue-rotate(20deg) saturate(150%) ";
        } else if (type === "mint") {
            ctx.filter = "contrast(1) brightness(1.05)  saturate(155%)  ";
        } else if (type === "nebula") {
            ctx.filter = "contrast(1.1) brightness(1.1)";
        } else if (type === "juice") {
            if (method === "thumb" && matchMobile) {
                ctx.filter =
                    "contrast(1.17) brightness(0.99) saturate(185%) blur(0px)";
            } else {
                ctx.filter =
                    "contrast(1.17) brightness(0.99) saturate(185%) blur(0.4px)";
            }
        } else if (type === "futurama") {
            ctx.filter = "contrast(1.3) brightness(0.94) saturate(145%) ";
        } else if (type === "zoidberg") {
            ctx.filter =
                "contrast(1.2) brightness(0.81) saturate(130%)  hue-rotate(1.4deg)";
        } else if (type === "tentacion") {
            ctx.filter = "contrast(1.2) brightness(1.1) saturate(137%)";
        } else if (type === "moonshine") {
            if (method === "thumb" && matchMobile) {
                ctx.filter =
                    "contrast(1.3) brightness(0.99) saturate(50%) blur(0.8px)";
            } else {
                if (matchMobile) {
                    ctx.filter =
                        "contrast(1) brightness(0.99) saturate(40%) blur(1.2px)";
                } else {
                    ctx.filter =
                        "contrast(1) brightness(0.99) saturate(40%) blur(2.8px)";
                }
            }
        } else if (type === "vintage") {
            ctx.filter = " contrast(1.1)  brightness(0.85)  saturate(25%)";
        } else {
        }

        ctx.drawImage(CropImageHolder, 0, 0, w, h);

        //////////////////////VIGNETTE
        var width = w;
        var height = h;
        if (type === "floyd") {
            var gradient = ctx.createRadialGradient(
                w / 6.7,
                h / 2,
                0,
                w / 2.8,
                h / 2,
                w * 0.65
            );

            gradient.addColorStop(0, "rgba(40, 181, 32, 0.118)");
            gradient.addColorStop(1, "rgba(0,0,0,0.3)");

            ctx.fillStyle = gradient;
            ctx.fillRect(0, 0, width, height);
        } else if (type === "mint") {
            var gradient = ctx.createRadialGradient(
                w / 2,
                h / 2,
                0,
                w / 2,
                h / 2,
                w * 0.65
            );

            gradient.addColorStop(0, "rgba(255, 255, 255,0.03)");
            gradient.addColorStop(1, "rgba(0,0,0,0.23)");

            ctx.fillStyle = gradient;
            ctx.fillRect(0, 0, width, height);
        } else if (type === "lift" || type === "juice") {
            var gradient = ctx.createRadialGradient(
                w / 2,
                h / 3,
                0,
                w / 2,
                h / 3,
                w * 0.66
            );

            gradient.addColorStop(0, "rgba(255, 255, 255,0.02)");
            gradient.addColorStop(1, "rgba(255, 255, 255,0.02)");

            ctx.fillStyle = gradient;
            ctx.fillRect(0, 0, width, height);
        } else if (type === "moonshine") {
            var gradient = ctx.createLinearGradient(
                w / 2,
                h / 3,
                0,
                w / 2,
                h / 3,
                w * 0.4
            );

            gradient.addColorStop(0, " rgba(255, 255, 255,0.001)");
            gradient.addColorStop(1, "#403d6149");

            ctx.fillStyle = gradient;
            ctx.fillRect(0, 0, width, height);

            var gradient = ctx.createRadialGradient(
                w / 2,
                h / 2,
                0,
                w / 2,
                h / 2,
                w * 0.3
            );

            gradient.addColorStop(0, "rgba(0,0,0,0.02)");
            gradient.addColorStop(1, " rgba(255, 255, 255,0.001)");

            ctx.fillStyle = gradient;
            ctx.fillRect(0, 0, width, height);
        } else if (type === "vintage") {
            var gradient = ctx.createRadialGradient(
                w / 2,
                h / 2,
                0,
                w / 2,
                h / 2,
                w * 0.6
            );

            gradient.addColorStop(0, "rgba(255, 255, 255,0.0001)");
            gradient.addColorStop(1, "rgba(255, 255, 255,0.0001)");
            ctx.fillStyle = gradient;
            ctx.fillRect(0, 0, width, height);
        } else if (type === "nebula") {
            var gradient = ctx.createRadialGradient(
                w / 2,
                h / 2,
                0,
                w / 2,
                h / 2,
                w * 0.65
            );

            gradient.addColorStop(0, "rgba(255, 255, 255, 0.01)");
            gradient.addColorStop(1, "rgba(0, 0, 0, 0.08)");

            ctx.fillStyle = gradient;
            ctx.fillRect(0, 0, width, height);
        } else if (type === "futurama") {
            var gradient = ctx.createRadialGradient(
                w / 2,
                h / 2,
                0,
                w / 2,
                h / 2,
                w * 0.68
            );

            gradient.addColorStop(0, "rgb(163, 218, 37,0.004)");
            gradient.addColorStop(1, "rgb(27, 194, 236,0.004)");

            ctx.fillStyle = gradient;
            ctx.fillRect(0, 0, width, height);
        } else if (type === "tentacion") {
            var gradient = ctx.createLinearGradient(
                w / 2,
                h / 71,
                0,
                w / 19,
                h / 2,
                w * 0.4
            );

            gradient.addColorStop(0, "rgba(255, 255, 255,0)");
            gradient.addColorStop(1, "#20422e5a");

            ctx.fillStyle = gradient;
            ctx.fillRect(0, 0, width, height);
        } else {
        }
        //////////////////////VIGNETTE

        //////////////////////GRADIENT BRIGHTNESS

        if (type === "normal") {
        } else {
            var gradient = ctx.createRadialGradient(
                w / 2,
                h / 2,
                0,
                w / 2,
                h / 2,
                w * 0.6
            );
            if (type === "lift") {
                gradient.addColorStop(0, "#dcecdf16");
                gradient.addColorStop(1, "#dceaec1a");
            } else if (type === "tentacion") {
                gradient.addColorStop(0, "#c9d6df11");
                gradient.addColorStop(1, "#bef0ce13");
            } else if (type === "jentle") {
                gradient.addColorStop(0, "rgba(255, 255, 255,0.003)");
                gradient.addColorStop(1, "rgba(255, 255, 255,0.003)");
            } else if (type === "vintage") {
                gradient.addColorStop(0, "rgba(255, 255, 255,0.005)");
                gradient.addColorStop(1, "rgba(255, 255, 255,0.005)");
            } else if (
                type === "zoidberg" ||
                type === "futurama" ||
                type === "floyd" ||
                type === "mint"
            ) {
                gradient.addColorStop(0, "rgba(255, 255, 255,0.006)");
                gradient.addColorStop(1, "rgba(255, 255, 255,0.005)");
            } else {
                gradient.addColorStop(0, "rgba(255, 255, 255,0.02)");
                gradient.addColorStop(1, "rgba(255, 255, 255,0.01)");
            }

            ctx.fillStyle = gradient;
            ctx.fillRect(0, 0, width, height);
        }

        //////////////////////GRADIENT BRIGHTNESS

        ////////////////// /////// GLOBALCOMPOSITEOPERATION
        if (type === "floyd") {
            ////////////composition
            ctx.globalCompositeOperation = "overlay";
            ctx.fillStyle = "#a58e89cc";
            ctx.fillRect(0, 0, width, height);
            ctx.globalCompositeOperation = "overlay";
            ctx.fillStyle = "#a58e89cc";
            ctx.fillRect(0, 0, width, height);

            ////////////composition
        } else if (type === "jentle") {
            ////////////composition
            ctx.globalCompositeOperation = "overlay";
            ctx.fillStyle = "#d3b5b5";
            ctx.fillRect(0, 0, width, height);
            ctx.globalCompositeOperation = "soft-light";
            ctx.fillStyle = "#d3b5b5";
            ctx.fillRect(0, 0, width, height);

            ////////////composition
        } else if (type === "mint") {
            ////////////composition
            ctx.globalCompositeOperation = "overlay";
            ctx.fillStyle = "#25302f18";
            ctx.fillRect(0, 0, width, height);
            ctx.globalCompositeOperation = "soft-light";
            ctx.fillStyle = "#25302f18";
            ctx.fillRect(0, 0, width, height);

            ////////////composition
        } else if (type === "nebula") {
            ////////////composition
            ctx.globalCompositeOperation = "overlay";
            ctx.fillStyle = "#6e6b72cc";
            ctx.fillRect(0, 0, width, height);
            ctx.globalCompositeOperation = "soft-light";
            ctx.fillStyle = "#6c6971cc";
            ctx.fillRect(0, 0, width, height);

            ////////////composition
        } else if (type === "moonshine") {
            ////////////composition
            ctx.globalCompositeOperation = "soft-light";
            ctx.fillStyle = "#77479f68";
            ctx.fillRect(0, 0, width, height);
            ctx.globalCompositeOperation = "overlay";
            ctx.fillStyle = "#69359368";
            ctx.fillRect(0, 0, width, height);

            ////////////composition
        } else if (type === "juice") {
            ////////////composition
            ctx.globalCompositeOperation = "overlay";
            ctx.fillStyle = "#5b6c7014";
            ctx.fillRect(0, 0, width, height);
            ctx.globalCompositeOperation = "overlay";
            ctx.fillStyle = "#5b6c7014";
            ctx.fillRect(0, 0, width, height);
            ctx.globalCompositeOperation = "overlay";
            ctx.fillStyle = "#5b6c7014";
            ctx.fillRect(0, 0, width, height);
            ////////////composition
        } else if (type === "futurama") {
            ////////////composition
            ctx.globalCompositeOperation = "overlay";
            ctx.fillStyle = "#3950681f";
            ctx.fillRect(0, 0, width, height);
            ctx.globalCompositeOperation = "screen";
            ctx.fillStyle = "#42806c1f";
            ctx.fillRect(0, 0, width, height);

            ////////////composition
        } else if (type === "zoidberg") {
            ////////////composition
            ctx.globalCompositeOperation = "overlay";
            ctx.fillStyle = "#69758113";
            ctx.fillRect(0, 0, width, height);
            ctx.globalCompositeOperation = "overlay";
            ctx.fillStyle = "#69758113";
            ctx.fillRect(0, 0, width, height);

            ////////////composition
        } else if (type === "tentacion") {
            ////////////composition
            ctx.globalCompositeOperation = "soft-light";
            ctx.fillStyle = "#2c2b2937";
            ctx.fillRect(0, 0, width, height);
            ctx.globalCompositeOperation = "soft-light";
            ctx.fillStyle = "#2c2b2937";
            ctx.fillRect(0, 0, width, height);

            ////////////composition
        } else if (type === "vintage") {
            ////////////composition

            ctx.globalCompositeOperation = "soft-light";
            ctx.fillStyle = "#8888b14e";
            ctx.fillRect(0, 0, width, height);
            ctx.globalCompositeOperation = "overlay";
            ctx.fillStyle = "#5c83944e";
            ctx.fillRect(0, 0, width, height);
            ctx.globalCompositeOperation = "soft-light";
            ctx.fillStyle = "#8888b14e";
            ctx.fillRect(0, 0, width, height);
            ////////////composition
        } else {
        }
        ////////////////// /////// GLOBALCOMPOSITEOPERATION

        ////////////////////GRADIENT BLENDING
        if (
            type === "jentle" ||
            type === "mint" ||
            type === "nebula" ||
            type === "juice" ||
            type === "futurama" ||
            type === "zoidberg" ||
            type === "tentacion" ||
            type === "moonshine" ||
            type === "vintage" ||
            type === "floyd"
        ) {
            var gradient = FilterGradient(width, height, type);
            var screen = blend(
                ctx,
                gradient,
                width,
                height,
                function (bottomPixel: any, topPixel: any) {
                    return 255 - ((255 - topPixel) * (255 - bottomPixel)) / 255;
                }
            );
            ctx.putImageData(screen, 0, 0);
        }

        ////////////////////GRADIENT BLENDING
    }






    useLayoutEffect(() => {
        if (canvasData) {
            const ctx = cropCanvasRef.current.getContext("2d");
            ctx.clearRect(0, 0, cropCanvasRef.current.width, cropCanvasRef.current.height);

            const Newcropimage: any = new Image();
            Newcropimage.src = canvasData;
            Newcropimage.onload = function () {
                if (CropImageHolder === Newcropimage) {
                } else {

                    //////////////
                    setCropImageHolder(Newcropimage);

                    let newWidth = 1300;
                    let newHeight = 1300;
                    const aspectRatio = Newcropimage.naturalWidth / Newcropimage.naturalHeight;

                    if (newWidth / newHeight > aspectRatio) {
                        newWidth = newHeight * aspectRatio;
                    } else {
                        newHeight = newWidth / aspectRatio;
                    }

                    setimageHeight(newHeight);
                    setimageWidth(newWidth);
                    ///GiveImageSpecificDimensions(Newcropimage);
                }


            }
        }
    }, [canvasData]);




    const cropCanvasRef: any = useRef(null);

    const canvasRefdummy: any = useRef(null);

    var filz = 'zoidberg';

    var fil = 'lift';

    var fil = 'lift';

    useLayoutEffect(() => {
        if (CropImageHolder && cropCanvasRef.current) {


            const ctx = cropCanvasRef.current.getContext("2d");

            const w = imageWidth;
            const h = imageHeight;

            cropCanvasRef.current.width = w;
            cropCanvasRef.current.height = h;



            ////////////////////////////////





            requestAnimationFrame(() => {

                Draw(fil, ctx, w, h, '');


            });





        }
    }, [CropImageHolder, cropCanvasRef, imageHeight, imageWidth])





    function blend(
        background: any,
        foreground: any,
        width: any,
        height: any,
        transform: any
    ) {
        var bottom = background.getImageData(0, 0, width, height);
        var top = foreground.getImageData(0, 0, width, height);

        for (var i = 0, size = top.data.length; i < size; i += 4) {
            // red
            top.data[i + 0] = transform(bottom.data[i + 0], top.data[i + 0]);
            // green
            top.data[i + 1] = transform(bottom.data[i + 1], top.data[i + 1]);
            // blue
            top.data[i + 2] = transform(bottom.data[i + 2], top.data[i + 2]);
            // the fourth slot is alpha. We don't need that (so skip by 4)
        }

        return top;
    }

    function FilterGradient(width: any, height: any, type: string) {
        var ctx = canvasRefdummy.current.getContext("2d");
        canvasRefdummy.current.height = height;
        canvasRefdummy.current.width = width;

        if (type === "jentle") {
            var gradient = ctx.createLinearGradient(
                width / 73.6,
                height / 33.4,
                0,
                width / 2,
                height / 2,
                width * 0.6
            );
        } else if (type === "mint" || type === "floyd") {
            var gradient = ctx.createLinearGradient(
                width / 73.6,
                height / 33.4,
                0,
                width / 2,
                height / 2,
                width * 0.6
            );
        } else if (type === "tentacion") {
            var gradient = ctx.createLinearGradient(
                width / 2,
                height / 2,
                0,
                width / 2,
                height / 2,
                width * 0.6
            );
        } else if (type === "nebula") {
            var gradient = ctx.createLinearGradient(
                width / 2,
                height / 2,
                0,
                width / 2,
                height / 2,
                width * 0.6
            );
        } else if (type === "juice") {
            var gradient = ctx.createRadialGradient(
                width / 2,
                height / 2,
                0,
                width / 2,
                height / 2,
                width * 0.7
            );
        } else if (type === "moonshine") {
            var gradient = ctx.createRadialGradient(
                width / 2,
                height / 2,
                0,
                width / 2,
                height / 2,
                width * 0.7
            );
        } else if (type === "vintage") {
            var gradient = ctx.createRadialGradient(
                width / 2,
                height / 3,
                0,
                width / 2,
                height / 2,
                width * 0.66
            );
        } else if (type === "futurama" || type === "zoidberg") {
            var gradient = ctx.createLinearGradient(
                width / 73.6,
                height / 33.4,
                0,
                width / 2,
                height / 2,
                width * 0.6
            );
        } else {
        }

        if (type === "jentle") {
            gradient.addColorStop(0, "#2c1f1f");
            gradient.addColorStop(0.4, "#41243c");
            gradient.addColorStop(1, "#272122");
        } else if (type === "mint") {
            gradient.addColorStop(0.25, "#2c3431");
            gradient.addColorStop(0.5, "#26483e");
            gradient.addColorStop(0.75, "#232831");
            gradient.addColorStop(1, "#273131");
        } else if (type === "nebula") {
            gradient.addColorStop(0, "#3b1858");
            gradient.addColorStop(1, "#151569");
        } else if (type === "juice") {
            gradient.addColorStop(0, "#242424");
            gradient.addColorStop(1, "#313131");
        } else if (type === "moonshine") {
            gradient.addColorStop(0, "#0e0e14");
            gradient.addColorStop(1, "#17171f");
        } else if (type === "futurama") {
            gradient.addColorStop(0.25, "#5c384c");
            gradient.addColorStop(0.5, "#5c384c");
            gradient.addColorStop(0.75, "#3e452f");
            gradient.addColorStop(1, " #2e3c3f");
        } else if (type === "zoidberg") {
            gradient.addColorStop(0, "#244a82");
            gradient.addColorStop(1, "#4d3017");
        } else if (type === "tentacion") {
            gradient.addColorStop(0, "#293746");
            gradient.addColorStop(0.6, "#3a2922");
            gradient.addColorStop(1, "#3b355d");
        } else if (type === "vintage") {
            gradient.addColorStop(0, "#2f3340");
            gradient.addColorStop(1, "#3d4358");
        } else if (type === "floyd") {
            gradient.addColorStop(0, "#3b3939");
            gradient.addColorStop(1, "#191d1b");
        } else {
        }

        ctx.fillStyle = gradient;

        ctx.fillRect(0, 0, width, height);

        return ctx;
    }




    return (
        <>
            {finalImage.length > 0 ? <><Masonry
                columns={3}
                spacing={0}
                style={{
                    padding: "0px",
