import React, {
  useRef,
  useState,
  useCallback,
  useEffect,
  useLayoutEffect,
} from "react";
import { matchPc, matchTablet } from "../DetectDevice";
import { Grid } from "@material-ui/core";
import MoreHorizIcon from "@material-ui/icons/MoreHoriz";
import { useSelector, useDispatch } from "react-redux";
import { useSpring, animated } from "react-spring";
import Masonry from "@mui/lab/Masonry";
import CircleIcon from "@mui/icons-material/Circle";
import { SuperCrop } from "./SuperCrop";
import MusicNoteIcon from "@mui/icons-material/MusicNote";
import PhotoIcon from "@mui/icons-material/Photo";
import GifIcon from "@mui/icons-material/Gif";
import ChevronRightIcon from "@mui/icons-material/ChevronRight";
import Axios from "axios";
import CropIcon from "@mui/icons-material/Crop";
import CheckIcon from "@mui/icons-material/Check";
import { PreviewCanvasCropAll } from "./PreviewCanvasCropAll";
import { PreviewCanvas } from "./PreviewCanvas";

function ImageTobeFilteredx({
  filterImage,
  index,
  itemUploadRef,
  setActivatefilterImage,
}: any): JSX.Element {
  ///
  ///
  ///
  const filterbaseimageRef: any = useRef(null);

  const closegate = () => {
    if (index === 0) {
      ////
      addfilter();
    } else {
      setActivatefilterImage(false);
    }
  };

  function toasterGradient(width: any, height: any) {
    var texture = document.createElement("canvas");
    var ctx = texture.getContext("2d");

    texture.width = width;
    texture.height = height;

    // Fill a Radial Gradient
    // https://developer.mozilla.org/en-US/docs/Web/API/CanvasRenderingContext2D/createRadialGradient

    var gradient = ctx.createRadialGradient(
      width / 2,
      height / 2,
      0,
      width / 2,
      height / 2,
      width * 0.6
    );

    gradient.addColorStop(0, "#804e0f");
    gradient.addColorStop(1, "#3b003b");

    ctx.fillStyle = gradient;
    ctx.fillRect(0, 0, width, height);

    return ctx;
  }

  function blend(
    background: any,
    foreground: any,
    width: any,
    height: any,
    transform: any
  ) {
    // Side note: grabbing this data is the most expensive piece. For better
    // performance, you could consider caching this data
    var bottom = background.getImageData(0, 0, width, height);
    var top = foreground.getImageData(0, 0, width, height);

    for (var i = 0, size = top.data.length; i < size; i += 4) {
      // red
      top.data[i + 0] = transform(bottom.data[i + 0], top.data[i + 0]);
      // green
      top.data[i + 1] = transform(bottom.data[i + 1], top.data[i + 1]);
      // blue
      top.data[i + 2] = transform(bottom.data[i + 2], top.data[i + 2]);
      // the fourth slot is alpha. We don't need that (so skip by 4)
    }

    return top;
  }

  const addfilter = () => {
    if (index === 0) {
      const previewFileReadimage = new Image();
      previewFileReadimage.src = itemUploadRef.current[index].src;
      previewFileReadimage.onload = function () {
        const ctx = canvasRef.current.getContext("2d");

        var width = window.innerWidth;
        var scale = width / previewFileReadimage.naturalWidth;
        var height = previewFileReadimage.naturalHeight * scale;

        canvasRef.current.height = height;
        canvasRef.current.width = width;

        var gradient = toasterGradient(width, height);

        ////////////////////
        requestAnimationFrame(() => {
          if (gradient) {
            ctx.drawImage(
              gradient.canvas,
              0,
              0,
              canvasRef.current.width,
              canvasRef.current.height
            );

            var screen = blend(
              ctx,
              gradient,
              width,
              height,
              function (bottomPixel: any, topPixel: any) {
                return 255 - ((255 - topPixel) * (255 - bottomPixel)) / 255;
              }
            );

            // replace `ctx.drawImage(gradient.canvas, 0, 0)` with this:
            ctx.putImageData(screen, 0, 0);
          }
        });
      };

      ////////////////////
      ////
    }
  };

  const canvasRef: any = useRef(null);

  return (
    <>
      {itemUploadRef.current[index] ? (
        <>
          {" "}
          <img
            id={itemUploadRef.current[index]}
            ref={filterbaseimageRef}
            onClick={closegate}
            src={
              itemUploadRef.current ? itemUploadRef.current[index].src : null
            }
            style={{
              width: "30%",
              height: "auto",
              position: "relative",
              margin: "auto",
              marginRight: index === 0 ? "20px" : "0px",
            }}
          />
          <canvas
            ref={canvasRef}
            style={{
              padding: "0px",
            }}
          />
        </>
      ) : null}
    </>
  );
}

export const ImageTobeFiltered = React.memo(ImageTobeFilteredx);
