import React, { useState, useEffect, useRef } from "react";

import Arrow from "./Arrow";
import Dots from "./Dots";
import SliderNumber from "./SliderNumber";
import { Paper, Grid } from "@material-ui/core";
import { useSpring, animated, useTransition } from "react-spring";

const Slider = ({ slides }: any) => {
  const [index, set] = useState(0);
  const [indexSlow, Slowset] = useState(0);
  const getWidth = () => window.innerWidth;
  ///var newGetWidth = getWidth() * slides.length;

  const slideTimer = useRef<ReturnType<typeof setTimeout> | null>(null);
  const slideTimerx = useRef<ReturnType<typeof setTimeout> | null>(null);
  const slideTimerj = useRef<ReturnType<typeof setTimeout> | null>(null);

  const SlideimageRef = useRef<HTMLImageElement>(null);
  const [height, setheight] = useState(0);

  const transitions = useTransition(index, {
    key: index,
    from: { opacity: 0 },
    enter: { opacity: 1 },
    leave: { opacity: 0 },
    config: { duration: 3000 },
  });

  const slideImageOnLoad = (item: number) => {
    if (item === 0 && height === 0) {
      if (SlideimageRef.current && SlideimageRef.current.clientHeight) {
        setheight(SlideimageRef.current.clientHeight);
      }
    }
  };
  const [touchPosition, setTouchPosition] = useState(null);
  // ...
  const handleTouchStart = (e: any) => {
    e.preventDefault();
    const touchDown = e.touches[0].clientX;
    setTouchPosition(touchDown);
  };

  const handleTouchMove = (e: any) => {
    e.preventDefault();
    if (slideTimerj.current) {
      clearTimeout(slideTimerj.current);
    }
    slideTimerj.current = setTimeout(function () {
      const touchDown = touchPosition;

      if (touchDown === null) {
        return;
      }

      const currentTouch = e.touches[0].clientX;
      const diff = touchDown - currentTouch;

      if (diff > 40) {
        nextSlide();
      } else if (diff < -40) {
        prevSlide();
      } else {
      }

      console.log(diff);
      //alert(diff);

      setTouchPosition(null);
      return false;
    }, 200);
    return false;
  };

  const waitChangeIndex = (a: number) => {
    if (slideTimer.current) {
      clearTimeout(slideTimer.current);
    }
    slideTimer.current = setTimeout(function () {
      Slowset(a);
    }, 500);
  };

  const nextSlide = () => {
    ///set((state) => (state + 1) % slides.length);
    if (index === slides.length - 1) {
      set(0);
      waitChangeIndex(0);
    } else {
      set(index + 1);
      waitChangeIndex(index + 1);
    }
  };

  const prevSlide = () => {
    if (index === 0) {
      set(slides.length - 1);
      waitChangeIndex(slides.length - 1);
    } else {
      set(index - 1);
      waitChangeIndex(index - 1);
    }
  };

  return (
    <Grid
      item
      onTouchStart={handleTouchStart}
      onTouchMove={handleTouchMove}
      xs={12}
    >
      <SliderNumber activeSlide={indexSlow} total={slides.length} />
      {transitions((style, i) => (
        <Grid xs="12">
          <animated.img
            style={{
              ...style,
              height: "auto",
              width: "100%",
              objectFit: "contain",
              position: "absolute",
              top: "0",
              left: "0",
            }}
            src={`./images/thumbs/${slides[i]}`}
          />

          <animated.img
            ref={SlideimageRef}
            onLoad={() => {
              slideImageOnLoad(i);
            }}
            style={{
              ...style,
              height: "auto",
              width: "100%",
              objectFit: "contain",
              position: "absolute",
              top: "0",
              left: "0",
              zIndex: 1,
            }}
            src={`./images/${slides[i]}`}
          />
        </Grid>
      ))}{" "}
      <Arrow
        direction="left"
        clickSlidep={prevSlide}
        clickSliden={nextSlide}
        height={height}
      />
      <Dots slides={slides} activeSlide={indexSlow} />
    </Grid>
  );
};

export default Slider;
