import React, { useState, useEffect, useRef } from "react";

import Arrow from "./Arrow";
import Dots from "./Dots";
import SliderNumber from "./SliderNumber";
import { Paper, Grid } from "@material-ui/core";
import { useSpring, animated, useTransition } from "react-spring";

const Slider = ({ slides }: any) => {
  const [index, set] = useState(0);
  const [indexSlow, Slowset] = useState(0);
  const getWidth = () => window.innerWidth;
  ///var newGetWidth = getWidth() * slides.length;

  const slideTimer = useRef<ReturnType<typeof setTimeout> | null>(null);
  const slideTimerx = useRef<ReturnType<typeof setTimeout> | null>(null);
  const slideTimerj = useRef<ReturnType<typeof setTimeout> | null>(null);

  const SlideimageRef = useRef<HTMLImageElement>(null);
  const SlideimageRefthumb = useRef<HTMLImageElement>(null);
  const [height, setheight] = useState(0);

  const transitions = useTransition(index, {
    key: index,
    from: { opacity: 0 },
    enter: { opacity: 1 },
    leave: { opacity: 0 },
    config: { duration: 2950 },
  });

  const slideImageOnLoad = (item: number) => {
    if (item === 0) {
      if (SlideimageRef.current && SlideimageRef.current.clientHeight) {
        setheight(SlideimageRef.current.clientHeight);
      }
    }
  };

  const slideImageOnLoadthumb = (item: number) => {
    if (item === 0 && height === 0) {
      if (
        SlideimageRefthumb.current &&
        SlideimageRefthumb.current.clientHeight
      ) {
        setheight(SlideimageRefthumb.current.clientHeight);
      }
    }
  };

  const [touchPosition, setTouchPosition] = useState(null);
  // ...
  const handleTouchStart = (e: any, TouchType: boolean) => {
    e.preventDefault();
    var touchDown = null;
    TouchType ? (touchDown = e.touches[0].clientX) : (touchDown = e.clientX);
    setTouchPosition(touchDown);
  };

  const handleTouchMove = (e: any, TouchType: boolean) => {
    e.preventDefault();
    if (slideTimerj.current) {
      clearTimeout(slideTimerj.current);
    }
    slideTimerj.current = setTimeout(function () {
      const touchDown = touchPosition;

      if (touchDown === null) {
        return;
      }

      var currentTouch = null;
      TouchType
        ? (currentTouch = e.touches[0].clientX)
        : (currentTouch = e.clientX);
      const diff = touchDown - currentTouch;

      if (diff > 40) {
        nextSlide();
      } else if (diff < -40) {
        prevSlide();
      } else {
      }

      console.log(diff);
      //alert(diff);

      setTouchPosition(null);
      return false;
    }, 200);
    return false;
  };

  const waitChangeIndex = (a: number) => {
    if (slideTimer.current) {
      clearTimeout(slideTimer.current);
    }
    slideTimer.current = setTimeout(function () {
      Slowset(a);
    }, 500);
  };

  const nextSlide = () => {
    ///set((state) => (state + 1) % slides.length);
    if (index === slides.length - 1) {
      set(0);
      waitChangeIndex(0);
    } else {
      set(index + 1);
      waitChangeIndex(index + 1);
    }
  };

  const prevSlide = () => {
    if (index === 0) {
      set(slides.length - 1);
      waitChangeIndex(slides.length - 1);
    } else {
      set(index - 1);
      waitChangeIndex(index - 1);
    }
  };

  const GotoDots = (clickedDot: number) => {
    set(clickedDot);
    waitChangeIndex(clickedDot);
  };

  /* useEffect(() => {
    const t = setInterval(() => set(state => (state + 1) % slides.length), 4000)
    return () => clearTimeout(t)
  }, []) */

  return (
    <Grid
      item
      onTouchStart={(e: any) => {
        handleTouchStart(e, true);
      }}
      onTouchMove={handleTouchMove}
      xs={12}
      style={{
        position: "relative",
        height: `${height}px`,
        opacity: height === 0 ? 0 : 1,
      }}
    >
      <SliderNumber activeSlide={indexSlow} total={slides.length} />
      <Arrow
        direction="left"
        clickSlidep={prevSlide}
        clickSliden={nextSlide}
        height={height}
      />
      {transitions((style, i) => (
        <Grid key={i} xs={12} item>
          <animated.img
            ref={SlideimageRefthumb}
            onLoad={() => {
              slideImageOnLoadthumb(i);
            }}
            className="turdark"
            style={{
              ...style,
              height: "auto",
              width: "100%",
              objectFit: "contain",
              position: "absolute",
              top: "0",
              left: "0",
            }}
            src={`./images/thumbs/${slides[i]}`}
          />

          <animated.img
            ref={SlideimageRef}
            onLoad={() => {
              slideImageOnLoad(i);
            }}
            className="turdark"
            style={{
              ...style,
              cursor: "pointer",
              height: "auto",
              width: "100%",
              objectFit: "contain",
              position: "absolute",
              top: "0",
              left: "0",
              zIndex: 1,
            }}
            src={`./images/${slides[i]}`}
          />
        </Grid>
      ))}{" "}
      <Dots GotoDots={GotoDots} slides={slides} activeSlide={indexSlow} />
    </Grid>
  );
};

export default Slider;
