import { animated, useSpring } from "react-spring";
import { Grid } from "@material-ui/core";
import { ImodalFormLoginError } from "./appFolder-Interfaces";

export default function ModalFormLoginError({
  device,
  type,
  ErrorDisplay,
  darkmode,
  WidthHolder,
}: ImodalFormLoginError) {
  ///
  ///
  ///
  ///USE SLIDE DOWN ANIMATION FROM REACT SPRING
  const animationModalLogError = useSpring({
    config: {
      duration: 100,
    },
    width: "100%",
    opacity: ErrorDisplay,
    transform: ErrorDisplay ? `translateY(0%)` : `translateY(100%)`,
  });

  ///
  ///
  ///
  ///USE SLIDE DOWN ANIMATION FROM REACT SPRING
  const animationModalErrorPasswordLog = useSpring({
    config: {
      duration: 100,
    },
    opacity: ErrorDisplay,
    marginTop: "-74px",
    zIndex: 0,
    transform: ErrorDisplay ? `translateY(100%)` : `translateY(0%)`,
  });

  var ErrorColor = "";
  var ErrorTextColor = "";
  var errorwidth = "";
  var textfont = "";

  if (device === "pc") {
    errorwidth = WidthHolder;
    textfont = "1.1vw";
  } else if (device === "tablet") {
    errorwidth = WidthHolder;
    textfont = "1vw";
  } else {
    errorwidth = "100%";
    textfont = "";
  }

  if (darkmode) {
    ErrorColor =
      "linear-gradient(0deg,  rgba(17, 17, 17, 0.025), rgba(17, 17, 17, 0.05),rgba(17, 17, 17, 0.075),rgba(17, 17, 17, 0.1),rgba(17, 17, 17, 0.125),rgba(17, 17, 17, 0.15),rgba(17, 17, 17, 0.175),rgba(17, 17, 17, 0.2),rgba(17, 17, 17, 0.225),rgba(17, 17, 17, 0.25))";

    ErrorTextColor = "#dddddd";
  } else {
    ErrorColor =
      "linear-gradient(0deg,  rgba(221, 221, 221, 0.05), rgba(221, 221, 221, 0.1),rgba(221, 221, 221, 0.15),rgba(221, 221, 221, 0.2),rgba(221, 221, 221, 0.25),rgba(221, 221, 221, 0.3),rgba(221, 221, 221, 0.35),rgba(221, 221, 221, 0.4),rgba(221, 221, 221, 0.45),rgba(221, 221, 221, 0.5))";

    ErrorTextColor = "#222222";
  }

  return (
    <>
      {type ? (
        <animated.div style={animationModalLogError}>
          <Grid
            container
            style={{
              width: errorwidth,
              height: "34px",
              backgroundImage: ErrorColor,
              margin: "auto",
              borderRadius: "0px",
              marginBottom: "-1.5px",
              display: "flex",
            }}
          >
            <Grid
              item
              xs={12}
              style={{
                color: ErrorTextColor,

                display: "flex",
                alignItems: "center",
                paddingRight: "15px",
                justifyContent: "flex-end",
                textAlign: "right",
                fontWeight: "normal",
                fontFamily: "sans-serif",
              }}
            >
              <Grid item xs={2}></Grid>
              <Grid
                item
                style={{
                  opacity: 0.5,
                  fontSize: textfont,
                  transform: "scale(0.85)",
                }}
                xs={10}
              >
                <span style={{ textAlign: "right", marginRight: "-20px" }}>
                  Username required
                </span>
              </Grid>
            </Grid>
          </Grid>
        </animated.div>
      ) : (
        <animated.div style={animationModalErrorPasswordLog}>
          <Grid
            container
            style={{
              width: errorwidth,
              height: "34px",

              backgroundImage: ErrorColor,
              margin: "auto",
              borderRadius: "0px",

              display: "flex",
            }}
          >
            {" "}
            <Grid
              item
              xs={12}
              style={{
                color: ErrorTextColor,

                display: "flex",
                alignItems: "center",
                paddingRight: "15px",
                justifyContent: "flex-end",
                textAlign: "right",
                fontWeight: "normal",
                fontFamily: "sans-serif",
              }}
            >
              <Grid item xs={2}></Grid>
              <Grid
                item
                xs={10}
                style={{
                  opacity: 0.5,
                  fontSize: textfont,
                  transform: "scale(0.85)",
                }}
              >
                <span style={{ textAlign: "right", marginRight: "-20px" }}>
                  Password required
                </span>
              </Grid>
            </Grid>
          </Grid>
        </animated.div>
      )}
    </>
  );
}
