import React, { useState, useCallback, useEffect, useRef } from "react";

import Axios from "axios";
import MoreHorizIcon from "@material-ui/icons/MoreHoriz";
/// USE DISPATCH
import { useSelector, useDispatch } from "react-redux";
import Palette, { usePalette } from "react-palette";
import CheckIcon from "@mui/icons-material/Check";
import { Grid } from "@material-ui/core";
import { matchPc, matchTablet } from "../DetectDevice";
import { UpdateColorAction } from "../GlobalActions";
import { useSpring, animated } from "react-spring";

function AboutColorx({ zoomed }: any): JSX.Element {
  const dispatch = useDispatch();
  ///

  const [se, setse] = useState<any>(null);

  var ColorData = [
    "#FF0000",
    "#FFA500",
    "#FFFF00",
    "#DFFF00.",
    "#00FF00.",
    "#00FF7F",
    "#00FFFF",
    "#0080FF",
    "#0000FF",
    "#8F00FF",
    "#FF00FF",
    "#FF007F",
  ];

  /// GET DARKMODE FROM REDUX STORE
  interface RootStateGlobalReducer {
    GlobalReducer: {
      darkmode: boolean;
    };
  }
  const { darkmode } = useSelector((state: RootStateGlobalReducer) => ({
    ...state.GlobalReducer,
  }));

  const darkmodeReducer = darkmode;

  ///
  ///
  /// GET COLOR FROM REDUX STORE
  interface RootStateReducerColor {
    GlobalReducerColor: {
      color: string;
      colordark: string;
      colortype: number;
    };
  }
  const { color, colordark, colortype } = useSelector(
    (state: RootStateReducerColor) => ({
      ...state.GlobalReducerColor,
    })
  );
  const colorReducer = color;
  const colorReducerdark = colordark;
  const colortypeReducer = colortype;

  var showCheck1: any = "hidden";
  var showCheck2: any = "hidden";
  var showCheck3: any = "hidden";
  var showCheck4: any = "hidden";
  var showCheck5: any = "hidden";

  if (colortypeReducer === 0) {
    showCheck1 = "visible";
  } else if (colortypeReducer === 1) {
    showCheck2 = "visible";
  } else if (colortypeReducer === 2) {
    showCheck3 = "visible";
  } else if (colortypeReducer === 3) {
    showCheck4 = "visible";
  } else {
    showCheck5 = "visible";
  }
  ///
  ///
  ///
  /// GET LOGGED USER DATA FROM REDUX STORE
  interface RootStateReducerImage {
    UserdataReducer: {
      image: string;
      id: number;
    };
  }
  const { image, id } = useSelector((state: RootStateReducerImage) => ({
    ...state.UserdataReducer,
  }));
  const idReducer = id;

  const imageReducer = image;

  ///
  ///
  ///
  /// REACT PALLETTE (VIBRANTCOLOR DETECTION)
  ////  const { data, loading, error } = usePalette(`./images/profile/${imageReducer}`);

  const { data } = usePalette(se);

  ///
  ///
  ///
  ///DISPATCH

  ///
  ///
  ///
  ///DOT ENV DATA
  const { REACT_APP_SUPERSTARZ_URL } = process.env;

  var colorboy: any = {
    color1: "",
    color2: "",
    colortype: 0,
    id: idReducer,
  };

  const updateColor = useCallback(
    (type: number) => {
      if (type === 0) {
        colorboy = {
          color1: data.lightVibrant,
          color2: data.darkVibrant,
          colortype: 0,
          id: idReducer,
        };
      } else if (type === 1) {
        colorboy = {
          color1: data.vibrant,
          color2: "",
          colortype: 1,
          id: idReducer,
        };
      } else if (type === 2) {
        colorboy = {
          color1: darkmodeReducer ? data.darkVibrant : data.lightVibrant,
          color2: "",
          colortype: 2,
          id: idReducer,
        };
      } else if (type === 3) {
        colorboy = {
          color1: data.muted,
          color2: "",
          colortype: 3,
          id: idReducer,
        };
      } else {
        colorboy = {
          color1: darkmodeReducer ? data.darkMuted : data.lightMuted,
          color2: "",
          colortype: 4,
          id: idReducer,
        };
      }

      Axios.put(
        `${REACT_APP_SUPERSTARZ_URL}/update_color`,
        { values: colorboy },
        {}
      )
        .then((response) => {
          if (response.data.message === "color updated") {
            dispatch(UpdateColorAction(colorboy));
          }
        })
        .catch(function (error) {
          alert("about color error");
        });
    },
    [REACT_APP_SUPERSTARZ_URL, dispatch, data, colorboy]
  );

  const updatecolor1 = () => {
    updateColor(0);
  };

  const updatecolor2 = () => {
    updateColor(1);
  };

  const updatecolor3 = () => {
    updateColor(2);
  };

  const updatecolor4 = () => {
    updateColor(3);
  };

  const updatecolor5 = () => {
    updateColor(4);
  };

  const initialRawSignValue = {
    inputedEmail: "",
    inputedUsername: "",
    inputedPassword: "",
  };
  const initialErrorSignValue = {
    inputedEmail: 0,
    inputedUsername: 0,
    inputedPassword: 0,
  };

  const [rawSignupValues, setRawSignupValues] = useState(initialRawSignValue);
  const [errorsSignupValues, setErrorsSignupValues] = useState(
    initialErrorSignValue
  );

  const UsernameCheckingTimer = useRef<ReturnType<typeof setTimeout> | null>(
    null
  );

  const clickcolortimer = useRef<ReturnType<typeof setTimeout> | null>(null);

  if (clickcolortimer.current) {
    clearTimeout(clickcolortimer.current);
  }
  clickcolortimer.current = setTimeout(() => {}, 3000);

  const updateTextFeildValues = useCallback(() => {}, [
    REACT_APP_SUPERSTARZ_URL,
    dispatch,
    data,
    colorboy,
  ]);

  return (
    <>
      <Grid
        container
        onTouchStart={handleTouchStartOptions}
        onTouchMove={handleTouchMoveOptions}
        style={{
          zIndex: 1,
          padding: "0px",
          top: matchPc ? (typeTop ? "-2vh" : "0vh") : "0vh",
          position: "relative",
          margin: "0 auto",
          overflow: "hidden",
          left: "0px",
          height: typeTop ? (matchPc ? "24.4vh" : "20.5vh") : "auto",
          paddingBottom: "1.4vh",
        }}
      >
        <animated.div ref={optionsCollectImageRef} style={modalanimation}>
          <Grid
            item
            style={{
              margin: "auto",
              textAlign: "center",
              position: "relative",
              bottom: typeTop ? "-2.6vh" : "0.2em",
              left: typeTop ? "-3px" : matchPc ? "-2px" : "0px",
            }}
          >
            <CircleIcon
              onClick={() => {
                nextSlidePc();
              }}
              className="buttonshake"
              style={{
                fontSize: typeTop ? "1.7vw" : "1.3vw",
                cursor: "pointer",
                color: darkmodeReducer
