import { Grid } from "@material-ui/core";
import MoreHorizIcon from "@material-ui/icons/MoreHoriz";
import { useSelector, useDispatch } from "react-redux";
import { useSpring, animated } from "react-spring";
import Masonry from "@mui/lab/Masonry";
import CircleIcon from "@mui/icons-material/Circle";
import { SuperCrop } from "./SuperCrop";
import MusicNoteIcon from "@mui/icons-material/MusicNote";
import PhotoIcon from "@mui/icons-material/Photo";
import GifIcon from "@mui/icons-material/Gif";
import ChevronRightIcon from "@mui/icons-material/ChevronRight";
import Axios from "axios";
import CropIcon from "@mui/icons-material/Crop";
import CheckIcon from "@mui/icons-material/Check";
import { PreviewCanvasCropAll } from "./PreviewCanvasCropAll";
import { ImageTobeFiltered } from "./ImageTobeFiltered";
import { FilterModeArrow } from "./FilterModeArrow";

function FilterModex({
  filterImage,
  selectedImage,
  itemUploadRef,
  setActivatefilterImage,
  ActivatefilterImage,
}: any): JSX.Element {
  ///
  ///
  ///DARKMODE FROM REDUX
  interface RootStateGlobalReducer {
    GlobalReducer: {
      darkmode: boolean;
    };
  }
  const { darkmode } = useSelector((state: RootStateGlobalReducer) => ({
    ...state.GlobalReducer,
  }));

  const darkmodeReducer = darkmode;

  const originalfilterImageRef = useRef<HTMLDivElement>(null);

  const [ShowHideNegativeValue, setShowHideNegativeValue] = useState("-");

  const [translate, setTranslate] = useState(0);
  const [stalestate] = useState(1);

  const [ActiveSlide, setActiveSlide] = useState(0);

  const [slideGo, setslideGo] = useState<boolean>(true);

  var getSliderWidthNew = 0;

  const [transitionFast, settransitionFast] = useState<boolean>(false);

  const [hideArrow, sethideArrow] = useState<boolean>(false);

  const getCropHeightRef2: any = useRef<HTMLDivElement>(null);

  const getCropHeightRef: any = useRef<HTMLDivElement>(null);

  const getScreenWidthRef: any = useRef<HTMLDivElement>(null);

  const getCropHeightRefFull: any = useRef<HTMLDivElement>(null);

  const getImageWidth = useRef<any>([]);

  const [wideimage, setwideimage] = useState(false);

  const [filterHeight, setfilterHeight] = useState(0);

  const [show, setshow] = useState(false);

  const [filterWidthForSlider, setfilterWidthForSlider] = useState(0);

  const addpostImageRef = (imageRef: any) => {
    if (imageRef && !getImageWidth.current.includes(imageRef)) {
      getImageWidth.current.push(imageRef);
    }
    ////console.log(postItemsRef.current[1]);
  };

  ///
  ///
  ///
  /// ACTIVATES SLIDER ITEM TO BE VIEWED ON CLICK
  const clickOptions = (i: number) => {
    //setTranslate(getImageWidth.current[0].clientWidth * i);
    //setActiveSlide(i);
  };

  ///
  ///
  ///
  ///CREATE OPTIONS SLIDER ANIMATION WITH  REACT SPRING
  const modalanimation = useSpring({
    config: {
      mass: 1,
      tension: 120,
      friction: 14,
    },
    transform: `translateX(${ShowHideNegativeValue}${translate}px)`,
    transition: transitionFast ? "0s" : "0.20s",
    height: "auto",
    display: "flex",
    width: `auto`,
    padding: "0px",
    margin: "auto",
  });

  const [FilterSliderHeight, setFilterSliderHeight] = useState(0);
  const [FilterSliderWidth, setFilterSliderWidth] = useState(0);
  const [FilterSliderHeight2, setFilterSliderHeight2] = useState(0);
  const [FilterSliderContainerWidth, setFilterSliderContainerWidth] =
    useState(0);

  const [screenWidth, setscreenWidth] = useState(0);

  ///
  ///
  ///
  ///GET OPTIONS SLIDER IMAGE WIDTH FROM MATERIAL UI GRID
  useEffect(() => {
    if (getCropHeightRef.current && getCropHeightRef.current.clientWidth) {
      var wi = getCropHeightRef.current.clientWidth;
      setFilterSliderHeight(getCropHeightRef.current.clientHeight);
      setFilterSliderHeight2(getCropHeightRef2.current.clientHeight);
      setFilterSliderWidth(wi);

      setscreenWidth(getScreenWidthRef.current.clientWidth);
    }
  }, []);

  ///
  ///
  ///
  ///GET OPTIONS SLIDER IMAGE WIDTH FROM MATERIAL UI GRID
  useEffect(() => {
    if (getImageWidth.current[0] && show) {
      setFilterSliderContainerWidth(
        selectedImage.length * getImageWidth.current[0].clientWidth
      );
    }
  }, [selectedImage, getImageWidth, show]);
  ///

  const Imageloaded = ({ index }: any) => {};

  ///
  ///
  ///
  ///GET OPTIONS SLIDER IMAGE WIDTH FROM MATERIAL UI GRID
  useEffect(() => {
    if (itemUploadRef.current[0] && ActivatefilterImage) {
      if (itemUploadRef.current[0].width > itemUploadRef.current[0].height) {
        setwideimage(true);
        setshow(true);
      } else {
        setshow(true);
      }
    }
  }, [ActivatefilterImage]);

  ///
  ///
  ///
  ///
  useEffect(() => {
    if (getImageWidth.current[0] && show) {
      setfilterHeight(getImageWidth.current[0].clientHeight);
      setfilterWidthForSlider(getImageWidth.current[0].clientWidth);
    }
  }, [show, getImageWidth]);
  ///

  ///
  ///
  ///
  ///
  useEffect(() => {
    if (screenWidth > FilterSliderContainerWidth) {
      sethideArrow(true);
    }
  }, [screenWidth, FilterSliderContainerWidth]);
  ///

  const clickSlideprev = () => {
    if (translate === 0) {
      var hiddenContentWidth = FilterSliderContainerWidth - screenWidth;
      setTranslate((translate: any) => hiddenContentWidth);
      setActiveSlide((ActiveSlide: any) =>
        Math.ceil(hiddenContentWidth / filterWidthForSlider)
      );
    } else {
      var tt = ActiveSlide - 1;
      setTranslate((translate: any) => filterWidthForSlider * tt);
      setActiveSlide((ActiveSlide: any) => ActiveSlide - 1);
    }
  };

  const clickSlidenext = () => {
    if (slideGo) {
      var tt = ActiveSlide + 1;
      var hiddenContentWidth = FilterSliderContainerWidth - screenWidth;

      if (filterWidthForSlider * tt > hiddenContentWidth) {
        setTranslate((translate: any) => hiddenContentWidth);
        setActiveSlide((ActiveSlide: any) => ActiveSlide + 1);
        setslideGo(false);
      } else {
        setTranslate((translate: any) => filterWidthForSlider * tt);
        setActiveSlide((ActiveSlide: any) => ActiveSlide + 1);
      }
    } else {
      setTranslate((translate: any) => 0);
      setActiveSlide((ActiveSlide: any) => 0);
      setslideGo(true);
    }
  };

  const canvasRef: any = useRef(null);

  const canvasRefdummy: any = useRef(null);

  function blend(
    background: any,
    foreground: any,
    width: any,
    height: any,
    transform: any
  ) {
    var bottom = background.getImageData(0, 0, width, height);
    var top = foreground.getImageData(0, 0, width, height);
    for (var i = 0, size = top.data.length; i < size; i += 4) {
      // red
      top.data[i + 0] = transform(bottom.data[i + 0], top.data[i + 0]);
      // green
      top.data[i + 1] = transform(bottom.data[i + 1], top.data[i + 1]);
      // blue
      top.data[i + 2] = transform(bottom.data[i + 2], top.data[i + 2]);
      // the fourth slot is alpha. We don't need that (so skip by 4)
    }

    return top;
  }

  function FilterGradient(width: any, height: any, type: string) {
    var ctx = canvasRefdummy.current.getContext("2d");
    canvasRefdummy.current.height = height;
    canvasRefdummy.current.width = width;

    if (type === "jentle") {
      var gradient = ctx.createLinearGradient(
        width / 73.6,
        height / 33.4,
        0,
        width / 2,
        height / 2,
        width * 0.6
      );
    } else if (type === "mint") {
      var gradient = ctx.createLinearGradient(
        width / 73.6,
        height / 33.4,
        0,
        width / 2,
        height / 2,
        width * 0.6
      );
    } else if (type === "tentacion") {
      var gradient = ctx.createLinearGradient(
        width / 2,
        height / 2,
        0,
        width / 2,
        height / 2,
        width * 0.3
      );
    } else if (type === "nebula") {
      var gradient = ctx.createRadialGradient(
        width / 2,
        height / 2,
        0,
        width / 2,
        height / 2,
        width * 0.5
      );
    } else if (type === "juice") {
      var gradient = ctx.createRadialGradient(
        width / 2,
        height / 2,
        0,
        width / 2,
        height / 2,
        width * 0.7
      );
    } else if (type === "moonshine") {
      var gradient = ctx.createRadialGradient(
        width / 2,
        height / 2,
        0,
        width / 2,
        height / 2,
        width * 0.7
      );
    } else if (type === "vintage") {
      var gradient = ctx.createRadialGradient(
        width / 2,
        height / 66,
        0,
        width / 2,
        height / 2,
        width * 0.6
      );
    } else if (type === "futurama" || type === "kemi") {
      var gradient = ctx.createLinearGradient(
        width / 73.6,
        height / 33.4,
        0,
        width / 2,
        height / 2,
        width * 0.6
      );
    } else {
    }

    if (type === "jentle") {
      gradient.addColorStop(0, "#2c1f1f");
      gradient.addColorStop(0.4, "#30192c");
      gradient.addColorStop(1, "#272122");
    } else if (type === "mint") {
      gradient.addColorStop(0.25, "#192c26");
      gradient.addColorStop(0.5, "#192421");
      gradient.addColorStop(0.75, "#1f2530");
      gradient.addColorStop(1, "#242c2c");
    } else if (type === "nebula") {
      gradient.addColorStop(0.5, "#231f29");
      gradient.addColorStop(0.7, "#1b1625");
      gradient.addColorStop(1, "#15151d");
    } else if (type === "juice") {
      gradient.addColorStop(0, "#242424");
      gradient.addColorStop(1, "#313131");
    } else if (type === "moonshine") {
      gradient.addColorStop(0, "#0e0e14");
      gradient.addColorStop(1, "#17171f");
    } else if (type === "futurama") {
      gradient.addColorStop(0.25, "  #35272f");
      gradient.addColorStop(0.5, "#3d2a42");
      gradient.addColorStop(0.75, "#2c3022");
      gradient.addColorStop(1, " #293033");
    } else if (type === "kemi") {
      gradient.addColorStop(0, "#122e5a");
      gradient.addColorStop(1, "#382413");
    } else if (type === "tentacion") {
      gradient.addColorStop(0.2, "#241f1b");
      gradient.addColorStop(0.6, "#181010");
      gradient.addColorStop(0.8, "#311c31");
      gradient.addColorStop(1, "#242120");
    } else if (type === "vintage") {
      gradient.addColorStop(0, "#293129");
      gradient.addColorStop(1, "#272c38");
    } else {
    }

    ctx.fillStyle = gradient;

    ctx.fillRect(0, 0, width, height);

    return ctx;
  }

  const ApplyImageFilter = (type: string, index: number) => {
    //////////////////INITIALIZE  CANVAS
    const previewFileReadimage = new Image();
    previewFileReadimage.src = itemUploadRef.current[index].src;
    previewFileReadimage.onload = function () {
      const ctx = canvasRef.current.getContext("2d");
      if (
        previewFileReadimage.naturalWidth > previewFileReadimage.naturalHeight
      ) {
        var widthx = window.innerWidth / 2.2;
        var scalex = widthx / previewFileReadimage.naturalWidth;
        var heightx = previewFileReadimage.naturalHeight * scalex;
        var width = window.innerWidth * 1.3;
        var scale = width / previewFileReadimage.naturalWidth;
        var height = previewFileReadimage.naturalHeight * scale;
      } else {
        var heightx = window.innerHeight / 1.2;
        var scalex = heightx / previewFileReadimage.naturalHeight;
        var widthx = previewFileReadimage.naturalWidth * scalex;
        var height = window.innerHeight * 1.3;
        var scale = height / previewFileReadimage.naturalHeight;
        var width = previewFileReadimage.naturalWidth * scale;
      }

      canvasRef.current.height = height;
      canvasRef.current.width = width;
      //////////////////INITIALIZE  CANVAS

      requestAnimationFrame(() => {
        //////////////////CSS EDIT IMAGE WITH CTX.FILTER
        if (type === "lift") {
          ctx.filter =
            "contrast(1.08) brightness(1.02) blur(0px) saturate(120%)";
        } else if (type === "floyd") {
          ctx.filter = "contrast(1.3) brightness(1.01) saturate(111%)";
        } else if (type === "jentle") {
          ctx.filter =
            "contrast(1.08) brightness(0.94) hue-rotate(20deg) saturate(110%) ";
        } else if (type === "mint") {
          ctx.filter = "contrast(1.3) brightness(1.01)  ";
        } else if (type === "nebula") {
          ctx.filter = "contrast(1.28) brightness(1.14)";
        } else if (type === "juice") {
          ctx.filter =
            "contrast(1.16) brightness(0.99) saturate(180%) blur(0.33px)";
        } else if (type === "futurama") {
          ctx.filter = "contrast(1.2) brightness(0.97) saturate(140%) ";
        } else if (type === "kemi") {
          ctx.filter =
            "contrast(1.15) brightness(1.03) saturate(105%)  hue-rotate(2deg)";
        } else if (type === "tentacion") {
          ctx.filter = "contrast(1.3) brightness(0.98) saturate(120%)";
        } else if (type === "moonshine") {
          ctx.filter =
            "contrast(1.35) brightness(1.08) saturate(45%) blur(1.82px)";
        } else if (type === "vintage") {
          ctx.filter = " contrast(1.05)  brightness(0.95)  saturate(12%)";
        } else {
        }
        //////////////////CSS EDIT IMAGE WITH CTX.FILTER

        ctx.drawImage(
          previewFileReadimage,
          0,
          0,
          canvasRef.current.width,
          canvasRef.current.height
        );

        //////////////////////VIGNETTE
        var w = canvasRef.current.width;
        var h = canvasRef.current.height;
        if (type === "floyd") {
          var gradient = ctx.createRadialGradient(
            w / 2,
            h / 3,
            0,
            w / 2,
            h / 3,
            w * 0.6
          );

          gradient.addColorStop(0, "rgba(255, 255, 255,0.0)");
          gradient.addColorStop(1, "rgba(100,100,100,0.2)");

          ctx.fillStyle = gradient;
          ctx.fillRect(0, 0, width, height);
        } else if (type === "lift" || type === "mint" || type === "juice") {
          var gradient = ctx.createRadialGradient(
            w / 2,
            h / 3,
            0,
            w / 2,
            h / 3,
            w * 0.66
          );

          gradient.addColorStop(0, "rgba(255, 255, 255,0.05)");
          gradient.addColorStop(1, "rgba(255, 255, 255,0.05)");

          ctx.fillStyle = gradient;
          ctx.fillRect(0, 0, width, height);
        } else if (type === "moonshine") {
          var gradient = ctx.createLinearGradient(
            w / 2,
            h / 3,
            0,
            w / 2,
            h / 3,
            w * 0.4
          );

          gradient.addColorStop(0, " rgba(255, 255, 255,0.001)");
          gradient.addColorStop(1, "#403d6149");

          ctx.fillStyle = gradient;
          ctx.fillRect(0, 0, width, height);

          var gradient = ctx.createRadialGradient(
            w / 2,
            h / 2,
            0,
            w / 2,
            h / 2,
            w * 0.3
          );

          gradient.addColorStop(0, "rgba(0,0,0,0.02)");
          gradient.addColorStop(1, " rgba(255, 255, 255,0.001)");

          ctx.fillStyle = gradient;
          ctx.fillRect(0, 0, width, height);
        } else if (type === "vintage") {
          var gradient = ctx.createRadialGradient(
            w / 2,
            h / 2,
            0,
            w / 2,
            h / 2,
            w * 0.6
          );

          gradient.addColorStop(0, "rgba(255, 255, 255,0.005)");
          gradient.addColorStop(1, "rgba(255, 255, 255,0.01)");
          ctx.fillStyle = gradient;
          ctx.fillRect(0, 0, width, height);
        } else if (type === "nebula") {
          var gradient = ctx.createRadialGradient(
            w / 2,
            h / 2,
            0,
            w / 2,
            h / 2,
            w * 0.65
          );

          gradient.addColorStop(0, "rgba(255, 255, 255,0.02)");
          gradient.addColorStop(1, "rgba(0,0,0,0.14)");

          ctx.fillStyle = gradient;
          ctx.fillRect(0, 0, width, height);
        } else if (type === "futurama") {
          var gradient = ctx.createRadialGradient(
            w / 2,
            h / 2,
            0,
            w / 2,
            h / 2,
            w * 0.68
          );

          gradient.addColorStop(0, "rgb(163, 218, 37,0.08)");
          gradient.addColorStop(1, "rgb(27, 194, 236,0.08)");

          ctx.fillStyle = gradient;
          ctx.fillRect(0, 0, width, height);
        } else if (type === "tentacion") {
          var gradient = ctx.createLinearGradient(
            w / 2,
            h / 71,
            0,
            w / 19,
            h / 2,
            w * 0.4
          );

          gradient.addColorStop(0, "rgba(255, 255, 255,0)");
          gradient.addColorStop(1, "#3d3d285a");

          ctx.fillStyle = gradient;
          ctx.fillRect(0, 0, width, height);
        } else {
        }
        //////////////////////VIGNETTE

        ////////////////// /////// GLOBALCOMPOSITEOPERATION
        if (type === "lift") {
          ////////////composition
          ctx.globalCompositeOperation = "overlay";
          ctx.fillStyle = "rgba(100, 100, 100,0.07)";
          ctx.fillRect(0, 0, width, height);
          ////////////composition
        } else if (type === "floyd") {
          ////////////composition
          ctx.globalCompositeOperation = "overlay";
          ctx.fillStyle = "#a58e89cc";
          ctx.fillRect(0, 0, width, height);
          ////////////composition
        } else if (type === "jentle") {
          ////////////composition
          ctx.globalCompositeOperation = "overlay";
          ctx.fillStyle = "#d3b5b5";
          ctx.fillRect(0, 0, width, height);
          ////////////composition
        } else if (type === "mint") {
          ////////////composition
          ctx.globalCompositeOperation = "overlay";
          ctx.fillStyle = "#25302f18";
          ctx.fillRect(0, 0, width, height);
          ////////////composition
        } else if (type === "nebula") {
          ////////////composition
          ctx.globalCompositeOperation = "soft-light";
          ctx.fillStyle = "#6e5a6acc";
          ctx.fillRect(0, 0, width, height);
          ////////////composition
        } else if (type === "moonshine") {
          ////////////composition
          ctx.globalCompositeOperation = "soft-light";
          ctx.fillStyle = "#8b62ac68";
          ctx.fillRect(0, 0, width, height);
          ////////////composition
        } else if (type === "juice") {
          ////////////composition
          ctx.globalCompositeOperation = "overlay";
          ctx.fillStyle = "#5b6c7014";
          ctx.fillRect(0, 0, width, height);
          ////////////composition
        } else if (type === "futurama") {
          ////////////composition
          ctx.globalCompositeOperation = "screen";
          ctx.fillStyle = "#11181f1f";
          ctx.fillRect(0, 0, width, height);
          ////////////composition
        } else if (type === "kemi") {
          ////////////composition
          ctx.globalCompositeOperation = "screen";
          ctx.fillStyle = "#69758113";
          ctx.fillRect(0, 0, width, height);
          ////////////composition
        } else if (type === "tentacion") {
          ////////////composition
          ctx.globalCompositeOperation = "soft-light";
          ctx.fillStyle = "#2c2b2937";
          ctx.fillRect(0, 0, width, height);
          ////////////composition
        } else if (type === "vintage") {
          ////////////composition
          ctx.globalCompositeOperation = "soft-light";
          ctx.fillStyle = "#1011134e";
          ctx.fillRect(0, 0, width, height);
          ////////////composition
        } else {
        }
        ////////////////// /////// GLOBALCOMPOSITEOPERATION

        ////////////////////GRADIENT BLENDING
        if (
          type === "jentle" ||
          type === "mint" ||
          type === "nebula" ||
          type === "juice" ||
          type === "futurama" ||
          type === "kemi" ||
          type === "tentacion" ||
          type === "moonshine" ||
          type === "vintage"
        ) {
          var gradient = FilterGradient(width, height, type);
          var screen = blend(
            ctx,
            gradient,
            width,
            height,
            function (bottomPixel: any, topPixel: any) {
              return 255 - ((255 - topPixel) * (255 - bottomPixel)) / 255;
            }
          );
          ctx.putImageData(screen, 0, 0);
        }
        ////////////////////GRADIENT BLENDING
      });

      canvasRef.current.style.width = `${widthx}px`;
      canvasRef.current.style.height = `${heightx}px`;
    };
  };

  return (
    <>
      <Grid
        container
        style={{
          padding: "0px",
          position: "relative",
          margin: "0 auto",
          overflow: "hidden",
          left: "0px",
          height: "auto",
          paddingBottom: "0px",
        }}
      >
        <Grid
          item
          xs={12}
          style={{
            padding: "0px",
            height: `${FilterSliderHeight2}px`,
          }}
        >
          <button
            onClick={() => {
              ApplyImageFilter("floyd", 2);
            }}
          >
            filter
          </button>
        </Grid>
      </Grid>
      {selectedImage ? (
        <>
          {hideArrow ? null : (
            <FilterModeArrow
              filterHeight={filterHeight}
              clickSlidenext={clickSlidenext}
              clickSlideprev={clickSlideprev}
            />
          )}

          <Grid
            container
            style={{
              zIndex: 1,
              padding: "0px",
              position: "relative",
              margin: "0 auto",
              overflow: "hidden",
              left: "0px",
              height: "auto",
              paddingBottom: "0px",
            }}
          >
            <canvas
              ref={canvasRef}
              style={{
                padding: "0px",
              }}
            />
            <canvas
              ref={canvasRefdummy}
              style={{
                padding: "0px",
                display: "none",
              }}
            />
            <animated.div style={modalanimation}>
              {selectedImage.map((imagee: any, index: any) => {
                return (
                  <div key={index} style={{ padding: "0px" }}>
                    <img
                      ref={addpostImageRef}
                      onClick={() => {
                        clickOptions(index);
                      }}
                      id={itemUploadRef.current[index]}
                      src={
                        itemUploadRef.current[index]
                          ? itemUploadRef.current[index].src
                          : null
                      }
                      style={{
                        width: wideimage ? `${FilterSliderWidth}px` : "auto",
                        height: wideimage ? "auto" : `${FilterSliderHeight}px`,
                        position: "relative",
                        margin: "auto",
                        padding: "0px",
                      }}
                    />
                  </div>
                );
              })}{" "}
            </animated.div>
          </Grid>
        </>
      ) : null}

      <Grid
        container
        style={{ height: "100%", position: "fixed", top: "-800vh" }}
      >
        <Grid
          item
          xs={12}
          style={{
            padding: "0px",
          }}
        >
          <Grid
            item
            ref={getScreenWidthRef}
            xs={12}
            style={{ width: "100%" }}
          ></Grid>
          <Grid
            item
            ref={getCropHeightRef}
            xs={5}
            style={{ height: "81%" }}
          ></Grid>
          <Grid
            item
            ref={getCropHeightRef2}
            xs={5}
            style={{ height: "19%" }}
          ></Grid>
        </Grid>
      </Grid>
    </>
  );
}

export const FilterMode = React.memo(FilterModex);
