import React, { useState, useRef, useEffect, useCallback } from "react";
import { ArrowBillboard } from "./ArrowBillboard";
import { matchPc } from "../DetectDevice";
import { Grid } from "@material-ui/core";
import { animated, useTransition } from "react-spring";
import { useSelector } from "react-redux";
import { DotsBillboard } from "./DotsBillboard";
import { ShowReactions } from "./ShowReactions";
import Masonry from "@mui/lab/Masonry";

function MiniFormReactionx({
  containerHeight,
  blinkenx,
  showCommentLoader,
  Datax,
  zoomedModal,
  wideImage,
  commentDatax,
  callObserver,
  commentData,
  lastItemElement,
  showComment2,
  commentData2,
  showComment3,
  commentData3,
}: any): JSX.Element {
  const { REACT_APP_APPX_STATE } = process.env;

  ///
  ///
  ///
  /// INTERFACE/TYPES FOR SCREENHEIGHT AND DARKMODE
  interface RootStateGlobalReducer {
    GlobalReducer: {
      darkmode: boolean;
      screenHeight: number;
    };
  }

  ///
  ///
  ///
  /// GET SCREENHEIGHT FROM REDUX STORE
  const { screenHeight, darkmode } = useSelector(
    (state: RootStateGlobalReducer) => ({
      ...state.GlobalReducer,
    })
  );
  const screenHeightReducer = screenHeight;
  const darkmodeReducer = darkmode;

  return (
    <Grid
      xs={12}
      item
      className={
        matchPc
          ? darkmodeReducer
            ? "postscroll-dark"
            : "postscroll-light"
          : darkmodeReducer
          ? "postscroll-darkm"
          : "postscroll-lightm"
      }
      style={{
        height: `${containerHeight - 79}px`,
        padding: "0vh",
        position: "relative",
        overflowY: "auto",
        overflowX: `hidden`,
        paddingBottom: "0vh",
      }}
    >
      {commentDatax.length > 0 ? (
        <Masonry
          columns={matchPc ? 2 : 1}
          spacing={1}
          style={{
            padding: "0px",
          }}
        >
          {commentDatax.map((post: any, i: any) => (
            <ShowReactions
              callObserver={callObserver}
              length={commentData.length}
              AddRef={lastItemElement}
              post={post}
              i={i}
              zoomedModal={zoomedModal}
              wideImage={wideImage}
            />
          ))}
        </Masonry>
      ) : null}

      {commentData
        ? commentData.map((post: any, i: any) => (
            <>
              <ShowReactions
                callObserver={callObserver}
                length={commentData.length}
                AddRef={lastItemElement}
                post={post}
                i={i}
                zoomedModal={zoomedModal}
                wideImage={wideImage}
              />
            </>
          ))
        : null}
      {showComment2
        ? commentData2
          ? commentData2.map((post: any, i: any) => (
              <>
                <ShowReactions
                  callObserver={callObserver}
                  length={commentData2.length}
                  AddRef={lastItemElement}
                  post={post}
                  i={i}
                  zoomedModal={zoomedModal}
                  wideImage={wideImage}
                />
              </>
            ))
          : null
        : null}

      {showComment3
        ? commentData3
          ? commentData3.map((post: any, i: any) => (
              <>
                <ShowReactions
                  callObserver={callObserver}
                  length={commentData2.length}
                  AddRef={lastItemElement}
                  post={post}
                  i={i}
                  zoomedModal={zoomedModal}
                  wideImage={wideImage}
                />
              </>
            ))
          : null
        : null}
      <>
        <Grid
          item
          xs={12}
          ref={lastItemElement}
          style={{
            marginTop: "200px",
            height: "10px",
          }}
        ></Grid>
      </>
    </Grid>
  );
}

export const MiniFormReaction = React.memo(MiniFormReactionx);
