  var circleIdentify = typeTop ? 0 : 4;
  var circleIdentify2 = typeTop ? 1 : 5;

  ///
  ///
  ///
  ///OPTIONS SLIDER VARIABLES DISCRIBING ITEMS

  var optionsNameData = typeTop
    ? ["settings", "upload", "filter feeds", "albums", "playlist", "showroom"]
    : ["filter feeds", "albums", "playlist", "showroom", "settings", "upload"];

  ///
  ///
  ///
  ///CREATE OPTIONS SLIDER ANIMATION WITH  REACT SPRING

  const animationop = useSpring({
    config: {
      duration: typeTop ? 100 : 300,
    },
    opacity: typeTop
      ? optinstopshowingReducer
        ? 1
        : 0
      : optinstopshowingReducer
      ? 0
      : 1,
    transform: typeTop
      ? optinstopshowingReducer
        ? `translateY(0%)`
        : "translateY(-100%)"
      : optinstopshowingReducer
      ? `translateY(-100%)`
      : "translateY(0%)",
  });

  ///
  ///
  ///
  ///CREATE OPTIONS SLIDER ANIMATION WITH  REACT SPRING
  const modalanimation = useSpring({
    config: {
      mass: 1,
      tension: 120,
      friction: 14,
    },
    transform: `translateX(${ShowHideNegativeValue}${translate}px)`,
    transition: transitionFast ? "0s" : "0.15s",
    height: "auto",
    display: "flex",
    width: `auto`,
    padding: "0px",
    margin: "auto",
    paddingLeft: typeTop
      ? matchPc
        ? "0px"
        : matchTablet
        ? "22px"
        : "9px"
      : "0px",
  });

  ///
  ///
  ///
  /// ACTIVATES SLIDER ITEM TO BE VIEWED ON CLICK
  const clickOptions = (i: number, type: number) => {
    if (type === 1) {
      setTranslate(stalestate + i);
      setActiveSlide(i);
    } else {
      setTranslate(getSliderWidthNew * i + 1.5 * i);
      setActiveSlide(i);
    }
  };

  ///
  ///
  /// GET COLOR FROM REDUX STORE
  interface RootStateReducerColor {
    GlobalReducerColor: {
      color: string;
      colordark: string;
      colortype: number;
    };
  }
  const { color, colordark, colortype } = useSelector(
    (state: RootStateReducerColor) => ({
      ...state.GlobalReducerColor,
    })
  );
  const colorReducer = color;
  const colorReducerdark = colordark;
  const colortypeReducer = colortype;

  ///
  ///
  ///
  ///PUSH OPTIONS SLIDE A LITTLE BIT ON LAST SLIDE ITEM
  const TouchJoltOnLastNEXT = () => {
    setShowHideNegativeValue("-");
    var i = ActiveSlide;
    var colorPaddingAllowance = 1.67 + i / 10;
    var getSliderWidthNewx = getSliderWidthNew / 2;
    setTranslate(getSliderWidthNew * i + getSliderWidthNewx);
    nextJoltTimer.current = setTimeout(function () {
      setTranslate(getSliderWidthNew * 0 + colorPaddingAllowance * 0);
      setActiveSlide((ActiveSlide) => 0);
    }, 200);
  };

  ///
  ///
  ///
  ///PUSH OPTIONS SLIDE A LITTLE BIT ON FIRST SLIDE ITEM
  const TouchJoltOnLastPREV = () => {
    setShowHideNegativeValue("");
    var i = ActiveSlide;
    var colorPaddingAllowance = 1.67 + i / 10;
    var ix = optionsImages.length - 1;
    var getSliderWidthNewx = getSliderWidthNew / 2;
    setTranslate(getSliderWidthNewx);
    prevJoltTimer.current = setTimeout(function () {
      setShowHideNegativeValue("-");
      setTranslate(getSliderWidthNew * ix + colorPaddingAllowance * ix);
      setActiveSlide((ActiveSlide) => ix);
    }, 200);
  };

  ///
  ///
  ///
  /// NEXT SLIDE  FOR PC
  const nextSlidePc = () => {
    if (typeTop) {
      closeoptionsslide();
    } else {
      var i = ActiveSlide + 1;
      setShowHideNegativeValue("-");
      ///set((state) => (state + 1) % slides.length)
      if (ActiveSlide === optionsImages.length - 1) {
        settransitionFast(true);
        setTranslate(0);
        setActiveSlide(0);
      } else {
        settransitionFast(false);
        setTranslate(stalestate + i);
        setActiveSlide(i);
      }
    }
  };

  ///
  ///
  ///
  /// NEXT SLIDE
  const nextSlide = () => {
    var i = ActiveSlide + 1;
    var colorPaddingAllowance = 1.67 + i / 10;
    ///set((state) => (state + 1) % slides.length)
    if (ActiveSlide === optionsImages.length - 1) {
      settransitionFast(true);
      TouchJoltOnLastNEXT();
    } else {
      if (nextJoltTimer.current) {
        clearTimeout(nextJoltTimer.current);
      }
      setShowHideNegativeValue("-");
      settransitionFast(false);
      setTranslate(getSliderWidthNew * i + colorPaddingAllowance * i);
      setActiveSlide((ActiveSlide) => ActiveSlide + 1);
    }
  };

  ///
  ///
  ///
  /// PREV SLIDE
  const prevSlide = () => {
    ///set((state) => (state + 1) % slides.length)
    var i = ActiveSlide - 1;
    var colorPaddingAllowance = 1.67 + i / 10;
    if (ActiveSlide === 0) {
      settransitionFast(true);
      if (typeTop) {
        closeoptionsslide();
      } else {
        TouchJoltOnLastPREV();
      }
    } else {
      if (prevJoltTimer.current) {
        clearTimeout(prevJoltTimer.current);
      }
      setShowHideNegativeValue("-");
      settransitionFast(false);
      setTranslate(getSliderWidthNew * i + colorPaddingAllowance * i);
      setActiveSlide((ActiveSlide) => ActiveSlide - 1);
    }
  };

  ///
  ///
  ///
  /// HANDLE TOUCH START EVENT
  const handleTouchStartOptions = (e: any) => {
    ////onMouseDown onMouseMove
    ////touchDown = e.clientX
    const touchDown = e.touches[0].clientX;
    setTouchPosition(touchDown);
  };

  ///
  ///
  ///
  /// HANDLE TOUCH MOVE EVENT
  const handleTouchMoveOptions = (e: any) => {
    if (handleTouchMoveTimer.current) {
      clearTimeout(handleTouchMoveTimer.current);
    }
    handleTouchMoveTimer.current = setTimeout(function () {
      const touchDown = touchPosition;

      if (touchDown === null) {
        return;
      }
      ////currentTouch = e.clientX
      const currentTouch = e.touches[0].clientX;
      const diff = touchDown - currentTouch;

      if (diff > 18) {
        nextSlide();
      } else if (diff < -18) {
        prevSlide();
      } else {
      }

      setTouchPosition(null);
      return false;
    }, 50);

    return false;
  };

  const closeoptionsslide = () => {
    sethaltedTop(false);
    dispatch(UpdateOptionsTop(false));
  };

  return (
    <>
      {optionsShow ? (
        <>
          {optinstopshowingReducer ? (
            typeTop ? null : (
              <>
                {" "}
                <Grid
                  container
                  style={{
                    width: matchPc ? "10%" : matchTablet ? "30%" : "10%",
                    margin: "auto",
                  }}
                >
                  <span>
                    <MoreHorizIcon
                      className={
                        darkmodeReducer
                          ? "make-small-icons-clickable-dark dontallowhighlighting  "
                          : "make-small-icons-clickable-light  dontallowhighlighting  "
                      }
                      onClick={closeoptionsslide}
                      style={{
                        fontSize: matchPc ? "2.55vw" : "4.7vh",
                        verticalAlign: "middle",
                        justifyContent: "center",
                        zIndex: 2,
                        marginTop: matchPc ? "9vh" : "6.5vh",
                        position: "absolute",
                      }}
                    />
                  </span>{" "}
                </Grid>
              </>
            )
          ) : null}

          <animated.div style={animationop}>
            <Grid
              container
              className={
                typeTop
                  ? darkmodeReducer
                    ? `optionsTop-background-dark `
                    : `optionsTop-background-light `
                  : ""
              }
              onTouchStart={handleTouchStartOptions}
              onTouchMove={handleTouchMoveOptions}
              style={{
                zIndex: 1,
                padding: "0px",
                top: matchPc ? (typeTop ? "-2vh" : "0vh") : "0vh",
                position: "relative",
                margin: "0 auto",
                overflow: "hidden",
                left: "0px",
                height: typeTop ? (matchPc ? "24.4vh" : "20.5vh") : "auto",
                paddingBottom: "1px",
              }}
            >
              <animated.div ref={optionsCollectImageRef} style={modalanimation}>
                <Grid
                  item
                  style={{
                    margin: "auto",
                    textAlign: "center",
                    position: "relative",
                    bottom: typeTop ? "-1.4em" : "0.2em",
                    left: typeTop ? "-3px" : matchPc ? "-2px" : "0px",
                  }}
                >
                  <CircleIcon
                    onClick={() => {
                      nextSlidePc();
                    }}
                    className="buttonshake"
                    style={{
                      fontSize: typeTop ? "1.7vw" : "1.3vw",
                      cursor: "pointer",
                      color: darkmodeReducer
                        ? typeTop
                          ? "rgba(200, 200, 200, 0.5)"
                          : "rgba(200, 200, 200, 0.1)"
                        : typeTop
                        ? "rgba(005, 005, 005, 0.4)"
                        : "rgba(005, 005, 005, 0.2)",
                      display: matchPc ? "block" : "none",
                    }}
                  />
                </Grid>
                {optionsImages.map((im: any, i: any) => (
                  <Grid key={i} item xs={12}>
                    {typeTop ? (
                      <Grid
                        item
                        xs={12}
                        style={{
                          margin: "auto",
                          textAlign: "center",
                          position: "relative",
                          top: matchPc ? "0.9em" : "0.54em",
                        }}
                      >
                        <Grid
                          item
                          xs={12}
                          style={{
                            fontSize: matchPc ? "1.1vw" : "2.08vh",
                            fontWeight: "bolder",
                            fontFamily: "Arial, Helvetica, sans-serif",
                            visibility:
                              ActiveSlide === i ? "visible" : "hidden",
                            filter: darkmodeReducer
                              ? "drop-shadow(1.2px 0.1px 1.92px rgba(255, 255, 255, 0.4))"
                              : "drop-shadow(1.2px 0.1px 1.92px rgba(41, 53, 70, 8.35))",
                            color: darkmodeReducer ? "#dddddd" : "#0b111b",
                          }}
                        >
                          {" "}
                          {optionsNameData[i]}
                        </Grid>
                      </Grid>
                    ) : null}
                    {i === circleIdentify || i === circleIdentify2 ? (
                      <>
                        {i === circleIdentify ? (
                          <>
                            {" "}
                            <div
                              onClick={() => {
                                clickOptions(i, optionsClickType);
                              }}
                              style={{
                                cursor: ActiveSlide === i ? "pointer" : "alias",
                                width: `${getSliderWidthNew}px`,
                                height: `${getSliderWidthNew}px`,
                                backgroundColor: darkmodeReducer
                                  ? "rgba(010,010,010, 0.68)"
                                  : "rgba(220,220,220, 0.7)",
                                borderRadius: "50%",
                                marginTop: "2.15vh",
                                textAlign: "center",
                                alignItems: "center",
                                display: "grid",

                                justifyContent: "center",
                                boxShadow: darkmodeReducer
                                  ? ActiveSlide === i
                                    ? colortypeReducer === 0
                                      ? `0 0 2.6px ${colorReducerdark}`
                                      : `0 0 2.6px ${colorReducer}`
                                    : typeTop
                                    ? "0 0 5.5px#aaaaaa"
                                    : ""
                                  : ActiveSlide === i
                                  ? `0 0 1.5px ${colorReducer}`
                                  : typeTop
                                  ? "0 0 5.45px#222222"
                                  : ``,
                              }}
                            >
                              {" "}
                              <SettingsIcon
                                style={{
                                  fontSize: matchPc ? "2vw" : "5vh",
                                  color: darkmodeReducer
                                    ? "#eeeeee"
                                    : "#222222",
                                }}
                                className="zuperkinginfo"
                              />
                            </div>
                          </>
                        ) : (
                          <>
                            {" "}
                            <div
                              onClick={() => {
                                clickOptions(i, optionsClickType);
                              }}
                              style={{
                                cursor: ActiveSlide === i ? "pointer" : "alias",
                                width: `${getSliderWidthNew}px`,
                                height: `${getSliderWidthNew}px`,
                                backgroundColor: darkmodeReducer
                                  ? "rgba(010,010,010, 0.68)"
                                  : "rgba(220,220,220, 0.7)",
                                borderRadius: "50%",
                                marginTop: "2.15vh",
                                textAlign: "center",
                                alignItems: "center",
                                display: "grid",
                                justifyContent: "center",
                                boxShadow: darkmodeReducer
                                  ? ActiveSlide === i
                                    ? colortypeReducer === 0
                                      ? `0 0 2.6px ${colorReducerdark}`
                                      : `0 0 2.6px ${colorReducer}`
                                    : typeTop
                                    ? "0 0 5.5px#aaaaaa"
                                    : ""
                                  : ActiveSlide === i
                                  ? `0 0 1.5px ${colorReducer}`
                                  : typeTop
                                  ? "0 0 5.45px#222222"
                                  : ``,
                              }}
                            >
                              {" "}
                              <CircleIcon
                                style={{
                                  fontSize: matchPc ? "1.65vw" : "4.5vh",
                                  color: darkmodeReducer
                                    ? "#eeeeee"
                                    : "#222222",
                                }}
                                className="zuperkinginfo"
                              />
                            </div>
                          </>
                        )}
                      </>
                    ) : (
                      <img
                        alt={` ${optionsNameData[i]}  option`}
                        onClick={() => {
                          clickOptions(i, optionsClickType);
                        }}
                        style={{
                          cursor: ActiveSlide === i ? "pointer" : "alias",
                          width: `${getSliderWidthNew}px`,
                          height: `${getSliderWidthNew}px`,
                          borderRadius: "50%",
                          padding: "0px",
                          objectFit: "cover",
                          marginLeft: "2px",
                          marginTop: "14px",

                          boxShadow: darkmodeReducer
                            ? ActiveSlide === i
                              ? `0 0 3px ${colorReducerdark}`
                              : typeTop
                              ? "0 0 3.5px#aaaaaa"
                              : ""
                            : ActiveSlide === i
                            ? `0 0 4px ${colorReducer}`
                            : typeTop
                            ? `0 0 3.45px#222222`
                            : "",

                          marginBottom: "2.2px",
                        }}
                        src={`./images/options/${im}`}
                      />
                    )}

                    {typeTop ? null : (
                      <Grid
                        item
                        xs={12}
                        style={{
                          margin: "auto",
                          textAlign: "center",
                          position: "relative",
                          bottom:
                            i === circleIdentify || i === circleIdentify2
                              ? matchPc
                                ? "-0.5em"
                                : "-0.3em"
                              : "0.2em",
                        }}
                      >
                        <Grid
                          item
                          xs={12}
                          style={{
                            fontSize: matchPc ? "1.1vw" : "2vh",
                            fontWeight: "bolder",
                            fontFamily: "Arial, Helvetica, sans-serif",
                            visibility:
                              ActiveSlide === i ? "visible" : "hidden",
                            opacity: typeTop
                              ? 0
                              : optinstopshowingReducer
                              ? 0
                              : 1,
                            color: darkmodeReducer ? "#dddddd" : "#0b111b",
                          }}
                        >
                          {" "}
                          {optionsNameData[i]}
                        </Grid>
                      </Grid>
                    )}
                  </Grid>
                ))}
              </animated.div>
            </Grid>
          </animated.div>
        </>
      ) : (
        <>
          <Grid container>
            <Grid
              item
              xs={9}
              style={{
                textAlign: "right",
                marginTop: matchPc ? "6.4vh" : matchTablet ? "8.8vh" : "8.3vh",
              }}
            >
              <span
                style={{
                  padding: "16px",
                  cursor: "pointer",
                }}
              >
                <CircleIcon
                  style={{
                    fontSize: matchPc
                      ? "1.2vw"
                      : matchTablet
                      ? "2.5vh"
                      : "2.3vh",
                    color: darkmodeReducer
                      ? "rgba(200, 200, 200, 0.1)"
                      : "rgba(005, 005, 005, 0.2)",
                  }}
                />
              </span>
            </Grid>
          </Grid>
        </>
      )}
    </>
  );
}

export const OptionsSlider = React.memo(OptionsSliderx);
