import React, { useRef, useState, useCallback, useEffect } from "react";
import { Ispinnerinterface, IFormHolder } from "../log/log-Interfaces";
import Axios from "axios";
import SuperstarzIconLight from "./../images/ssmall.png";
import SuperstarzIconDark from "./../images/sdsmall.png";
import { TextFieldLogin } from "../log/TextFieldLogin";
import { TextFieldSignup } from "../log/TextFieldSignup";
import { ModalFormSignupError } from "../log/ModalFormSignupError";
import { ModalFormLoginError } from "../log/ModalFormLoginError";
import { Button, Grid } from "@material-ui/core";
import { matchPc, matchTablet } from "../DetectDevice";
import { RootStateOrAny, useSelector, useDispatch } from "react-redux";
import { IsLoggedAction } from "../log/actions/IsLoggedAction";
import { UserdataAction } from "../log/actions/UserdataAction";
import { ActivateLoaderAction, HideLoaderAction } from "../GlobalActions";
//////import { useHistory } from "react-router-dom";
import { PasswordCheck } from "../log/PasswordCheck";
import { UpdateColorAction } from "../GlobalActions";
import { UserInfoUpdateAction } from "../log/actions/UserdataAction";
import KeyboardDoubleArrowRightIcon from "@mui/icons-material/KeyboardDoubleArrowRight";
import ChevronRightIcon from "@mui/icons-material/ChevronRight";
import ChevronLeftIcon from "@mui/icons-material/ChevronLeft";
import ReplyAllIcon from "@mui/icons-material/ReplyAll";
import QuickreplyIcon from "@mui/icons-material/Quickreply";
import { ICommentTemplate, IGrid } from "../log/log-Interfaces";
import CircleIcon from "@mui/icons-material/Circle";
import CommentIcon from "@mui/icons-material/Comment";
import { IconButton, InputAdornment, TextField } from "@material-ui/core";
import TextareaAutosize from "@mui/base/TextareaAutosize";
import { DialogContent } from "@material-ui/core";
import { animated } from "react-spring";
import Masonry from "@mui/lab/Masonry";
import AddIcon from "@mui/icons-material/Add";
import PanoramaFishEyeIcon from "@mui/icons-material/PanoramaFishEye";
import HowToRegIcon from "@mui/icons-material/HowToReg";
import DownloadDoneIcon from "@mui/icons-material/DownloadDone";

///Axios.defaults.withCredentials = true;

function ShowReactionsx({
  i,
  zoomedModal,
  wideImage,
  post,
  AddRef,
  length,
  callObserver,
}: any) {
  const { REACT_APP_SUPERSTARZ_URL, REACT_APP_APPX_STATE } = process.env;

  ///
  ///
  ///
  /// INTERFACE/TYPES FOR SCREENHEIGHT AND DARKMODE
  interface RootStateGlobalReducer {
    GlobalReducer: {
      darkmode: boolean;
      screenHeight: number;
    };
  }

  ///
  ///
  ///
  /// GET DARKMODE FROM REDUX STORE
  const { darkmode } = useSelector((state: RootStateGlobalReducer) => ({
    ...state.GlobalReducer,
  }));
  const darkmodeReducer = darkmode;

  ///
  ///
  ///
  /// GET LOGGED USER DATA FROM REDUX STORE
  interface RootStateReducerImage {
    UserdataReducer: {
      image: string;
      imageThumb: string;
      id: number;
    };
  }
  const { image, imageThumb, id } = useSelector(
    (state: RootStateReducerImage) => ({
      ...state.UserdataReducer,
    })
  );
  const imageReducer = image;
  const imageReducerThumb = imageThumb;
  const idReducer = id;

  const [profileImagethumbTop, setprofileImagethumbTop] = useState<number>(0);
  const [profileImagethumbLeft, setprofileImagethumbLeft] = useState<number>(0);

  useEffect(() => {
    if (i + 1 === length) {
      callObserver();
    }
  }, [i]);

  const [clicked, setclicked] = useState<boolean>(false);

  const flipper = () => {
    setclicked(true);

    setTimeout(() => {
      setclicked(false);
    }, 5500);
  };

  var optionsClass = "";
  var fontOptions = "";

  if (matchPc) {
    optionsClass = "post-optionsImagePc";
    fontOptions = "1.2vw";
  } else if (matchTablet) {
    optionsClass = "profile-optionsImageTablet";
    fontOptions = "5rem";
  } else {
    optionsClass = "profile-optionsImageMobile";
    fontOptions = "1.9rem";
  }

  const [Added, setAdded] = useState(0);

  /*///////////////////////////////////////////////////////////////////////////CONNECT CALL BACK END*/
  var vala = {
    myId: idReducer,
    friendId: post.sender,
  };

  const callAddfav = () => {
    setAdded(100);
    Axios.post(`${REACT_APP_SUPERSTARZ_URL}/add_fav`, { values: vala }, {})
      .then((response) => {
        if (response.data.message === "added") {
          setTimeout(() => {
            setAdded(1);
          }, 2000);
        }
      })
      .catch(function (error) {
        alert("post fav error");
      });
  };

  var valax = {
    myId: idReducer,
    friendId: post.sender,
  };

  const callDelfav = () => {
    setAdded(100);
    Axios.post(`${REACT_APP_SUPERSTARZ_URL}/remove_fav`, { values: valax })
      .then((response) => {
        if (response.data.message === "removed") {
          setTimeout(() => {
            setAdded(0);
          }, 2000);
        }
      })
      .catch(function (error) {
        alert("post fav error");
      });
  };

  /*///////////////////////////////////////////////////////////////////////////CONNECT CALL BACK END*/

  return (
    <>
      {clicked ? (
        <>
          {" "}
          <span style={{ textAlign: "center", fontSize: "1.7vh" }}>
            <Grid
              className={`  ${optionsClass}   `}
              style={{
                backgroundColor: `${post.color1}`,
                zIndex: 7,
                left: `-1vw`,
                top: `3.2vh`,
                opacity: 0.8,
                position: "relative",
              }}
            >
              {Added === 0 ? (
                <>
                  {" "}
                  <AddIcon
                    style={{
                      fontSize: fontOptions,
                      color: "#ffffff",
                    }}
                    className="zuperkinginfo "
                  />{" "}
                </>
              ) : Added === 100 ? (
                <>
                  {" "}
                  <PanoramaFishEyeIcon
                    style={{
                      fontSize: fontOptions,
                      color: "#ffffff",
                    }}
                    className="zuperkinginfo  changeOpacity"
                  />{" "}
                </>
              ) : (
                <>
                  {" "}
                  <DownloadDoneIcon
                    style={{
                      fontSize: fontOptions,
                      color: "#ffffff",
                    }}
                    className="zuperkinginfo"
                  />{" "}
                </>
              )}
            </Grid>
            <img
              onClick={() => {
                if (Added) {
                  callDelfav();
                } else {
                  callAddfav();
                }
              }}
              title={post.sender}
              className={
                darkmodeReducer
                  ? `turprofileDark image-gray-on-click `
                  : ` turprofileLight image-gray-on-click`
              }
              style={{
                display: "block",
                zIndex: 2,
                borderRadius: "50%",
                cursor: "pointer",
                position: "relative",
                padding: "10px",
                margin: "auto",
                objectFit: "contain",
                width: "70%",
              }}
              src={`${post.profile_image}`}
              alt="Superstarz Billboard "
            />
            <span
              className=""
              style={{
                display: "block",
                marginTop: "2px",
                fontWeight: "bold",
                fontFamily: "Arial, Helvetica, sans-seri",
                color: darkmodeReducer ? "#ffffff" : "#000000",
              }}
            >
              {post.username}
            </span>
            <div style={{ height: "10px" }}></div>
          </span>
        </>
      ) : (
        <>
          {" "}
          <span
            style={{
              textAlign: "left",
              marginTop: "5px",
              fontSize: "1.7vh",
            }}
          >
            <img
              title={post.username}
              onClick={() => {
                flipper();
              }}
              className={
                darkmodeReducer
                  ? `turprofileDark image-gray-on-click `
                  : ` turprofileLight image-gray-on-click`
              }
              style={{
                display: "block",
                zIndex: 2,
                borderRadius: "0%",
                cursor: "pointer",
                position: "relative",
                padding: "0px",
                margin: "auto",
                objectFit: "contain",
                width: "100%",
              }}
              src={`${post.profile_image}`}
              alt="Superstarz Billboard "
            />

            <span
              className=""
              style={{
                paddingLeft: "15px",
                display: "block",
                marginTop: "2px",
                fontWeight: "normal",
                fontFamily: "Arial, Helvetica, sans-seri",
                color: darkmodeReducer ? "#ffffff" : "#000000",
              }}
            >
              {post.username}
            </span>
          </span>
        </>
      )}
    </>
    /*MOBILE  MOBILE  MOBILE  MOBILE  SIGNUP MOBILE  MOBILEMOBILE  MOBILE  MOBILE  MOBILE  MOBILE  MOBILE  MOBILEMOBILE  MOBILE  MOBILE  MOBILE  MOBILE  MOBILE  MOBILEMOBILE  MOBILE  MOBILE  MOBILE  MOBILE  MOBILE  MOBILE  MOBILE*/
  );
}

export const ShowReactions = React.memo(ShowReactionsx);
