import React, { useState, useEffect, useRef } from "react";

import Arrow from "./Arrow";
import Dots from "./Dots";
import { Paper, Grid } from "@material-ui/core";
import { useSpring, animated, useTransition } from "react-spring";

const Slider = ({ slides }: any) => {
  const [index, set] = useState(0);
  const getWidth = () => window.innerWidth;
  ///var newGetWidth = getWidth() * slides.length;

  const SlideimageRef = useRef<HTMLImageElement>(null);
  const [height, setheight] = useState(0);

  const transitions = useTransition(index, {
    key: index,
    from: { opacity: 0 },
    enter: { opacity: 1 },
    leave: { opacity: 0 },
    config: { duration: 1000 },
  });

  /**  useEffect(() => {
    const t = setInterval(
      () => set((state) => (state + 0) % slides.length),
      2000
    );
    return () => clearTimeout(t);
  }, []);    */

  const nextSlide = () => {
    set((state) => (state + 1) % slides.length);
  };

  const prevSlide = () => {
    if (index === 0) {
      set(slides.length - 1);
    } else {
      set(index - 1);
    }
  };

  const slideImageOnLoad = () => {
    if (SlideimageRef.current && SlideimageRef.current.clientHeight) {
      setheight(SlideimageRef.current.clientHeight);
    }
  };

  return (
    <Grid item xs={12}>
      <slideNumber height={height} />
      {transitions((style, i) => (
        <>
          <animated.img
            ref={SlideimageRef}
            onLoad={slideImageOnLoad}
            style={{
              ...style,

              height: "auto",
              width: "100%",
              objectFit: "contain",
              position: "absolute",
              top: "0",
              left: "0",
            }}
            src={`./images/thumbs/${slides[i]}`}
          />

          <animated.img
            onLoad={slideImageOnLoad}
            style={{
              ...style,
              height: "auto",
              width: "100%",
              objectFit: "contain",
              position: "absolute",
              top: "0",
              left: "0",
              zIndex: 1,
            }}
            src={`./images/${slides[i]}`}
          />
        </>
      ))}{" "}
      <Arrow
        direction="left"
        clickSlidep={prevSlide}
        clickSliden={nextSlide}
        height={height}
      />
      <Dots slides={slides} activeSlide={index} />
    </Grid>
  );
};

export default Slider;
