import React, { useRef, useState, useEffect } from "react";
import { matchPc, matchTablet } from "../DetectDevice";
import { Grid } from "@material-ui/core";
import MoreHorizIcon from "@material-ui/icons/MoreHoriz";
import { useSelector, useDispatch } from "react-redux";
import { useSpring, animated } from "react-spring";
import Masonry from "@mui/lab/Masonry";
import CircleIcon from "@mui/icons-material/Circle";
import Cropper from "react-easy-crop";
import MusicNoteIcon from "@mui/icons-material/MusicNote";
import PhotoIcon from "@mui/icons-material/Photo";
import GifIcon from "@mui/icons-material/Gif";
import ChevronRightIcon from "@mui/icons-material/ChevronRight";
import Slider from "@mui/material/Slider";
import { AnySoaRecord } from "dns";
import { PreviewCanvas } from "./PreviewCanvas";

function SuperCropx({ cropimage, selectedImage }: any): JSX.Element {
  const cropCanvasRef: any = useRef(null);

  ///
  ///
  ///
  ///
  const [previmage, setprevimage] = useState<Array<any>>([]);
  const [image, setimage] = useState<Array<any>>([]);
  const [imageWidth, setimageWidth] = useState<Array<any>>([]);
  const [imageHeight, setimageHeight] = useState<Array<any>>([]);

  ////
  ////
  ////
  const [cropArea, setcropArea] = useState<any>(null);
  const [crop, setcrop] = useState<any>({ x: 0, y: 0 });
  const [zoom, setzoom] = useState(1);

  const onCropComplete = (
    croppedAreaPercentage: any,
    croppedAreaPixels: any
  ) => {
    setcropArea(croppedAreaPixels);
    console.log(croppedAreaPercentage);
  };

  ///
  ///
  ///DARKMODE FROM REDUX
  interface RootStateGlobalReducer {
    GlobalReducer: {
      darkmode: boolean;
    };
  }
  const { darkmode } = useSelector((state: RootStateGlobalReducer) => ({
    ...state.GlobalReducer,
  }));

  const darkmodeReducer = darkmode;

  const getSliderWidthRef = useRef<HTMLDivElement>(null);
  const [getSliderWidth, setgetSliderWidth] = useState(0);

  const [CropImageHolder, setCropImageHolder] = useState<any>(null);

  const [OriginalImageWidth, setOriginalImageWidth] = useState<number>(0);
  const [OriginalImageHeight, setOriginalImageHeight] = useState<number>(0);
  ///
  ///
  ///
  ///GET OPTIONS SLIDER IMAGE WIDTH FROM MATERIAL UI GRID
  useEffect(() => {
    if (getSliderWidthRef.current && getSliderWidthRef.current.clientWidth) {
      setgetSliderWidth(getSliderWidthRef.current.clientWidth);
    }
  }, []);
  ///

  useEffect(() => {
    const Newcropimage = new Image();
    Newcropimage.src = cropimage;
    (Newcropimage.onload = function () {
      setCropImageHolder(Newcropimage);
      setOriginalImageWidth(Newcropimage.naturalWidth);
      setOriginalImageHeight(Newcropimage.naturalHeight);
    }),
      [cropimage];
  });

  useEffect(() => {
    if (CropImageHolder && cropCanvasRef.current) {
      const ctx = cropCanvasRef.current.getContext("2d");

      ctx.drawImage(
        CropImageHolder,
        0,
        0,
        imageW,
        imageH,
        crop.x,
        crop.y,
        imageW * zoom,
        imageH * zoom
      );

      canvasRef.current.style.width = `${getSliderWidth}px`;
      canvasRef.current.style.height = `${imageHeight}px`;
    }
  }, [CropImageHolder, canvasRef, getSliderWidth, imageHeight, crop]);

  return (
    <>
      <Grid
        container
        style={{ position: "relative", width: "100%", height: "70%" }}
      >
        <Grid item xs={12}>
          <canvas
            className={
              darkmodeReducer ? "turlightpostdarkx" : "turlightpostlightx"
            }
            ref={cropCanvasRef}
            key={cropimage}
            style={{
              padding: "0px",
            }}
          />
        </Grid>
      </Grid>

      <Grid
        item
        xs={12}
        style={{
          padding: "0px",
          backgroundColor: "#00ccff",
        }}
      >
        <Grid
          item
          ref={getSliderWidthRef}
          xs={4}
          style={{
            padding: "0px",
          }}
        ></Grid>
      </Grid>
      <Masonry columns={matchPc ? 3 : 1} spacing={0}>
        {selectedImage
          ? selectedImage.map((photo: any, index: any) => {
              return (
                <div key={index}>
                  <PreviewCanvas
                    crop={crop}
                    zoom={zoom}
                    index={index}
                    image={photo}
                    getSliderWidth={getSliderWidth}
                  />
                </div>
              );
            })
          : null}{" "}
      </Masonry>
    </>
  );
}

export const SuperCrop = React.memo(SuperCropx);
