import React, { useRef, useEffect, useState, useCallback } from "react";
import {
  IconButton,
  InputAdornment,
  Grid,
  DialogContent,
  Paper,
  TextField,
  Button,
} from "@material-ui/core";

import { IDialogeDataMobile, IGrid } from "./appFolder-Interfaces";
import { useSpring, animated } from "react-spring";
import ModalFormSignupError from "./ModalFormSignupError";
import ModalFormLoginError from "./ModalFormLoginError";
import ServerError from "./ServerError";
import VisibilityOffIcon from "@material-ui/icons/VisibilityOff";
import VisibilityIcon from "@material-ui/icons/Visibility";
import SuperstarzIconLight from "./../images/ssmall.png";
import SuperstarzIconDark from "./../images/sdsmall.png";
import Axios from "axios";
import { useHistory } from "react-router-dom";
import image1 from "./images/modalpic1.jpg";
import image2 from "./images/modalpic2.jpg";
import image3 from "./images/modalpic3.png";
import image4 from "./images/modalpic4.jpg";
import image5 from "./images/modalpic5.jpg";
import image6 from "./images/modalpic6.jpg";
import TextFieldLogin from "./TextFieldLogin";
import TextFieldSignup from "./TextFieldSignup";
import withClasses from "./modalHOC";

function MobileModalLogx(DialogeDataMobile: IDialogeDataMobile) {
  const [showimage, setShowimage] = useState<string>(" ");
  const ModalSlidingImageRef = useRef<HTMLImageElement>(null);

  ///
  ///
  ///
  /// DYNAMIC MODAL LAYOUT VARIABLES
  ///WITHHOLDER JUST A PLACEHOLDER FOR PC MODAL LOG
  var WidthHolder: string = "100%";
  var mobilemodalrad = "4px";
  DialogeDataMobile.mobileZoom
    ? (mobilemodalrad = "0px")
    : (mobilemodalrad = "4px");
  ///
  ///
  ///
  /// DYNAMIC MODAL LAYOUT VARIABLES

  ///
  ///
  ///
  ///CREATE A SLIDE UP ANIMATION WITH  REACT SPRING
  const mobileLogmodalanimation: any = useSpring({
    config: {
      duration: 115,
    },
    opacity: DialogeDataMobile.mobileZoom ? 1 : 0.98,
    position: DialogeDataMobile.mobileZoom ? `static` : `fixed`,
    top: "27vh",
    zindex: 100,
    transform: DialogeDataMobile.mobileZoom
      ? `translateY(0%)`
      : `translateY(-10%)`,
  });

  ///
  ///
  ///SUPERSTARZ ICON SELECT
  var SuperIcon = "";
  DialogeDataMobile.darkmode
    ? (SuperIcon = SuperstarzIconDark)
    : (SuperIcon = SuperstarzIconLight);

  return (
    <DialogContent
      className="fadermodal FormDialog-container-mobile dontallowhighlighting"
      style={{
        overflow: "auto",
        cursor: "pointer",
        padding: "0px",
        zIndex: 100,
      }}
    >
      <ServerError
        device="mobile"
        serverEmojiplain={serverEmojiplain}
        setServerErrorData={setServerErrorData}
        serverErrorDisplay={serverErrorDisplay}
        serverErrorData={serverErrorData}
        darkmode={DialogeDataMobile.darkmode}
      />
      <animated.div style={DialogeDataMobile.modalanimation}>
        <Paper
          className={DialogeDataMobile.PaperStyle}
          style={{
            cursor: "default",
          }}
        >
          <Grid container>
            <Grid item xs={12} style={{ marginTop: "-2.5px" }}>
              <img
                onClick={() =>
                  DialogeDataMobile.setMobileZoom(!DialogeDataMobile.mobileZoom)
                }
                onLoad={onimageload}
                src={showimage}
                className="modalMobileImageStyle"
                alt="Log Image"
              />
            </Grid>
            <Grid
              item
              xs={12}
              className={
                DialogeDataMobile.mobileZoom ? "zoomMobile" : "smallMobile"
              }
            >
              {" "}
              <animated.div style={mobileLogmodalanimation}>
                <Paper
                  style={{
                    backgroundImage: DialogeDataMobile.PaperStyle,
                    cursor: "default",
                    height: "99vh",
                    borderRadius: "0px",
                    marginTop: "-1.9px",
                    borderTopRightRadius: mobilemodalrad,
                    borderTopLeftRadius: mobilemodalrad,
                  }}
                >
                  <Grid xs={12} item className="formholder">
                    {DialogeDataMobile.formtype ? (
                      <Grid
                        item
                        xs={12}
                        className=""
                        style={{ marginTop: "-1.5px" }}
                      >
                        <ModalFormLoginError
                          type={true}
                          device="mobile"
                          ErrorDisplay={errorsLoginValues.inputedUsername}
                          darkmode={DialogeDataMobile.darkmode}
                          WidthHolder={WidthHolder}
                        />

                        <TextFieldLogin
                          updateLoginvalues={updateLoginvalues}
                          rawLoginValues={rawLoginValues}
                          ShowLoginPasswordForaWhile={
                            ShowLoginPasswordForaWhile
                          }
                          loginShowPassword={loginShowPassword}
                          size="small"
                          passwordType={false}
                        />
                        <TextFieldLogin
                          updateLoginvalues={updateLoginvalues}
                          rawLoginValues={rawLoginValues}
                          ShowLoginPasswordForaWhile={
                            ShowLoginPasswordForaWhile
                          }
                          loginShowPassword={loginShowPassword}
                          size="small"
                          passwordType={true}
                        />
                        <ModalFormLoginError
                          type={false}
                          ErrorDisplay={errorsLoginValues.inputedPassword}
                          device="mobile"
                          darkmode={DialogeDataMobile.darkmode}
                          WidthHolder={WidthHolder}
                        />
                        <Grid
                          container
                          style={{ marginTop: "40px", zIndex: 2 }}
                        >
                          <Grid item xs={4}></Grid>
                          <Grid item className="buttonpad buttonshake" xs={4}>
                            <Button
                              onClick={logmein}
                              style={DialogeDataMobile.loginstyle}
                              fullWidth={true}
                              variant="outlined"
                              size="large"
                              color="primary"
                            >
                              Log In
                            </Button>
                          </Grid>{" "}
                        </Grid>
                      </Grid>
                    ) : (
                      <Grid
                        item
                        xs={12}
                        className=""
                        style={{ marginTop: "-1.5px" }}
                      >
                        <ModalFormSignupError
                          device="mobile"
                          ErrorType={0}
                          textField="email"
                          errorFormChecking={errorFormChecking.inputedEmail}
                          WidthHolder={WidthHolder}
                          darkmode={DialogeDataMobile.darkmode}
                          type={true}
                          cantPassBadEmail={cantPassBadEmail}
                          ErrorDisplay={emailErrorDisplay}
                          ErrorData={emailErrorData}
                        />

                        <TextFieldSignup
                          updateSignvalues={updateSignvalues}
                          rawSignupValues={rawSignupValues}
                          ShowSignupPasswordForaWhile={
                            ShowSignupPasswordForaWhile
                          }
                          signupShowPassword={signupShowPassword}
                          size="small"
                          emailType={true}
                          passwordType={false}
                        />

                        <ModalFormSignupError
                          device="mobile"
                          ErrorType={errorsSignupValues.inputedUsername}
                          textField="username"
                          errorFormChecking={errorFormChecking.inputedUsername}
                          WidthHolder={WidthHolder}
                          darkmode={DialogeDataMobile.darkmode}
                          type={true}
                          cantPassBadEmail={cantPassBadEmail}
                          ErrorDisplay={usernameErrorDisplay}
                          ErrorData={usernameErrorData}
                        />

                        <TextFieldSignup
                          updateSignvalues={updateSignvalues}
                          rawSignupValues={rawSignupValues}
                          ShowSignupPasswordForaWhile={
                            ShowSignupPasswordForaWhile
                          }
                          signupShowPassword={signupShowPassword}
                          size="small"
                          emailType={false}
                          passwordType={false}
                        />

                        <TextFieldSignup
                          updateSignvalues={updateSignvalues}
                          rawSignupValues={rawSignupValues}
                          ShowSignupPasswordForaWhile={
                            ShowSignupPasswordForaWhile
                          }
                          signupShowPassword={signupShowPassword}
                          size="small"
                          emailType={false}
                          passwordType={true}
                        />

                        <ModalFormSignupError
                          device="mobile"
                          ErrorType={0}
                          textField="password"
                          errorFormChecking={errorFormChecking.inputedPassword}
                          darkmode={DialogeDataMobile.darkmode}
                          WidthHolder={WidthHolder}
                          type={false}
                          cantPassBadEmail={cantPassBadEmail}
                          ErrorDisplay={passwordErrorDisplay}
                          ErrorData={passwordErrorData}
                        />

                        <Grid
                          container
                          style={{ marginTop: "40px", zIndex: 2 }}
                          className="modal-hold-signup"
                        >
                          <Grid item xs={1}></Grid>
                          <Grid item className="buttonpad buttonshake" xs={4}>
                            <Button
                              style={DialogeDataMobile.signupstyle}
                              onClick={signmeup}
                              fullWidth={true}
                              variant="contained"
                              size="large"
                              color="secondary"
                            >
                              Sign Up
                            </Button>
                          </Grid>{" "}
                        </Grid>
                      </Grid>
                    )}
                  </Grid>
                </Paper>
              </animated.div>
            </Grid>
          </Grid>
        </Paper>
      </animated.div>
    </DialogContent>
  );
}

const enhancedMobileModalLogx = withClasses(MobileModalLogx, "myClassName");
export const MobileModalLog = React.memo(enhancedMobileModalLogx);
