import React, { useState } from "react";

import Slide from "./Slide";
import Arrow from "./Arrow";
import Dots from "./Dots";
import { useSpring, animated, useTransition } from "react-spring";

const Slider = ({ slides }: any) => {
  const getWidth = () => window.innerWidth;
  var newGetWidth = getWidth() * slides.length;

  const [slideState, setSlideState] = useState({
    activeSlide: 0,
    translate: 0,
    transition: 0.45,
  });

  const { translate, transition, activeSlide } = slideState;

  const [index, setIndex] = useState(0);

  const nextSlide = () => {
    setIndex(index + 1);
    if (activeSlide === slides.length - 1) {
      return setSlideState({
        ...slideState,
        translate: 0,
        activeSlide: 0,
      });
    }

    setSlideState({
      ...slideState,
      activeSlide: activeSlide + 1,
      translate: (activeSlide + 1) * getWidth(),
    });
  };

  const prevSlide = () => {
    if (activeSlide === 0) {
      return setSlideState({
        ...slideState,
        translate: (slides.length - 1) * getWidth(),
        activeSlide: slides.length - 1,
      });
    }

    setSlideState({
      ...slideState,
      activeSlide: activeSlide - 1,
      translate: (activeSlide - 1) * getWidth(),
    });
  };

  const transitions = useTransition(slides[index], (item) => item.id, {
    from: { opacity: 0 },
    enter: { opacity: 1 },
    leave: { opacity: 0 },
    config: { tension: 220, friction: 120 }, /// config: config.molasses,
    ///onRest: () => setItems([]),
  });

  return (
    <div
      style={{
        position: "relative",
        width: "100vw",
        height: "auto",
        margin: "0 auto",
        overflow: "hidden",
      }}
    >
      {slides.map((slide: any, i: any) =>
        transitions(({}, slide: any, i: any) => (
          <animated.div key={i}>
            <Slide key={i} content={slide} />
          </animated.div>
        ))
      )}

      <Arrow direction="left" clickSlide={prevSlide} />

      <Arrow direction="right" clickSlide={nextSlide} />

      <Dots slides={slides} activeSlide={activeSlide} />
    </div>
  );
};

export default Slider;
