import React, { useState, useCallback, useEffect, useRef } from "react";

import Axios from "axios";
import MoreHorizIcon from "@material-ui/icons/MoreHoriz";
/// USE DISPATCH
import { useSelector, useDispatch } from "react-redux";
import Palette, { usePalette } from "react-palette";
import CheckIcon from "@mui/icons-material/Check";
import { Grid } from "@material-ui/core";
import { matchPc, matchTablet } from "../DetectDevice";
import { UpdateColorAction } from "../GlobalActions";

function AboutColorx({ zoomed, sex, reft }: any): JSX.Element {
  const xx: any = useRef(null);
  const dispatch = useDispatch();
  ///

  const [se, setse] = useState<any>(null);

  /// GET DARKMODE FROM REDUX STORE
  interface RootStateGlobalReducer {
    GlobalReducer: {
      darkmode: boolean;
    };
  }
  const { darkmode } = useSelector((state: RootStateGlobalReducer) => ({
    ...state.GlobalReducer,
  }));

  const darkmodeReducer = darkmode;

  ///
  ///
  /// GET COLOR FROM REDUX STORE
  interface RootStateReducerColor {
    GlobalReducerColor: {
      color: string;
      colordark: string;
      colortype: number;
    };
  }
  const { color, colordark, colortype } = useSelector(
    (state: RootStateReducerColor) => ({
      ...state.GlobalReducerColor,
    })
  );
  const colorReducer = color;
  const colorReducerdark = colordark;
  const colortypeReducer = colortype;

  var showCheck1: any = "hidden";
  var showCheck2: any = "hidden";
  var showCheck3: any = "hidden";
  var showCheck4: any = "hidden";
  var showCheck5: any = "hidden";

  if (colortypeReducer === 0) {
    showCheck1 = "visible";
  } else if (colortypeReducer === 1) {
    showCheck2 = "visible";
  } else if (colortypeReducer === 2) {
    showCheck3 = "visible";
  } else if (colortypeReducer === 3) {
    showCheck4 = "visible";
  } else {
    showCheck5 = "visible";
  }
  ///
  ///
  ///
  /// GET LOGGED USER DATA FROM REDUX STORE
  interface RootStateReducerImage {
    UserdataReducer: {
      image: string;
      id: number;
    };
  }
  const { image, id } = useSelector((state: RootStateReducerImage) => ({
    ...state.UserdataReducer,
  }));
  const idReducer = id;

  const imageReducer = image;

  useEffect(() => {
    hhd();
  }, []);

  function blobToBase64(blob: any) {
    return new Promise((resolve, _) => {
      const reader = new FileReader();
      reader.onloadend = () => resolve(reader.result);
      reader.readAsDataURL(blob);
    });
  }

  const hhd = async () => {
    const res = await fetch(xx.current);
    const datax = await res.blob();

    var bx = await blobToBase64(datax);
    setse(datax);
  };

  const ggg = async () => {
    var ctx = xx.current.getContext("2d");

    const previewFileReadimage: any = new Image();

    try {
      const res = await fetch(reft.current.src);
      const datax = await res.blob();

      try {
        const datax = await res.blob();

        try {
          var bx = await blobToBase64(datax);
        } catch (err) {
          alert("hhx");
        }
      } catch (err) {
        alert("hhz");
      }
    } catch (err) {
      alert("hhk");
    }

    previewFileReadimage.src = bx;
    previewFileReadimage.origin = "anonymous";

    previewFileReadimage.onload = function () {
      ///
      ///

      ctx.drawImage(previewFileReadimage, 0, 0);

      var ratio = 4;

      let data, length;

      var w = xx.current.width;
      var h = xx.current.height;

      let i = -4,
        count = 0;

      data = ctx.getImageData(0, 0, w, h);
      length = data.data.length;

      try {
        data = ctx.getImageData(0, 0, w, h);
        length = data.data.length;
      } catch {
        alert(length);
      }

      let R, G, B;

      R = G = B = 0;

      while ((i += ratio * 4) < length) {
        ++count;

        R += data.data[i];
        G += data.data[i + 1];
        B += data.data[i + 2];
      }

      R = ~~(R / count);
      G = ~~(G / count);
      B = ~~(B / count);

      alert(R);
      alert(G);
      alert(B);

      ///use this
    };
  };

  useEffect(() => {
    ggg();
  }, []);

  ///
  ///
  ///
  /// REACT PALLETTE (VIBRANTCOLOR DETECTION)
  ////  const { data, loading, error } = usePalette(`./images/profile/${imageReducer}`);

  const { data } = usePalette(se);

  ///
  ///
  ///
  ///DISPATCH

  ///
  ///
  ///
  ///DOT ENV DATA
  const { REACT_APP_SUPERSTARZ_URL } = process.env;

  var colorboy: any = {
    color1: "",
    color2: "",
    colortype: 0,
    id: idReducer,
  };

  const updateColor = useCallback(
    (type: number) => {
      if (type === 0) {
        colorboy = {
          color1: data.lightVibrant,
          color2: data.darkVibrant,
          colortype: 0,
          id: idReducer,
        };
      } else if (type === 1) {
        colorboy = {
          color1: data.vibrant,
          color2: "",
          colortype: 1,
          id: idReducer,
        };
      } else if (type === 2) {
        colorboy = {
          color1: darkmodeReducer ? data.darkVibrant : data.lightVibrant,
          color2: "",
          colortype: 2,
          id: idReducer,
        };
      } else if (type === 3) {
        colorboy = {
          color1: data.muted,
          color2: "",
          colortype: 3,
          id: idReducer,
        };
      } else {
        colorboy = {
          color1: darkmodeReducer ? data.darkMuted : data.lightMuted,
          color2: "",
          colortype: 4,
          id: idReducer,
        };
      }

      Axios.put(
        `${REACT_APP_SUPERSTARZ_URL}/update_color`,
        { values: colorboy },
        {}
      )
        .then((response) => {
          if (response.data.message === "color updated") {
            dispatch(UpdateColorAction(colorboy));
          }
        })
        .catch(function (error) {
          alert("about color error");
        });
    },
    [REACT_APP_SUPERSTARZ_URL, dispatch, data, colorboy]
  );

  const updatecolor1 = () => {
    updateColor(0);
  };

  const updatecolor2 = () => {
    updateColor(1);
  };

  const updatecolor3 = () => {
    updateColor(2);
  };

  const updatecolor4 = () => {
    updateColor(3);
  };

  const updatecolor5 = () => {
    updateColor(4);
