import React, { useRef, useState } from "react";

function ImageTobeFilteredx({
  filterImage,
  index,
  itemUploadRef,
  setActivatefilterImage,
}: any): JSX.Element {
  ///
  ///
  ///
  const filterbaseimageRef: any = useRef(null);

  const canvasRef: any = useRef(null);

  const canvasRefdummy: any = useRef(null);

  const closegate = () => {
    if (index === 0) {
      ////
      ///addfilter();
    } else {
      setActivatefilterImage(false);
    }
  };

  function blend(
    background: any,
    foreground: any,
    width: any,
    height: any,
    transform: any
  ) {
    var bottom = background.getImageData(0, 0, width, height);
    var top = foreground.getImageData(0, 0, width, height);
    for (var i = 0, size = top.data.length; i < size; i += 4) {
      // red
      top.data[i + 0] = transform(bottom.data[i + 0], top.data[i + 0]);
      // green
      top.data[i + 1] = transform(bottom.data[i + 1], top.data[i + 1]);
      // blue
      top.data[i + 2] = transform(bottom.data[i + 2], top.data[i + 2]);
      // the fourth slot is alpha. We don't need that (so skip by 4)
    }

    return top;
  }

  function FilterGradient(width: any, height: any, type: string) {
    var ctx = canvasRefdummy.current.getContext("2d");
    canvasRefdummy.current.height = height;
    canvasRefdummy.current.width = width;

    if (type === "jentle") {
      var gradient = ctx.createRadialGradient(
        width / 2,
        height / 2,
        0,
        width / 2,
        height / 2,
        width * 0.8
      );
    } else if (type === "mint") {
      var gradient = ctx.createRadialGradient(
        width / 2,
        height / 2,
        0,
        width / 33,
        height / 2,
        width * 0.6
      );
    } else if (type === "nebula") {
      var gradient = ctx.createRadialGradient(
        width / 2,
        height / 2,
        0,
        width / 2,
        height / 2,
        width * 0.5
      );
    } else if (type === "juice") {
      var gradient = ctx.createRadialGradient(
        width / 2,
        height / 2,
        0,
        width / 2,
        height / 2,
        width * 0.7
      );
    } else {
    }

    if (type === "jentle") {
      gradient.addColorStop(0, "#3b2323");
      gradient.addColorStop(1, "#46202a");
    } else if (type === "mint") {
      gradient.addColorStop(0, "#002b22");
      gradient.addColorStop(1, "#002b22");
    } else if (type === "nebula") {
      gradient.addColorStop(0, "#39245a");
      gradient.addColorStop(1, "#12122c");
    } else if (type === "juice") {
      gradient.addColorStop(0, "#555555");
      gradient.addColorStop(1, "#222222");
    } else {
    }

    ctx.fillStyle = gradient;
    ctx.fillRect(0, 0, width, height);

    return ctx;
  }

  const ApplyImageFilter = (type: string) => {
    //////////////////INITIALIZE  CANVAS
    const previewFileReadimage = new Image();
    previewFileReadimage.src = itemUploadRef.current[index].src;
    previewFileReadimage.onload = function () {
      const ctx = canvasRef.current.getContext("2d");
      if (
        previewFileReadimage.naturalWidth > previewFileReadimage.naturalHeight
      ) {
        var widthx = window.innerWidth;
        var scalex = widthx / previewFileReadimage.naturalWidth;
        var heightx = previewFileReadimage.naturalHeight * scalex;
        var width = window.innerWidth * 3;
        var scale = width / previewFileReadimage.naturalWidth;
        var height = previewFileReadimage.naturalHeight * scale;
      } else {
        var heightx = window.innerHeight / 1.2;
        var scalex = heightx / previewFileReadimage.naturalHeight;
        var widthx = previewFileReadimage.naturalWidth * scalex;
        var height = window.innerHeight * 3;
        var scale = height / previewFileReadimage.naturalHeight;
        var width = previewFileReadimage.naturalWidth * scale;
      }

      canvasRef.current.height = height;
      canvasRef.current.width = width;
      //////////////////INITIALIZE  CANVAS

      requestAnimationFrame(() => {
        //////////////////CSS EDIT IMAGE WITH CTX.FILTER
        if (type === "lift") {
          ctx.filter =
            "contrast(1.1) brightness(1.04) blur(0px) saturate(120%)";
        } else if (type === "floyd") {
          ctx.filter = "contrast(1.4) brightness(1.04) saturate(108%)";
        } else if (type === "jentle") {
          ctx.filter =
            "contrast(1.2) brightness(0.8) hue-rotate(20deg) saturate(110%) ";
        } else if (type === "mint") {
          ctx.filter = "contrast(1.2) brightness(1.05)";
        } else if (type === "nebula") {
          ctx.filter = "contrast(1.32) brightness(1.07)";
        } else if (type === "juice") {
          ctx.filter =
            "contrast(1.35) brightness(1.15) saturate(110%) blur(2px)";
        } else {
        }
        //////////////////CSS EDIT IMAGE WITH CTX.FILTER

        ctx.drawImage(
          previewFileReadimage,
          0,
          0,
          canvasRef.current.width,
          canvasRef.current.height
        );

        //////////////////////VIGNETTE
        var w = canvasRef.current.width;
        var h = canvasRef.current.height;
        if (type === "floyd") {
          var gradient = ctx.createRadialGradient(
            w / 2,
            h / 3,
            0,
            w / 2,
            h / 3,
            w * 0.6
          );

          gradient.addColorStop(0, "rgba(255, 255, 255,0.2)");
          gradient.addColorStop(1, "rgba(100,100,100,0.2)");

          ctx.fillStyle = gradient;
          ctx.fillRect(0, 0, width, height);
        } else if (type === "lift" || type === "mint") {
          var gradient = ctx.createRadialGradient(
            w / 2,
            h / 3,
            0,
            w / 2,
            h / 3,
            w * 0.66
          );

          gradient.addColorStop(0, "rgba(255, 255, 255,0.05)");
          gradient.addColorStop(1, "rgba(255, 255, 255,0.05)");

          ctx.fillStyle = gradient;
          ctx.fillRect(0, 0, width, height);
        } else if (type === "nebula") {
          var gradient = ctx.createRadialGradient(
            w / 2,
            h / 2,
            0,
            w / 2,
            h / 2,
            w * 0.68
          );

          gradient.addColorStop(0, "rgba(255, 255, 255,0.05)");
          gradient.addColorStop(1, "rgba(0,0,0,0.3)");

          ctx.fillStyle = gradient;
          ctx.fillRect(0, 0, width, height);
        } else {
        }
        //////////////////////VIGNETTE

        ////////////////// /////// GLOBALCOMPOSITEOPERATION
        if (type === "lift") {
          ////////////composition
          ctx.globalCompositeOperation = "overlay";
          ctx.fillStyle = "rgba(100, 100, 100,0.07)";
          ctx.fillRect(0, 0, width, height);
          ////////////composition
        } else if (type === "floyd") {
          ////////////composition
          ctx.globalCompositeOperation = "overlay";
          ctx.fillStyle = "rgba(110,93,90,0.8)";
          ctx.fillRect(0, 0, width, height);
          ////////////composition
        } else if (type === "jentle") {
          ////////////composition
          ctx.globalCompositeOperation = "overlay";
          ctx.fillStyle = "#c0b3b3";
          ctx.fillRect(0, 0, width, height);
          ////////////composition
        } else if (type === "mint") {
          ////////////composition
          ctx.globalCompositeOperation = "overlay";
          ctx.fillStyle = "#3b474619";
          ctx.fillRect(0, 0, width, height);
          ////////////composition
        } else if (type === "nebula") {
          ////////////composition
          ctx.globalCompositeOperation = "overlay";
          ctx.fillStyle = "rgba(110,93,90,0.8)";
          ctx.fillRect(0, 0, width, height);
          ////////////composition
        } else if (type === "juice") {
          ////////////composition
          ctx.globalCompositeOperation = "multiply";
          ctx.fillStyle = "#ffffff";
          ctx.fillRect(0, 0, width, height);
          ////////////composition
        } else {
        }
        ////////////////// /////// GLOBALCOMPOSITEOPERATION

        ////////////////////GRADIENT BLENDING
        if (
          type === "jentle" ||
          type === "mint" ||
          type === "nebula" ||
          type === "juice"
        ) {
          var gradient = FilterGradient(width, height, type);
          var screen = blend(
            ctx,
            gradient,
            width,
            height,
            function (bottomPixel: any, topPixel: any) {
              return 255 - ((255 - topPixel) * (255 - bottomPixel)) / 255;
            }
          );
          ctx.putImageData(screen, 0, 0);
        }
        ////////////////////GRADIENT BLENDING
      });

      canvasRef.current.style.width = `${widthx}px`;
      canvasRef.current.style.height = `${heightx}px`;
    };
  };

  return (
    <>
      {itemUploadRef.current[index] ? (
        <>
          {" "}
          <button
            onClick={() => {
              ApplyImageFilter("lift");
            }}
          >
            lit
          </button>
          <button
            onClick={() => {
              ApplyImageFilter("floyd");
            }}
          >
            {" "}
            floyd{" "}
          </button>
          <button
            onClick={() => {
              ApplyImageFilter("jentle");
            }}
          >
            {" "}
            jentle{" "}
          </button>
          <button
            onClick={() => {
              ApplyImageFilter("mint");
            }}
          >
            {" "}
            mint{" "}
          </button>
          <button
            onClick={() => {
              ApplyImageFilter("nebula");
            }}
          >
            {" "}
            nebula{" "}
          </button>
          <button
            onClick={() => {
              ApplyImageFilter("juice");
            }}
          >
            {" "}
            juice{" "}
          </button>
          <img
            id={itemUploadRef.current[index]}
            ref={filterbaseimageRef}
            onClick={closegate}
            src={
              itemUploadRef.current ? itemUploadRef.current[index].src : null
            }
            style={{
              width: "30%",
              height: "auto",
              position: "relative",
              margin: "auto",
              marginRight: index === 0 ? "20px" : "0px",
            }}
          />
          <canvas
            ref={canvasRef}
            style={{
              padding: "0px",
            }}
          />
          <canvas
            ref={canvasRefdummy}
            style={{
              padding: "0px",
              display: "none",
            }}
          />
        </>
      ) : null}
    </>
  );
}

export const ImageTobeFiltered = React.memo(ImageTobeFilteredx);
