import React, {
  useRef,
  useEffect,
  useCallback,
  useState,
  useLayoutEffect,
} from "react";
import ControlPointDuplicateIcon from "@mui/icons-material/ControlPointDuplicate";
import { Grid, Switch, DialogContent } from "@material-ui/core";
import { matchPc, matchTablet, matchMobile } from "../DetectDevice";
import { useSpring, animated } from "react-spring";
import { OptionsSlider } from "../profile/OptionsSlider";
import { RootStateOrAny, useSelector, useDispatch } from "react-redux";
import Masonry from "@mui/lab/Masonry";
import Axios from "axios";
import CheckIcon from "@mui/icons-material/Check";
import date from "date-and-time";
import { UserInfoUpdatePROFILE } from "../log/actions/UserdataAction";
import { UserInfoUpdateBILLBOARD } from "../log/actions/UserdataAction";
import { usePalette } from "react-palette";
import { UpdateColorAction } from "../GlobalActions";
import EditTwoToneIcon from "@mui/icons-material/EditTwoTone";
import DoneOutlineIcon from '@mui/icons-material/DoneOutline';

import DoneIcon from '@mui/icons-material/Done';
import CropIcon from '@mui/icons-material/Crop';
import Slider from "@material-ui/core/Slider";

function Taskbarx({
  showModalUploadTask,
  uploadClose,

}: any): JSX.Element {



  ///
  ///
  ///
  /// INTERFACE/TYPES FOR SCREENHEIGHT AND DARKMODE
  interface RootStateGlobalReducer {
    GlobalReducer: {
      darkmode: boolean;
      screenHeight: number;
    };
  }

  ///
  ///
  ///
  /// GET DARKMODE FROM REDUX STORE
  const { darkmode } = useSelector((state: RootStateGlobalReducer) => ({
    ...state.GlobalReducer,
  }));
  const darkmodeReducer = darkmode;


  const animation = useSpring({
    config: {
      duration: 600,
    },
    opacity: showModalUploadTask ? 1 : 0,
    transform: showModalUploadTask ? `translateX(0%)` : `translateX(-100%)`,
    padding: "0px",

  });




  const cropCanvasRef: any = useRef(null);

  const navRef: any = useRef(null);

  const IconRef: any = useRef(null);

  const IconRef2: any = useRef(null);

  const [allowCropAllCanvas, setallowCropAllCanvas] = useState<boolean>(true);

  const [CropImageHolder, setCropImageHolder] = useState<any>(null);

  const [imageHeight, setimageHeight] = useState(0);
  const [imageWidth, setimageWidth] = useState(0);

  const [Box, setBox] = useState<boolean>(false);


  const [cropInitial, setcropInitial] = useState<any>({ x: 0, y: 0 });

  const [cropInitialx, setcropInitialx] = useState<any>({ x: 0, y: 0 });

  const [cropOffset, setcropOffset] = useState<any>({ x: 0, y: 0 });

  const [Drag, setDrag] = useState<boolean>(false);

  const [crop, setcrop] = useState<any>({ x: 0, y: 0 });

  const allowscrolltimer = useRef<ReturnType<typeof setTimeout> | null>(null);

  var extendxy = 3.3;

  const [allowOverflow, setallowOverflow] = useState<boolean>(false);

  const [optionscropshow, setoptionscropshow] = useState<boolean>(true);

  const [zoom, setzoom] = useState<any>(2);

  const [multiPost, setmultiPost] = useState(false);


  const updatezoom = (e: any, data: any) => {
    setzoom(data);
    handleTouchDrag(null, 1000);
  };






  const { activatefilterImage, activatecropImage, selectedImage, cropimage } = useSelector(
    (state: RootStateOrAny) => ({
      ...state.GlobalNavuploadReducer,
    })
  );
  const activatefilterImageReducer = activatefilterImage;
  const activatecropImageReducer = activatecropImage;
  const selectedImageReducer = selectedImage;
  const cropimageReducer = cropimage;


  useEffect(() => {
    const ctx = cropCanvasRef.current.getContext("2d");
    ctx.clearRect(0, 0, cropCanvasRef.current.width, cropCanvasRef.current.height);
    setzoom(2);
    setBox(false);
    setcrop({
      ...crop,
      x: 0,
      y: 0,
    });

  }, [CropImageHolder, showModalUploadTask]);


  useEffect(() => {

    if (selectedImageReducer.length > 1) {
      setmultiPost(true)
    }

  }, [selectedImageReducer]);









  const resizeCanvas = useCallback(() => {
    var imgAspectRatio = cropCanvasRef.current.width / cropCanvasRef.current.height;

    var screenW = navRef.current.clientWidth;
    var screenH = navRef.current.clientHeight;
    let canvasW = screenW
    let canvasH = screenH

    if (imgAspectRatio >= 2.2 || imgAspectRatio <= 0.464) { setBox(true); }

    if (imgAspectRatio <= 1.63) {
      canvasH = screenH;
      canvasW = canvasH * imgAspectRatio;
    } else {
      canvasW = screenW;
      canvasH = canvasW / imgAspectRatio;
    }
    cropCanvasRef.current.style.height = `${canvasH}px`;
    cropCanvasRef.current.style.width = `${canvasW}px`;

    IconRef.current.style.top = `${canvasH / 1.19}px`;
    IconRef2.current.style.top = `${canvasH / 1.19}px`;
  }, [cropCanvasRef])





  function GiveImageSpecificDimensions(Newcropimage: any) {
    const imgAspectRatio = Newcropimage.naturalWidth / Newcropimage.naturalHeight;

    var canvasW;
    var canvasH;
    if (imgAspectRatio <= 1.55) {
      var screenH = 1920;
      canvasH = screenH
      canvasH = screenH;
      canvasW = canvasH * imgAspectRatio;
    } else {
      var screenW = 2048;
      canvasW = screenW
      canvasW = screenW;
      canvasH = canvasW / imgAspectRatio;
    }
    setimageHeight(canvasH);
    setimageWidth(canvasW);
  }


  useLayoutEffect(() => {
    const Newcropimage: any = new Image();
    Newcropimage.src = cropimageReducer;
    Newcropimage.onload = function () {
      if (CropImageHolder === Newcropimage) {
      } else {

        //////////////
        setCropImageHolder(Newcropimage);
        GiveImageSpecificDimensions(Newcropimage);
      }
    }
  }, [cropimageReducer]);



  useLayoutEffect(() => {
    if (CropImageHolder && cropCanvasRef.current) {
      const ctx = cropCanvasRef.current.getContext("2d");


      const imgAspectRatio = imageWidth / imageHeight;



      ////////////////////////
      var a = 1.2; var b = 0.8;


      if (zoom <= 1) {
        a = 1.2; b = 1.2;
      }
      else if (zoom <= 2) {
        a = 1; b = 1;
      } else if (zoom <= 3) {
        a = 1.2; b = 1.2;
      } else if (zoom <= 4) {
        a = 1.4; b = 1.4;
      } else { }



      if (Box) {
        if (imgAspectRatio === 1) {
          if (zoom > 2) {
            cropCanvasRef.current.width = imageWidth / a;
            cropCanvasRef.current.height = imageWidth / a;
          } else {
            cropCanvasRef.current.width = imageWidth;
            cropCanvasRef.current.height = imageWidth;
          }
        }
        else if (imgAspectRatio <= 1.3) {
          if (zoom > 2) {
            cropCanvasRef.current.width = imageWidth / a;
            cropCanvasRef.current.height = imageWidth / a;
          } else {
            cropCanvasRef.current.width = imageWidth / a;
            cropCanvasRef.current.height = imageWidth * a;
          }

        } else {
          if (zoom > 2) {
            cropCanvasRef.current.width = imageHeight / b;
            cropCanvasRef.current.height = imageHeight / b;
          } else {
            cropCanvasRef.current.width = imageHeight / b;
            cropCanvasRef.current.height = imageHeight;
          }

        }
      } else {
        cropCanvasRef.current.width = imageWidth;
        cropCanvasRef.current.height = imageHeight;


      }
      ////////////////////////////////
      resizeCanvas();
      const w = imageWidth;
      const h = imageHeight;


      requestAnimationFrame(() => {
        if (Box) {
          ctx.drawImage(CropImageHolder, crop.x, crop.y, w, h);
        } else {
          ctx.drawImage(CropImageHolder, 0, 0, w, h);
        }
      });

    }
  }, [CropImageHolder, imageWidth, imageHeight, Box, crop, zoom, cropCanvasRef])









  const handleTouchStart = (e: any, type: any) => {
    if (Box) {
      setoptionscropshow(false);
      if (type === 0) {
        setcropInitial({
          ...cropInitial,
          x: e.clientX * extendxy - cropOffset.x,
          y: e.clientY * extendxy - cropOffset.y,
        });
      } else {
        setcropInitial({
          ...cropInitial,
          x: e.touches[0].clientX * extendxy - cropOffset.x,
          y: e.touches[0].clientY * extendxy - cropOffset.y,
        });
      }
      if (e.target) {
        setDrag(true);
      }
    } else {
      setBox(true);
    }
  };




  const handleTouchEnd = () => {
    if (allowscrolltimer.current) {
      clearTimeout(allowscrolltimer.current);
    }
    allowscrolltimer.current = setTimeout(function () {

    }, 1200);
    setoptionscropshow(true);
    setcropInitial({
      ...cropInitial,
      x: crop.x,
      y: crop.y,
    });
    setDrag(false);
  };




  const handleTouchDrag = (e: any, type: any) => {
    if (Drag || type === 1000) {
      var xx = cropInitial.x;
      var yy = cropInitial.y;

      if (e) {
        if (type === 0) {
          xx = e.clientX * extendxy - cropInitial.x;
          yy = e.clientY * extendxy - cropInitial.y;
        } else {
          xx = e.touches[0].clientX * extendxy - cropInitial.x;
          yy = e.touches[0].clientY * extendxy - cropInitial.y;

        }
      }

      ///////////////////////////////////////////////
      var fz = imageWidth - cropCanvasRef.current.width;
      fz *= -1;
      if (xx > 0) {
        xx = 0;
      } else if (xx < fz) {
        xx = fz
      }
      //////////////////////////////
      var ff = imageHeight - cropCanvasRef.current.height;
      ff *= -1;
      if (yy > 0) {
        yy = 0;
      } else if (yy < ff) {
        yy = ff
      }
      //////////////////////////////////
      setcrop({
        ...crop,
        x: xx,
        y: yy,
      });
      setcropOffset({
        ...cropOffset,
        x: xx,
        y: yy,
      });
    }
  };






  return (
    <>
      <animated.div className={darkmodeReducer ? 'modal-containerDark' : 'modal-containerLight'}

        style={{
          ...animation, width: '94%',
          height: "100vh",
          position: "fixed",
          top: '-0vh',
          zIndex: 100,
          cursor: "default",
          overflow: "hidden",
        }}>
        <DialogContent
          style={{
            padding: "0px",
          }}>



          <Grid
            container
            style={{
              padding: "0px", width: '100%', height: "100%",
            }}>


            <Grid ref={navRef}
              item
              xs={12}
              style={{
                padding: "0px", width: '94%', height: multiPost ? "80%" : '100%', position: 'fixed', top: '-20000000000px'
              }}>

            </Grid>



            {optionscropshow ? <Slider
              value={zoom}
              onChange={updatezoom}
              defaultValue={1}
              max={4}
              min={0}
              step={0.0000000000000000000000000000000000000000000001}
              style={{ display: Box ? 'block' : 'none', width: '8%', left: '47%', top: '5vh', position: 'fixed', }}
            />
              : null}



            {allowCropAllCanvas ? (

              <canvas
                onMouseOver={() => {
                  ///mouseover(0);
                }}
                onMouseUp={(e: any) => {
                  handleTouchEnd();
                }}
                onTouchEnd={(e: any) => {
                  handleTouchEnd();
                }}
                onMouseDown={(e: any) => {
                  handleTouchStart(e, 0);
                }}
                onTouchStart={(e: any) => {
                  handleTouchStart(e, 1);
                }}
                onMouseMove={(e: any) => {
                  handleTouchDrag(e, 0);
                }}
                onTouchMove={(e: any) => {
                  handleTouchDrag(e, 1);
                }}
                ref={cropCanvasRef}
                style={{
                  padding: "0px",
                  margin: "auto",
                  cursor: "pointer",
                  overflow: 'hidden'
                }} />
            ) : null}




            <DoneIcon
              ref={IconRef}
              className={
                darkmodeReducer
                  ? "make-small-icons-clickable-darkab dontallowhighlighting zuperkingIcon "
                  : "make-small-icons-clickable-lightab  dontallowhighlighting zuperkingIcon  "
              }
              style={{
                color: "#ffffff",
                fontSize: "3.1vw",
                position: "absolute",
                opacity: 0.94,
                left: '43%',
                right: "",
                display: optionscropshow ? 'block' : 'none'
              }}
            />



            <CropIcon
              onClick={() => {
                if (Box) {
                  setBox(false);
                } else {
                  setBox(true);
                }
              }}

              ref={IconRef2}
              className={
                darkmodeReducer
                  ? "make-small-icons-clickable-darkab dontallowhighlighting zuperkingIcon "
                  : "make-small-icons-clickable-lightab  dontallowhighlighting zuperkingIcon  "
              }
              style={{
                color: "#ffffff",
                fontSize: "2.9vw",
                position: "absolute",
                bottom: '5vh',
                opacity: 0.94,
                right: '43%',
                display: optionscropshow ? 'block' : 'none'
              }}
            />


            <Grid
              item
              xs={12}
              style={{
                padding: "0px",
              }}>



              <div>

              </div>        </Grid>



          </Grid>

        </DialogContent> </animated.div>


    </>
  );
}

export const Taskbar = React.memo(Taskbarx);
