import React, { useRef, useState, useEffect, useCallback } from "react";
import { Paper, Grid, Box } from "@material-ui/core";
import { useSelector } from "react-redux";
import RemoveRedEyeIcon from "@mui/icons-material/RemoveRedEye";
import MoreHorizIcon from "@material-ui/icons/MoreHoriz";
function Postx({
  pey,
  refy,
  onPostsItemload,
  post,
  itemheight,
  itemheighthold,
  postbackheight,
}: any) {
  var paddingTopemo = "5vh";
  var fontSizeemo = "2.7%";
  ///
  ///
  ///
  /// INTERFACE/TYPES FOR SCREENHEIGHT AND DARKMODE
  interface RootStateGlobalReducer {
    GlobalReducer: {
      darkmode: boolean;
      screenHeight: number;
    };
  }

  ///
  ///
  ///
  /// GET SCREENHEIGHT FROM REDUX STORE
  const { screenHeight, darkmode } = useSelector(
    (state: RootStateGlobalReducer) => ({
      ...state.GlobalReducer,
    })
  );
  const screenHeightReducer = screenHeight;
  const darkmodeReducer = darkmode;

  const [itemInfoTopShow, setitemInfoTopShow] = useState<boolean>(false);

  ///
  ///
  ///
  ///GET OPTIONS SLIDER IMAGE WIDTH FROM MATERIAL UI GRID
  useEffect(() => {
    setitemInfoTopShow(false);
    setTimeout(function () {
      setitemInfoTopShow(true);
    }, 500);
  }, [itemheighthold, itemheight]);

  return (
    <>
      <div key={pey} className="pics">
        <img
          onLoad={(e: any) => {
            onPostsItemload(e, pey);
          }}
          ref={refy}
          className={darkmodeReducer ? "turlightpostdark" : "turlightpostlight"}
          src={`./images/posts/${post.item1}`}
          alt="a superstarz post "
          style={{
            marginTop: pey === 0 ? "0px" : `-${postbackheight}px`,
            width: "100%",
            height: itemheight[pey],
            position: "relative",
            display: "block",
            padding: "0px",
            objectFit: "contain",
          }}
        />
        <div
          className={
            darkmodeReducer ? "post-background-dark" : "post-background-light"
          }
          style={{
            height: `${postbackheight}px`,
            bottom: `${postbackheight}px`,
            position: "relative",
            transition: "all 350ms ease",
            opacity: itemInfoTopShow ? 1 : 0,
          }}
        >
          <div
            style={{
              top: `-${itemheighthold[pey]}px`,
              position: "relative",
              transition: "all 350ms ease",
              opacity: 1,
              display: "flex",
              alignItems: "center",
              justifyContent: "left",
              paddingLeft: "20px",
              zIndex: 1,
            }}
          >
            <RemoveRedEyeIcon
              className={
                darkmodeReducer
                  ? "make-small-icons-clickable-light  dontallowhighlighting   zuperking"
                  : "make-small-icons-clickable-dark  dontallowhighlighting  zuperking"
              }
              style={{
                fontSize: "2.2vw",
                opacity: itemInfoTopShow
                  ? itemheight[pey] === "auto"
                    ? 0
                    : 0.7
                  : 0,
              }}
            />
          </div>
          <div
            style={{
              top: `-${itemheighthold[pey]}px`,
              position: "relative",
              transition: "all 350ms ease",
              opacity: 1,
              display: "flex",
              alignItems: "center",
              justifyContent: "right",
              zIndex: 2,
              marginTop: "-10px",
            }}
          >
            <img
              className={darkmodeReducer ? "turpostDark" : "turpostLight"}
              src={`./images/emotions/love.png`}
              alt="a superstarz post "
              style={{
                cursor: "pointer",
                boxShadow: darkmodeReducer
                  ? "0 0 1px #555555"
                  : "0 0 4.5px #aaaaaa",
                width: fontSizeemo,
                marginLeft: "93%",
                height: "auto",
                padding: "0px",
                objectFit: "contain",
                borderRadius: "50%",
                position: "relative",
                zIndex: 1,
              }}
            />
          </div>
          <div
            style={{
              top: `-${itemheighthold[pey]}px`,
              position: "relative",
              transition: "all 350ms ease",
              opacity: 1,
              display: "flex",
              alignItems: "center",
              justifyContent: "right",
              zIndex: 2,
              paddingTop: paddingTopemo,
            }}
          >
            <img
              className={darkmodeReducer ? "turpostDark" : "turpostLight"}
              src={`./images/emotions/cool.png`}
              alt="a superstarz post "
              style={{
                cursor: "pointer",
                boxShadow: darkmodeReducer
                  ? "0 0 1px #555555"
                  : "0 0 4.5px #aaaaaa",
                width: fontSizeemo,
                marginLeft: "93%",
                height: "auto",
                padding: "0px",
                objectFit: "contain",
                borderRadius: "50%",
                position: "relative",
                zIndex: 1,
              }}
            />
          </div>
          <div
            style={{
              top: `-${itemheighthold[pey]}px`,
              position: "relative",
              transition: "all 350ms ease",
              opacity: 1,
              display: "flex",
              alignItems: "center",
              justifyContent: "right",
              zIndex: 2,
              paddingTop: paddingTopemo,
            }}
          >
            <img
              className={darkmodeReducer ? "turpostDark" : "turpostLight"}
              src={`./images/emotions/oo.png`}
              alt="a superstarz post "
              style={{
                cursor: "pointer",
                boxShadow: darkmodeReducer
                  ? "0 0 1px #555555"
                  : "0 0 4.5px #aaaaaa",
                width: fontSizeemo,
                marginLeft: "93%",
                height: "auto",
                padding: "0px",
                objectFit: "contain",
                borderRadius: "50%",
                position: "relative",
                zIndex: 1,
              }}
            />
          </div>
          <div
            style={{
              top: `-${itemheighthold[pey]}px`,
              position: "relative",
              transition: "all 350ms ease",
              opacity: 1,
              display: "flex",
              alignItems: "center",
              justifyContent: "right",
              zIndex: 2,
              paddingTop: paddingTopemo,
            }}
          >
            <img
              className={darkmodeReducer ? "turpostDark" : "turpostLight"}
              src={`./images/emotions/laugh.png`}
              alt="a superstarz post "
              style={{
                cursor: "pointer",
                boxShadow: darkmodeReducer
                  ? "0 0 1px #555555"
                  : "0 0 3.5px #aaaaaa",
                width: fontSizeemo,
                marginLeft: "93%",
                height: "auto",
                padding: "0px",
                objectFit: "contain",
                borderRadius: "50%",
                position: "relative",
                zIndex: 1,
              }}
            />
          </div>

          <div
            className="zuperxyinfo"
            style={{
              top: `-18vh`,
              position: "relative",
              transition: "all 350ms ease",
              opacity: 1,
              display: "flex",
              alignItems: "center",
              justifyContent: "left",
              zIndex: 1,
              paddingLeft: "2vw",
              fontFamily: "Arial, Helvetica, sans-serif",
            }}
          >
            <h1>Tanu</h1>
          </div>
          <div
            className="zuperxyinfo"
            style={{
              top: `-16vh`,
              position: "relative",
              transition: "all 350ms ease",
              opacity: 1,
              display: "flex",
              alignItems: "center",
              justifyContent: "left",
              zIndex: 1,
              paddingLeft: "2vw",
              fontFamily: "kaushan_scriptregular",
            }}
          >
            <h1>World Cup Finals</h1>
          </div>

          <div
            className="zuperxyinfo"
            style={{
              top: `-23.5vh`,
              position: "relative",
              transition: "all 350ms ease",
              opacity: 1,
              display: "flex",
              alignItems: "center",
              justifyContent: "left",
              zIndex: 2,
              height: "0px",
              width: "98%",
              paddingLeft: "2vw",
              fontFamily: "kaushan_scriptregular",
              backgroundColor: "#00ccff",
            }}
          >
            <span style={{ marginLeft: "94%" }}>
              <MoreHorizIcon style={{ fontSize: "3.3rem" }} />
            </span>
          </div>

          <div
            style={{
              top: `-23.5vh`,
              position: "relative",
              transition: "all 350ms ease",
              opacity: 1,
              display: "flex",
              alignItems: "center",
              justifyContent: "left",
              zIndex: 2,
              height: "0px",

              paddingLeft: "2vw",
              fontFamily: "kaushan_scriptregular",
              backgroundColor: "#00ccff",
            }}
          >
            <span style={{ marginLeft: "94%" }}>
              <MoreHorizIcon style={{ fontSize: "3.3rem" }} />
            </span>
          </div>
        </div>
      </div>
    </>
  );
}

export const Post = React.memo(Postx);
