import React, { useState } from "react";
import { animated, useSpring } from "react-spring";
import { Grid } from "@material-ui/core";
import ErrorOutlineIcon from "@material-ui/icons/ErrorOutline";
import { ImodalFormLoginError } from "./appFolder-Interfaces";
import { makeStyles, withStyles } from "@material-ui/core/styles";
import CircularProgress from "@material-ui/core/CircularProgress";
import LinearProgress from "@material-ui/core/LinearProgress";

export default function ModalFormLoginError({
  type,
  ErrorDisplay,
  darkmode,
  WidthHolder,
}: ImodalFormLoginError) {
  ///
  ///
  ///
  ///USE SLIDE DOWN ANIMATION FROM REACT SPRING
  const animationModalLogError = useSpring({
    config: {
      duration: 100,
    },
    width: "100%",
    opacity: ErrorDisplay,
    transform: ErrorDisplay ? `translateY(0%)` : `translateY(100%)`,
  });

  ///
  ///
  ///
  ///USE SLIDE DOWN ANIMATION FROM REACT SPRING
  const animationModalErrorPasswordLog = useSpring({
    config: {
      duration: 100,
    },
    opacity: ErrorDisplay,
    marginTop: "-80px",
    zIndex: 0,
    transform: ErrorDisplay ? `translateY(100%)` : `translateY(0%)`,
  });

  var ErrorBacklight = "";
  var ErrorColor = "";
  var ErrorTextColor = "";
  var ErrorIconColor = "";

  if (darkmode) {
    ErrorBacklight = "turdark";
    ErrorColor = "#00ccff";
    ErrorTextColor = "#dddddd";
    ErrorIconColor = "orange";
  } else {
    ErrorBacklight = "turlight";
    ErrorColor = "#eeeeee";
    ErrorTextColor = "#222222";
    ErrorIconColor = "red";
  }

  return (
    <>
      {type ? (
        <animated.div style={animationModalLogError}>
          <Grid
            className={ErrorBacklight}
            xs={12}
            style={{
              width: WidthHolder,
              height: "34px",
              backgroundColor: ErrorColor,
              margin: "auto",
              borderRadius: "0px",
              marginBottom: "-1.5px",
              display: "flex",
            }}
          >
            <Grid
              xs={12}
              style={{
                color: ErrorTextColor,
                fontSize: "0.83vw",
                display: "flex",
                alignItems: "center",
                paddingRight: "15px",
                justifyContent: "flex-end",
                textAlign: "left",
                fontWeight: "normal",
                fontFamily: "sans-serif",
              }}
            >
              <Grid xs={2}>
                <ErrorOutlineIcon
                  style={{
                    marginLeft: "8px",
                    marginRight: "12px",
                    color: ErrorIconColor,
                  }}
                />
              </Grid>
              <Grid style={{ opacity: 0.7 }} xs={10}>
                Username required
              </Grid>
            </Grid>
          </Grid>
        </animated.div>
      ) : (
        <animated.div style={animationModalErrorPasswordLog}>
          <Grid
            className={ErrorBacklight}
            xs={12}
            style={{
              width: WidthHolder,
              height: "34px",

              backgroundColor: ErrorColor,
              margin: "auto",
              borderRadius: "0px",

              display: "flex",
            }}
          >
            {" "}
            <Grid
              xs={12}
              style={{
                color: ErrorTextColor,
                fontSize: "0.83vw",
                display: "flex",
                alignItems: "center",
                paddingRight: "15px",
                justifyContent: "flex-end",
                textAlign: "left",
                fontWeight: "normal",
                fontFamily: "sans-serif",
              }}
            >
              <Grid xs={2}>
                <ErrorOutlineIcon
                  style={{
                    marginLeft: "8px",
                    marginRight: "12px",
                    color: ErrorIconColor,
                  }}
                />
              </Grid>
              <Grid xs={10} style={{ opacity: 0.6 }}>
                Password required
              </Grid>
            </Grid>
          </Grid>
        </animated.div>
      )}
    </>
  );
}
