import React, { useState, useEffect, useRef } from "react";
import { matchPc, matchTablet } from "../DetectDevice";
import { Grid } from "@material-ui/core";
import MoreHorizIcon from "@material-ui/icons/MoreHoriz";
import { useSelector, useDispatch } from "react-redux";
import { useSpring, animated } from "react-spring";

import CircleIcon from "@mui/icons-material/Circle";
import SettingsIcon from "@mui/icons-material/Settings";

function ImageFilterinnerx({
  ActivatefilterImage,
  optionsShow,
  optinstopshowingReducer,
  typeTop,
  closeoptionsslide,
  animationop,
  optionsCollectImageRef,
  handleTouchStartOptions,
  handleTouchMoveOptions,
  modalanimation,
  nextSlidePc,
  optionsImages,
  ActiveSlide,
  optionsNameData,
  clickOptions,
  optionsClickType,
  getSliderWidthNew,
  itemUploadRef,
  ApplyImageFilter,
  imageFiltersRef,
  getImageWidth,
  setfilterThumbWidthViewable,
  setfilterThumbWidthFull,
  screenWidth,
}: any): JSX.Element {
  const getFilterWidthViewable = useRef<any>([]);
  const getFilterWidthfull = useRef<any>([]);
  ///
  ///
  ///
  ///DARKMODE FROM REDUX
  interface RootStateGlobalReducer {
    GlobalReducer: {
      darkmode: boolean;
    };
  }
  const { darkmode } = useSelector((state: RootStateGlobalReducer) => ({
    ...state.GlobalReducer,
  }));

  const darkmodeReducer = darkmode;

  var circleIdentify = typeTop ? 0 : 4;
  var circleIdentify2 = typeTop ? 1 : 5;

  ///
  ///
  /// GET COLOR FROM REDUX STORE
  interface RootStateReducerColor {
    GlobalReducerColor: {
      color: string;
      colordark: string;
      colortype: number;
    };
  }
  const { color, colordark, colortype } = useSelector(
    (state: RootStateReducerColor) => ({
      ...state.GlobalReducerColor,
    })
  );
  const colorReducer = color;
  const colorReducerdark = colordark;
  const colortypeReducer = colortype;

  const [showfiltersOnload, setshowfiltersOnload] = useState(false);

  const addimageFiltersRef = (imageRef: any) => {
    if (imageRef && !imageFiltersRef.current.includes(imageRef)) {
      imageFiltersRef.current.push(imageRef);
    }
    ////console.log(postItemsRef.current[1]);
  };

  useEffect(() => {
    if (itemUploadRef.current[0] && imageFiltersRef && ActivatefilterImage) {
      setshowfiltersOnload(true);

      ApplyImageFilter("lift", 0, 1, imageFiltersRef, "thumb");
    }
  }, [itemUploadRef, ActivatefilterImage, imageFiltersRef]);

  const startImageFilter = (i: number) => {
    clickOptions(i, optionsClickType, "filter");

    ApplyImageFilter(optionsNameData[i], 0, 0, getImageWidth, "image");
  };

  useEffect(() => {
    setfilterThumbWidthViewable(getFilterWidthViewable.current.clientWidth);
    setfilterThumbWidthFull(optionsCollectImageRef.current.clientWidth);
  }, [getFilterWidthViewable, optionsCollectImageRef]);

  return (
    <>
      {optionsShow ? (
        <>
          {optinstopshowingReducer ? (
            typeTop ? null : (
              <>
                {" "}
                <Grid
                  container
                  style={{
                    width: matchPc ? "10%" : matchTablet ? "30%" : "10%",
                    margin: "auto",
                  }}
                >
                  <span>
                    <MoreHorizIcon
                      className={
                        darkmodeReducer
                          ? "make-small-icons-clickable-dark dontallowhighlighting  "
                          : "make-small-icons-clickable-light  dontallowhighlighting  "
                      }
                      onClick={closeoptionsslide}
                      style={{
                        fontSize: matchPc ? "2.55vw" : "4.7vh",
                        verticalAlign: "middle",
                        justifyContent: "center",
                        zIndex: 2,
                        marginTop: matchPc ? "9vh" : "6.5vh",
                        position: "absolute",
                      }}
                    />
                  </span>{" "}
                </Grid>
              </>
            )
          ) : null}

          <animated.div    ref={getFilterWidthViewable} style={animationop}>
            <Grid
              container
              className={
                typeTop
                  ? darkmodeReducer
                    ? `optionsTop-background-dark `
                    : `optionsTop-background-light `
                  : ""
              }
              onTouchStart={handleTouchStartOptions}
              onTouchMove={handleTouchMoveOptions}
              style={{
                zIndex: 1,
                padding: "0px",
                top: matchPc ? (typeTop ? "-2vh" : "0vh") : "0vh",
                position: "relative",
                margin: "0 auto",
                overflow: "hidden",
                left: "0px",
                height: typeTop ? (matchPc ? "24.4vh" : "20.5vh") : "auto",
                paddingBottom: "1px",
              }}
            >
              <animated.div ref={optionsCollectImageRef} style={modalanimation}>
                {optionsImages.map((im: any, i: any) => (
                  <Grid key={i} item xs={12}>
                    <Grid
                      item
                      xs={12}
                      style={{
                        margin: "auto",
                        textAlign: "center",
                        position: "relative",
                        top: matchPc ? "1.55vh" : "0.54em",
                      }}
                    >
                      <Grid
                        item
                        xs={12}
                        style={{
                          margin: "auto",
                          marginLeft: "2vw",
                          paddingBottom: "0px",
                          fontFamily: "kaushan_scriptregular",
                          fontSize: matchPc
                            ? "1.25vw"
                            : matchTablet
                            ? "2.08vh"
                            : "2vh",
                          fontWeight: "bolder",
                          visibility: ActiveSlide === i ? "visible" : "hidden",
                          filter: darkmodeReducer
                            ? "drop-shadow(1.2px 0.1px 1.92px rgba(255, 255, 255, 0.4))"
                            : "drop-shadow(1.2px 0.1px 1.92px rgba(41, 53, 70, 8.35))",
                          color: darkmodeReducer ? "#dddddd" : "#0b111b",
                        }}
                      >
                        {" "}
                        {optionsNameData[i]}
                      </Grid>
                    </Grid>

                    {showfiltersOnload ? (
                      <img
                        ref={addimageFiltersRef}
                        alt={` ${optionsNameData[i]}  option`}
                        onClick={() => {
                          startImageFilter(i);
                        }}
                        style={{
                          cursor: ActiveSlide === i ? "pointer" : "copy",
                          width: `${getSliderWidthNew + 20}px`,
                          height: `${getSliderWidthNew + 20}px`,
                          borderRadius: "50%",
                          padding: "0px",
                          objectFit: "cover",
                          marginLeft: "2vw",
                          marginTop: "13px",
                          boxShadow: darkmodeReducer
                            ? ActiveSlide === i
                              ? colortypeReducer === 0
                                ? `0 0 6.8px ${colorReducerdark}`
                                : `0 0 6.8px ${colorReducer}`
                              : typeTop
                              ? "0 0 12.5px#aaaaaa"
                              : ""
                            : ActiveSlide === i
                            ? `0 0 5.7px ${colorReducer}`
                            : typeTop
                            ? `0 0 14.45px#222222`
                            : "",
                        }}
                        src={
                          itemUploadRef.current[0]
                            ? itemUploadRef.current[0].src
                            : null
                        }
                      />
                    ) : null}
                  </Grid>
                ))}
              </animated.div>
            </Grid>
          </animated.div>
        </>
      ) : (
        <>
          <Grid container>
            <Grid
              item
              xs={9}
              style={{
                textAlign: "right",
                marginTop: matchPc ? "6.4vh" : matchTablet ? "8.8vh" : "8.3vh",
              }}
            >
              <span
                style={{
                  padding: "16px",
                  cursor: "pointer",
                }}
              >
                <CircleIcon
                  style={{
                    fontSize: matchPc
                      ? "1.2vw"
                      : matchTablet
                      ? "2.5vh"
                      : "2.3vh",
                    color: darkmodeReducer
                      ? "rgba(200, 200, 200, 0.1)"
                      : "rgba(005, 005, 005, 0.2)",
                  }}
                />
              </span>
            </Grid>
          </Grid>
        </>
      )}
    </>
  );
}

export const ImageFilterinner = React.memo(ImageFilterinnerx);
