import { animated, useSpring } from "react-spring";
import { Grid } from "@material-ui/core";
import ErrorOutlineIcon from "@material-ui/icons/ErrorOutline";
import { ImodalFormSignupError } from "./appFolder-Interfaces";
import { makeStyles } from "@material-ui/core/styles";
import CircularProgress from "@material-ui/core/CircularProgress";

export default function ModalLogFormError({
  ErrorType,
  textField,
  errorFormChecking,
  type,
  cantPassBadEmail,
  ErrorDisplay,
  ErrorData,
  darkmode,
  WidthHolder,
  device,
}: ImodalFormSignupError) {
  ///
  ///
  ///USE SLIDE DOWN ANIMATION FROM REACT SPRING
  const animationModalError = useSpring({
    config: {
      duration: 100,
    },
    opacity: ErrorDisplay,
    transform: ErrorData ? `translateY(0%)` : `translateY(100%)`,
  });

  ///
  ///
  ///
  ///USE SLIDE DOWN ANIMATION FROM REACT SPRING
  const animationModalErrorPassword = useSpring({
    config: {
      duration: 100,
    },
    opacity: ErrorDisplay,
    marginTop: "-64px",
    zIndex: 0,
    transform: ErrorData ? `translateY(100%)` : `translateY(0%)`,
  });

  var ErrorColor = "";
  var ErrorWidth;
  var ErrorTextColor = "";
  var ErrorIconColor = "";

  if (device === "pc") {
    ErrorTextColor = "";
    ErrorIconColor = "";
  } else if (device === "Tablet") {
  } else {
  }
  if (darkmode) {
    ErrorColor =
      "linear-gradient(0deg,  rgba(17, 17, 17, 0.025), rgba(17, 17, 17, 0.05),rgba(17, 17, 17, 0.075),rgba(17, 17, 17, 0.1),rgba(17, 17, 17, 0.125),rgba(17, 17, 17, 0.15),rgba(17, 17, 17, 0.175),rgba(17, 17, 17, 0.2),rgba(17, 17, 17, 0.225),rgba(17, 17, 17, 0.25))";

    ErrorTextColor = "#dddddd";
    ErrorIconColor = "red";
  } else {
    ErrorColor =
      "linear-gradient(0deg,  rgba(221, 221, 221, 0.05), rgba(221, 221, 221, 0.1),rgba(221, 221, 221, 0.15),rgba(221, 221, 221, 0.2),rgba(221, 221, 221, 0.25),rgba(221, 221, 221, 0.3),rgba(221, 221, 221, 0.35),rgba(221, 221, 221, 0.4),rgba(221, 221, 221, 0.45),rgba(221, 221, 221, 0.5))";

    ErrorTextColor = "#222222";
    ErrorIconColor = "blue";
  }

  ///
  ///
  ///
  ///MATERIAL UI SPINNER STYLES (INSPIRED BY FACEBOOK SPINNERS)
  const useStylesmodalformsignup = makeStyles((theme) => ({
    root: {
      position: "relative",
      textAlign: "left",
      left: 10,
    },
    bottom: {
      color: theme.palette.grey[theme.palette.type === "light" ? 300 : 700],
    },
    top: {
      color: ErrorIconColor,
      animationDuration: "550ms",
      position: "absolute",
      left: 0,
    },
    circle: {
      strokeLinecap: "round",
    },
  }));

  const classes = useStylesmodalformsignup();

  var displayStatus1 = "display-on";
  var displayStatus2 = "display-off";

  if (textField === "username" && ErrorType !== 4 && ErrorType !== 1) {
    displayStatus1 = "display-off";
    displayStatus2 = "display-on";
  } else {
    displayStatus1 = "display-on";
    displayStatus2 = "display-off";
  }

  var showEmojiAvailable = "none";
  var showEmojiTaken = "none";
  var errorwidth = "";
  var iconfont = "";
  var textfont = "";

  if (ErrorType === 2) {
    showEmojiAvailable = "none";
    showEmojiTaken = "block";
  } else if (ErrorType === 3) {
    showEmojiAvailable = "block";
    showEmojiTaken = "none";
  }
  return (
    <>
      {type ? (
        <animated.div style={animationModalError}>
          <Grid
            container
            style={{
              width: WidthHolder,
              height: "34px",
              backgroundImage: ErrorColor,
              margin: "auto",
              borderRadius: "0px",
              marginBottom: "6.5px",
              display: "flex",
            }}
          >
            <Grid
              item
              className={errorFormChecking ? "display-off" : "display-on"}
              xs={12}
              style={{
                color: ErrorTextColor,
                alignItems: "center",
                paddingRight: "15px",
                justifyContent: "flex-end",
                textAlign: "right",
                fontWeight: "normal",
                fontFamily: "sans-serif",
              }}
            >
              <Grid item xs={2} className={displayStatus1}>
                <ErrorOutlineIcon
                  className={cantPassBadEmail ? "element-bop" : ""}
                  style={{
                    opacity: 1,
                    marginLeft: "8px",
                    marginRight: "12px",
                    color: ErrorIconColor,
                    fontSize: "1vw",
                  }}
                />
              </Grid>
              <Grid item xs={2} className={displayStatus2}>
                <span
                  style={{
                    display: showEmojiTaken,
                    fontSize: "1.5vw",
                    textAlign: "right",
                    marginLeft: "8px",
                    marginRight: "12px",
                    marginTop: "-3px",
                  }}
                >
                  &#128549;
                </span>
                <span
                  style={{
                    display: showEmojiAvailable,
                    fontSize: "1.5vw",
                    textAlign: "right",
                    marginLeft: "8px",
                    marginRight: "12px",
                    marginTop: "-3px",
                  }}
                >
                  &#128515;
                </span>
              </Grid>
              <Grid item style={{ opacity: 0.5, fontSize: "1vw" }} xs={10}>
                {ErrorData}
              </Grid>
            </Grid>
            <Grid
              item
              className={errorFormChecking ? "display-on" : "display-off"}
              xs={12}
              style={{
                color: ErrorTextColor,
                fontSize: "0.83vw",
                alignItems: "center",
                paddingRight: "15px",
                justifyContent: "flex-start",
                textAlign: "left",
                fontWeight: "normal",
                fontFamily: "sans-serif",
              }}
            >
              {" "}
              <div className={classes.root}>
                <CircularProgress
                  variant="determinate"
                  className={classes.bottom}
                  size={15}
                  thickness={4}
                  {...ErrorData}
                  value={100}
                />
                <CircularProgress
                  variant="indeterminate"
                  disableShrink
                  className={classes.top}
                  classes={{
                    circle: classes.circle,
                  }}
                  size={15}
                  thickness={4}
                  {...ErrorData}
                />
              </div>{" "}
            </Grid>
          </Grid>
        </animated.div>
      ) : (
        <animated.div style={animationModalErrorPassword}>
          <Grid
            container
            style={{
              width: WidthHolder,
              height: "34px",
              backgroundImage: ErrorColor,
              margin: "auto",
              borderRadius: "0px",
              display: "flex",
            }}
          >
            {" "}
            <Grid
              className={errorFormChecking ? "display-off" : "display-on"}
              item
              xs={12}
              style={{
                color: ErrorTextColor,

                alignItems: "center",
                paddingRight: "15px",
                justifyContent: "flex-end",
                textAlign: "right",
                fontWeight: "normal",
                fontFamily: "sans-serif",
              }}
            >
              <Grid item xs={2}>
                <ErrorOutlineIcon
                  className={cantPassBadEmail ? "element-bop" : ""}
                  style={{
                    fontSize: "1vw",
                    marginLeft: "8px",
                    marginRight: "27px",

                    color: ErrorIconColor,
                  }}
                />
              </Grid>
              <Grid item xs={10} style={{ opacity: 0.5, fontSize: "1vw" }}>
                {ErrorData}
              </Grid>
            </Grid>
            <Grid
              item
              className={errorFormChecking ? "display-on" : "display-off"}
              xs={12}
              style={{
                color: ErrorTextColor,
                fontSize: "0.83vw",
                alignItems: "center",
                paddingRight: "15px",
                justifyContent: "flex-start",
                textAlign: "left",
                fontWeight: "normal",
                fontFamily: "sans-serif",
              }}
            >
              {" "}
              <div className={classes.root}>
                <CircularProgress
                  variant="determinate"
                  className={classes.bottom}
                  size={15}
                  thickness={4}
                  {...ErrorData}
                  value={100}
                />
                <CircularProgress
                  variant="indeterminate"
                  disableShrink
                  className={classes.top}
                  classes={{
                    circle: classes.circle,
                  }}
                  size={15}
                  thickness={4}
                  {...ErrorData}
                />
              </div>{" "}
            </Grid>
          </Grid>
        </animated.div>
      )}
    </>
  );
}
