import React, { useState, useEffect, useCallback } from "react";
import profilePic from "./images/profile/2602017071517530029820170521192154199Screenshot_2016-05-29-20-10-52_1.jpg";
import MoreHorizIcon from "@material-ui/icons/MoreHoriz";
import { matchPc, matchTablet } from "./DetectDevice";
import Axios from "axios";
import { SupercheckOptions } from "./SupercheckOptions";
import SuperstarzIconLight from "./images/ssmall.png";
import SuperstarzIconDark from "./images/sdsmall.png";
import {
  Paper,
  Grid,
  makeStyles,
  createMuiTheme,
  MuiThemeProvider,
} from "@material-ui/core";
import { RootStateOrAny, useSelector } from "react-redux";
import { DarkmodeAction } from "./GlobalActions";

function Supercheck() {
  ////const dispatch = useDispatch();

  ///
  ///
  ///
  ///DARKMODE FROM REDUX
  interface RootStateGlobalReducer {
    GlobalReducer: {
      darkmode: boolean;
    };
  }

  const { darkmode } = useSelector((state: RootStateGlobalReducer) => ({
    ...state.GlobalReducer,
  }));
  const darkmodeReducer = darkmode;

  const { REACT_APP_SUPERSTARZ_URL } = process.env;

  ///
  ///
  ///
  ///LOGOUT
  const logout = () => {
    Axios.post(`http://${REACT_APP_SUPERSTARZ_URL}/logout`, {
      withCredentials: true,
    })
      .then((response) => {
        if (response.data.message === "cookie deleted") {
          alert("logout  complete");
        } else if (response.data.message === "cookie null") {
          alert("logged out  already");
        }
      })
      .catch(function (error) {
        alert("Connection failure ");
      });
  };

  interface IsuperCheckVariables {
    logoimage: string;
    moreoptions: string;
  }

  var superCheckVariables: IsuperCheckVariables = {
    logoimage: "",

    moreoptions: "",
  };

  var superCheckVariablesDARK: IsuperCheckVariables = {
    logoimage: SuperstarzIconDark,
    moreoptions: "supercheck-options-dark",
  };

  var superCheckVariablesLIGHT: IsuperCheckVariables = {
    logoimage: SuperstarzIconLight,
    moreoptions: "supercheck-options-light",
  };

  if (darkmodeReducer) {
    superCheckVariables = superCheckVariablesDARK;
  } else {
    superCheckVariables = superCheckVariablesLIGHT;
  }

  ///
  ///
  ///
  ///MUI PAPER STYLES FROM REDUX
  const { PaperStyleLight, PaperStyleDark } = useSelector(
    (state: RootStateOrAny) => ({
      ...state.PaperReducerLightnDark,
    })
  );

  var PaperStyleReducer = " ";

  var imageWidth = "";
  var optionsClass = "";
  var iconSize = "";
  var fontOptions = "";
  var pad = "";
  var padR = "";
  var margintopp = "";
  var justifyContentt = "";

  if (matchPc) {
    imageWidth = "30%";
    iconSize = "4.7%";
    optionsClass = "supercheck-optionsPc";
    fontOptions = "3.8rem";
    pad = "33px";
    padR = "20px";
    margintopp = "-2vh";
    justifyContentt = "flex-start";
  } else if (matchTablet) {
    imageWidth = "50%";
    iconSize = "11%";
    optionsClass = "supercheck-optionsTablet";
    fontOptions = "4rem";
    pad = "33px";
    padR = "20px";
    margintopp = "0.8vh";
    justifyContentt = "flex-start";
  } else {
    imageWidth = "66%";
    iconSize = "18%";
    optionsClass = "supercheck-optionsMobile";
    fontOptions = "2rem";
    pad = "10px";
    padR = "6px";
    margintopp = "0.4vh";
    justifyContentt = "flex-start";
  }

  const [showModal, setShowModal] = useState<boolean>(false);

  ///
  ///
  ///
  /// CLOSE MODAL (STARTS AN ONPOPSTATE EVENT)
  const closemodal = useCallback(
    (backbutton: number) => {
      //pop states fires when back and forward buttons used
      if (backbutton === 1) {
        window.onpopstate = () => {
          window.history.pushState(null, "", window.location.href);
          window.onpopstate = null;
          setShowModal(false);
        };
      } else {
        window.history.pushState(null, "", ".");
        window.onpopstate = null;
        setShowModal(false);
      }
    },
    [showModal]
  );

  ///
  ///
  ///
  ///OPEN MODAL THEN CALL CLOSEMODAL FUNCTION WHICH WAITS FOR A POP EVENT(for closing modal)
  const Openmodal = useCallback(() => {
    setShowModal(true);
    //pushstate add enteries to your history
    window.history.pushState(null, "", "Options");
    closemodal(1);
  }, [showModal, closemodal]);

  return (
    <MuiThemeProvider theme={themeGeneralSettings}>
      <Paper className={classes.PaperStyle} style={{ borderRadius: "0px" }}>
        <Grid
          container
          style={{
            position: "absolute",
            top: "0em",

            transition: "opacity 1.5s",
            display: "flex",
          }}
        >
          <SupercheckOptions
            closemodal={closemodal}
            setShowModal={setShowModal}
            showModal={showModal}
          />
          <Grid
            xs={12}
            item
            style={{
              fontSize: "2.41vw",
              fontWeight: "bolder",
              display: "flex",
              alignItems: "center",
              justifyContent: justifyContentt,
              marginTop: margintopp,
              padding: pad,
              paddingLeft: padR,
            }}
          >
            <img
              className="buttonshake"
              onClick={logout}
              style={{
                objectFit: "contain",
                width: iconSize,
                opacity: "1",
                zIndex: 21,
                display: showModal ? "none" : "block",
              }}
              src={superCheckVariables.logoimage}
              alt="SuperstarZ logo"
            />
          </Grid>
        </Grid>

        <Grid container xs={12} style={{ height: "100vh", width: "100%" }}>
          <Grid item xs={12}>
            <Grid
              item
              xs={12}
              className="center-content-vertically dontallowhighlighting"
              style={{
                cursor: "pointer",
                zIndex: 0,
                margin: "auto",
                display: "grid",
                position: "relative",
                marginTop: "-16px",
              }}
            >
              <Grid
                onClick={Openmodal}
                className={`${superCheckVariables.moreoptions}   ${optionsClass}  buttonshakelogoption `}
                style={{ zIndex: 1 }}
              >
                <MoreHorizIcon
                  className="make-plainIcons-clickable"
                  style={{ fontSize: fontOptions }}
                />
              </Grid>

              <img
                className="make-small-image-clickable-neutral"
                style={{
                  position: "relative",
                  objectFit: "contain",
                  width: imageWidth,
                  borderRadius: "200px",
                  margin: "auto",
                }}
                src={profilePic}
                alt="SuperstarZ logo"
              />
            </Grid>
          </Grid>
        </Grid>
      </Paper>
    </MuiThemeProvider>
  );
}

export default Supercheck;
