import "./App.css";
import "typeface-roboto";
import React, { useState, useEffect } from "react";
import imyz from "./images/s.png";
import imyzd from "./images/sd.png";
import { Login } from "./app-folder/Login";
import {
  useTheme,
  Paper,
  useMediaQuery,
  Grid,
  Typography,
  makeStyles,
  createMuiTheme,
  MuiThemeProvider,
  DialogContent,
} from "@material-ui/core";
import Option from "./app-folder/Option";
import * as CSS from "csstype";

function App(): JSX.Element {
  var usetheme = useTheme();
  var match = useMediaQuery(usetheme.breakpoints.up("sm"));
  const [darkmode, setDarkmode] = useState<boolean>(false);
  const [textVisible, setTextVisible] = useState<boolean>(true);

  interface IappVariables {
    shade: string;
    shade2: string;
    shade2num: string;
    shade2nump: string;
    logoimage: string;
    secondarymaincolor: string;
    maincolor: string;
    shade2nump22: string;
  }

  var appVariables: IappVariables = {
    shade: "",
    shade2: "",
    shade2num: "",
    shade2nump: "",
    logoimage: "",
    secondarymaincolor: "",
    maincolor: "",
    shade2nump22: "",
  };

  var appVariablesDARK: IappVariables = {
    shade: "#cccccc",
    shade2: "#cccccc",
    shade2num: "1.1",
    shade2nump: "1.8",
    logoimage: imyzd,
    secondarymaincolor: "#ffffff",
    maincolor: "#ffffff",
    shade2nump22: "5.5",
  };

  var appVariablesLIGHT: IappVariables = {
    shade: "#0b1728",
    shade2: "#0b1728",
    shade2num: "1.5",
    shade2nump: "1.5",
    logoimage: imyz,
    secondarymaincolor: "#0b1728",
    maincolor: "#0b1728",
    shade2nump22: "8",
  };

  if (darkmode) {
    appVariables = appVariablesDARK;
  } else {
    appVariables = appVariablesLIGHT;
  }

  let themeGeneralSettings = createMuiTheme({
    palette: {
      primary: {
        main: `${appVariables.maincolor}`,
      },
      secondary: {
        main: `${appVariables.secondarymaincolor}`,
      },
      type: darkmode ? "dark" : "light",
    },
  });

  var useStyle = makeStyles((themeGeneralSettings) => ({
    paperstyle: {
      [themeGeneralSettings.breakpoints.up("xs")]: {
        height: "100vh",
        textAlign: "center",
        backgroundImage: darkmode
          ? "linear-gradient(0deg, #282c34, #282c36, #191919 )"
          : "linear-gradient(0deg, rgb(222,225,225), rgb(238,237,239),rgb(250,250,255) )",
      },
    },

    icon: {
      [themeGeneralSettings.breakpoints.up("xs")]: {
        objectFit: "contain",
        width: "80%",
      },
      [themeGeneralSettings.breakpoints.up("sm")]: {
        objectFit: "contain",
        width: "85%",
      },
      [themeGeneralSettings.breakpoints.up("md")]: {
        objectFit: "contain",
        width: "56%",
        transition: " 0.5s transform ease-in",
        cursor: "pointer",
      },
      "&:hover": {
        transition: "560ms transform ease-in",
        transform: "scale(1.06)",
      },
      "&:active": {
        transition: "200ms transform ease-in",
        transform: "scale(0.94)",
      },
    },

    h1: {
      [themeGeneralSettings.breakpoints.up("xs")]: {
        fontFamily: "kaushan_scriptregular",
        fontSize: "14.8vw",
        fontWeight: "1000",
        transition: "opacity 1.5s",
        opacity: textVisible ? "1" : "0",
      },
      [themeGeneralSettings.breakpoints.up("sm")]: {
        fontFamily: "kaushan_scriptregular",
        fontSize: "12.2vw",
        fontWeight: "1000",
        transition: "opacity 1.5s",
        opacity: textVisible ? "1" : "0",
      },
      [themeGeneralSettings.breakpoints.up("md")]: {
        fontFamily: "kaushan_scriptregular",
        fontSize: "2.6vw",
        fontWeight: "1000",
        transition: "opacity 1.5s",
        opacity: textVisible ? "1" : "0",
      },
    },

    littletext: {
      [themeGeneralSettings.breakpoints.up("xs")]: {
        fontSize: "4.2vw",
        transition: "opacity 1.5s",
        opacity: textVisible ? "1" : "0",
      },
      [themeGeneralSettings.breakpoints.up("sm")]: {
        fontSize: "3vw",
      },
      [themeGeneralSettings.breakpoints.up("md")]: {
        fontSize: "1.2vw",
        transition: "opacity 1.5s",
        opacity: textVisible ? "1" : "0",
      },
    },

    loginclassmobile: {
      [themeGeneralSettings.breakpoints.up("xs")]: {
        padding: "8.5px",
        borderRadius: "50px",
        fontSize: "13px",
        mozBoxShadow: `0 0 ${appVariables.shade2num}px ${appVariables.shade2} `,
        webkitBoxShadow: `0 0 ${appVariables.shade2num}px ${appVariables.shade2} `,
        boxShadow: `0 0 ${appVariables.shade2num}px ${appVariables.shade2} `,
      },
    },

    signupclassmobile: {
      [themeGeneralSettings.breakpoints.up("xs")]: {
        padding: "10.5px",
        borderRadius: "50px",
        fontSize: "13px",
        mozBoxShadow: `0 0 4.5px ${appVariables.shade}`,
        webkitBoxShadow: `0 0 4.5px ${appVariables.shade} `,
        boxShadow: `0 0 4.5px ${appVariables.shade}`,
      },
    },
    loginclasspc: {
      [themeGeneralSettings.breakpoints.up("xs")]: {
        padding: "8.9px",
        borderRadius: "6.5px",
        mozBoxShadow: `0 0 ${appVariables.shade2nump}px  ${appVariables.shade}`,
        webkitBoxShadow: `0 0 ${appVariables.shade2nump}px  ${appVariables.shade}`,
        boxShadow: `0 0 ${appVariables.shade2nump}px  ${appVariables.shade}`,
      },
    },
    signupclasspc: {
      [themeGeneralSettings.breakpoints.up("xs")]: {
        padding: "8.9px",
        borderRadius: "6.5px",
        mozBoxShadow: `0 0  ${appVariables.shade2nump22}px  ${appVariables.shade}`,
        webkitBoxShadow: `0 0  ${appVariables.shade2nump22}px  ${appVariables.shade}`,
        boxShadow: `0 0  ${appVariables.shade2nump22}px  ${appVariables.shade}`,
      },
    },
  }));
  var classes = useStyle();

  const fontDataPc: CSS.Properties = { fontSize: "2.2rem" };

  const fontDataMobile: CSS.Properties = {
    fontSize: "1.7rem",
  };

  const marginDataPc: CSS.Properties = {
    marginLeft: "-35px",
  };

  const marginDataMobile: CSS.Properties = {
    marginLeft: "-15.5px",
  };

  useEffect(
    () => {
      var timer1 = setTimeout(() => setTextVisible(false), 6 * 1100);
      // this will clear Timeout
      // when component unmount like in willComponentUnmount
      // and show will not change to true
      return () => {
        clearTimeout(timer1);
      };
    },
    // useEffect will run only one time with empty []
    // if you pass a value to array,
    // like this - [data]
    // than clearTimeout will run every time
    // this value changes (useEffect re-run)
    [textVisible]
  );

  const textvisibility = (timer1: any) => {
    setTextVisible(true);
    clearTimeout(timer1);
  };

  return (
    <MuiThemeProvider theme={themeGeneralSettings}>
      <Paper
        onMouseOver={textvisibility}
        onTouchStart={textvisibility}
        className={classes.paperstyle}
        style={{ borderRadius: "0px" }}
      >
        <Grid xs={12} container className="fadeboyin" style={{ zIndex: 3 }}>
          {match ? (
            <Option
              switchsize="medium"
              darkmode={darkmode}
              setDarkmode={setDarkmode}
              marginData={marginDataPc}
              fontData={fontDataPc}
              superFont={classes.h1}
            />
          ) : (
            <Option
              switchsize="small"
              darkmode={darkmode}
              setDarkmode={setDarkmode}
              marginData={marginDataMobile}
              fontData={fontDataMobile}
              superFont={classes.h1}
            />
          )}

          <Grid
            container
            xs={12}
            className={match ? "containerapp    " : "containerappmobile   "}
          >
            <Grid xs={3} sm={4} md={4}></Grid>
            <Grid
              item
              className="centericon   dontallowselect"
              xs={6}
              sm={4}
              md={4}
            >
              <img
                className={classes.icon}
                src={appVariables.logoimage}
                alt="SuperstarZ logo"
              />
            </Grid>

            <Grid xs={12}>
              <Typography
                style={{
                  fontFamily: "kaushan_scriptregular",
                  marginTop: "10px",
                  fontSize: "2.5vh",
                  position: "absolute",
                  textAlign: "center",
                  width: "100%",
                }}
              >
                <p
                  style={{ marginLeft: "1px", fontWeight: "normal" }}
                  className={classes.littletext}
                >
                  Express Yourself..
                </p>
              </Typography>
            </Grid>

            <Grid xs={3} sm={3} md={4}></Grid>
            <Login
              loginclass={classes.loginclassmobile}
              signupclass={classes.signupclassmobile}
              match={match}
            />
          </Grid>
        </Grid>
      </Paper>
    </MuiThemeProvider>
  );
}

export default App;
