import React, { useRef, useState, useCallback, useEffect } from "react";
import { Grid } from "@material-ui/core";
import CloseIcon from "@mui/icons-material/Close";
import { RootStateOrAny, useSelector, useDispatch } from "react-redux";

function AudioEditorx({
  AudioUrl,
  setShowAudio,
  ShowAudio,
  setinteractContentAudio,
  setinteractContentAudiotype,
}: any): JSX.Element {
  // Reference to the audio element


  interface HTMLaudioElementWithCapture extends HTMLAudioElement {
    captureStream(): MediaStream;
  }

  const audioPlayerRef = useRef<HTMLaudioElementWithCapture>(null);



  // States
  const [recordedAudioUrl, setRecordedAudioUrl] = useState(null);
  const [recording, setRecording] = useState(false);
  const [processing, setProcessing] = useState(false);

  const recorderRef = useRef<MediaRecorder | null>(null);

  // Redux state for dark mode
  interface RootStateGlobalReducer {
    GlobalReducer: {
      darkmode: boolean;
    };
  }
  const { darkmode } = useSelector((state: RootStateGlobalReducer) => ({
    ...state.GlobalReducer,
  }));

  const darkmodeReducer = darkmode;

  const startRecording = () => {
    const audio = audioPlayerRef.current;
    if (audio) {
      const stream = audio.captureStream();
      const options = { mimeType: "audio/webm" };

      recorderRef.current = new MediaRecorder(stream, options);

      const chunks: any[] = [];
      recorderRef.current.ondataavailable = (e: any) => chunks.push(e.data);
      recorderRef.current.onstop = () => {
        const blob = new Blob(chunks, { type: options.mimeType });
        const url: any = URL.createObjectURL(blob);
        setRecordedAudioUrl(url);
        setProcessing(false);
      };

      recorderRef.current.start();
      setRecording(true);
    }
  };


  const stopRecording = () => {
    if (recorderRef.current && recorderRef.current.state === "recording") {
      recorderRef.current.stop();
      setRecording(false);
    }
  };

  const [p, setp] = useState(false);

  const saveRecording = useCallback(() => {
    setinteractContentAudio(recordedAudioUrl);
    setinteractContentAudiotype(1);
    /// setShowAudio(false);
    setp(true);
  }, [
    recordedAudioUrl,
    setinteractContentAudio,
    setinteractContentAudiotype,
    setShowAudio,
  ]);

  const closeEditor = () => {
    setShowAudio(false);
  };

  function readFileAsDataUrl(file: any) {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();

      reader.onloadend = () => {
        resolve(reader.result);
      };

      reader.onerror = () => {
        reject(reader.error);
      };

      // Read the file and get the Data URL directly
      reader.readAsDataURL(file);
    });
  }


  const { PaperStyleLight, PaperStyleDark } = useSelector(
    (state: RootStateOrAny) => ({
      ...state.PaperReducerLightnDark,
    })
  );

  ///
  ///
  ///
  /// GET LOGGED USER DATA FROM REDUX STORE
  interface RootStateReducerImage {
    UserdataReducer: {
      image: string;
      imageThumb: string;
      id: number;
      username: string;
      memeberPageid: number;
      MemberProfileData: any;
    };
  }
  const { username, image, imageThumb, id, memeberPageid, MemberProfileData } =
    useSelector((state: RootStateReducerImage) => ({
      ...state.UserdataReducer,
    }));

  const [imageReducer, setimageReducer] = useState("");

  const [imageReducerThumb, setimageReducerThumb] = useState("");




  return (
    <>
      <Grid
        xs={12}
        style={{
          display: "block",
          position: "fixed",
          top: "0vh",
          height: "100vh",
          backgroundImage: darkmodeReducer ? PaperStyleDark : PaperStyleLight,
          width: "100%",
          margin: "auto",
          textAlign: "center",
          zIndex: '2000'
        }}
      >

        <img
          src={imageThumb}
          style={{
            width: "20%",
            height: "auto",
          }}
        />

        <img
          src={image}
          style={{
            width: "20%",
            height: "auto",
          }}
        />
        {

          p ? recordedAudioUrl ?
            <audio
              ref={audioPlayerRef}
              src={recordedAudioUrl}
              controls
              style={{ width: "50%", margin: "auto", textAlign: "center" }}
            ></audio> : null :

            AudioUrl ?
              <audio
                ref={audioPlayerRef}
                src={AudioUrl}
                controls
                style={{ width: "50%", margin: "auto", textAlign: "center" }}
              ></audio> : null}

        <Grid
          item
          xs={12}
          style={{
            position: "relative",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            fontSize: "24px",
            fontFamily: "Helvetica, Arial, sans-serif",
            color: "#ffffff",
          }}
        >
          <button
            onClick={startRecording}
            disabled={recording}
            style={{ margin: "10px" }}
          >
            Start Recording
          </button>
          <button
            onClick={stopRecording}
            disabled={!recording}
            style={{ margin: "10px" }}
          >
            Stop Recording
          </button>
          <button
            onClick={saveRecording}
            disabled={!recordedAudioUrl}
            style={{ margin: "10px" }}
          >
            Save
          </button>
        </Grid>

        <Grid
          item
          xs={12}
          style={{
            margin: "auto",
            textAlign: "right",
            left: "5px",
            height: "20px",
            position: "fixed",
            top: "3vh",
            right: "3vw",
            display: "block",
          }}
        >
          <CloseIcon
            onClick={() => {

              setShowAudio(false)

              ///closeEditor
            }}
            className={
              darkmode
                ? "make-small-icons-clickable-lightCrop dontallowhighlighting zuperkingIcon "
                : "make-small-icons-clickable-darkCrop dontallowhighlighting zuperkingIcon  "
            }
            style={{ margin: "auto", color: "#ffffff", fontSize: "2.5vw" }}
          />
        </Grid>
      </Grid>


    </>
  );
}

export const AudioEditor = React.memo(AudioEditorx);
