import React, {
  useRef,
  useState,
  useCallback,
  useEffect,
  useLayoutEffect,
} from "react";
import { Grid, Box } from "@material-ui/core";
import { matchPc, matchTablet, matchMobile } from "../DetectDevice";
import { RootStateOrAny, useSelector, useDispatch } from "react-redux";
import MusicNoteIcon from "@mui/icons-material/MusicNote";
import PhotoIcon from "@mui/icons-material/Photo";
import GifIcon from "@mui/icons-material/Gif";
import ChevronRightIcon from "@mui/icons-material/ChevronRight";
import SwipeIcon from "@mui/icons-material/Swipe";
import { useSpring, animated } from "react-spring";

function OptionsUploadSliderx({
  ActiveSlide,
  optionsNameData,
  clickOptions,
  optionsClickType,
  getSliderWidthNew,
  setopeninteraction,
  ClickMonster,
  optionsImages,
  selectedImage,
  imageHandleChange,
  imageHandleChangexx,
  handleTouchStartOptions,
  handleTouchMoveOptions,
}: any): JSX.Element {
  ///
  ///
  ///
  /// INTERFACE/TYPES FOR SCREENHEIGHT AND DARKMODE
  interface RootStateGlobalReducer {
    GlobalReducer: {
      darkmode: boolean;
      screenHeight: number;
    };
  }

  ///
  ///
  ///
  /// GET SCREENHEIGHT FROM REDUX STORE
  const { screenHeight, darkmode } = useSelector(
    (state: RootStateGlobalReducer) => ({
      ...state.GlobalReducer,
    })
  );
  const screenHeightReducer = screenHeight;
  const darkmodeReducer = darkmode;

  ///
  ///
  /// GET COLOR FROM REDUX STORE
  interface RootStateReducerColor {
    GlobalReducerColor: {
      color: string;
      colordark: string;
      colortype: number;
    };
  }
  const { color, colordark, colortype } = useSelector(
    (state: RootStateReducerColor) => ({
      ...state.GlobalReducerColor,
    })
  );
  const colorReducer = color;
  const colorReducerdark = colordark;
  const colortypeReducer = colortype;

  return (
    <>
      <input
        onChange={imageHandleChange}
        type="file"
        name="superImages"
        accept="image/*"
        multiple
        id="file"
        style={{ visibility: "hidden" }}
      />
      <input
        onChange={imageHandleChangexx}
        type="file"
        name="superImages"
        accept="image/*"
        multiple
        id="fileint"
        style={{ visibility: "hidden" }}
      />
      <Grid
        container
        onTouchStart={handleTouchStartOptions}
        onTouchMove={handleTouchMoveOptions}
        style={{
          zIndex: 1,
          padding: "0px",
          top: matchPc ? "-2vh" : "0vh",
          position: "relative",
          margin: "0 auto",
          overflow: "hidden",
          left: "0px",
          height: matchPc ? "24.4vh" : "20.5vh",
          paddingBottom: "1px",
          marginTop: selectedImage.length > 0 ? "0px" : "20vh",
        }}
      >
        <animated.div ref={optionsCollectImageRef} style={modalanimation}>
          {optionsImages.map((im: any, i: any) => (
            <>
              <Grid key={i} item xs={12} style={{ opacity: 1 }}>
                <Grid
                  item
                  xs={12}
                  style={{
                    margin: "auto",
                    textAlign: "center",
                    position: "relative",
                    padding: "0px",
                    paddingBottom: "0px",
                    top: "1vh",
                  }}
                >
                  <Grid
                    item
                    xs={12}
                    style={{
                      padding: "0px",
                      fontSize: matchPc ? "1.1vw" : "2vh",
                      fontWeight: "bolder",
                      fontFamily: "Arial, Helvetica, sans-serif",
                      visibility: ActiveSlide === i ? "visible" : "hidden",
                      color: darkmodeReducer ? "#dddddd" : "#0b111b",
                    }}
                  >
                    {" "}
                    {optionsNameData[i]}
                  </Grid>
                </Grid>

                {ActiveSlide === 1 && i === 1 ? (
                  <label htmlFor="file">
                    <div
                      onClick={() => {
                        clickOptions(i, optionsClickType, "");
                      }}
                      style={{
                        cursor: ActiveSlide === i ? "pointer" : "copy",
                        opacity: ActiveSlide === i ? 0.5 : 1,
                        width: `${getSliderWidthNew}px`,
                        height: `${getSliderWidthNew}px`,
                        backgroundColor: darkmodeReducer
                          ? "rgba(010,010,010, 0.68)"
                          : "rgba(170,170,170, 0.5)",
                        borderRadius: "50%",
                        marginTop: "2.15vh",
                        textAlign: "center",
                        alignItems: "center",
                        display: "grid",
                        justifyContent: "center",
                        boxShadow: darkmodeReducer
                          ? ActiveSlide === i
                            ? `0 0 5.5px ${colorReducer}`
                            : "0 0 1.5px#aaaaaa"
                          : ActiveSlide === i
                          ? `0 0 5.5px ${colorReducer}`
                          : "0 0 1.45px#222222",
                      }}
                    >
                      {i === 1 ? (
                        <PhotoIcon
                          style={{
                            fontSize: matchPc ? "2vw" : "5vh",
                            color: darkmodeReducer ? "#eeeeee" : "#222222",
                          }}
                          className="zuperkinginfo"
                        />
                      ) : null}
                    </div>
                  </label>
                ) : (
                  <>
                    {" "}
                    <span>
                      <div
                        onClick={() => {
                          ClickMonster(i);
                        }}
                        style={{
                          cursor: ActiveSlide === i ? "pointer" : "copy",
                          width: `${getSliderWidthNew}px`,
                          height: `${getSliderWidthNew}px`,
                          backgroundColor: darkmodeReducer
                            ? "rgba(010,010,010, 0.68)"
                            : "rgba(170,170,170, 0.5)",
                          borderRadius: "50%",
                          marginTop: "2.15vh",
                          textAlign: "center",
                          alignItems: "center",
                          display: "grid",
                          opacity: ActiveSlide === i ? 0.5 : 1,
                          justifyContent: "center",
                          boxShadow: darkmodeReducer
                            ? ActiveSlide === i
                              ? colortypeReducer === 0
                                ? `0 0 5.5px ${colorReducerdark}`
                                : `0 0 5.5px ${colorReducer}`
                              : "0 0 1.5px#aaaaaa"
                            : ActiveSlide === i
                            ? `0 0 5.5px ${colorReducer}`
                            : "0 0 1.45px#222222",
                        }}
                      >
                        {i === 0 ? (
                          <MusicNoteIcon
                            style={{
                              fontSize: matchPc ? "2vw" : "5vh",
                              color: darkmodeReducer ? "#eeeeee" : "#222222",
                            }}
                            className="zuperkinginfo"
                          />
                        ) : null}
                        {i === 1 ? (
                          <PhotoIcon
                            style={{
                              fontSize: matchPc ? "2vw" : "5vh",
                              color: darkmodeReducer ? "#eeeeee" : "#222222",
                            }}
                            className="zuperkinginfo"
                          />
                        ) : null}

                        {i === 2 ? (
                          <SwipeIcon
                            onClick={() => {
                              setopeninteraction(true);
                            }}
                            style={{
                              fontSize: matchPc ? "2.5vw" : "5vh",
                              color: darkmodeReducer ? "#eeeeee" : "#222222",
                            }}
                            className="zuperkinginfo"
                          />
                        ) : null}
                        {i === 3 ? (
                          <ChevronRightIcon
                            style={{
                              fontSize: matchPc ? "2.3vw" : "5vh",
                              color: darkmodeReducer ? "#eeeeee" : "#222222",
                            }}
                            className="zuperkinginfo"
                          />
                        ) : null}
                      </div>
                    </span>
                  </>
                )}
              </Grid>
            </>
          ))}
        </animated.div>
      </Grid>{" "}
    </>
  );
}

export const OptionsUploadSlider = React.memo(OptionsUploadSliderx);
