import { animated, useSpring } from "react-spring";
import { Grid, DialogContent } from "@material-ui/core";
import ErrorOutlineIcon from "@material-ui/icons/ErrorOutline";
import { IServerError } from "./appFolder-Interfaces";

export default function ServerError({
  serverErrorDisplay,
  serverErrorData,
  darkmode,
}: IServerError) {
  //
  //
  //
  //USE SLIDE DOWN ANIMATION FROM REACT SPRING SERVER ERROR
  const serverErrorAnimation = useSpring({
    config: {
      duration: 500,
    },
    opacity: serverErrorDisplay,
    transform: serverErrorData ? `translateY(0%)` : `translateY(-100%)`,
  });

  var ErrorBacklight = "";
  var ErrorColor = "";
  var ErrorTextColor = "";
  var ErrorIconColor = "";

  if (darkmode) {
    ErrorBacklight = "turdark";
    ErrorColor = "#222222";
    ErrorTextColor = "#dddddd";
    ErrorIconColor = "orange";
  } else {
    ErrorBacklight = "turlight";
    ErrorColor = "#eeeeee";
    ErrorTextColor = "#222222";
    ErrorIconColor = "red";
  }

  return (
    <>
      <DialogContent
        className="dialog-container dontallowhighlighting  turlight "
        style={{
          padding: "0px",
          height: "40px",
          position: "fixed",
          display: "flex",
          top: "0em",
          zIndex: 2,
          width: "100%",
          backgroundColor: "#eeeeee",
          marginTop: "0.3px",
        }}
      >
        <animated.div style={serverErrorAnimation}>
          <Grid
            xs={12}
            style={{
              color: "#000000",
              fontSize: "0.83vw",
              display: "flex",
              alignContent: "center",
              alignItems: "center",
              paddingRight: "15px",
              justifyContent: "flex-end",
              textAlign: "left",
              fontWeight: "normal",
              fontFamily: "sans-serif",
            }}
          >
            <Grid xs={2}>
              <ErrorOutlineIcon
                style={{
                  marginLeft: "8px",
                  marginRight: "12px",
                  color: "red",
                  fontSize: "1.8vw",
                }}
              />
            </Grid>
            <Grid style={{ opacity: 0.6 }} xs={10}>
              {serverErrorData}
            </Grid>
          </Grid>
        </animated.div>
      </DialogContent>
    </>
  );
}
