import React, { useRef, useState, useCallback } from "react";
import { matchPc, matchTablet } from "../DetectDevice";
import { Grid } from "@material-ui/core";
import MoreHorizIcon from "@material-ui/icons/MoreHoriz";
import { useSelector, useDispatch } from "react-redux";
import { useSpring, animated } from "react-spring";
import Masonry from "@mui/lab/Masonry";
import CircleIcon from "@mui/icons-material/Circle";
import { SuperCrop } from "./SuperCrop";
import MusicNoteIcon from "@mui/icons-material/MusicNote";
import PhotoIcon from "@mui/icons-material/Photo";
import GifIcon from "@mui/icons-material/Gif";
import ChevronRightIcon from "@mui/icons-material/ChevronRight";
import Axios from "axios";

function UploadMenux({
  optionsShow,
  optinstopshowingReducer,
  typeTop,
  closeoptionsslide,
  animationop,
  optionsCollectImageRef,
  handleTouchStartOptions,
  handleTouchMoveOptions,
  modalanimation,
  nextSlidePc,
  optionsImages,
  ActiveSlide,
  optionsNameData,
  clickOptions,
  optionsClickType,
  getSliderWidthNew,
}: any): JSX.Element {
  ///
  ///
  ///
  ///DARKMODE FROM REDUX
  interface RootStateGlobalReducer {
    GlobalReducer: {
      darkmode: boolean;
    };
  }
  const { darkmode } = useSelector((state: RootStateGlobalReducer) => ({
    ...state.GlobalReducer,
  }));

  const darkmodeReducer = darkmode;

  const { REACT_APP_SUPERSTARZ_URL } = process.env;

  ///
  ///
  /// GET COLOR FROM REDUX STORE
  interface RootStateReducerColor {
    GlobalReducerColor: {
      color: string;
      colordark: string;
      colortype: number;
    };
  }
  const { color, colordark, colortype } = useSelector(
    (state: RootStateReducerColor) => ({
      ...state.GlobalReducerColor,
    })
  );
  const colorReducer = color;
  const colorReducerdark = colordark;
  const colortypeReducer = colortype;

  const [selectedImage, setselectedImage] = useState<Array<any>>([]);
  const [cropimage, setcropimage] = useState<string | undefined>("");
  const [showCrop, setshowCrop] = useState<boolean>(false);

  const [selectedImageUpload, setselectedImageUpload] = useState<any>(null);

  const updateColor = useCallback(() => {
    Axios.post(
      `http://${REACT_APP_SUPERSTARZ_URL}/upload`,

      {
        withCredentials: true,
      }
    )
      .then((response) => {
        if (response.data.message === "ok") {
          alert("about color errorxx");
        }
      })
      .catch(function (error) {
        alert("about color error");
      });
  }, [REACT_APP_SUPERSTARZ_URL]);

  const imageHandleChange = (e: any) => {
    if (e.target.files && e.target.files.length > 0) {
      const FileArray = Array.from(e.target.files).map((file: any) =>
        URL.createObjectURL(file)
      );

      setselectedImage((prevImages) => prevImages.concat(FileArray));
      Array.from(e.target.files).map((file: any) => URL.revokeObjectURL(file));
      setcropimage(FileArray[0]);
      setshowCrop(true);

      const formData = new FormData();

      for (let i = 0; i < e.target.files; i++) {
        formData.append(e.target.files[i], e.target.files[i].name);
      }

      updateColor(ormData);
    }
  };

  const RenderPreview = (source: any) => {
    return source.map((photo: any) => {
      return (
        <img
          className={
            darkmodeReducer ? "turlightpostdarkx" : "turlightpostlightx"
          }
          src={photo}
          key={photo}
          style={{ padding: "0px", borderRadius: "8px" }}
        />
      );
    });
  };

  return (
    <>
      {showCrop ? (
        <SuperCrop cropimage={cropimage} />
      ) : (
        <>
          {" "}
          <Grid
            container
            onTouchStart={handleTouchStartOptions}
            onTouchMove={handleTouchMoveOptions}
            style={{
              zIndex: 1,
              padding: "0px",
              top: matchPc ? "-2vh" : "0vh",
              position: "relative",
              margin: "0 auto",
              overflow: "hidden",
              left: "0px",
              height: matchPc ? "24.4vh" : "20.5vh",
              paddingBottom: "1px",
            }}
          >
            <animated.div ref={optionsCollectImageRef} style={modalanimation}>
              {optionsImages.map((im: any, i: any) => (
                <Grid key={i} item xs={12}>
                  {ActiveSlide === 1 && i === 1 ? (
                    <label htmlFor="file">
                      <div
                        onClick={() => {
                          clickOptions(i, optionsClickType, "");
                        }}
                        style={{
                          cursor: ActiveSlide === i ? "pointer" : "alias",
                          width: `${getSliderWidthNew}px`,
                          height: `${getSliderWidthNew}px`,
                          backgroundColor: darkmodeReducer
                            ? "rgba(010,010,010, 0.68)"
                            : "rgba(220,220,220, 0.7)",
                          borderRadius: "50%",
                          marginTop: "2.15vh",
                          textAlign: "center",
                          alignItems: "center",
                          display: "grid",
                          justifyContent: "center",
                          boxShadow: darkmodeReducer
                            ? ActiveSlide === i
                              ? colortypeReducer === 0
                                ? `0 0 5.5px ${colorReducerdark}`
                                : `0 0 5.5px ${colorReducer}`
                              : "0 0 5.5px#aaaaaa"
                            : ActiveSlide === i
                            ? `0 0 5.5px ${colorReducer}`
                            : "0 0 5.45px#222222",
                        }}
                      >
                        <PhotoIcon
                          style={{
                            fontSize: matchPc ? "2vw" : "5vh",
                            color: darkmodeReducer ? "#eeeeee" : "#222222",
                          }}
                          className="zuperkinginfo"
                        />
                      </div>
                    </label>
                  ) : (
                    <>
                      {" "}
                      <div
                        onClick={() => {
                          clickOptions(i, optionsClickType, "");
                        }}
                        style={{
                          cursor: ActiveSlide === i ? "pointer" : "alias",
                          width: `${getSliderWidthNew}px`,
                          height: `${getSliderWidthNew}px`,
                          backgroundColor: darkmodeReducer
                            ? "rgba(010,010,010, 0.68)"
                            : "rgba(220,220,220, 0.7)",
                          borderRadius: "50%",
                          marginTop: "2.15vh",
                          textAlign: "center",
                          alignItems: "center",
                          display: "grid",

                          justifyContent: "center",
                          boxShadow: darkmodeReducer
                            ? ActiveSlide === i
                              ? colortypeReducer === 0
                                ? `0 0 5.5px ${colorReducerdark}`
                                : `0 0 5.5px ${colorReducer}`
                              : "0 0 5.5px#aaaaaa"
                            : ActiveSlide === i
                            ? `0 0 5.5px ${colorReducer}`
                            : "0 0 5.45px#222222",
                        }}
                      >
                        {i === 0 ? (
                          <MusicNoteIcon
                            style={{
                              fontSize: matchPc ? "2vw" : "5vh",
                              color: darkmodeReducer ? "#eeeeee" : "#222222",
                            }}
                            className="zuperkinginfo"
                          />
                        ) : null}
                        {i === 1 ? (
                          <PhotoIcon
                            style={{
                              fontSize: matchPc ? "2vw" : "5vh",
                              color: darkmodeReducer ? "#eeeeee" : "#222222",
                            }}
                            className="zuperkinginfo"
                          />
                        ) : null}
                        {i === 2 ? (
                          <GifIcon
                            style={{
                              fontSize: matchPc ? "2.5vw" : "5vh",
                              color: darkmodeReducer ? "#eeeeee" : "#222222",
                            }}
                            className="zuperkinginfo"
                          />
                        ) : null}
                        {i === 3 ? (
                          <ChevronRightIcon
                            style={{
                              fontSize: matchPc ? "2.3vw" : "5vh",
                              color: darkmodeReducer ? "#eeeeee" : "#222222",
                            }}
                            className="zuperkinginfo"
                          />
                        ) : null}
                      </div>
                    </>
                  )}
                  <Grid
                    item
                    xs={12}
                    style={{
                      margin: "auto",
                      textAlign: "center",
                      position: "relative",
                      bottom: "-0.5em",
                    }}
                  >
                    <Grid
                      item
                      xs={12}
                      style={{
                        fontSize: matchPc ? "1.1vw" : "2vh",
                        fontWeight: "bolder",
                        fontFamily: "Arial, Helvetica, sans-serif",
                        visibility: ActiveSlide === i ? "visible" : "hidden",
                        color: darkmodeReducer ? "#dddddd" : "#0b111b",
                      }}
                    >
                      {" "}
                      {optionsNameData[i]}
                    </Grid>
                  </Grid>
                </Grid>
              ))}
            </animated.div>
          </Grid>
        </>
      )}
      <input
        onChange={imageHandleChange}
        type="file"
        name="superImages"
        accept="image/*"
        multiple
        id="file"
        style={{ visibility: "hidden" }}
      />

      <Grid
        item
        xs={12}
        style={{
          padding: "0px",
          height: "auto",
          paddingLeft: "6vw",
        }}
      >
        {selectedImage ? (
          <Masonry columns={matchPc ? 3 : 1} spacing={10}>
            {RenderPreview(selectedImage)}
          </Masonry>
        ) : null}
      </Grid>
    </>
  );
}

export const UploadMenu = React.memo(UploadMenux);
