import React, { useState } from "react";
import {
  IconButton,
  InputAdornment,
  Grid,
  DialogContent,
  Paper,
  makeStyles,
  TextField,
  Button,
} from "@material-ui/core";
import { IDialogeDataMobile, IGrid } from "./appFolder-Interfaces";
import { useSpring, animated } from "react-spring";

import ModalFormSignupError from "./ModalFormSignupError";
import ModalFormLoginError from "./ModalFormLoginError";
import ServerError from "./ServerError";
import VisibilityOffIcon from "@material-ui/icons/VisibilityOff";
import VisibilityIcon from "@material-ui/icons/Visibility";

function MobileModalLogx(DialogeDataMobile: IDialogeDataMobile) {
  const [mobileZoom, setMobileZoom] = useState<boolean>(false);

  ///
  ///
  ///
  /// DYNAMIC MODAL LAYOUT VARIABLES
  const wideImageControlTrue: string = "75vw";
  const wideImageControlfalse: string = "70vw";
  const zoomImageControl: string = "100vw";
  const GridMiniAwide: IGrid = 8;
  const GridMiniBwide: IGrid = 4;
  const GridMiniAlong: IGrid = 7;
  const GridMiniBlong: IGrid = 5;
  const GridZoomAwide: IGrid = 8;
  const GridZoomBwide: IGrid = 4;
  const GridZoomAlong: IGrid = 6;
  const GridZoomBlong: IGrid = 6;

  let GridHolderA: IGrid = 7;
  let GridHolderB: IGrid = 5;

  let GridxA: IGrid = 7;
  let GridxB: IGrid = 5;

  let GridyA: IGrid = 7;
  let GridyB: IGrid = 5;

  let wideImageControl: string = "70vw";
  let imageReal: string = "75vw";

  let borderGrid: string = "11px";
  var WidthHolder: string = "82%";

  var opacitySlidingModalImage = "0";
  var zIndexModalImageSmall = 10;
  var zIndexModalImageZoom = 0;
  var opacityFixedModalImage = "1";
  var slidingModalImageBack = "none";
  var slidingImageWidth = "50%";
  var mobilemodalrad = "8px";

  mobileZoom ? (mobilemodalrad = "0px") : (mobilemodalrad = "8px");

  if (DialogeDataMobile.zoomedModal) {
    if (DialogeDataMobile.imageHeightoverflow) {
      opacitySlidingModalImage = "1";
      zIndexModalImageSmall = 0;
      zIndexModalImageZoom = 10;
      opacityFixedModalImage = "0";
      slidingModalImageBack = "block";
    }

    borderGrid = "0px";

    if (DialogeDataMobile.wideImage) {
      slidingImageWidth = "66%";
      WidthHolder = "76%";
      wideImageControl = wideImageControlTrue;
      GridxA = GridZoomAwide;
      GridxB = GridZoomBwide;
    } else {
      slidingImageWidth = "50%";
      WidthHolder = "88%";
      wideImageControl = wideImageControlfalse;
      GridxA = GridZoomAlong;
      GridxB = GridZoomBlong;
    }
    imageReal = zoomImageControl;
    GridHolderA = GridxA;
    GridHolderB = GridxB;
  } else {
    WidthHolder = "82%";
    borderGrid = "11px";

    if (DialogeDataMobile.wideImage) {
      wideImageControl = wideImageControlTrue;
      GridyA = GridMiniAwide;
      GridyB = GridMiniBwide;
    } else {
      wideImageControl = wideImageControlfalse;
      GridyA = GridMiniAlong;
      GridyB = GridMiniBlong;
    }
    imageReal = wideImageControl;
    GridHolderA = GridyA;
    GridHolderB = GridyB;
  }
  ///
  ///
  ///
  /// DYNAMIC MODAL LAYOUT VARIABLES

  ///

  ///
  ///
  ///
  ///CREATE A SLIDE UP ANIMATION WITH  REACT SPRING
  const mobileLogmodalanimation: any = useSpring({
    config: {
      duration: 115,
    },
    opacity: mobileZoom ? 1 : 0.98,
    position: mobileZoom ? `static` : `fixed`,
    top: "27vh",
    zindex: 100,
    transform: mobileZoom ? `translateY(0%)` : `translateY(-10%)`,
  });

  return (
    <DialogContent
      className="fadermodal FormDialog-container-mobile dontallowhighlighting"
      style={{
        overflow: "auto",
        cursor: "pointer",
        padding: "0px",
        zIndex: 100,
      }}
    >
      <ServerError
        device="mobile"
        serverEmojiplain={DialogeDataMobile.serverEmojiplain}
        setServerErrorData={DialogeDataMobile.setServerErrorData}
        serverErrorDisplay={DialogeDataMobile.serverErrorDisplay}
        serverErrorData={DialogeDataMobile.serverErrorData}
        darkmode={DialogeDataMobile.darkmode}
      />
      <animated.div style={DialogeDataMobile.modalanimation}>
        <Paper
          className={DialogeDataMobile.PaperStyle}
          style={{
            cursor: "default",
          }}
        >
          <Grid container>
            <Grid item xs={12} style={{ marginTop: "-2.5px" }}>
              <img
                onClick={() => setMobileZoom(!mobileZoom)}
                onLoad={DialogeDataMobile.onimageload}
                src={DialogeDataMobile.showimage}
                className="modalMobileImageStyle"
                alt="Logsmall"
              />
            </Grid>
            <Grid
              item
              xs={12}
              className={mobileZoom ? "zoomMobile" : "smallMobile"}
            >
              {" "}
              <animated.div style={mobileLogmodalanimation}>
                <Paper
                  style={{
                    backgroundImage: DialogeDataMobile.PaperStyle,
                    cursor: "default",
                    height: "99vh",
                    borderRadius: "0px",
                    marginTop: "-1.9px",
                    borderTopRightRadius: mobilemodalrad,
                    borderTopLeftRadius: mobilemodalrad,
                  }}
                >
                  <Grid xs={12} item className="formholder">
                    {DialogeDataMobile.formtype ? (
                      <Grid
                        item
                        xs={12}
                        className=""
                        style={{ marginTop: "-1.5px" }}
                      >
                        <ModalFormLoginError
                          type={true}
                          device="mobile"
                          ErrorDisplay={
                            DialogeDataMobile.errorsLoginInputedUsername
                          }
                          darkmode={DialogeDataMobile.darkmode}
                          WidthHolder={WidthHolder}
                        />

                        <TextField
                          style={{
                            transform: "scale(0.93)",
                            width: "100%",
                            paddingBottom: "60px",
                          }}
                          label="Username"
                          margin="normal"
                          type="text"
                          onChange={DialogeDataMobile.updateLoginvalues}
                          name="inputedUsername"
                          value={DialogeDataMobile.rawLogininputedUsername}
                          variant="standard"
                        />

                        <TextField
                          size="small"
                          style={{
                            transform: "scale(0.93)",
                            width: "100%",
                            paddingBottom: "40px",
                            zIndex: 1,
                          }}
                          label="Password"
                          onChange={DialogeDataMobile.updateLoginvalues}
                          type={
                            DialogeDataMobile.loginShowPassword
                              ? "text"
                              : "password"
                          }
                          name="inputedPassword"
                          value={DialogeDataMobile.rawLogininputedPassword}
                          variant="standard"
                          InputProps={{
                            endAdornment: (
                              <InputAdornment position="end">
                                <IconButton
                                  onClick={
                                    DialogeDataMobile.ShowLoginPasswordForaWhile
                                  }
                                  onMouseDown={
                                    DialogeDataMobile.ShowLoginPasswordForaWhile
                                  }
                                  aria-label="toggle password visibility"
                                >
                                  {DialogeDataMobile.loginShowPassword ? (
                                    <VisibilityIcon />
                                  ) : (
                                    <VisibilityOffIcon />
                                  )}
                                </IconButton>
                              </InputAdornment>
                            ),
                          }}
                        />
                        <ModalFormLoginError
                          type={false}
                          ErrorDisplay={
                            DialogeDataMobile.errorsLoginInputedPassword
                          }
                          device="mobile"
                          darkmode={DialogeDataMobile.darkmode}
                          WidthHolder={WidthHolder}
                        />

                        <Grid
                          container
                          style={{ marginTop: "40px", zIndex: 2 }}
                        >
                          <Grid item xs={4}></Grid>
                          <Grid item className="buttonpad buttonshake" xs={4}>
                            <Button
                              onClick={DialogeDataMobile.logmein}
                              style={DialogeDataMobile.loginstyle}
                              fullWidth={true}
                              variant="outlined"
                              size="large"
                              color="primary"
                            >
                              Log In
                            </Button>
                          </Grid>{" "}
                        </Grid>
                      </Grid>
                    ) : (
                      <Grid
                        item
                        xs={12}
                        className=""
                        style={{ marginTop: "-1.5px" }}
                      >
                        <ModalFormSignupError
                          device="mobile"
                          ErrorType={0}
                          textField="email"
                          errorFormChecking={
                            DialogeDataMobile.errorFormCheckinginputedEmail
                          }
                          WidthHolder={WidthHolder}
                          darkmode={DialogeDataMobile.darkmode}
                          type={true}
                          cantPassBadEmail={DialogeDataMobile.cantPassBadEmail}
                          ErrorDisplay={DialogeDataMobile.emailErrorDisplay}
                          ErrorData={DialogeDataMobile.emailErrorData}
                        />

                        <TextField
                          size="small"
                          style={{
                            transform: "scale(0.93)",
                            width: "100%",
                            paddingBottom: "40px",
                          }}
                          label="Email"
                          type="email"
                          onChange={DialogeDataMobile.updateSignvalues}
                          name="inputedEmail"
                          value={DialogeDataMobile.rawSignupValuesinputedEmail}
                          variant="standard"
                        />

                        <ModalFormSignupError
                          device="mobile"
                          ErrorType={
                            DialogeDataMobile.errorsSignupValuesinputedUsername
                          }
                          textField="username"
                          errorFormChecking={
                            DialogeDataMobile.errorFormCheckinginputedUsername
                          }
                          WidthHolder={WidthHolder}
                          darkmode={DialogeDataMobile.darkmode}
                          type={true}
                          cantPassBadEmail={DialogeDataMobile.cantPassBadEmail}
                          ErrorDisplay={DialogeDataMobile.usernameErrorDisplay}
                          ErrorData={DialogeDataMobile.usernameErrorData}
                        />
                        <TextField
                          size="small"
                          style={{
                            transform: "scale(0.93)",
                            width: "100%",
                            paddingBottom: "65px",
                          }}
                          label="Username"
                          type="text"
                          onChange={DialogeDataMobile.updateSignvalues}
                          name="inputedUsername"
                          value={
                            DialogeDataMobile.rawSignupValuesinputedUsername
                          }
                          variant="standard"
                        />

                        <TextField
                          size="small"
                          style={{
                            transform: "scale(0.93)",
                            width: "100%",
                            paddingBottom: "32px",
                          }}
                          label="Password"
                          type={
                            DialogeDataMobile.signupShowPassword
                              ? "text"
                              : "password"
                          }
                          onChange={DialogeDataMobile.updateSignvalues}
                          name="inputedPassword"
                          value={
                            DialogeDataMobile.rawSignupValuesinputedPassword
                          }
                          variant="standard"
                          InputProps={{
                            endAdornment: (
                              <InputAdornment position="end">
                                <IconButton
                                  onClick={
                                    DialogeDataMobile.ShowSignupPasswordForaWhile
                                  }
                                  onMouseDown={
                                    DialogeDataMobile.ShowSignupPasswordForaWhile
                                  }
                                  aria-label="toggle password visibility"
                                >
                                  {DialogeDataMobile.signupShowPassword ? (
                                    <VisibilityIcon />
                                  ) : (
                                    <VisibilityOffIcon />
                                  )}
                                </IconButton>
                              </InputAdornment>
                            ),
                          }}
                        />
                        <ModalFormSignupError
                          device="mobile"
                          ErrorType={0}
                          textField="password"
                          errorFormChecking={
                            DialogeDataMobile.errorFormCheckinginputedPassword
                          }
                          darkmode={DialogeDataMobile.darkmode}
                          WidthHolder={WidthHolder}
                          type={false}
                          cantPassBadEmail={DialogeDataMobile.cantPassBadEmail}
                          ErrorDisplay={DialogeDataMobile.passwordErrorDisplay}
                          ErrorData={DialogeDataMobile.passwordErrorData}
                        />

                        <Grid
                          container
                          style={{ marginTop: "40px", zIndex: 2 }}
                          className="modal-hold-signup"
                        >
                          <Grid item xs={4}></Grid>
                          <Grid item className="buttonpad buttonshake" xs={4}>
                            <Button
                              style={DialogeDataMobile.signupstyle}
                              onClick={DialogeDataMobile.signmeup}
                              fullWidth={true}
                              variant="contained"
                              size="large"
                              color="secondary"
                            >
                              Sign Up
                            </Button>
                          </Grid>{" "}
                        </Grid>
                      </Grid>
                    )}
                  </Grid>
                </Paper>
              </animated.div>
            </Grid>
          </Grid>
        </Paper>
      </animated.div>
    </DialogContent>
  );
}

export const MobileModalLog = React.memo(MobileModalLogx);
