import React, { useRef, useState } from "react";
import { Paper, Grid, Box } from "@material-ui/core";
import { matchPc, matchTablet } from "../DetectDevice";
import { RootStateOrAny, useSelector, useDispatch } from "react-redux";
import CircleIcon from "@mui/icons-material/Circle";
import { useSpring, animated } from "react-spring";

function OptionsSliderx({ getSliderWidth }: any) {
  var getSliderWidthNew = getSliderWidth / 1.4;
  var ActiveDot = getSliderWidthNew / 2.4;
  const optionsCollectImageRef = useRef<HTMLDivElement>(null);
  const optionsImages = ["1.jpg", "2.jpg", "3.jpg", "4.jpg", "5.jpg", "6.jpg"];
  const [translate, setTranslate] = useState(0);
  const [stalestate, setstalestate] = useState(1);

  const [ActiveSlide, setActiveSlide] = useState(0);

  ///
  ///CREATE A SLIDE UP ANIMATION WITH  REACT SPRING
  const modalanimation = useSpring({
    config: {
      duration: 300,
    },
    transform: `translateX(-${translate}px)`,
    transition: "0.45s",
    height: "auto",
    display: "flex",
    width: `auto`,

    margin: "auto",
  });

  ///
  ///
  ///
  /// PROVIDES DYNAMIC HEIGHT FOR SLIDER CONTENT FROM IMAGE
  const clickOptions = (i: number, type: number) => {
    if (type === 1) {
      setTranslate(stalestate + i);
      setActiveSlide(i);
    } else {
      setTranslate(getSliderWidthNew * i + 1.5 * i);
      setActiveSlide(i);
    }
  };

  return (
    <>
      <Grid
        container
        style={{
          position: "relative",
          margin: "0 auto",
          overflow: "hidden",
          left: "0px",
          paddingBottom: "19px",
          backgroundColor: "#00ccff",
        }}
      >
        <animated.div ref={optionsCollectImageRef} style={modalanimation}>
          {optionsImages.map((im: any, i: any) => (
            <>
              <Grid xs={12}>
                <img
                  onClick={() => {
                    clickOptions(i, 1);
                  }}
                  className="turprofileLight"
                  style={{
                    width: `${getSliderWidthNew}px`,
                    height: `${getSliderWidthNew}px`,
                    borderRadius: "50%",
                    padding: "3px",
                    objectFit: "cover",
                    marginLeft: "2px",
                    marginTop: "14px",
                    boxShadow: "0 0 3px red",
                    marginBottom: "2.2px",
                  }}
                  src={`./images/options/${im}`}
                />
                <CircleIcon
                  className="zuperking"
                  style={{
                    margin: "auto",
                    color: "#ffffff",
                    position: "relative",
                    bottom: "0.3em",
                    left: "39%",
                  }}
                />
              </Grid>
            </>
          ))}
        </animated.div>
      </Grid>{" "}
    </>
  );
}

export const OptionsSlider = React.memo(OptionsSliderx);
