import React, { useRef, useState, useCallback } from "react";
import { Paper, Grid, Box } from "@material-ui/core";
import billboardPictureData from "../images/k.jpg";
import { matchPc, matchTablet } from "../DetectDevice";
import { useSelector, useDispatch } from "react-redux";
import "./profile.css";
import { DarkmodeToggleAction } from ".././GlobalActions";
import profilePictureData from "../images/filC2592017-08-17-12-31-0029820170419090518full_size_20170419012414.jpg";

function Profile() {
  ///
  ///
  ///
  /// GET DARKMODE FROM REDUX STORE
  interface RootStateGlobalReducer {
    GlobalReducer: {
      darkmode: boolean;
    };
  }
  const { darkmode } = useSelector((state: RootStateGlobalReducer) => ({
    ...state.GlobalReducer,
  }));

  const darkmodeReducer = darkmode;

  //////////////////////////////////BILLBOARD VARIABLES FOR DEVICE TYPES
  var widthh = matchPc ? "65.5vw" : "98vw";
  var topp = matchPc ? "2.8em" : matchTablet ? "3.5em" : "1.25em";
  var usernameClass = matchPc
    ? "usernamePc"
    : matchTablet
    ? "usernameTablet"
    : "usernameMobile";

  var widthName = matchPc ? "65.5vw" : "98vw";
  var topName = matchPc ? "8.8em" : matchTablet ? "11.3em" : "3.5em";
  var name = matchPc ? "namePc" : matchTablet ? "nameTablet" : "nameMobile";

  var widthConnect = matchPc ? "65.5vw" : "98vw";
  var bottomConnect = matchPc ? "9.5em" : matchTablet ? "11.3em" : "4.05em";
  var favclass = matchPc ? "favPc" : matchTablet ? "favTablet" : "favMobile";
  var fanclass = matchPc ? "fanPc" : matchTablet ? "fanTablet" : "fanMobile";

  var fontConnectText = matchPc ? "1vw" : matchTablet ? "2.5vw" : "1.73vh";
  var fontConnectnum = matchPc ? "1.75vw" : matchTablet ? "3.9vw" : "2.4vh";

  var widthProfilePic = matchPc ? "18%" : matchTablet ? "42%" : "44vw";
  var topProfilePic = matchPc ? "34em" : matchTablet ? "27.3em" : "7.6em";
  var leftProfilePic = matchPc ? "1vw" : matchTablet ? "3vw" : "2.6vw";

  var billboardDynamicHeight = matchPc ? "70vh" : matchTablet ? "56vw" : "29vh";

  //////////////////////////////////BILLBOARD VARIABLES FOR DEVICE TYPES

  ///
  ///
  ///
  /// TOGGLEDARKMODE ON USERNAME CLICK
  var toggleDarkMode = false;
  const dispatch = useDispatch();
  const switchThemes = () => {
    if (darkmodeReducer) {
      toggleDarkMode = false;
    } else {
      toggleDarkMode = true;
    }
    dispatch(DarkmodeToggleAction());
    ////ACESSING LOCALSTORAGE
    localStorage.setItem("darkmode", toggleDarkMode.toString());
  };

  ///
  ///
  ///
  /// GET LOGGED USER DATA FROM REDUX STORE
  interface RootStateReducerImage {
    UserdataReducer: {
      image: string;
      username: string;
      quote: string;
      billboard1: string;
    };
  }
  const { image, username, quote, billboard1 } = useSelector(
    (state: RootStateReducerImage) => ({
      ...state.UserdataReducer,
    })
  );
  const imageReducer = image;
  const usernameReducer = username;
  const quoteReducer = quote;
  const billboard1Reducer = billboard1;

  ///
  ///
  ///
  return (
    <Paper>
      <Grid container style={{ height: "100vh" }}>
        {/*///////////////////////////////////////////////////////////////////////////BACKPAD BILLBOARD LIGHTINING/DARKEN*/}
        <Grid
          container
          style={{
            position: "fixed",
            top: "0em",
            width: "100%",
            zIndex: 2,
          }}
        >
          <Grid
            item
            component={Box}
            display={{ xs: "none", md: "block" }}
            md={2}
          ></Grid>
          <Grid
            item
            xs={12}
            md={8}
            style={{
              borderBottomLeftRadius: "7px",
              borderBottomRightRadius: "7px",
              height: billboardDynamicHeight,
              backgroundColor: darkmodeReducer
                ? "rgba(005, 005, 005, 0.15)"
                : "rgba(250, 250, 250, 0.15)",
            }}
          ></Grid>
        </Grid>
        {/*///////////////////////////////////////////////////////////////////////////BACKPAD BILLBOARD LIGHTINING/DARKEN*/}

        <Grid
          item
          component={Box}
          display={{ xs: "none", md: "block" }}
          md={2}
        ></Grid>
        <Grid item xs={12} md={8}>
          {/*///////////////////////////////////////////////////////////////////////////IMAGE*/}
          <img
            className={
              darkmodeReducer ? ` turdarkbillboard  ` : ` turlightbillboard  `
            }
            style={{
              position: "relative",
              zIndex: 0,
              objectFit: "cover",
              width: "100%",
              borderRadius: "0px",
              borderBottomLeftRadius: matchPc ? "7px" : "0em",
              borderBottomRightRadius: matchPc ? "7px" : "0em",
              height: billboardDynamicHeight,
            }}
            src={`./images/${billboard1Reducer}`}
            alt="Superstarz Billboard "
          />{" "}
          {/*///////////////////////////////////////////////////////////////////////////IMAGE*/}
          {/*///////////////////////////////////////////////////////////////////////////USERNAME*/}
          <Grid
            item
            xs={12}
            style={{
              position: "absolute",
              width: widthh,
              height: "auto",
              top: topp,
              textAlign: "right",
              zIndex: 3,
            }}
          >
            <span
              onClick={switchThemes}
              className={
                darkmodeReducer
                  ? `fontfamilyArial ${usernameClass} turdark`
                  : `fontfamilyArial ${usernameClass} turlight`
              }
              style={{
                cursor: "pointer",
                color: darkmodeReducer ? "#ffffff" : "#000000",
                backgroundColor: darkmodeReducer
                  ? "rgba(005, 005, 005, 0.42)"
                  : "rgba(250, 250, 250, 0.75)",
              }}
            >
              {usernameReducer}
            </span>
          </Grid>
          {/*///////////////////////////////////////////////////////////////////////////USERNAME*/}
          {/*///////////////////////////////////////////////////////////////////////////FULLNAME OR QUOTES*/}
          <Grid
            item
            xs={12}
            style={{
              position: "absolute",
              width: widthName,
              height: "auto",
              top: topName,
              textAlign: "right",
              zIndex: 3,
            }}
          >
            <span
              style={{
                color: darkmodeReducer ? "#ffffff" : "#000000",
                backgroundColor: darkmodeReducer
                  ? "rgba(005, 005, 005, 0.42)"
                  : "rgba(250, 250, 250, 0.75)",
              }}
              className={
                darkmodeReducer
                  ? `fontfamilyArial ${name} turdark`
                  : `fontfamilyArial ${name} turlight`
              }
            >
              {quoteReducer}
            </span>
          </Grid>
          {/*///////// i love the summers so much//////////////////////////////////////////////////////////////////FULLNAME OR QUOTES*/}
          {/*///////////////////////////////////////////////////////////////////////////FAVS*/}
          <Grid
            item
            xs={12}
            style={{
              position: "relative",
              width: widthConnect,
              height: "auto",
              bottom: bottomConnect,
              display: "flex",
              justifyContent: "flex-end",
              zIndex: 4,
            }}
          >
            <Grid item style={{ textAlign: "center" }}>
              {" "}
              <span
                style={{
                  fontWeight: "bolder",
                  fontSize: fontConnectText,
                  backgroundColor: darkmodeReducer
                    ? "rgba(005, 005, 005, 0.29)"
                    : "rgba(255, 255, 255, 0.35)",
                  padding: "1px  ",
                }}
                className={
                  darkmodeReducer
                    ? `fontfamilyArial zuperxyinfotext turdarkfavfan ${fanclass}  `
                    : `fontfamilyArial text-connect-light zuperxyinfotext turlight ${fanclass}  `
                }
              >
                FANS
              </span>
              <Grid xs={12} item style={{ height: "10px" }}></Grid>
              <span
                style={{
                  fontWeight: "bolder",
                  fontSize: fontConnectnum,
                  marginRight: matchPc ? "0.1vw" : "0.5vw",
                  backgroundColor: darkmodeReducer
                    ? "rgba(005, 005, 005, 0.2)"
                    : "rgba(255, 255, 255, 0.03)",
                }}
                className={`fontfamilyArial zuperxyinfo ${fanclass}  `}
              >
                147
              </span>{" "}
            </Grid>
            <Grid item style={{ textAlign: "center" }}>
              {" "}
              <span
                style={{
                  fontWeight: "bolder",
                  fontSize: fontConnectText,
                  backgroundColor: darkmodeReducer
                    ? "rgba(005, 005, 005, 0.29)"
                    : "rgba(255, 255, 255, 0.35)",

                  padding: "1px  ",
                }}
                className={
                  darkmodeReducer
                    ? `fontfamilyArial zuperxyinfotext turdarkfavfan ${favclass}  `
                    : `fontfamilyArial  text-connect-light zuperxyinfotext turlight ${favclass}  `
                }
              >
                FAVORITES
              </span>
              <Grid xs={12} item style={{ height: "10px" }}></Grid>
              <span
                style={{
                  backgroundColor: darkmodeReducer
                    ? "rgba(005, 005, 005, 0.2)"
                    : "rgba(255, 255, 255, 0.03)",
                  fontWeight: "bolder",
                  fontSize: fontConnectnum,

                  marginRight: matchPc ? "0.1vw" : "0.5vw",
                  marginTop: "9.2px",
                }}
                className={`fontfamilyArial zuperxyinfo  ${favclass}`}
              >
                20
              </span>
            </Grid>
          </Grid>
          {/*///////////////////////////////////////////////////////////////////////////FAVS*/}
          {/*///////////////////////////////////////////////////////////////////////////PROFILE PIC*/}
          <Grid
            item
            xs={12}
            style={{
              position: "absolute",
              width: widthProfilePic,
              height: "auto",
              marginLeft: leftProfilePic,
              top: topProfilePic,
              zIndex: 3,
            }}
          >
            <img
              className={darkmodeReducer ? `turdark` : ` turlight`}
              style={{
                position: "relative",
                zIndex: 0,
                objectFit: "contain",
                width: "100%",
                borderRadius: "50%",
                margin: "auto",
              }}
              src={`./images/profile/${imageReducer}`}
              alt="Superstarz Billboard "
            />{" "}
          </Grid>
          {/*///////////////////////////////////////////////////////////////////////////PROFILE PIC*/}
        </Grid>
      </Grid>
    </Paper>
  );
}

export default Profile;
