import ModalFormSignupError from "./ModalFormSignupError";
import ModalFormLoginError from "./ModalFormLoginError";
import ServerError from "./ServerError";
import { useSpring, animated } from "react-spring";
import { Scrollbars } from "react-custom-scrollbars";
import VisibilityOffIcon from "@material-ui/icons/VisibilityOff";
import VisibilityIcon from "@material-ui/icons/Visibility";
import {
  IconButton,
  InputAdornment,
  Grid,
  DialogContent,
  Paper,
  makeStyles,
  TextField,
  Button,
} from "@material-ui/core";
import { IDialogeData } from "./appFolder-Interfaces";

function PcModalLog(DialogeData: IDialogeData) {
  ///
  ///
  ///
  ///CREATE A SLIDE UP ANIMATION WITH  REACT SPRING
  const modalanimation = useSpring({
    config: {
      duration: 1000,
    },
    opacity: DialogeData.showModalForm ? 1 : 0.9,
    transform: DialogeData.showModalForm
      ? `translateY(0%)`
      : `translateY(100%)`,
  });

  const modalanimationTwo = useSpring({
    config: {
      duration: 900,
    },
    opacity: DialogeData.showModalForm ? 1 : 0.9,
    transform: DialogeData.showModalForm
      ? `translateY(0%)`
      : `translateY(100%)`,
  });

  ///
  ///
  ///
  /// MATERIAL UI  CUSTOM STYLES
  var useStyleModal = makeStyles(() => ({
    containerStyle: {
      width: DialogeData.imageReal,
      height: "auto",
      boxShadow: "0 5px 16px rgba(0, 0, 0, 0.2)",
      color: "#000000",
      position: "relative",
      zIndex: 10,
      borderRadius: DialogeData.borderGrid,
    },
    imageStyle: {
      opacity: DialogeData.opacityFixedModalImage,
      cursor: "pointer",
      objectFit: "cover",
      width: "100%",
      height: "100%",
      borderRadius: `${DialogeData.borderGrid} 0 0 10px`,
      background: "#000000",
    },
    imageStylex: {
      opacity: DialogeData.opacitySlidingModalImage,
      cursor: "pointer",
      width: "100%",
      height: "auto",
      borderRadius: `${DialogeData.borderGrid} 0 0 10px`,
      background: "#000000",
    },
  }));
  var classes = useStyleModal();

  return (
    <DialogContent
      style={{
        paddingLeft: "0px",
        height: "100%",
      }}
    >
      <DialogContent
        className="fadermodal-imageslider modalImageCustomSlider FormDialog-containerx dontallowhighlighting"
        onClick={DialogeData.onBackgroundFocus}
        style={{
          display: DialogeData.slidingModalImageBack,
          overflow: "auto",
          cursor: "pointer",
          height: "101%",
          padding: "0px",
          width: DialogeData.slidingImageWidth,
        }}
        ref={DialogeData.ModalBackgroundRef}
      >
        {" "}
        <Scrollbars style={{ width: "100%", height: "100%" }}>
          <animated.div style={modalanimationTwo}>
            {" "}
            <img
              onClick={DialogeData.zoomlogmodal}
              src={DialogeData.showimage}
              className={classes.imageStylex}
              alt="Logzoom"
            />
          </animated.div>
        </Scrollbars>
      </DialogContent>

      <DialogContent
        className="fadermodal FormDialog-container dontallowhighlighting"
        onClick={DialogeData.onBackgroundFocus}
        style={{ overflow: "hidden", cursor: "pointer" }}
        ref={DialogeData.ModalBackgroundRef}
      >
        <ServerError
          serverEmojiplain={DialogeData.serverEmojiplain}
          setServerErrorData={DialogeData.setServerErrorData}
          serverErrorDisplay={DialogeData.serverErrorDisplay}
          serverErrorData={DialogeData.serverErrorData}
          darkmode={DialogeData.darkmode}
        />
        <animated.div style={modalanimation}>
          <Paper
            className={DialogeData.PaperStyle}
            style={{
              borderRadius: DialogeData.borderGrid,
              cursor: "default",
            }}
          >
            <Grid container className={classes.containerStyle}>
              <Grid item xs={DialogeData.GridHolderA}>
                <img
                  onClick={DialogeData.iconclicked}
                  className="log-logo make-small-icons-clickable-neutralB"
                  className={
                    DialogeData.zoomedModal
                      ? "log-logo  make-small-icons-clickable-neutralB"
                      : "hide-logo "
                  }
                  src={DialogeData.SuperIcon}
                  alt="SuperstarZ logosmallmode"
                />

                <img
                  onClick={DialogeData.zoomlogmodal}
                  onLoad={DialogeData.onimageload}
                  src={DialogeData.showimage}
                  className={classes.imageStyle}
                  alt="Logsmall"
                />
              </Grid>

              {DialogeData.formtype ? (
                <Grid item xs={DialogeData.GridHolderB}>
                  <Grid xs={12} item className="formholder">
                    <Grid
                      item
                      xs={12}
                      className="center-content-vertically"
                      style={{ marginTop: "-5px" }}
                    >
                      <img
                        onClick={DialogeData.iconclicked}
                        className={
                          DialogeData.zoomedModal
                            ? "zoom-login-logo  make-small-icons-clickable-neutral"
                            : "hide-logo "
                        }
                        src={DialogeData.SuperIcon}
                        alt="SuperstarZ logo"
                      />

                      <button
                        onClick={DialogeData.logout}
                        style={{
                          cursor: "pointer",
                          opacity: 0.1,
                          position: "fixed",
                          top: "0.5em",
                          borderRadius: "50%",
                        }}
                      >
                        {" "}
                        ....
                      </button>
                      <ModalFormLoginError
                        type={true}
                        ErrorDisplay={DialogeData.errorsLoginInputedUsername}
                        darkmode={DialogeData.darkmode}
                        WidthHolder={DialogeData.WidthHolder}
                      />

                      <TextField
                        style={{
                          width: DialogeData.WidthHolder,
                          paddingBottom: "60px",
                        }}
                        inputProps={{ style: { fontSize: "2.5vh" } }}
                        InputLabelProps={{ style: { fontSize: "1.8vh" } }}
                        label="Username"
                        type="text"
                        onChange={DialogeData.updateLoginvalues}
                        name="inputedUsername"
                        value={DialogeData.rawLogininputedUsername}
                        variant="filled"
                      />

                      <TextField
                        style={{
                          width: DialogeData.WidthHolder,
                          paddingBottom: "40px",
                          zIndex: 1,
                        }}
                        inputProps={{ style: { fontSize: "2.5vh" } }}
                        InputLabelProps={{ style: { fontSize: "1.8vh" } }}
                        label="Password"
                        onChange={DialogeData.updateLoginvalues}
                        type={
                          DialogeData.loginShowPassword ? "text" : "password"
                        }
                        name="inputedPassword"
                        value={DialogeData.rawLogininputedPassword}
                        variant="filled"
                        InputProps={{
                          endAdornment: (
                            <InputAdornment position="end">
                              <IconButton
                                onClick={DialogeData.ShowLoginPasswordForaWhile}
                                onMouseDown={
                                  DialogeData.ShowLoginPasswordForaWhile
                                }
                                aria-label="toggle password visibility"
                              >
                                {DialogeData.loginShowPassword ? (
                                  <VisibilityIcon />
                                ) : (
                                  <VisibilityOffIcon />
                                )}
                              </IconButton>
                            </InputAdornment>
                          ),
                        }}
                      />
                      <ModalFormLoginError
                        type={false}
                        ErrorDisplay={DialogeData.errorsLoginInputedPassword}
                        darkmode={DialogeData.darkmode}
                        WidthHolder={DialogeData.WidthHolder}
                      />

                      <Grid
                        container
                        style={{ marginTop: "70px", zIndex: 300 }}
                      >
                        <Grid item xs={4}></Grid>
                        <Grid item className="buttonpad buttonshake" xs={4}>
                          <Button
                            onClick={DialogeData.logmein}
                            className={DialogeData.loginclass}
                            fullWidth={true}
                            variant="outlined"
                            size="large"
                            color="primary"
                          >
                            Log In
                          </Button>
                        </Grid>{" "}
                      </Grid>
                    </Grid>
                  </Grid>
                </Grid>
              ) : (
                <Grid xs={DialogeData.GridHolderB} item className="formholder">
                  <Grid
                    item
                    xs={12}
                    className="center-content-vertically"
                    style={{ padding: "0px" }}
                  >
                    <img
                      onClick={DialogeData.iconclicked}
                      className={
                        DialogeData.zoomedModal
                          ? "zoom-signup-logo make-small-icons-clickable-neutral"
                          : "hide-logo make-small-icons-clickable-neutral "
                      }
                      src={DialogeData.SuperIcon}
                      alt="SuperstarZ logo"
                    />
                    <ModalFormSignupError
                      ErrorType={0}
                      textField="email"
                      errorFormChecking={
                        DialogeData.errorFormCheckinginputedEmail
                      }
                      WidthHolder={DialogeData.WidthHolder}
                      darkmode={DialogeData.darkmode}
                      type={true}
                      cantPassBadEmail={DialogeData.cantPassBadEmail}
                      ErrorDisplay={DialogeData.emailErrorDisplay}
                      ErrorData={DialogeData.emailErrorData}
                    />
                    <TextField
                      style={{
                        width: DialogeData.WidthHolder,
                        paddingBottom: "45px",
                      }}
                      inputProps={{ style: { fontSize: "2.5vh" } }}
                      InputLabelProps={{ style: { fontSize: "1.8vh" } }}
                      label="Email"
                      type="email"
                      onChange={DialogeData.updateSignvalues}
                      name="inputedEmail"
                      value={DialogeData.rawSignupValuesinputedEmail}
                      variant="filled"
                    />
                    <ModalFormSignupError
                      ErrorType={DialogeData.errorsSignupValuesinputedUsername}
                      textField="username"
                      errorFormChecking={
                        DialogeData.errorFormCheckinginputedUsername
                      }
                      WidthHolder={DialogeData.WidthHolder}
                      darkmode={DialogeData.darkmode}
                      type={true}
                      cantPassBadEmail={DialogeData.cantPassBadEmail}
                      ErrorDisplay={DialogeData.usernameErrorDisplay}
                      ErrorData={DialogeData.usernameErrorData}
                    />
                    <TextField
                      style={{
                        width: DialogeData.WidthHolder,
                        paddingBottom: "45px",
                      }}
                      inputProps={{ style: { fontSize: "2.5vh" } }}
                      InputLabelProps={{ style: { fontSize: "1.8vh" } }}
                      label="Username"
                      type="text"
                      onChange={DialogeData.updateSignvalues}
                      name="inputedUsername"
                      value={DialogeData.rawSignupValuesinputedUsername}
                      variant="filled"
                    />

                    <TextField
                      style={{
                        width: DialogeData.WidthHolder,
                        paddingBottom: "30px",
                        zIndex: 1,
                      }}
                      inputProps={{ style: { fontSize: "2.5vh" } }}
                      InputLabelProps={{ style: { fontSize: "1.8vh" } }}
                      label="Password"
                      type={
                        DialogeData.signupShowPassword ? "text" : "password"
                      }
                      onChange={DialogeData.updateSignvalues}
                      name="inputedPassword"
                      value={DialogeData.rawSignupValuesinputedPassword}
                      variant="filled"
                      InputProps={{
                        endAdornment: (
                          <InputAdornment position="end">
                            <IconButton
                              onClick={DialogeData.ShowSignupPasswordForaWhile}
                              onMouseDown={
                                DialogeData.ShowSignupPasswordForaWhile
                              }
                              aria-label="toggle password visibility"
                            >
                              {DialogeData.signupShowPassword ? (
                                <VisibilityIcon />
                              ) : (
                                <VisibilityOffIcon />
                              )}
                            </IconButton>
                          </InputAdornment>
                        ),
                      }}
                    />
                    <ModalFormSignupError
                      ErrorType={0}
                      textField="password"
                      errorFormChecking={
                        DialogeData.errorFormCheckinginputedPassword
                      }
                      darkmode={DialogeData.darkmode}
                      WidthHolder={DialogeData.WidthHolder}
                      type={false}
                      cantPassBadEmail={DialogeData.cantPassBadEmail}
                      ErrorDisplay={DialogeData.passwordErrorDisplay}
                      ErrorData={DialogeData.passwordErrorData}
                    />

                    <Grid container className="modal-hold-signup">
                      <Grid item xs={4}></Grid>
                      <Grid item className="buttonpad buttonshake" xs={4}>
                        <Button
                          className={DialogeData.signupclass}
                          onClick={DialogeData.signmeup}
                          fullWidth={true}
                          variant="contained"
                          size="large"
                          color="secondary"
                        >
                          Sign Up
                        </Button>
                      </Grid>{" "}
                    </Grid>
                  </Grid>
                </Grid>
              )}
            </Grid>
          </Paper>
        </animated.div>
      </DialogContent>
    </DialogContent>
  );
}

export default PcModalLog;
