import React, { useRef, useState } from "react";
import { Paper, Grid, Box } from "@material-ui/core";
import { matchPc, matchTablet } from "../DetectDevice";
import { RootStateOrAny, useSelector, useDispatch } from "react-redux";

import { useSpring, animated } from "react-spring";

function OptionsSliderx({ getSliderWidth }: any) {
  const optionsCollectImageRef = useRef<HTMLImageElement>(null);
  const [optionsImageDimension, setoptionsImageDimension] = useState(0);

  const optionsImages = ["1.jpg", "2.jpg", "3.jpg", "4.jpg", "5.jpg", "6.jpg"];

  var getSliderWidthAverage = optionsImageDimension * optionsImages.length + 60;

  const [translate, setTranslate] = useState(0);

  ///
  ///CREATE A SLIDE UP ANIMATION WITH  REACT SPRING
  const modalanimation = useSpring({
    config: {
      duration: 300,
    },
    transform: `translateX(-${translate}px)`,
    transition: "0.45s",
    height: "auto",
    display: "flex",
    width: `${getSliderWidthAverage}px`,
    backgroundColor: "#00ccff",
  });

  ///
  ///
  ///
  /// PROVIDES DYNAMIC HEIGHT FOR SLIDER CONTENT FROM IMAGE
  const cl = (i: number) => {
    setTranslate(optionsImageDimension * i);
  };

  ///
  ///
  ///
  /// PROVIDES DYNAMIC HEIGHT FOR SLIDER CONTENT FROM IMAGE
  const sliderFirstImageOnLoad = (item: number) => {
    if (item === 0) {
      if (
        optionsCollectImageRef.current &&
        optionsCollectImageRef.current.clientWidth
      ) {
        setoptionsImageDimension(optionsCollectImageRef.current.clientWidth);
      }
    }
  };

  return (
    <>
      <Grid
        container
        style={{ position: "relative", margin: "0 auto", overflow: "hidden" }}
      >
        <animated.div style={modalanimation}>
          {optionsImages.map((im: any, i: any) => (
            <img
              onClick={() => {
                cl(i);
              }}
              onLoad={() => {
                sliderFirstImageOnLoad(i);
              }}
              ref={optionsCollectImageRef}
              className="turprofileLight"
              style={{
                width: "6vw",
                height: "6vw",
                borderRadius: "50%",
                padding: "3px",
                objectFit: "cover",
                marginLeft: "13px",
                marginTop: "15px",
                boxShadow: "0 0 3.8px blue",
                marginBottom: "2px",
              }}
              src={`./images/options/${im}`}
            />
          ))}
        </animated.div>
      </Grid>{" "}
    </>
  );
}

export const OptionsSlider = React.memo(OptionsSliderx);
