import React, { useState, useCallback, useEffect } from "react";

import Axios from "axios";
import MoreHorizIcon from "@material-ui/icons/MoreHoriz";
/// USE DISPATCH
import { useSelector, useDispatch } from "react-redux";
import { usePalette } from "react-palette";
import CheckIcon from "@mui/icons-material/Check";
import { Grid } from "@material-ui/core";
import { matchPc, matchTablet } from "../DetectDevice";
import { UpdateColorAction } from "../GlobalActions";

function AboutColorx({ zoomed }: any): JSX.Element {
  const dispatch = useDispatch();
  ///
  /// GET DARKMODE FROM REDUX STORE
  interface RootStateGlobalReducer {
    GlobalReducer: {
      darkmode: boolean;
    };
  }
  const { darkmode } = useSelector((state: RootStateGlobalReducer) => ({
    ...state.GlobalReducer,
  }));

  const darkmodeReducer = darkmode;

  ///
  ///
  /// GET COLOR FROM REDUX STORE
  interface RootStateReducerColor {
    GlobalReducerColor: {
      color: string;
      colordark: string;
      colortype: number;
    };
  }
  const { color, colordark, colortype } = useSelector(
    (state: RootStateReducerColor) => ({
      ...state.GlobalReducerColor,
    })
  );
  const colorReducer = color;
  const colorReducerdark = colordark;
  const colortypeReducer = colortype;

  var showCheck1: any = "hidden";
  var showCheck2: any = "hidden";
  var showCheck3: any = "hidden";
  var showCheck4: any = "hidden";
  var showCheck5: any = "hidden";

  if (colortypeReducer === 0) {
    showCheck1 = "visible";
  } else if (colortypeReducer === 1) {
    showCheck2 = "visible";
  } else if (colortypeReducer === 2) {
    showCheck3 = "visible";
  } else if (colortypeReducer === 3) {
    showCheck4 = "visible";
  } else {
    showCheck5 = "visible";
  }
  ///
  ///
  ///
  /// GET LOGGED USER DATA FROM REDUX STORE
  interface RootStateReducerImage {
    UserdataReducer: {
      image: string;
      id: number;
    };
  }
  const { image, id } = useSelector((state: RootStateReducerImage) => ({
    ...state.UserdataReducer,
  }));
  const idReducer = id;
  const imageReducer = image;

  const colorvalues = {
    color1: "#99999",
    color2: "#00ccff",
    colortype: 7,
    id: idReducer,
  };

  ///
  ///
  ///
  /// REACT PALLETTE (VIBRANTCOLOR DETECTION)
  ////  const { data, loading, error } = usePalette(`./images/profile/${imageReducer}`);
  const { data } = usePalette(`./images/profile/${imageReducer}`);

  ///
  ///
  ///
  ///DISPATCH

  ///
  ///
  ///
  ///DOT ENV DATA
  const { REACT_APP_SUPERSTARZ_URL } = process.env;

  ///
  ///
  ///MODAL ZOOMED STATE
  const updateColor = useCallback(
    ({ type }: any) => {
      var col1: any = "";
      var col2: any = "";
      var ctype: any = 2;
      s;
      if (type === 0) {
        (col1 = data.lightVibrant), (col2 = data.darkVibrant), (ctype = 0);
      } else if (type === 1) {
        (col1 = data.vibrant), (col2 = ""), (ctype = 0);
      } else if (type === 2) {
      } else if (type === 3) {
      } else if (type === 4) {
      }

      var colorboy = {
        color1: col1,
        color2: col2,
        colortype: ctype,
        id: 2,
      };
      Axios.put(
        `http://${REACT_APP_SUPERSTARZ_URL}/update_color`,
        { values: colorboy },
        {
          withCredentials: true,
        }
      )
        .then((response) => {
          alert("good");
          if (response.data.message === "color updated") {
            dispatch(UpdateColorAction(colorboy));
          }
        })
        .catch(function (error) {
          alert("about color error");
        });
    },
    [REACT_APP_SUPERSTARZ_URL, dispatch]
  );

  return (
    <>
      <Grid container>
        <Grid
          item
          xs={12}
          style={{
            marginTop: zoomed ? "6.5vh" : "5vh",
            padding: "0px",
            marginLeft: zoomed ? "9.4vw" : "2.3vw",
          }}
        >
          {/*/////////////////////////////////////////////////////////////////////////// 1*/}
          <div
            style={{
              width: "9vh",
              margin: "auto",
              textAlign: "center",
              top: matchPc ? (zoomed ? "2.8vh" : "1.8vh") : "0.54em",
              position: "absolute",
              marginLeft: "0%",
              visibility: showCheck1,
            }}
          >
            <div
              style={{
                fontSize: matchPc ? "1.1vw" : "2.08vh",
                fontWeight: 900,
                fontFamily: "Arial, Helvetica, sans-serif",
                color: darkmodeReducer ? "#dddddd" : "#0b111b",
              }}
            >
              <CheckIcon />
            </div>
          </div>
          <Grid
            item
            xs={2}
            style={{
              width: `9vh`,
              height: `9vh`,
              position: "absolute",
              backgroundColor: darkmodeReducer
                ? colorReducerdark
                : colorReducer,
              borderRadius: "50%",
              zIndex: 0,
              marginLeft: "0%",
              cursor: "pointer",
            }}
          >
            {" "}
          </Grid>
          <Grid
            item
            xs={2}
            onClick={updateColor}
            style={{
              textAlign: "center",
              alignItems: "center",
              display: "grid",
              justifyContent: "center",
              margin: "auto",
              width: `8vh`,
              height: `8vh`,
              position: "absolute",
              backgroundColor: darkmodeReducer
                ? "rgba(010,010,010, 0.7)"
                : "rgba(220,220,220, 0.7)",

              borderRadius: "50%",
              zIndex: 1,
              marginLeft: "0.25vw",
              marginTop: "0.5vh",
              cursor: "pointer",
            }}
          >
            {" "}
          </Grid>
          {/*///////////////////////////////////////////////////////////////////////////1*/}
          {/*///////////////////////////////////////////////////////////////////////////2*/}
          <div
            style={{
              width: "9vh",
              margin: "auto",
              textAlign: "center",
              top: matchPc ? (zoomed ? "2.8vh" : "1.8vh") : "0.54em",
              position: "absolute",
              marginLeft: "7%",
              visibility: showCheck2,
            }}
          >
            <div
              style={{
                fontSize: matchPc ? "1.1vw" : "2.08vh",
                fontWeight: "bolder",
                fontFamily: "Arial, Helvetica, sans-serif",

                color: darkmodeReducer ? "#dddddd" : "#0b111b",
              }}
            >
              <CheckIcon />
            </div>
          </div>
          <Grid
            item
            xs={2}
            style={{
              width: `9vh`,
              height: `9vh`,
              position: "absolute",
              backgroundColor: data.vibrant,
              borderRadius: "50%",
              zIndex: 0,
              marginLeft: "7%",
              cursor: "pointer",
            }}
          >
            {" "}
          </Grid>
          {/*///////////////////////////////////////////////////////////////////////////2*/}
          {/*///////////////////////////////////////////////////////////////////////////3*/}
          <div
            style={{
              width: "9vh",
              margin: "auto",
              textAlign: "center",
              top: matchPc ? (zoomed ? "2.8vh" : "1.8vh") : "0.54em",
              position: "absolute",
              marginLeft: "14%",
              visibility: showCheck3,
            }}
          >
            <div
              style={{
                fontSize: matchPc ? "1.1vw" : "2.08vh",
                fontWeight: "bolder",
                fontFamily: "Arial, Helvetica, sans-serif",

                color: darkmodeReducer ? "#dddddd" : "#0b111b",
              }}
            >
              <CheckIcon />
            </div>
          </div>
          <Grid
            item
            xs={2}
            style={{
              width: `9vh`,
              height: `9vh`,
              position: "absolute",
              backgroundColor: darkmodeReducer
                ? data.darkVibrant
                : data.lightVibrant,
              borderRadius: "50%",
              zIndex: 0,
              marginLeft: "14%",
              cursor: "pointer",
            }}
          >
            {" "}
          </Grid>
          {/*///////////////////////////////////////////////////////////////////////////3*/}
          {/*///////////////////////////////////////////////////////////////////////////4*/}
          <div
            style={{
              width: "9vh",
              margin: "auto",
              textAlign: "center",
              top: matchPc ? (zoomed ? "2.8vh" : "1.8vh") : "0.54em",
              position: "absolute",
              marginLeft: "21%",
              visibility: showCheck4,
            }}
          >
            <div
              style={{
                fontSize: matchPc ? "1.1vw" : "2.08vh",
                fontWeight: "bolder",
                fontFamily: "Arial, Helvetica, sans-serif",

                color: darkmodeReducer ? "#dddddd" : "#0b111b",
              }}
            >
              <CheckIcon />
            </div>
          </div>
          <Grid
            item
            xs={2}
            style={{
              width: `9vh`,
              height: `9vh`,
              position: "absolute",
              backgroundColor: data.muted,
              borderRadius: "50%",
              zIndex: 0,
              marginLeft: "21%",
              cursor: "pointer",
            }}
          >
            {" "}
          </Grid>{" "}
          {/*///////////////////////////////////////////////////////////////////////////4*/}
          {/*///////////////////////////////////////////////////////////////////////////5*/}
          <div
            style={{
              width: "9vh",
              margin: "auto",
              textAlign: "center",
              top: matchPc ? (zoomed ? "2.8vh" : "1.8vh") : "0.54em",
              position: "absolute",
              marginLeft: "28%",
              visibility: showCheck5,
            }}
          >
            <div
              style={{
                fontSize: matchPc ? "1.1vw" : "2.08vh",
                fontWeight: "bolder",
                fontFamily: "Arial, Helvetica, sans-serif",

                color: darkmodeReducer ? "#dddddd" : "#0b111b",
              }}
            >
              <CheckIcon />
            </div>
          </div>
          <Grid
            item
            xs={2}
            style={{
              width: `9vh`,
              height: `9vh`,
              position: "absolute",
              backgroundColor: darkmodeReducer
                ? data.darkMuted
                : data.lightMuted,
              borderRadius: "50%",
              zIndex: 0,
              marginLeft: "28%",
              cursor: "pointer",
            }}
          >
            {" "}
          </Grid>
          {/*///////////////////////////////////////////////////////////////////////////5*/}
        </Grid>{" "}
      </Grid>
    </>
  );
}

export const AboutColor = React.memo(AboutColorx);
