import React, {
  useState,
  useRef,
  useCallback,
  useEffect,
  useLayoutEffect,
} from "react";
import { Arrow } from "./Arrow";
import { Dots } from "./Dots";
import { SliderNumber } from "./SliderNumber";
import { Grid } from "@material-ui/core";
import { animated, useTransition } from "react-spring";
import { RootStateOrAny, useSelector, useDispatch } from "react-redux";
import { matchMobile, matchPc, matchTablet } from "../DetectDevice";
import ZoomOutIcon from "@mui/icons-material/ZoomOut";
import { Loader } from "./Loader";
import { UpdateInteract } from "../GlobalActions";
import { useSpring } from "react-spring";
import SearchIcon from '@material-ui/icons/Search';
import PersonIcon from '@material-ui/icons/Person';
import SubjectIcon from '@material-ui/icons/Subject';
import NotificationsIcon from '@material-ui/icons/Notifications';
import TheatersIcon from '@material-ui/icons/Theaters';
import QueueMusicIcon from '@material-ui/icons/QueueMusic';
import PhotoAlbumIcon from '@material-ui/icons/PhotoAlbum';
import SettingsIcon from '@material-ui/icons/Settings';

function ActualMenux({ showModalFormMenu, setshowModalFormMenu }: any): JSX.Element {
  const styles = useSpring({
    from: { transform: "translateX(-100%)" },
    to: { transform: showModalFormMenu ? "translateX(0%)" : "translateX(-100%)" },
    config: { tension: 280, friction: 60 }, // You can adjust these for smoother or stiffer animations
  });



  ///
  ///
  ///
  /// GET LOGGED USER DATA FROM REDUX STORE
  interface RootStateReducerImage {
    UserdataReducer: {
      image: string;
      imageThumb: string;
      id: number;
      username: string;
      memeberPageid: number;
      MemberProfileData: any;
    };
  }
  const { username, image, imageThumb, id, memeberPageid, MemberProfileData } =
    useSelector((state: RootStateReducerImage) => ({
      ...state.UserdataReducer,
    }));

  /// GET DARKMODE FROM REDUX STORE
  interface RootStateGlobalReducer {
    GlobalReducer: {
      darkmode: boolean;
    };
  }
  const { darkmode } = useSelector((state: RootStateGlobalReducer) => ({
    ...state.GlobalReducer,
  }));

  const darkmodeReducer = darkmode;

  ///
  ///
  ///MUI PAPER STYLES FROM REDUX
  const { PaperStyleLight, PaperStyleDark } = useSelector(
    (state: RootStateOrAny) => ({
      ...state.PaperReducerLightnDark,
    })
  );


  var superFont = "";

  if (matchPc) {
    superFont = "super-starz-text-Pcx";

    ///
  } else if (matchTablet) {
    superFont = "super-starz-text-Tabletx";

    ///
  } else {
    superFont = "super-starz-text-Mobilex";
  }



  var MenuOptions = ["Search", "Profile", "My Feeds", "Notification", "Showroom", "Playlist", "Gallery", "Setting"];




  var menufont = '1.4vw';


  return (
    <>



      <animated.div
        onClick={() => {

          setshowModalFormMenu(false);
        }}
        style={{
          ...styles,
          width: '100%',
          height: '100vh',
          backgroundColor: darkmodeReducer ? 'rgb(20, 20, 20, 0.32)' : 'rgb(255, 255, 255, 0.22)',
          position: 'fixed',
          cursor: 'pointer',
          top: 0,
          left: 0
        }}
      >
        {/* Menu content goes here */}
      </animated.div >


      <animated.div
        style={{
          ...styles,
          width: '20%',
          height: '100vh',
          backgroundImage: darkmodeReducer ? PaperStyleDark : PaperStyleLight,
          position: 'fixed',
          top: 0,
          left: 0
        }}
      >

        <Grid
          xs={12}
          className={
            darkmodeReducer
              ? ` ${superFont} `
              : ` ${superFont}`
          }
          style={{
            height: '10vh',


          }}>





          <Grid
            xs={12} style={{ position: 'fixed', top: '2.8vh', zIndex: 1 }}>



            <span style={{ paddingLeft: '2vw' }}>


              < span
                className={
                  darkmodeReducer
                    ? "text-superstarz-dark   text-superstarz-dark-colorA  "
                    : "text-superstarz-light  text-superstarz-light-colorA  "
                }
                style={{
                  color: darkmodeReducer ? "#eeeeee" : "#444444",
                }}
              >
                Clik
              </span>
              {' '}
              <span
                style={{
                  color: darkmodeReducer ? "#ffe680" : "#ffcc00",
                  opacity: darkmodeReducer ? "1" : "1",

                }}
                className={
                  darkmodeReducer
                    ? "text-superstarz-dark     text-superstarz-dark-colorB  "
                    : "text-superstarz-light   text-superstarz-light-colorB   "
                }
              >
                Bate
              </span>


            </span>
          </Grid>
          <Grid
            xs={12} style={{ position: 'fixed', top: '1vh', zIndex: 2, right: '0px' }}>



            <div style={{ paddingRight: '20px' }}>


              <img src={image} style={{ width: '5vw', height: '5vw', borderRadius: '50%' }} />

            </div>
          </Grid>


          <Grid
            xs={12} style={{ position: 'fixed', top: '17vh', zIndex: 2, right: '0px', width: '100%' }}>

            <Grid
              xs={12} style={{ width: '100%', alignContent: 'center', display: 'grid' }}>


              {
                MenuOptions.map((Title: string, i: number) => (
                  <span key={i} style={{ padding: '1.6vh' }}>




                    {i === 0 ? <span>
                      <SearchIcon
                        className={
                          darkmodeReducer
                            ? "make-small-icons-clickable-darkab dontallowhighlighting zuperkingIcon "
                            : "make-small-icons-clickable-lightab  dontallowhighlighting zuperking"
                        }
                        style={{
                          color: darkmodeReducer ? "#ffffff" : '#777777',
                          fontSize: menufont,
                          marginLeft: '3vw',
                          transform: 'scale(1.9)'
                        }}
                      />
                    </span> : null}



                    {i === 1 ? <span>
                      <PersonIcon
                        className={
                          darkmodeReducer
                            ? "make-small-icons-clickable-darkab dontallowhighlighting zuperkingIcon "
                            : "make-small-icons-clickable-lightab  dontallowhighlighting zuperking"
                        }
                        style={{
                          color: darkmodeReducer ? "#ffffff" : '#777777',
                          fontSize: menufont,
                          marginLeft: '3vw',
                          transform: 'scale(1.9)'
                        }}
                      />
                    </span> : null}



                    {i === 2 ?
                      <span>
                        <SubjectIcon
                          className={
                            darkmodeReducer
                              ? "make-small-icons-clickable-darkab dontallowhighlighting zuperkingIcon "
                              : "make-small-icons-clickable-lightab  dontallowhighlighting zuperking"
                          }
                          style={{
                            color: darkmodeReducer ? "#ffffff" : '#777777',
                            fontSize: menufont,
                            marginLeft: '3vw',
                            transform: 'scale(1.9)'
                          }}
                        />
                      </span>
                      : null}




                    {i === 3 ? <span>
                      <NotificationsIcon
                        className={
                          darkmodeReducer
                            ? "make-small-icons-clickable-darkab dontallowhighlighting zuperkingIcon "
                            : "make-small-icons-clickable-lightab  dontallowhighlighting zuperking"
                        }
                        style={{
                          color: darkmodeReducer ? "#ffffff" : '#777777',
                          fontSize: menufont,
                          marginLeft: '3vw',
                          transform: 'scale(1.9)'
                        }}
                      />
                    </span>

                      : null}



                    {i === 4 ? <span>
                      <TheatersIcon
                        className={
                          darkmodeReducer
                            ? "make-small-icons-clickable-darkab dontallowhighlighting zuperkingIcon "
                            : "make-small-icons-clickable-lightab  dontallowhighlighting zuperking"
                        }
                        style={{
                          color: darkmodeReducer ? "#ffffff" : '#777777',
                          fontSize: menufont,
                          marginLeft: '3vw',
                          transform: 'scale(1.9)'
                        }}
                      />
                    </span> : null}



                    {i === 5 ? <span>
                      <QueueMusicIcon
                        className={
                          darkmodeReducer
                            ? "make-small-icons-clickable-darkab dontallowhighlighting zuperkingIcon "
                            : "make-small-icons-clickable-lightab  dontallowhighlighting zuperking"
                        }
                        style={{
                          color: darkmodeReducer ? "#ffffff" : '#777777',
                          fontSize: menufont,
                          marginLeft: '3vw',
                          transform: 'scale(1.9)'
                        }}
                      />
                    </span> : null}




                    {i === 6 ? <span>
                      <PhotoAlbumIcon
                        className={
                          darkmodeReducer
                            ? "make-small-icons-clickable-darkab dontallowhighlighting zuperkingIcon "
                            : "make-small-icons-clickable-lightab  dontallowhighlighting zuperking"
                        }
                        style={{
                          color: darkmodeReducer ? "#ffffff" : '#777777',
                          fontSize: menufont,
                          marginLeft: '3vw',
                          transform: 'scale(1.9)'
                        }}
                      />
                    </span> : null}



                    {i === 7 ? <span>
                      <SettingsIcon
                        className={
                          darkmodeReducer
                            ? "make-small-icons-clickable-darkab dontallowhighlighting zuperkingIcon "
                            : "make-small-icons-clickable-lightab  dontallowhighlighting zuperking"
                        }
                        style={{
                          color: darkmodeReducer ? "#ffffff" : '#777777',
                          fontSize: menufont,
                          marginLeft: '3vw',
                          transform: 'scale(1.9)'
                        }}
                      />
                    </span> : null}



                    <span style={{
                      color: darkmodeReducer ? "#ffffff" : '#777777',
                      fontSize: "1.3vw",
                      marginLeft: '2vw'
                    }}>
                      {Title}
                    </span>
                  </span>
                ))
              }


            </Grid>



          </Grid>


        </Grid>




      </animated.div>
    </>
  );
}

export const ActualMenu = React.memo(ActualMenux);
