import React, {
  useRef,
  useState,
  useEffect,
  useLayoutEffect,
  useCallback,
} from "react";

import { Grid } from "@material-ui/core";
import MoreHorizIcon from "@material-ui/icons/MoreHoriz";
import { useSelector, useDispatch } from "react-redux";
import { useSpring, animated } from "react-spring";
import Masonry from "@mui/lab/Masonry";
import CircleIcon from "@mui/icons-material/Circle";
import Cropper from "react-easy-crop";
import MusicNoteIcon from "@mui/icons-material/MusicNote";
import PhotoIcon from "@mui/icons-material/Photo";
import CloseIcon from "@mui/icons-material/Close";
import GifIcon from "@mui/icons-material/Gif";
import ChevronRightIcon from "@mui/icons-material/ChevronRight";
import Slider from "@mui/material/Slider";
import { AnySoaRecord } from "dns";
import { PreviewCanvas } from "./PreviewCanvas";
import { PreviewCanvasCropAll } from "./PreviewCanvasCropAll";
import CropIcon from "@mui/icons-material/Crop";
import CheckIcon from "@mui/icons-material/Check";
import { matchPc, matchTablet, matchMobile } from "../DetectDevice";


//import { createFFmpeg, fetchFile } from '@ffmpeg/ffmpeg';
const { createFFmpeg, fetchFile } = require('@ffmpeg/ffmpeg');

function VideoEditorx({ VideoUrl, VideoUrl2, ShowVideo2, ShowVideo, setShowVideo2, setShowVideo }: any): JSX.Element {

  ///
  ///
  ///DARKMODE FROM REDUX
  interface RootStateGlobalReducer {
    GlobalReducer: {
      darkmode: boolean;
    };
  }
  const { darkmode } = useSelector((state: RootStateGlobalReducer) => ({
    ...state.GlobalReducer,
  }));

  const darkmodeReducer = darkmode;

  const [Trim, setTrim] = useState<any>(null);

  const ffmpeg = createFFmpeg({ log: true });



  const laod = async () => {



    await ffmpeg.load();

    alert('k');
  }



  const go = useCallback(async (videoUrl: any) => {


    try {

      ///console.log(ffmpeg);

      await ffmpeg.load();



      // Writing the file to FFmpeg filesystem
      const fetchedVideo = await fetchFile(videoUrl);
      ffmpeg.FS('writeFile', 'input.mp4', fetchedVideo);

      // Execute the FFmpeg command
      // -ss for start time, -t for duration
      await ffmpeg.run('-i', 'input.mp4', '-ss', '00:01:00', '-t', '10', 'output.mp4');

      // Reading the result
      const output = ffmpeg.FS('readFile', 'output.mp4');

      // Create a Blob URL
      const videoBlob = new Blob([output.buffer], { type: 'video/mp4' });
      const trimmedVideoUrl = URL.createObjectURL(videoBlob);


      // Use trimmedVideoUrl as the source for your video player
      setTrim(trimmedVideoUrl);



      ///return trimmedVideoUrl;

    } catch (error) {


      alert(error);

      console.error('Error processing video with FFmpeg:', error);
    }
  }, [ffmpeg, createFFmpeg, fetchFile])



  return <>

    <Grid xs={12} style={{ display: ShowVideo || ShowVideo2 ? 'block' : 'none', position: 'fixed', top: '0vh', height: '100vh', backgroundColor: '#555555', width: '100%', margin: 'auto', textAlign: 'center' }} >

      <Grid
        item
        xs={12}
        style={{
          margin: "auto",
          textAlign: "right",
          left: '5px',
          height: "20px",
          position: 'fixed',
          top: '3vh',
          right: '3vw'
        }}
      >
        <CloseIcon
          onClick={() => {
            setShowVideo(false);
            setShowVideo2(false);

          }}
          className={
            darkmodeReducer
              ? "make-small-icons-clickable-lightCrop dontallowhighlighting zuperkingIcon "
              : "make-small-icons-clickable-darkCrop dontallowhighlighting zuperkingIcon  "
          }
          style={{
            margin: "auto",
            color: "#ffffff",
            fontSize: matchMobile
              ? `3vh`
              : `2.5vw`,

          }}
        />
      </Grid>

      <button

        onClick={() => {

          go(VideoUrl);

        }} style={{ padding: '8vh' }}></button>

      {


        Trim && <video src={Trim} controls style={{ width: '40%', margin: 'auto' }}></video>}


      {ShowVideo && <video src={VideoUrl} controls style={{ width: '40%', margin: 'auto' }}></video>}

      {ShowVideo2 && <video src={VideoUrl2} controls style={{ width: '40%', margin: 'auto' }}></video>}





    </Grid >

  </>;
}

export const VideoEditor = React.memo(VideoEditorx);
