import React, {
  useRef,
  useState,
  useCallback,
  useEffect,
  useLayoutEffect,
} from "react";
import { matchPc, matchTablet } from "../DetectDevice";
import { Grid } from "@material-ui/core";
import MoreHorizIcon from "@material-ui/icons/MoreHoriz";
import { useSelector, useDispatch } from "react-redux";
import { useSpring, animated } from "react-spring";
import Masonry from "@mui/lab/Masonry";
import CircleIcon from "@mui/icons-material/Circle";
import { SuperCrop } from "./SuperCrop";
import MusicNoteIcon from "@mui/icons-material/MusicNote";
import PhotoIcon from "@mui/icons-material/Photo";
import GifIcon from "@mui/icons-material/Gif";
import ChevronRightIcon from "@mui/icons-material/ChevronRight";
import Axios from "axios";
import CropIcon from "@mui/icons-material/Crop";
import CheckIcon from "@mui/icons-material/Check";
import { PreviewCanvasCropAll } from "./PreviewCanvasCropAll";
import { PreviewCanvas } from "./PreviewCanvas";

function ImageTobeFilteredx({
  filterImage,
  index,
  itemUploadRef,
  setActivatefilterImage,
}: any): JSX.Element {
  ///
  ///
  ///
  const filterbaseimageRef: any = useRef(null);

  const closegate = () => {
    if (index === 0) {
      ////
      addfilter();
    } else {
      setActivatefilterImage(false);
    }
  };

  const addfilter = () => {
    if (index === 0) {
      const previewFileReadimage = new Image();
      previewFileReadimage.src = itemUploadRef.current[index].src;
      previewFileReadimage.onload = function () {
        const ctx = canvasRef.current.getContext("2d");

        canvasRef.current.height = previewFileReadimage.naturalHeight;
        canvasRef.current.width = previewFileReadimage.naturalWidth;

        ////////////////////
        requestAnimationFrame(() => {
          ctx.drawImage(
            previewFileReadimage,
            0,
            0,
            canvasRef.current.width,
            canvasRef.current.height
          );
          const scannedImage = ctx.getImageData(
            0,
            0,
            canvasRef.current.width,
            canvasRef.current.height
          );

          const scannedData = scannedImage.data;
          for (let i = 0; i < scannedData.length; i += 4) {
            const total =
              scannedData[i] + scannedData[i + 1] + scannedData[i + 2];
            const averageColorValue = total / 3;
            scannedData[i] = averageColorValue;
            scannedData[i + 1] = averageColorValue;
            scannedData[i + 2] = averageColorValue;
          }

          ctx.putImageData(scannedData, 0, 0);
        });
      };

      ////////////////////
      ////
    }
  };

  const canvasRef: any = useRef(null);

  return (
    <>
      {itemUploadRef.current[index] ? (
        <>
          {" "}
          <img
            id={itemUploadRef.current[index]}
            ref={filterbaseimageRef}
            onClick={closegate}
            src={
              itemUploadRef.current ? itemUploadRef.current[index].src : null
            }
            style={{
              width: "30%",
              height: "auto",
              position: "relative",
              margin: "auto",
              marginRight: index === 0 ? "20px" : "0px",
            }}
          />
          <canvas
            ref={canvasRef}
            style={{
              padding: "0px",
            }}
          />
        </>
      ) : null}
    </>
  );
}

export const ImageTobeFiltered = React.memo(ImageTobeFilteredx);
