import React, {
  useState,
  useRef,
  useCallback,
  useEffect,
  useLayoutEffect,
} from "react";
import { Arrow } from "./Arrow";
import { Dots } from "./Dots";
import { SliderNumber } from "./SliderNumber";
import { Grid } from "@material-ui/core";
import { animated, useTransition } from "react-spring";
import { RootStateOrAny, useSelector, useDispatch } from "react-redux";
import { matchMobile, matchPc, matchTablet } from "../DetectDevice";
import ZoomOutIcon from "@mui/icons-material/ZoomOut";
import { Loader } from "./Loader";
import { UpdateInteract } from "../GlobalActions";
import { useSpring } from "react-spring";
import SearchIcon from '@material-ui/icons/Search';
import PersonIcon from '@material-ui/icons/Person';
import FaceIcon from '@material-ui/icons/Face';
import SubjectIcon from '@material-ui/icons/Subject';
import NotificationsIcon from '@material-ui/icons/Notifications';
import TheatersIcon from '@material-ui/icons/Theaters';
import QueueMusicIcon from '@material-ui/icons/QueueMusic';
import PhotoAlbumIcon from '@material-ui/icons/PhotoAlbum';
import SettingsIcon from '@material-ui/icons/Settings';
import {
  UpdateLoader,
  Updatepagenum
} from ".././GlobalActions";
import { UserInfoUpdateMEMBER } from "../log/actions/UserdataAction";
import SlowMotionVideoIcon from '@material-ui/icons/SlowMotionVideo';





function ActualMenux({ showModalFormMenu, setshowModalFormMenu, postData, setsuperSettings, scrollToRef }: any): JSX.Element {



  useEffect(() => {

    setMovePlay(false);
  }, [showModalFormMenu])

  ////
  ////
  ////
  const styles = useSpring({
    from: { transform: "translateX(-100%)" },
    to: { transform: showModalFormMenu ? "translateX(0%)" : "translateX(-100%)" },
    config: { tension: 280, friction: 60 }, // You can adjust these for smoother or stiffer animations
  });


  ///
  ///
  ///
  /// GET LOGGED USER DATA FROM REDUX STORE
  interface RootStateReducerImage {
    UserdataReducer: {
      image: string;
      imageThumb: string;
      id: number;
      username: string;
      memeberPageid: number;
      MemberProfileData: any;
    };
  }
  const { username, image, imageThumb, id, memeberPageid, MemberProfileData } =
    useSelector((state: RootStateReducerImage) => ({
      ...state.UserdataReducer,
    }));

  const imageReducer = image;
  const idReducer = id;
  const usernameReducer = username;
  const memeberPageidReducer = memeberPageid;
  const MemberProfileDataReducer = MemberProfileData;

  /// GET DARKMODE FROM REDUX STORE
  interface RootStateGlobalReducer {
    GlobalReducer: {
      darkmode: boolean;
    };
  }
  const { darkmode } = useSelector((state: RootStateGlobalReducer) => ({
    ...state.GlobalReducer,
  }));

  const darkmodeReducer = darkmode;

  ///
  ///
  ///MUI PAPER STYLES FROM REDUX
  const { PaperStyleLight, PaperStyleDark } = useSelector(
    (state: RootStateOrAny) => ({
      ...state.PaperReducerLightnDark,
    })
  );


  const dispatch = useDispatch();

  var superFont = "";

  if (matchPc) {
    superFont = "super-starz-text-Pcx";

    ///
  } else if (matchTablet) {
    superFont = "super-starz-text-Tabletx";

    ///
  } else {
    superFont = "super-starz-text-Mobilex";
  }



  var MenuOptions = ["Search", "My Profile", "My Feeds", "Notification", "Showroom", "Playlist", "Gallery", "Setting"];



  const Timer = useRef<ReturnType<typeof setTimeout> | null>(null);
  const Timer2 = useRef<ReturnType<typeof setTimeout> | null>(null);
  const Timervv = useRef<ReturnType<typeof setTimeout> | null>(null);

  var menufont = '1.35vw';

  var colr = darkmodeReducer ? "#ffffff" : '#111111';

  var pushh = '1vw';




  const GoToMemberF = () => {
    dispatch(Updatepagenum(0));
    dispatch(UserInfoUpdateMEMBER(-1));
    dispatch(UserInfoUpdateMEMBER(0));



  };


  const GoToMemberP = () => {
    dispatch(Updatepagenum(0));
    dispatch(UserInfoUpdateMEMBER(-1));

    dispatch(UserInfoUpdateMEMBER(idReducer));

    //


  };


  const GoToMemberLoaderUpP = () => {
    setshowModalFormMenu(false);
    if (Timervv.current) {
      clearTimeout(Timervv.current);
    }
    if (memeberPageidReducer === idReducer) {
    } else {
      dispatch(UpdateLoader(true));
    }
    Timervv.current = setTimeout(function () {
      GoToMemberP();
    }, 1000);
  };


  const GoToMemberLoaderUpF = () => {
    setshowModalFormMenu(false);
    if (Timervv.current) {
      clearTimeout(Timervv.current);
    }
    if (memeberPageidReducer === idReducer) {
    } else {
      dispatch(UpdateLoader(true));
    }
    Timervv.current = setTimeout(function () {
      GoToMemberF();
    }, 1000);
  };


  ///
  ///
  /// GET COLOR FROM REDUX STORE
  interface RootStateReducerColor {
    GlobalReducerColor: {
      color: string;
      colordark: string;
      colortype: number;
    };
  }
  const { color, colordark, colortype } = useSelector(
    (state: RootStateReducerColor) => ({
      ...state.GlobalReducerColor,
    })
  );
  const colorReducer = color;
  const colorReducerdark = colordark;
  const colortypeReducer = colortype;

  const [MovePlay, setMovePlay] = useState(false);

  const stylesxx = useSpring({
    transform: MovePlay ? "translateX(475%) scale(5)" : "translateX(0%) scale(5)",
    config: { tension: 280, friction: 60 }
  });


  const AnimatedIcon = animated(SlowMotionVideoIcon);



  return (
    <>



      <animated.div
        onClick={() => {

          setshowModalFormMenu(false);

        }}
        style={{
          ...styles,
          width: '100%',
          height: '100vh',
          backgroundColor: darkmodeReducer ? 'rgb(20, 20, 20, 0.4)' : 'rgb(255, 255, 255, 0.48)',
          position: 'fixed',
          cursor: 'pointer',
          top: 0,
          left: 0
        }}
      >

      </animated.div >


      <animated.div
        style={{
          ...styles,
          width: matchMobile ? '40%' : '20%',
          height: '100vh',
          backgroundImage: darkmodeReducer ? PaperStyleDark : PaperStyleLight,
          position: 'fixed',
          top: 0,
          left: 0
        }}
      >

        <Grid
          xs={12}
          className={
            darkmodeReducer
              ? ` ${superFont}  dontallowhighlighting `
              : ` ${superFont}  dontallowhighlighting `
          }
          style={{
            height: '10vh',


          }}>





          <Grid
            xs={12} style={{ position: 'fixed', top: '5.8vh', zIndex: 1 }}>



            <span onClick={() => {

              GoToMemberLoaderUpF();



            }} style={{ paddingLeft: '2.4vw', cursor: 'pointer' }}>


              < span
                className={
                  darkmodeReducer
                    ? "text-superstarz-dark   text-superstarz-dark-colorA  "
                    : "text-superstarz-light  text-superstarz-light-colorA  "
                }
                style={{
                  color: darkmodeReducer ? "#eeeeee" : "#444444",
                }}
              >
                Clik
              </span>
              {' '}
              <span
                style={{
                  color: darkmodeReducer ? "#ffe680" : "#ffcc00",
                  opacity: darkmodeReducer ? "1" : "1",

                }}
                className={
                  darkmodeReducer
                    ? "text-superstarz-dark     text-superstarz-dark-colorB  "
                    : "text-superstarz-light   text-superstarz-light-colorB   "
                }
              >
                Bate
              </span>


            </span>
          </Grid>



          <animated.div
            onClick={() => {
              setMovePlay(true);

              setTimeout(() => {
                setshowModalFormMenu(false);
                scrollToRef();

              }, 1500)

            }}
            style={{
              ...stylesxx,
              position: 'fixed',
              top: '17vh',
              marginLeft: '9vw',
            }}
          >
            <SlowMotionVideoIcon

              className={
                darkmodeReducer
                  ? "make-small-icons-clickable-darkMenu dontallowhighlighting zuperkingIcon"
                  : "make-small-icons-clickable-lightMenu dontallowhighlighting zuperking"
              }
              style={{
                fontSize: menufont,
                color: colorReducer,
                opacity: darkmodeReducer
                  ? '0.8' : '0.4'
              }}
            />

          </animated.div >








          <Grid
            xs={12} style={{ position: 'fixed', top: '4vh', zIndex: 2, right: '0px' }}>



            <div style={{ paddingRight: '1.2vw', }}>


              <img src={image} title={usernameReducer} onClick={() => {

                GoToMemberLoaderUpP();


              }} style={{ cursor: 'pointer', width: matchMobile ? '7vh' : '5vw', height: matchMobile ? '7vh' : '5vw', borderRadius: '50%' }} />

            </div>
          </Grid>


          <Grid
            xs={12} style={{ position: 'fixed', top: '27vh', zIndex: 20, right: '0px', width: '100%' }}>

            <Grid
              xs={12} style={{ width: '100%', alignContent: 'center', display: 'grid', opacity: '0.59' }}>


              {
                MenuOptions.map((Title: string, i: number) => (

                  <span onClick={() => {
                    if (i === 1) {

                      GoToMemberLoaderUpP();

                    }

                    if (i === 2) {

                      GoToMemberLoaderUpF();

                    }


                    if (i === 7) {
                      setsuperSettings(true);
                      setshowModalFormMenu(false);

                    }

                  }} key={i}
                    className={i === 6 || i === 5 || i === 4 || i === 3 || i === 0 ? "dontallowhighlighting " : "click-effect dontallowhighlighting "}
                    style={{
                      padding: '1.6vh',
                      cursor: i === 6 || i === 5 || i === 4 || i === 3 || i === 0 ? 'default' : 'pointer'
                    }}>




                    {i === 0 ? <span>
                      <SearchIcon
                        className={
                          darkmodeReducer
                            ? "make-small-icons-clickable-darkab dontallowhighlighting zuperkingIcon "
                            : "make-small-icons-clickable-lightab  dontallowhighlighting zuperking"
                        }
                        style={{
                          color: colr,
                          fontSize: menufont,
                          marginLeft: pushh,
                          transform: 'scale(1.9)', opacity: '0.3'
                        }}
                      />
                    </span> : null}



                    {i === 1 ? <span>
                      <FaceIcon
                        className={
                          darkmodeReducer
                            ? "make-small-icons-clickable-darkab dontallowhighlighting zuperkingIcon "
                            : "make-small-icons-clickable-lightab  dontallowhighlighting zuperking"
                        }
                        style={{
                          color: colr,
                          fontSize: menufont,
                          marginLeft: pushh,
                          transform: 'scale(1.9)'
                        }}
                      />
                    </span> : null}



                    {i === 2 ?
                      <span>
                        <SubjectIcon
                          className={
                            darkmodeReducer
                              ? "make-small-icons-clickable-darkab dontallowhighlighting zuperkingIcon "
                              : "make-small-icons-clickable-lightab  dontallowhighlighting zuperking"
                          }
                          style={{
                            color: colr,
                            fontSize: menufont,
                            marginLeft: pushh,
                            transform: 'scale(1.9)'
                          }}
                        />
                      </span>
                      : null}




                    {i === 3 ? <span>
                      <NotificationsIcon
                        className={
                          darkmodeReducer
                            ? "make-small-icons-clickable-darkab dontallowhighlighting zuperkingIcon "
                            : "make-small-icons-clickable-lightab  dontallowhighlighting zuperking"
                        }
                        style={{
                          color: colr,
                          fontSize: menufont,
                          marginLeft: pushh,
                          transform: 'scale(1.9)', opacity: '0.3'
                        }}
                      />
                    </span>

                      : null}



                    {i === 4 ? <span>
                      <TheatersIcon
                        className={
                          darkmodeReducer
                            ? "make-small-icons-clickable-darkab dontallowhighlighting zuperkingIcon "
                            : "make-small-icons-clickable-lightab  dontallowhighlighting zuperking"
                        }
                        style={{
                          color: colr,
                          fontSize: menufont,
                          marginLeft: pushh,
                          transform: 'scale(1.9)', opacity: '0.3'
                        }}
                      />
                    </span> : null}



                    {i === 5 ? <span>
                      <QueueMusicIcon
                        className={
                          darkmodeReducer
                            ? "make-small-icons-clickable-darkab dontallowhighlighting zuperkingIcon "
                            : "make-small-icons-clickable-lightab  dontallowhighlighting zuperking"
                        }
                        style={{
                          color: colr,
                          fontSize: menufont,
                          marginLeft: pushh,
                          transform: 'scale(1.9)', opacity: '0.3'
                        }}
                      />
                    </span> : null}




                    {i === 6 ? <span>
                      <PhotoAlbumIcon
                        className={
                          darkmodeReducer
                            ? "make-small-icons-clickable-darkab dontallowhighlighting zuperkingIcon "
                            : "make-small-icons-clickable-lightab  dontallowhighlighting zuperking"
                        }
                        style={{
                          color: darkmodeReducer ? "#ffffff" : '#777777',
                          fontSize: menufont,
                          marginLeft: pushh,
                          transform: 'scale(1.9)', opacity: '0.3'

                        }}
                      />
                    </span> : null}



                    {i === 7 ? <span>
                      <SettingsIcon
                        className={
                          darkmodeReducer
                            ? "make-small-icons-clickable-darkab dontallowhighlighting zuperkingIcon "
                            : "make-small-icons-clickable-lightab  dontallowhighlighting zuperking"
                        }
                        style={{
                          color: colr,
                          fontSize: menufont,
                          marginLeft: pushh,
                          transform: 'scale(1.9)',



                        }}
                      />
                    </span> : null}



                    <span



                      style={{
                        color: colr,
                        fontSize: "1.3vw",
                        marginLeft: '5vw',
                        opacity: i === 1 || i === 2 || i === 7 ? '1' : '0.3'
                      }}>
                      {Title}
                    </span>
                  </span>
                ))
              }


            </Grid>



          </Grid>


        </Grid >




      </animated.div >
    </>
  );
}

export const ActualMenu = React.memo(ActualMenux);
