import React, { useState, useRef } from "react";
import { Arrow } from "./Arrow";
import { Dots } from "./Dots";
import { SliderNumber } from "./SliderNumber";
import { Grid } from "@material-ui/core";
import { animated, useTransition } from "react-spring";
import { RootStateOrAny, useSelector, useDispatch } from "react-redux";

function Sliderx({
  slides,
  pey,
  refy,
  itemheight,
  onPostsItemClicked,
  onPostsItemload,
  post,
  itemFinalPostHeight,
  itemOriginalPostHeight,
  itemcroptype,
  itemCLICKED,
  ScrolltypeChange,
}: any): JSX.Element {
  const [sliderIndex, setSliderIndex] = useState(0);
  const [sliderIndexSlow, setSliderIndexSlow] = useState(0);
  const [allowAutoPlay, setAllowAutoPlay] = useState(true);
  const [showSliderLoader, setshowSliderLoader] = useState(true);

  const [autoSlideDuration] = useState(6000);
  const [sliderDuration] = useState(2000);

  /// const getWidth = () => window.innerWidth;
  ///var newGetWidth = getWidth() * slides.length;

  const waitChangeIndexTimer = useRef<ReturnType<typeof setTimeout> | null>(
    null
  );
  const waitChangeIndexTimer2 = useRef<ReturnType<typeof setTimeout> | null>(
    null
  );
  const handleTouchMoveTimer = useRef<ReturnType<typeof setTimeout> | null>(
    null
  );
  const callAutoPlayAgainTimer = useRef<ReturnType<typeof setTimeout> | null>(
    null
  );
  const autoPlayTimer = useRef<ReturnType<typeof setInterval> | null>(null);

  const SlideimageRef = useRef<HTMLImageElement>(null);

  const SlideimageRefthumb = useRef<HTMLImageElement>(null);

  const [imageHeight, setImageHeight] = useState(0);
  const [touchPosition, setTouchPosition] = useState(null);

  ///
  ///
  ///
  /// GET DARKMODE FROM REDUX STORE
  interface RootStateGlobalReducerx {
    ActiveAutoPlayReducer: {
      ActiveId: number;
    };
  }
  const { ActiveId } = useSelector((state: RootStateGlobalReducerx) => ({
    ...state.ActiveAutoPlayReducer,
  }));
  const ActiveIdReducer = ActiveId;

  ///
  ///
  ///
  /// GET COLOR FROM REDUX STORE
  interface RootStateReducerColor {
    GlobalReducerColor: {
      color: string;
    };
  }
  const { color } = useSelector((state: RootStateReducerColor) => ({
    ...state.GlobalReducerColor,
  }));
  const colorReducer = color;

  var autoSlideDisplay = "none";
  var sliderLoader = "";

  if (showSliderLoader) {
    autoSlideDisplay = "none";
    sliderLoader = "";
  } else {
    autoSlideDisplay = "block";
    sliderLoader = "superloaderAutoSlider";
  }

  ///
  ///
  ///
  /// SPRING TRANSITION WITH INDEX
  const transitions = useTransition(sliderIndex, {
    key: sliderIndex,
    from: { opacity: 0 },
    enter: { opacity: 1 },
    leave: { opacity: 0 },
    config: { duration: sliderDuration - 50 },
  });

  ///
  ///
  ///
  /// PROVIDES DYNAMIC HEIGHT FOR SLIDER CONTENT FROM IMAGE
  const sliderFirstImageOnLoad = (item: number) => {
    if (item === 0) {
      if (SlideimageRef.current && SlideimageRef.current.clientHeight) {
        setImageHeight(SlideimageRef.current.clientHeight);
      }
    }
  };

  ///
  ///
  ///
  /// PROVIDES DYNAMIC HEIGHT FOR SLIDER CONTENT FROM THUMBS IF IMAGE DOES NOT LOAD
  const sliderFirstImageOnLoadthumb = (item: number) => {
    if (item === 0 && imageHeight === 0) {
      if (
        SlideimageRefthumb.current &&
        SlideimageRefthumb.current.clientHeight
      ) {
        setImageHeight(SlideimageRefthumb.current.clientHeight);
      }
    }
  };

  ///
  ///
  ///
  /// HANDLE TOUCH START EVENT
  const handleTouchStart = (e: any) => {
    ////onMouseDown onMouseMove
    ////touchDown = e.clientX
    const touchDown = e.touches[0].clientX;
    setTouchPosition(touchDown);
  };

  ///
  ///
  ///
  /// HANDLE TOUCH MOVE EVENT
  const handleTouchMove = (e: any) => {
    if (handleTouchMoveTimer.current) {
      clearTimeout(handleTouchMoveTimer.current);
    }
    handleTouchMoveTimer.current = setTimeout(function () {
      const touchDown = touchPosition;

      if (touchDown === null) {
        return;
      }
      ////currentTouch = e.clientX
      const currentTouch = e.touches[0].clientX;
      const diff = touchDown - currentTouch;

      if (diff > 40) {
        nextSlide();
      } else if (diff < -40) {
        prevSlide();
      } else {
      }

      setTouchPosition(null);
      return false;
    }, 200);

    return false;
  };

  ///
  ///
  ///
  /// WAITS FOR SOME SECS BEFORE CHANGING SLIDE IDENTIFIER(DOTS,ACTIVESLIDES)
  const waitChangeIndex = (data: number, state: any) => {
    if (waitChangeIndexTimer.current) {
      clearTimeout(waitChangeIndexTimer.current);
    }
    waitChangeIndexTimer.current = setTimeout(function () {
      setSliderIndexSlow((state) => data);
    }, 500);
  };

  ///
  ///
  ///
  /// NEXT SLIDE
  const nextSlide = () => {
    ScrolltypeChange();
    if (allowAutoPlay) {
    } else {
      setshowSliderLoader(true);
      if (autoPlayTimer.current) {
        clearTimeout(autoPlayTimer.current);
      }
    }

    ///set((state) => (state + 1) % slides.length);
    if (sliderIndex === slides.length - 1) {
      setSliderIndex((sliderIndex) => 0);
      waitChangeIndex(0, sliderIndex);
    } else {
      setSliderIndex((sliderIndex) => sliderIndex + 1);
      waitChangeIndex(sliderIndex + 1, sliderIndex);
    }

    if (allowAutoPlay) {
    } else {
      if (callAutoPlayAgainTimer.current) {
        clearTimeout(callAutoPlayAgainTimer.current);
      }
      callAutoPlayAgainTimer.current = setTimeout(function () {
        SliderAutoPlay(1);
      }, sliderDuration);
    }
  };

  ///
  ///
  ///
  /// PREV SLIDE
  const prevSlide = () => {
    ScrolltypeChange();
    if (allowAutoPlay) {
    } else {
      setshowSliderLoader(true);
      if (autoPlayTimer.current) {
        clearTimeout(autoPlayTimer.current);
      }
    }

    if (sliderIndex === 0) {
      setSliderIndex((sliderIndex) => slides.length - 1);
      waitChangeIndex(slides.length - 1, sliderIndex);
    } else {
      setSliderIndex((sliderIndex) => sliderIndex - 1);
      waitChangeIndex(sliderIndex - 1, sliderIndex);
    }
    if (allowAutoPlay) {
    } else {
      if (callAutoPlayAgainTimer.current) {
        clearTimeout(callAutoPlayAgainTimer.current);
      }
      callAutoPlayAgainTimer.current = setTimeout(function () {
        SliderAutoPlay(1);
      }, sliderDuration);
    }
  };

  const startSlider = () => {
    setAllowAutoPlay(false);
    setshowSliderLoader(false);
    autoPlayTimer.current = setInterval(function () {
      ScrolltypeChange();
      setshowSliderLoader(true);

      setSliderIndex((state) => (state + 1) % slides.length);
      if (waitChangeIndexTimer2.current) {
        clearTimeout(waitChangeIndexTimer2.current);
      }
      waitChangeIndexTimer2.current = setTimeout(function () {
        setSliderIndexSlow((state) => (state + 1) % slides.length);
        setshowSliderLoader(false);
      }, 500);
    }, autoSlideDuration);
  };

  const SliderAutoPlay = (type: number) => {
    if (ActiveIdReducer === pey) {
      if (type === 1) {
        startSlider();
      } else {
        if (allowAutoPlay) {
          startSlider();
        } else {
          setAllowAutoPlay(true);
          setshowSliderLoader(true);
          if (autoPlayTimer.current) {
            clearInterval(autoPlayTimer.current);
          }
        }
      }
    } else {
      setAllowAutoPlay(true);
      setshowSliderLoader(true);
      if (autoPlayTimer.current) {
        clearInterval(autoPlayTimer.current);
      }
    }
  };
  ///
  ///
  ///
  /// CHANGE SLIDER CONTENT USING  DOTS
  const GotoDots = (clickedDot: number) => {
    ScrolltypeChange();
    setSliderIndex((sliderIndex) => clickedDot);
    waitChangeIndex(clickedDot, sliderIndex);
  };

  ///
  ///
  ///
  /// CHANGE SLIDER CONTENT USING  DOTS
  const checkifClicked = () => {
    if (itemCLICKED[pey]) {
      if (allowAutoPlay) {
        onPostsItemClicked(pey);
      } else {
        SliderAutoPlay(0);
      }
    } else {
      onPostsItemClicked(pey);
    }
  };

  ///
  ///
  ///
  /// CHANGE SLIDER CONTENT USING  DOTS
  const checkifClickedDouble = () => {
    if (itemCLICKED[pey]) {
      SliderAutoPlay(0);
    } else {
    }
  };
  ///
  ///
  ///
  /// CLICK BILLBOARD OPEN ON DOUBLE CLICK
  const clickslider = (e: any) => {
    switch (e.detail) {
      case 1:
        checkifClicked();
        break;
      case 2:
        checkifClickedDouble();
        break;
      case 3:
        checkifClickedDouble();
        break;
      case 4:
        checkifClickedDouble();
        break;
    }
  };

  return (
    <Grid
      item
      onTouchStart={handleTouchStart}
      onTouchMove={handleTouchMove}
      xs={12}
      style={{
        position: "relative",
        height: itemCLICKED[pey]
          ? `${itemOriginalPostHeight[pey]}px`
          : `${itemFinalPostHeight[pey]}px`,
        padding: "0px",
      }}
    >
      <Grid
        container
        className={
          darkmodeReducer
            ? `${sliderLoader} turdark`
            : `${sliderLoader} turlight`
        }
        style={{
          boxShadow: `0 0 3px ${colorReducer}`,
          backgroundColor: colorReducer,
          height: "3px",
          position: "absolute",
          display: autoSlideDisplay,
          zIndex: 100000,
          top: "0em",
        }}
      ></Grid>
      <SliderNumber
        activeSlide={sliderIndexSlow}
        total={slides.length}
        itemCLICKED={itemCLICKED}
        pey={pey}
      />
      <Arrow
        itemCLICKED={itemCLICKED}
        pey={pey}
        total={slides.length}
        direction="left"
        clickSlideprev={prevSlide}
        clickSlidenext={nextSlide}
        itemOriginalPostHeight={itemOriginalPostHeight}
      />
      {transitions((style, i) => (
        <>
          <animated.img
            className={
              darkmodeReducer ? "turlightpostdark" : "turlightpostlight"
            }
            src={`./images/posts/${slides[i]}`}
            alt="a superstarz post "
            style={{
              ...style,
              cursor: "alias",
              width: "100%",
              height: itemheight[pey],
              display: sliderIndex === i ? "block" : "none",
              position: "absolute",
              filter: "blur(3px)",
              padding: "0px",
              objectFit:
                itemcroptype[pey] === 1 || itemcroptype[pey] === 2
                  ? "cover"
                  : "cover",
              objectPosition:
                itemcroptype[pey] === 1 || itemcroptype[pey] === 2
                  ? "50% top"
                  : "50% 50",
            }}
          />

          <animated.img
            onLoad={(e: any) => {
              onPostsItemload(e, pey, i);
            }}
            onClick={clickslider}
            key={i}
            ref={refy}
            className={
              darkmodeReducer ? "turlightpostdark" : "turlightpostlight"
            }
            src={`./images/posts/${slides[i]}`}
            alt="a superstarz post "
            style={{
              ...style,
              cursor: "alias",
              width: "100%",
              height: itemheight[pey],
              position: "absolute",
              padding: "0px",
              objectFit:
                itemcroptype[pey] === 1 || itemcroptype[pey] === 2
                  ? "cover"
                  : "cover",
              objectPosition:
                itemcroptype[pey] === 1 || itemcroptype[pey] === 2
                  ? "50% top"
                  : "50% 50",
              zIndex: 1,
              float: "left",
            }}
          />
        </>
      ))}{" "}
      <Dots
        total={slides.length}
        itemCLICKED={itemCLICKED}
        pey={pey}
        GotoDots={GotoDots}
        slides={slides}
        activeSlide={sliderIndexSlow}
      />
    </Grid>
  );
}

export const Slider = React.memo(Sliderx);
