import React, { useRef, useEffect, useCallback } from "react";
import AddRoundedIcon from "@material-ui/icons/AddRounded";
import { Grid, Switch, DialogContent } from "@material-ui/core";
import { useSpring, animated } from "react-spring";
import * as CSS from "csstype";

interface IOption {
  closemodal: (backbutton: number) => void;
  showModal: boolean;
  setShowModal: (showModal: boolean) => void;
  darkmode: boolean;
  setDarkmode: (darkmodex: boolean) => void;
  marginData: CSS.Properties;
  fontData: CSS.Properties;
  switchsize: "medium" | "small" | undefined;
}

var toggleDarkMode: boolean = false;

export default function OptionInnerModal({
  closemodal,
  showModal,
  setShowModal,
  darkmode,
  setDarkmode,
  marginData,
  fontData,
  switchsize,
}: IOption): JSX.Element {
  /*USE SLIDE DOWN ANIMATION FROM REACT SPRING */
  const animation = useSpring({
    config: {
      duration: 500,
    },
    opacity: showModal ? 1 : 0.0,
    transform: showModal ? `translateY(0%)` : `translateY(-100%)`,
  });

  /*SETS DARKMODE FORM LOCAL STORAGE IF NOT EMPTY */
  useEffect(() => {
    let themelocaldata = JSON.parse(localStorage.getItem("darkmode")!);
    if (themelocaldata !== null) {
      setDarkmode(themelocaldata);
    }
  }, [setDarkmode]);

  /*ESCAPE KEY CLOSE MODAL */
  const escapePress = useCallback(
    (e) => {
      if (e.key === "Escape" && showModal) {
        closemodal(0);
      }
    },
    [setShowModal, showModal]
  );
  useEffect(() => {
    document.addEventListener("keydown", escapePress);
    return () => document.removeEventListener("keydown", escapePress);
  }, [escapePress]);
  /*ESCAPE */

  /* 
  USEREF TARGETS A DIV(BACKGROUND) AND CLOSES MODAL ON CLICK
   */
  const inputRef = useRef<HTMLInputElement>(null);

  const onBackgroundFocus = (e: any): void => {
    if (inputRef.current === e.target) {
      closemodal(0);
    }
  };
  /* 
  USEREF TARGETS A DIV(BACKGROUND) AND CLOSES MODAL ON CLICK
   */

  return (
    <>
      {showModal ? (
        <DialogContent
          onClick={onBackgroundFocus}
          ref={inputRef}
          style={{
            cursor: "pointer",
            height: "100vh",
            width: "100%",
            zIndex: 100,
            top: "0em",
            padding: "0px",
            position: "fixed",
          }}
        >
          <animated.div style={animation}>
            <Grid
              container
              xs={12}
              style={{
                backgroundImage:
                  "linear-gradient(to  bottom,rgba(236,239,253, 0.0),rgba(236,239,253, 0.05),rgba(236,239,253, 0.1),rgba(236,239,253,0.15),rgba(236,239,253,0.2),rgba(236,239,253,0.25),rgba(236,239,253,0.6) )",
                paddingTop: "2.8rem",
              }}
              className="theme-more-container"
            >
              <Grid
                container
                xs={12}
                style={{
                  backgroundImage:
                    "linear-gradient(to  bottom,rgba(000,000,005,0.03),rgba(000,000,005,0.012),rgba(000,000,005,0.025),rgba(000,000,005,0.037),rgba(000,000,005,0.050),rgba(000,000,005,0.073),rgba(000,000,005,0.085) )",
                }}
              >
                <Grid item sm={10} xs={7}></Grid>
                <Grid
                  item
                  xs={2}
                  sm={1}
                  className="switchopacity  zuperking"
                  style={marginData}
                >
                  <Switch
                    size={switchsize}
                    checked={darkmode}
                    onChange={() => {
                      toggleDarkMode = !darkmode;
                      setDarkmode(!darkmode);
                      localStorage.setItem(
                        "darkmode",
                        toggleDarkMode.toString()
                      );
                    }}
                  />
                </Grid>

                <Grid
                  item
                  xs={3}
                  className="dontallowselect"
                  sm={1}
                  style={{ textAlign: "right" }}
                >
                  <AddRoundedIcon
                    className={
                      darkmode
                        ? "make-small-icons-clickable-dark  dontallowselect  "
                        : "make-small-icons-clickable-light  dontallowselect  "
                    }
                    fontSize="large"
                    style={fontData}
                  />
                </Grid>
              </Grid>
            </Grid>
          </animated.div>
        </DialogContent>
      ) : null}
    </>
  );
}
