import React, {
  useRef,
  useState,
  useCallback,
  useEffect,
  useLayoutEffect,
} from "react";
import { matchPc, matchTablet } from "../DetectDevice";
import { Grid } from "@material-ui/core";
import MoreHorizIcon from "@material-ui/icons/MoreHoriz";
import { useSelector, useDispatch } from "react-redux";
import { useSpring, animated } from "react-spring";
import Masonry from "@mui/lab/Masonry";
import CircleIcon from "@mui/icons-material/Circle";
import { SuperCrop } from "./SuperCrop";
import MusicNoteIcon from "@mui/icons-material/MusicNote";
import PhotoIcon from "@mui/icons-material/Photo";
import GifIcon from "@mui/icons-material/Gif";
import ChevronRightIcon from "@mui/icons-material/ChevronRight";
import Axios from "axios";
import CropIcon from "@mui/icons-material/Crop";
import CheckIcon from "@mui/icons-material/Check";
import { PreviewCanvasCropAll } from "./PreviewCanvasCropAll";
import { ImageTobeFiltered } from "./ImageTobeFiltered";

function FilterModex({
  filterImage,
  selectedImage,
  itemUploadRef,
  setActivatefilterImage,
}: any): JSX.Element {
  ///
  ///
  ///DARKMODE FROM REDUX
  interface RootStateGlobalReducer {
    GlobalReducer: {
      darkmode: boolean;
    };
  }
  const { darkmode } = useSelector((state: RootStateGlobalReducer) => ({
    ...state.GlobalReducer,
  }));

  const darkmodeReducer = darkmode;

  return (
    <>
      {selectedImage ? (
        <Grid
          container
          className={
            darkmodeReducer
              ? `optionsTop-background-dark `
              : `optionsTop-background-light `
          }
          style={{
            zIndex: 1,
            padding: "0px",
            position: "relative",
            margin: "0 auto",
            overflow: "hidden",
            left: "0px",
            height: "auto",
            paddingBottom: "1px",
          }}
        >
          <animated.div ref={optionsCollectImageRef} style={modalanimation}>
            {selectedImage.map((imagee: any, index: any) => {
              <Grid key={index} item xs={12}>
                <img
                  id={itemUploadRef.current[index]}
                  src={
                    itemUploadRef.current[index]
                      ? itemUploadRef.current[index].src
                      : null
                  }
                  style={{
                    width: "30%",
                    height: "auto",
                    position: "relative",
                    margin: "auto",
                    marginRight: index === 0 ? "20px" : "0px",
                  }}
                />
              </Grid>;
            })}
          </animated.div>
        </Grid>
      ) : null}
    </>
  );
}

export const FilterMode = React.memo(FilterModex);
