import React, { useState, useRef, useEffect, useCallback } from "react";
import { ArrowBillboard } from "./ArrowBillboard";
import { matchPc } from "../DetectDevice";
import { Grid } from "@material-ui/core";
import { animated, useTransition } from "react-spring";
import { useSelector } from "react-redux";
import { DotsBillboard } from "./DotsBillboard";
import { ShowReactions } from "./ShowReactions";

function MiniFormReactionx({
  containerHeight,
  blinkenx,
  showCommentLoader,
  Datax,
  zoomedModal,
  wideImage,
  commentDatax,
  callObserver,
  commentData,
  lastItemElement,
  showComment2,
  commentData2,
  showComment3,
  commentData3,
  ModalImageRef,
}: any): JSX.Element {
  const { REACT_APP_APPX_STATE } = process.env;

  ///
  ///
  ///
  /// INTERFACE/TYPES FOR SCREENHEIGHT AND DARKMODE
  interface RootStateGlobalReducer {
    GlobalReducer: {
      darkmode: boolean;
      screenHeight: number;
    };
  }

  ///
  ///
  ///
  /// GET SCREENHEIGHT FROM REDUX STORE
  const { screenHeight, darkmode } = useSelector(
    (state: RootStateGlobalReducer) => ({
      ...state.GlobalReducer,
    })
  );
  const screenHeightReducer = screenHeight;
  const darkmodeReducer = darkmode;

  const [heightxx, setheightxx] = useState(0);

  useEffect(() => {
    var h = ModalImageRef.current.height;
    var hBad = h / 4;
    setheightxx(h - hBad);
  }, [ModalImageRef]);

  return (
    <Grid
      xs={12}
      item
      className={
        matchPc
          ? darkmodeReducer
            ? "postscroll-dark"
            : "postscroll-light"
          : darkmodeReducer
          ? "postscroll-darkm"
          : "postscroll-lightm"
      }
      style={{
        padding: "0vh",
        position: "relative",
        overflowY: `auto`,
        overflowX: "hidden",
        paddingBottom: "40vh",
        paddingRight: zoomedModal
          ? wideImage
            ? "5.8vw"
            : "8.6vw"
          : wideImage
          ? "4.4vw"
          : "5.1vw",
        display: "grid",
        gridGap: "0px",
        gridAutoFlow: "row",
        gridTemplateColumns: "30% 30% 30% 30%",
        width: "auto",
        height: `72vh`,
      }}
    >
      {commentDatax
        ? commentDatax.map((post: any, i: any) => (
            <>
              <ShowReactions
                callObserver={callObserver}
                length={commentData.length}
                AddRef={lastItemElement}
                post={post}
                i={i}
                zoomedModal={zoomedModal}
                wideImage={wideImage}
              />
            </>
          ))
        : null}

      {commentData
        ? commentData.map((post: any, i: any) => (
            <>
              <div style={{}}>
                <ShowReactions
                  callObserver={callObserver}
                  length={commentData.length}
                  AddRef={lastItemElement}
                  post={post}
                  i={i}
                  zoomedModal={zoomedModal}
                  wideImage={wideImage}
                />
              </div>
            </>
          ))
        : null}

      {showComment2
        ? commentData2
          ? commentData2.map((post: any, i: any) => (
              <>
                <ShowReactions
                  callObserver={callObserver}
                  length={commentData2.length}
                  AddRef={lastItemElement}
                  post={post}
                  i={i}
                  zoomedModal={zoomedModal}
                  wideImage={wideImage}
                />
              </>
            ))
          : null
        : null}

      {showComment3
        ? commentData3
          ? commentData3.map((post: any, i: any) => (
              <>
                <ShowReactions
                  callObserver={callObserver}
                  length={commentData2.length}
                  AddRef={lastItemElement}
                  post={post}
                  i={i}
                  zoomedModal={zoomedModal}
                  wideImage={wideImage}
                />
              </>
            ))
          : null
        : null}
      <>
        <Grid
          item
          xs={12}
          ref={lastItemElement}
          style={{
            marginTop: "0px",
            height: "5px",
          }}
        ></Grid>
      </>
    </Grid>
  );
}

export const MiniFormReaction = React.memo(MiniFormReactionx);
