import React, { useRef, useEffect, useCallback, Ref } from "react";
import { useSpring, animated } from "react-spring";
import { Grid, DialogContent, Paper } from "@material-ui/core";

import im from "./images/modalpic.jpg";
import { CenterFocusStrong } from "@material-ui/icons";

interface ImodalForm {
  CloseModalForm: (backbutton: number) => void;
  showModalForm: boolean;
  setShowModalForm: (showModal: boolean) => void;
}

export default function Modalx({
  CloseModalForm,
  showModalForm,
  setShowModalForm,
}: ImodalForm): JSX.Element {
  //
  //
  //
  //CREATE A SLIDE UP ANIMATION WITH  REACT SPRING
  const animation = useSpring({
    config: {
      duration: 500,
    },
    opacity: showModalForm ? 1 : 0.9,
    transform: showModalForm ? `translateY(0%)` : `translateY(100%)`,
  });

  //
  //
  //
  // USEREF TARGETS A DIV(BACKGROUND) AND CLOSES MODAL ON CLICK
  const ModalBackgroundRef = useRef<HTMLInputElement>(null);

  const onBackgroundFocus = (e: any): void => {
    if (ModalBackgroundRef.current === e.target) {
      CloseModalForm(0);
    }
  };
  //
  //
  //
  // ESCAPE KEY CLOSE MODAL
  const escapePress = useCallback(
    (e) => {
      if (e.key === "Escape" && showModalForm) {
        CloseModalForm(0);
      }
    },
    [setShowModalForm, showModalForm]
  );
  useEffect(() => {
    document.addEventListener("keydown", escapePress);
    return () => document.removeEventListener("keydown", escapePress);
  }, [escapePress]);

  return (
    <>
      {showModalForm ? (
        <DialogContent
          className="dialog-container dontallowhighlighting "
          onClick={onBackgroundFocus}
          style={{
            overflow: "hidden",
            height: "100vh",
            width: "100%",
            position: "fixed",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            top: "0em",
            zIndex: 8,
            backgroundImage:
              "linear-gradient(to bottom,rgb(160, 160, 160, 1),rgb(160, 160, 160, 0.95),rgb(160, 160, 160, 0.9),rgb(160, 160, 160, 0.85),rgb(160, 160, 160, 0.8)",
          }}
          ref={ModalBackgroundRef}
        >
          <animated.div style={animation}>
            <Paper style={{ borderRadius: "10px" }}>
              <Grid
                container
                xs={12}
                style={{
                  width: "800px",
                  height: "500px",
                  boxShadow: "0 5px 16px rgba(0, 0, 0, 0.2)",
                  color: "#000000",
                  position: "relative",
                  zIndex: 10,
                  borderRadius: "10px",
                }}
              >
                <Grid item xs={6}>
                  <img
                    src={im}
                    style={{
                      objectFit: "cover",
                      width: "100%",
                      height: "100%",
                      borderRadius: "10px 0 0 10px",
                      background: "#000000",
                    }}
                    alt="camera"
                  />
                </Grid>

                <Grid
                  item
                  xs={6}
                  style={{
                    display: "flex",
                    flexDirection: "column",
                    justifyContent: "center",
                    alignItems: "center",
                    lineHeight: "1.8",
                    textAlign: "center",
                    color: "#141414",
                  }}
                >
                  <h1>Camera</h1>
                  <p>get access</p>
                  <button>join</button>
                </Grid>
              </Grid>
            </Paper>
          </animated.div>
        </DialogContent>
      ) : null}
    </>
  );
}
