import React, { useRef, useState, useEffect, useCallback } from "react";
import { Paper, Grid, Box } from "@material-ui/core";
import RemoveRedEyeIcon from "@mui/icons-material/RemoveRedEye";
import MoreHorizIcon from "@material-ui/icons/MoreHoriz";
import { useSpring, animated, useTransition } from "react-spring";
import Crop54Icon from "@mui/icons-material/Crop54";
import CropPortraitIcon from "@mui/icons-material/CropPortrait";
import ViewArrayIcon from "@mui/icons-material/ViewArray";
import CropIcon from "@mui/icons-material/Crop";
import ViewColumnIcon from "@mui/icons-material/ViewColumn";
import AspectRatioTwoToneIcon from "@mui/icons-material/AspectRatioTwoTone";
import StarIcon from "@mui/icons-material/Star";
import Brightness2Icon from "@mui/icons-material/Brightness2";
import AlbumIcon from "@mui/icons-material/Album";
import BentoIcon from "@mui/icons-material/Bento";
import MoreVertIcon from "@mui/icons-material/MoreVert";
import CommentIcon from "@mui/icons-material/Comment";
import CircleIcon from "@mui/icons-material/Circle";
import { RootStateOrAny, useSelector, useDispatch } from "react-redux";
import { matchMobile, matchPc, matchTablet } from "../DetectDevice";
import { Slider } from "./Slider";
import { useInView } from "react-intersection-observer";

function MiniPostx({
  pey,
  addPostItemsRef,
  onPostsItemload,
  post,
  length,
  itemheight,
  itemheighthold,
  postbackheight,
  itemcroptype,
  itemFinalPostHeight,
  onPostsItemClicked,
  itemCLICKED,
  addpostDivRef,
  postDatainner,
  itemOriginalPostHeight,
  ActiveAutoPlay,
  setActiveAutoPlay,
  AUTOSlideLongImages,
  postDivRef,
  onLoadDataOnce,
  postDatainnerThumb,
  setcountAutoplay,
  countAutoplay,
  second,
  setsecond,
  secondgo,
  setsecondgo,
  scrollToPost,
  OpenModalForm,
  setDiscussionImage,
  postData,
  setCommentPostid,
  miniLayoutPost,
  setStopBodyScroll,
}: any) {
  const { REACT_APP_APPX_STATE } = process.env;

  var allow4dev = "";

  if (REACT_APP_APPX_STATE === "dev") {
    allow4dev = "http://localhost:1000";
  } else {
    allow4dev = "";
  }

  const Timer = useRef<ReturnType<typeof setTimeout> | null>(null);
  const Timer2 = useRef<ReturnType<typeof setTimeout> | null>(null);

  const { ref, inView, entry } = useInView({
    /* Optional options */
    threshold: 0.95,
  });

  const startinview = useCallback(() => {
    if (inView) {
      if (Timer.current) {
        clearTimeout(Timer.current);
      }

      if (countAutoplay === 0) {
        setcountAutoplay(1);
      }

      Timer.current = setTimeout(function () {
        if (countAutoplay === 1) {
          if (post.post_count === 1) {
          } else {
            setsecond(pey);
          }
        } else {
          if (post.post_count === 1) {
          } else {
            startSlider(0);
          }
        }
      }, 3000);
    } else {
      if (Timer.current) {
        clearTimeout(Timer.current);
      }
      if (countAutoplay !== 0) {
        setcountAutoplay(0);
      }
      if (secondgo) {
        setsecondgo(false);
      }
      if (second !== 0) {
        setsecond(0);
      }

      if (post.post_count === 1) {
      } else {
        stopSlider(0);
      }
    }
  }, [countAutoplay, inView]);

  useEffect(() => {
    startinview();
  }, [inView]);

  useEffect(() => {
    if (secondgo) {
      if (second === pey) {
        setsecondgo(false);
        startSlider(0);
      }
    } else {
    }
  }, [secondgo]);

  const [autoSlideDuration] = useState(6000);

  const dispatch = useDispatch();

  const [opacityController, setopacityController] = useState<boolean>(false);

  const [LImiter, setLImiter] = useState<boolean>(false);

  var emoOpacity = matchMobile ? 0.66 : 0.8;

  ///

  ///
  ///
  ///
  /// SPRING TRANSITION WITH INDEX
  const animationmenu = useSpring({
    config: {
      duration: 250,
    },
    opacity: opacityController ? 1 : 0,
    transform: opacityController ? `translateY(0%)` : `translateY(150%)`,
  });

  ///
  ///
  ///
  /// INTERFACE/TYPES FOR SCREENHEIGHT AND DARKMODE
  interface RootStateGlobalReducer {
    GlobalReducer: {
      darkmode: boolean;
      screenHeight: number;
    };
  }

  ///
  ///
  ///
  /// GET SCREENHEIGHT FROM REDUX STORE
  const { screenHeight, darkmode } = useSelector(
    (state: RootStateGlobalReducer) => ({
      ...state.GlobalReducer,
    })
  );
  const screenHeightReducer = screenHeight;
  const darkmodeReducer = darkmode;

  var textback = "";
  if (darkmodeReducer) {
    textback = "caption-darkPost";
  } else {
    textback = "caption-lightPost";
  }

  const showcaptionwaitTimer = useRef<ReturnType<typeof setTimeout> | null>(
    null
  );
  const [showSliderLoader, setshowSliderLoader] = useState(false);

  const [showSliderLoaderxx, setshowSliderLoaderxx] = useState(false);

  const autoPlayTimer = useRef<ReturnType<typeof setInterval> | null>(null);

  const [sliderIndex, setSliderIndex] = useState(0);
  const [sliderIndexSlow, setSliderIndexSlow] = useState(0);

  const [SpinLovely, setSpinLovely] = useState(0);
  const [Spincool, setSpincool] = useState(0);
  const [Spinfun, setSpinfun] = useState(0);
  const [Spincare, setSpincare] = useState(0);

  const [currentSpinState, setcurrentSpinState] = useState(0);

  const [Zoom1, setZoom1] = useState(false);
  const [Zoom2, setZoom2] = useState(false);
  const [Zoom3, setZoom3] = useState(false);
  const [Zoom4, setZoom4] = useState(false);

  const [ZoomBigEmo1, setZoomBigEmo1] = useState(false);
  const [ZoomBigEmo2, setZoomBigEmo2] = useState(false);
  const [ZoomBigEmo3, setZoomBigEmo3] = useState(false);
  const [ZoomBigEmo4, setZoomBigEmo4] = useState(false);

  const [Dragstart, setDragstart] = useState(false);

  const waitChangeIndexTimer2 = useRef<ReturnType<typeof setTimeout> | null>(
    null
  );

  useEffect(() => {
    if (onLoadDataOnce[pey]) {
      if (LImiter) {
      } else {
        setTimeout(() => {
          setopacityController(true);
        }, pey * 250);
        setLImiter(true);
      }
    }
  }, [onLoadDataOnce, LImiter]);

  const flashBlackAndWhite = () => {
    postDivRef.current[pey].style.filter = "grayscale(100%)";

    setTimeout(function () {
      postDivRef.current[pey].style.filter = "none";
    }, 500);
  };

  const stopSlider = (type: any) => {
    if (type === 1) {
      flashBlackAndWhite();
    }

    ///////////////////////////////
    const newArrxq = [...ActiveAutoPlay];
    newArrxq[pey] = true;
    setActiveAutoPlay(newArrxq);
    ////////////////////////////
    setshowSliderLoaderxx(false);
    setshowSliderLoader(true);

    if (autoPlayTimer.current) {
      clearInterval(autoPlayTimer.current);
    }
  };

  const autoPlaysec = useCallback(() => {
    if (sliderIndex === 0 && showSliderLoaderxx && countAutoplay === 1) {
      setTimeout(function () {
        if (countAutoplay === 1 && second !== 0) {
          stopSlider(0);

          if (Timer2.current) {
            clearTimeout(Timer2.current);
          }

          Timer2.current = setTimeout(function () {
            setcountAutoplay(0);
            setsecondgo(true);
          }, 500);
        }
      }, 2000);
    }
  }, [showSliderLoaderxx, countAutoplay, sliderIndex]);

  useEffect(() => {
    autoPlaysec();
  }, [sliderIndex]);

  ///
  ///
  ///
  /// SHOW  LOGIN PASSWORD FOR A WHILE
  const startSlider = useCallback(
    (ty: number) => {
      var peyx: number;

      if (ty === 1) {
        peyx = pey + 1;
      } else {
        peyx = pey;
      }

      if (itemCLICKED[peyx]) {
      } else {
        flashBlackAndWhite();
        //////the callback is passed the element, the index, and the array itself.
        ActiveAutoPlay.forEach(function (part: any, index: any, theArray: any) {
          if (peyx === index) {
            theArray[index] = false;
            setshowSliderLoaderxx(true);
          } else {
            theArray[index] = true;
          }
          if (index === ActiveAutoPlay.length) {
            setActiveAutoPlay(theArray);
          }
        });

        ///////////////////////////////
        const newArrxq = [...ActiveAutoPlay];
        newArrxq[peyx] = false;
        setActiveAutoPlay(newArrxq);
        ////////////////////////////
        setshowSliderLoader(false);
        autoPlayTimer.current = setInterval(function () {
          if (ActiveAutoPlay[peyx]) {
            AUTOSlideLongImages(peyx);
          } else {
          }

          setshowSliderLoader(true);

          setSliderIndex((state) => (state + 1) % postDatainner[peyx].length);
          if (waitChangeIndexTimer2.current) {
            clearTimeout(waitChangeIndexTimer2.current);
          }
          waitChangeIndexTimer2.current = setTimeout(function () {
            setSliderIndexSlow(
              (state) => (state + 1) % postDatainner[peyx].length
            );
            setshowSliderLoader(false);
          }, 500);
        }, autoSlideDuration);
      }
    },
    [ActiveAutoPlay]
  );

  const SliderAutoPlay = (type: number) => {
    if (type === 1) {
      startSlider(0);
    } else {
      if (ActiveAutoPlay[pey]) {
        startSlider(0);
      } else {
        stopSlider(1);
      }
    }
  };

  ///
  ///
  ///
  /// CHANGE SLIDER CONTENT USING  DOTS
  const checkifClicked = () => {
    if (itemCLICKED[pey]) {
      onPostsItemClicked(pey);
    } else {
      if (ActiveAutoPlay[pey]) {
      } else {
        stopSlider(0);
      }
      onPostsItemClicked(pey);
    }
  };

  ///
  ///
  ///
  /// CHANGE SLIDER CONTENT USING  DOTS
  const checkifClickedDouble = () => {};

  ///
  ///
  ///
  /// CLICK BILLBOARD OPEN ON DOUBLE CLICK
  const clickslider = (e: any) => {
    switch (e.detail) {
      case 1:
        checkifClicked();
        break;
      case 2:
        checkifClickedDouble();
        break;
      case 3:
        checkifClickedDouble();
        break;
      case 4:
        checkifClickedDouble();
        break;
    }
  };

  var postcropfont = matchPc ? "2.1vw" : matchTablet ? "4vh" : "3.6vh";
  var postcroppadding = matchPc ? "17px" : matchTablet ? "20px" : "4px";
  var cropTop: number = matchPc ? 1.5 : matchTablet ? 7 : -2;

  var posteyefont = matchPc ? "1.75vw" : matchTablet ? "3.4vh" : "3.3vh";
  var posteyeleft = matchPc ? "92.4%" : matchTablet ? "92.693%" : "90%";
  var eyeTop = matchPc ? "-9px" : matchTablet ? "-6px" : "-12px";

  var emotionClass = matchPc
    ? "turpostDark emotionspostPC "
    : matchTablet
    ? "turpostDark emotionspostTablet"
    : "turpostDark emotionspostMOBILE";

  var emo = matchPc
    ? itemcroptype[pey] === 2
      ? 3
      : 0
    : matchTablet
    ? 20
    : itemcroptype[pey] === 1
    ? 8
    : 10;

  var emolove = matchPc
    ? itemcroptype[pey] === 1
      ? 90
      : 120
    : matchTablet
    ? 170
    : itemcroptype[pey] === 1
    ? 130
    : 135;

  var emocool = matchPc
    ? itemcroptype[pey] === 1
      ? 90
      : 120
    : matchTablet
    ? 145
    : itemcroptype[pey] === 1
    ? 110
    : 115;

  var emocare = matchPc
    ? itemcroptype[pey] === 1
      ? 90
      : 120
    : matchTablet
    ? 115
    : itemcroptype[pey] === 1
    ? 90
    : 95;

  var emofunny = matchPc
    ? itemcroptype[pey] === 1
      ? 90
      : 120
    : matchTablet
    ? 90
    : itemcroptype[pey] === 1
    ? 70
    : 73;

  var profilewidth = matchPc ? "14%" : matchTablet ? "12.5%" : "15%";
  var postprofiletop = matchPc ? "13.8vh" : matchTablet ? "-9.3vh" : "-4.7vh";

  var postusernametop = matchPc ? "12vh" : matchTablet ? "-11.9vh" : "-5vh";

  var postusernamefont = matchPc ? "1vw" : matchTablet ? "2.32vh" : "1.7vh";
  var postusernameleft = matchPc ? "15.1%" : matchTablet ? "15.5%" : "20%";

  var postcirclefont = matchPc ? "0.7vw" : matchTablet ? "1.2vw" : "1.1vh";
  var dotspace = matchPc ? "1.7vw" : matchTablet ? "1.9vh" : "1.9vh";
  var dotspace2 = matchPc ? "0.9vw" : matchTablet ? "1.9vh" : "1.9vh";

  var posttopicfont = matchPc ? "0.9vw" : matchTablet ? "1.8vh" : "1.6vh";

  var postcaptiontop = matchPc ? "-1.85vh" : matchTablet ? "-9.2vh" : "-9.6vh";
  var postcaptionfont = matchPc ? "1.2vw" : matchTablet ? "2.35vh" : "1.82vh";
  var postcaptionline = matchPc ? "2.1" : matchTablet ? "1.9" : "1.95";
  var postcaptionleft = matchPc ? "11.1%" : matchTablet ? "15.5%" : "17.5%";
  var postcaptionheight = matchPc ? "10.1vh" : matchTablet ? "8.3vh" : "8.8vh";
  var postcaptionwidth = matchPc ? "79.5%" : matchTablet ? "76%" : "84%";

  var postcommenttop = matchPc
    ? itemcroptype[pey] === 1 || itemcroptype[pey] === 2
      ? itemheighthold[pey] - 30
      : itemheighthold[pey] - 6
    : matchTablet
    ? itemcroptype[pey] === 1 || itemcroptype[pey] === 2
      ? itemheighthold[pey] - 40
      : itemheighthold[pey] - 10
    : itemcroptype[pey] === 1 || itemcroptype[pey] === 2
    ? itemcroptype[pey] === 1
      ? itemheighthold[pey] - 24
      : itemheighthold[pey] - 40
    : itemheighthold[pey] - 13;
  var postcommentfont = matchPc ? "1.8vw" : matchTablet ? "4vh" : "3.15vh";
  var postcommentwidth = matchPc ? "99.7%" : matchTablet ? "97.5%" : "95.5%";

  var postoptionstop = matchPc ? "-2vh" : matchTablet ? "-15.4vh" : "-13.5vh";
  var postoptionsleft = matchPc ? "95.5%" : matchTablet ? "94.7%" : "91.5%";
  var postvertfont = matchPc ? "2.2vw" : matchTablet ? "3.6vh" : "3.6vh";

  var postdatetop = matchPc ? "2.15vh" : matchTablet ? "-7.7vh" : "-7.8vh";
  var postdatefont = matchPc ? "0.9vw" : matchTablet ? "1.25vh" : "1.25vh";
  var postdateleft = matchPc ? "98%" : matchTablet ? "98.5%" : "96.3%";

  var emocolor = "";

  const startSpin = () => {
    stopEmoAlreadySpinning();
    setSpinLovely(1);
    setcurrentSpinState(1);

    setZoomBigEmo1(true);
    setTimeout(() => {
      setZoomBigEmo1(false);
    }, 200);
    setTimeout(() => {
      setZoomBigEmo1(true);
    }, 500);
    setTimeout(() => {
      setZoomBigEmo1(false);
    }, 2500);
  };
  const startSpin2 = () => {
    stopEmoAlreadySpinning();
    setSpincool(1);
    setcurrentSpinState(2);

    setZoomBigEmo2(true);
    setTimeout(() => {
      setZoomBigEmo2(false);
    }, 200);
    setTimeout(() => {
      setZoomBigEmo2(true);
    }, 500);
    setTimeout(() => {
      setZoomBigEmo2(false);
    }, 2500);
  };
  const startSpin3 = () => {
    stopEmoAlreadySpinning();
    setSpincare(1);
    setcurrentSpinState(3);

    setZoomBigEmo3(true);
    setTimeout(() => {
      setZoomBigEmo3(false);
    }, 200);
    setTimeout(() => {
      setZoomBigEmo3(true);
    }, 500);
    setTimeout(() => {
      setZoomBigEmo3(false);
    }, 2500);
  };
  const startSpin4 = () => {
    stopEmoAlreadySpinning();
    setSpinfun(1);
    setcurrentSpinState(4);

    setZoomBigEmo4(true);
    setTimeout(() => {
      setZoomBigEmo4(false);
    }, 200);
    setTimeout(() => {
      setZoomBigEmo4(true);
    }, 500);
    setTimeout(() => {
      setZoomBigEmo4(false);
    }, 2500);
  };
  const stopEmoAlreadySpinning = () => {
    switch (currentSpinState) {
      case 1:
        setSpinLovely(0);
        break;
      case 2:
        setSpincool(0);
        break;
      case 3:
        setSpincare(0);
        break;
      case 4:
        setSpinfun(0);
        break;
      default:
    }
  };

  var themepadding = darkmodeReducer ? "turdarkemo" : "turlightemo";

  return (
    <>
      <animated.div ref={ref} style={animationmenu}>
        <div
          ref={addpostDivRef}
          style={{
            padding: "0px",
            width: "100%",
            marginTop: pey === 0 || pey === 1 ? "-0.5px" : "-1.1px",
            zIndex: length - 1 - pey,
            paddingLeft: matchMobile ? "0px" : "4px",
            paddingRight: matchMobile ? "0px" : "4px",
            paddingTop: matchMobile ? "10px" : "10px",
          }}
        >
          {/*///////////////////////////////////////////////////////////////////////////POST DATA*/}

          <Slider
            type={1}
            ActiveAutoPlay={ActiveAutoPlay}
            setActiveAutoPlay={setActiveAutoPlay}
            pey={pey}
            addPostItemsRef={addPostItemsRef}
            itemheight={itemheight}
            onPostsItemClicked={onPostsItemClicked}
            onPostsItemload={onPostsItemload}
            post={post}
            slides={postDatainner[pey]}
            slidesThumb={postDatainnerThumb[pey]}
            itemcroptype={itemcroptype}
            itemFinalPostHeight={itemFinalPostHeight}
            itemCLICKED={itemCLICKED}
            itemOriginalPostHeight={itemOriginalPostHeight}
            AUTOSlideLongImages={AUTOSlideLongImages}
            clickslider={clickslider}
            startSlider={startSlider}
            stopSlider={stopSlider}
            SliderAutoPlay={SliderAutoPlay}
            showSliderLoader={showSliderLoader}
            setshowSliderLoader={setshowSliderLoader}
            autoPlayTimer={autoPlayTimer}
            sliderIndex={sliderIndex}
            setSliderIndex={setSliderIndex}
            sliderIndexSlow={sliderIndexSlow}
            setSliderIndexSlow={setSliderIndexSlow}
            length={length}
          />
          {/*///////////////////////////////////////////////////////////////////////////POST DATA*/}
          {itemCLICKED[pey] ? null : (
            <>
              <div
                className={
                  darkmodeReducer
                    ? miniLayoutPost
                      ? ""
                      : "post-background-dark"
                    : miniLayoutPost
                    ? ""
                    : "post-background-light"
                }
                style={{
                  height: `${postbackheight / 2}px`,
                  marginTop: `-${postbackheight / 2}px`,
                  position: "relative",
                  transition: "all 350ms ease",
                  zIndex: 2,
                  borderBottomLeftRadius: "0px",
                  borderBottomRightRadius: "0px",
                }}
              >
                {/*///////////////////////////////////////////////////// opacity: 0.6,//////////////////////EMOTIONS*/}

                {/*///////////////////////////////////////////////////////////////////////////EMOTIONS*/}

                {/*///////////////////////////////////////////////////////////////////////////BACKPAD CLICKABLE*/}
                <div
                  onClick={clickslider}
                  style={{
                    opacity: darkmodeReducer ? 0.89 : 0.94,
                    cursor: "pointer",
                    top: `-1vh`,
                    position: "absolute",
                    zIndex: 6,
                    paddingLeft: "2vw",
                    fontFamily: "Arial, Helvetica, sans-seri",
                    height: "80px",
                    width: "100%",
                  }}
                ></div>
                {/*///////////////////////////////////////////////////////////////////////////BACKPAD CLICKABLE*/}
                {/*///////////////////////////////////////////////////////////////////////////PROFILE-PIC*/}
                <div
                  className="zuperxyinfo"
                  style={{
                    opacity: 1,
                    top: postprofiletop,
                    position: "relative",
                    display: "flex",
                    alignItems: "center",
                    justifyContent: "left",
                    zIndex: 1,
                    paddingLeft: matchPc
                      ? "1.4vw"
                      : matchTablet
                      ? "2.3vw"
                      : "2.1vw",
                    height: "0px",
                    fontFamily: "Arial, Helvetica, sans-seri",
                  }}
                >
                  <img
                    className={darkmodeReducer ? "turpostDark" : "turpostLight"}
                    src={`${post.profile_image}`}
                    alt="a superstarz post "
                    style={{
                      boxShadow: darkmodeReducer
                        ? "0 0 1px #555555"
                        : "0 0 3.5px #aaaaaa",
                      width: profilewidth,
                      height: "auto",
                      padding: "0px",
                      objectFit: "contain",
                      borderRadius: "50%",
                      position: "relative",
                      zIndex: 1,
                    }}
                  />
                </div>
                {/*///////////////////////////////////////////////////////////////////////////PROFILE-PIC*/}
                {/*///////////////////////////////////////////////////////////////////////////USERNAME AND TOPIC*/}
                <div
                  className={
                    darkmodeReducer
                      ? "zuperxyinfoPostDark"
                      : "zuperxyinfoPostLight"
                  }
                  style={{
                    visibility: miniLayoutPost ? "hidden" : "visible",
                    top: postusernametop,
                    position: "relative",
                    display: "flex", //flex
                    alignItems: "center",
                    justifyContent: "left",
                    zIndex: 1,
                    paddingLeft: "2vw",
                    fontFamily: "Arial, Helvetica, sans-seri",
                    marginLeft: postusernameleft,
                    height: "0px",
                  }}
                >
                  <span>
                    <span
                      className={
                        darkmodeReducer ? "zuperkinglight" : "zuperkinglight"
                      }
                      style={{
                        color: darkmodeReducer ? "#dddddd" : "#ffffff",
                      }}
                    >
                      <span
                        style={{
                          fontWeight: "normal",
                          fontSize: postusernamefont,
                        }}
                      >
                        {post.username}
                      </span>

                      <span
                        style={{
                          opacity: 0,
                          fontSize: dotspace,
                        }}
                      >
                        .
                      </span>
                      {itemcroptype[pey] === 1 ? (
                        <AlbumIcon
                          className="zuperkingIconPostLight"
                          style={{
                            fontSize: postcirclefont,
                            color: post.color1,
                          }}
                        />
                      ) : itemcroptype[pey] === 2 ? (
                        <AlbumIcon
                          className="zuperkingIconPostLight"
                          style={{
                            fontSize: postcirclefont,
                            color: post.color1,
                          }}
                        />
                      ) : (
                        <CircleIcon
                          className="zuperkingIconPostLight"
                          style={{
                            fontSize: postcirclefont,
                            color: post.color1,
                          }}
                        />
                      )}

                      <span
                        style={{
                          opacity: 0,
                          fontSize: dotspace2,
                        }}
                      ></span>

                      <span
                        style={{
                          fontSize: posttopicfont,
                          fontFamily: "Arial, Helvetica, sans-seri",
                          fontWeight: "normal",
                        }}
                      >
                        {" "}
                        {post.topic ? post.topic : "SuperstarZ"}
                      </span>
                    </span>
                  </span>
                </div>
                {/*///////////////////////////////////////////////////////////////////////////USERNAME AND TOPIC*/}

                <Grid
                  item
                  className={SpinLovely ? "" : "changeOpacity"}
                  xs={12}
                  style={{
                    padding: "0px",
                    height: "0px",
                    position: "fixed",
                    width: "100%",
                    top: "14%",
                  }}
                >
                  {" "}
                  <img
                    className={emotionClass}
                    src={`./images/emotions/love.png`}
                    alt="a superstarz post "
                    style={{
                      cursor: "pointer",
                      boxShadow: darkmodeReducer
                        ? "0 0 1px #555555"
                        : "0 0 0.1px #222222",
                      width: matchPc ? "20%" : "26%",
                      marginLeft: "38%",
                      opacity: "0.8",
                      height: "auto",
                      padding: "0px",
                      objectFit: "contain",
                      borderRadius: "50%",
                      transition: "transform 2s",
                      display: ZoomBigEmo1 ? "block" : "none",
                    }}
                  />
                </Grid>

                <Grid
                  item
                  className={Spincool ? "" : "changeOpacity"}
                  xs={12}
                  style={{
                    padding: "0px",
                    height: "0px",
                    position: "fixed",
                    width: "100%",
                    top: "14%",
                  }}
                >
                  {" "}
                  <img
                    className={emotionClass}
                    src={`./images/emotions/cool.png`}
                    alt="a superstarz post "
                    style={{
                      cursor: "pointer",
                      boxShadow: darkmodeReducer
                        ? "0 0 1px #555555"
                        : "0 0 0.1px #222222",
                      width: matchPc ? "20%" : "26%",
                      marginLeft: "38%",
                      opacity: "0.8",
                      height: "auto",
                      padding: "0px",
                      objectFit: "contain",
                      borderRadius: "50%",
                      transition: "transform 2s",
                      display: ZoomBigEmo2 ? "block" : "none",
                    }}
                  />
                </Grid>

                <Grid
                  item
                  className={Spincare ? "" : "changeOpacity"}
                  xs={12}
                  style={{
                    padding: "0px",
                    height: "0px",
                    position: "fixed",
                    width: "100%",
                    top: "14%",
                  }}
                >
                  {" "}
                  <img
                    className={emotionClass}
                    src={`./images/emotions/oo.png`}
                    alt="a superstarz post "
                    style={{
                      cursor: "pointer",
                      boxShadow: darkmodeReducer
                        ? "0 0 1px #555555"
                        : "0 0 0.1px #222222",
                      width: matchPc ? "20%" : "26%",
                      marginLeft: "38%",
                      opacity: "0.8",
                      height: "auto",
                      padding: "0px",
                      objectFit: "contain",
                      borderRadius: "50%",
                      transition: "transform 2s",
                      display: ZoomBigEmo3 ? "block" : "none",
                    }}
                  />
                </Grid>

                <Grid
                  item
                  className={Spinfun ? "" : "changeOpacity"}
                  xs={12}
                  style={{
                    padding: "0px",
                    height: "0px",
                    position: "fixed",
                    width: "100%",
                    top: "14%",
                  }}
                >
                  {" "}
                  <img
                    className={emotionClass}
                    src={`./images/emotions/laugh.png`}
                    alt="a superstarz post "
                    style={{
                      cursor: "pointer",
                      boxShadow: darkmodeReducer
                        ? "0 0 1px #555555"
                        : "0 0 0.1px #222222",
                      width: matchPc ? "20%" : "26%",
                      marginLeft: "38%",
                      opacity: "0.8",
                      height: "auto",
                      padding: "0px",
                      objectFit: "contain",
                      borderRadius: "50%",
                      transition: "transform 2s",
                      display: ZoomBigEmo4 ? "block" : "none",
                    }}
                  />
                </Grid>

                <Grid item xs={12} style={{ padding: "0px", height: "0px" }}>
                  {" "}
                </Grid>
              </div>{" "}
            </>
          )}
        </div>
      </animated.div>
    </>
  );
}

