import React, { useState, useRef } from 'react';
import { createFFmpeg, fetchFile } from '@ffmpeg/ffmpeg';

const ffmpeg = createFFmpeg({
    log: true,
    corePath: 'https://unpkg.com/@ffmpeg/core-mt@0.12.5/dist/umd/ffmpeg-core.js'
});

const VideoFFmpegx = ({ VideoUrl }: any) => {
    const [recordedVideoUrl, setRecordedVideoUrl] = useState(null);

    const processVideo = async (sourceVideoUrl: any) => {
        if (!ffmpeg.isLoaded()) {
            await ffmpeg.load();
        }


        alert('kjjk');
        // Writing the file to FFmpeg's virtual file system
        ffmpeg.FS('writeFile', 'input.webm', await fetchFile(sourceVideoUrl));

        // Running the FFmpeg command to cut the video from 1:00 to 1:10
        await ffmpeg.run('-ss', '00:01:00', '-i', 'input.webm', '-t', '10', '-c:v', 'libx264', '-c:a', 'aac', 'output.mp4');

        // Reading the result file
        const data = ffmpeg.FS('readFile', 'output.mp4');

        // Creating a URL for the output file
        const url: any = URL.createObjectURL(new Blob([data.buffer], { type: 'video/mp4' }));

        setRecordedVideoUrl(url);
    };

    return (
        <div>
            <button onClick={() => processVideo(VideoUrl)}>Process Video</button>
            {recordedVideoUrl && <video src={recordedVideoUrl} controls style={{ width: '100%' }}></video>}
        </div>
    );
};

export const VideoFFmpeg = React.memo(VideoFFmpegx);
