import React, { useState, useEffect } from "react";
import { matchPc, matchTablet } from "../DetectDevice";
import { Grid } from "@material-ui/core";
import MoreHorizIcon from "@material-ui/icons/MoreHoriz";
import { useSelector, useDispatch } from "react-redux";
import { useSpring, animated } from "react-spring";

import CircleIcon from "@mui/icons-material/Circle";
import SettingsIcon from "@mui/icons-material/Settings";

function MenuInnerx({
  optionsShow,
  optinstopshowingReducer,
  typeTop,
  closeoptionsslide,
  animationop,
  optionsCollectImageRef,
  handleTouchStartOptions,
  handleTouchMoveOptions,
  modalanimation,
  nextSlidePc,
  optionsImages,
  ActiveSlide,
  optionsNameData,
  clickOptions,
  optionsClickType,
  getSliderWidthNew,
  OpenUploadModal
}: any): JSX.Element {
  ///
  ///
  ///
  ///DARKMODE FROM REDUX
  interface RootStateGlobalReducer {
    GlobalReducer: {
      darkmode: boolean;
    };
  }
  const { darkmode } = useSelector((state: RootStateGlobalReducer) => ({
    ...state.GlobalReducer,
  }));

  const darkmodeReducer = darkmode;

  var circleIdentify = typeTop ? 0 : 4;
  var circleIdentify2 = typeTop ? 1 : 5;

  ///
  ///
  /// GET COLOR FROM REDUX STORE
  interface RootStateReducerColor {
    GlobalReducerColor: {
      color: string;
      colordark: string;
      colortype: number;
    };
  }
  const { color, colordark, colortype } = useSelector(
    (state: RootStateReducerColor) => ({
      ...state.GlobalReducerColor,
    })
  );
  const colorReducer = color;
  const colorReducerdark = colordark;
  const colortypeReducer = colortype;

  ///
  ///
  ///
  /// GET LOGGED USER DATA FROM REDUX STORE
  interface RootStateReducerImage {
    UserdataReducer: {
      image: string;
      imageThumb: string;
      id: number;
      username: string;
      memeberPageid: number;
      MemberProfileData: any;
    };
  }
  const { username, image, imageThumb, id, memeberPageid, MemberProfileData } =
    useSelector((state: RootStateReducerImage) => ({
      ...state.UserdataReducer,
    }));

  const [imageReducer, setimageReducer] = useState("");

  const [imageReducerThumb, setimageReducerThumb] = useState("");
  const [ColorMemberReducer, setColorMemberReducer] = useState("");

  const [selectedImage, setselectedImage] = useState([]);
  const [cropimage, setcropimage] = useState<any>(null);

  const circleColor = () => {
    if (memeberPageid === 0) {
      setColorMemberReducer(colorReducer);
    } else {
      setColorMemberReducer(MemberProfileData.usercolor1);
    }
  };

  useEffect(() => {
    circleColor();
  }, [MemberProfileData, colorReducer, imageThumb, image, memeberPageid]);


  const imageHandleChange = (e: any) => {
    if (e.target.files && e.target.files.length > 0) {
      const FileArray = Array.from(e.target.files).map((file: any) =>
        URL.createObjectURL(file)
      );

      if (e.target.files.length > 8) {
        ///setShowmaxPost(true);
        setTimeout(function () {
         /// setShowmaxPost(false);
        }, 3000);
      } else {
        setselectedImage([]);
        setselectedImage((prevImages: any) => prevImages.concat(FileArray));
        setcropimage(FileArray[0]);
        ///window.history.pushState(null, "", "Crop");
       /// dispatch(UpdateNavCropReducer(true));
      }
    }
  };                     

   

  return (
    <>
      <>
        {optinstopshowingReducer ? (
          typeTop ? null : (
            <>
              {" "}
              <Grid
                container
                style={{
                  width: matchPc ? "10%" : matchTablet ? "30%" : "10%",
                  margin: "auto",
                }}
              >
                <span>
                  <MoreHorizIcon
                    className={
                      darkmodeReducer
                        ? "make-small-icons-clickable-dark dontallowhighlighting  "
                        : "make-small-icons-clickable-light  dontallowhighlighting  "
                    }
                    onClick={closeoptionsslide}
                    style={{
                      fontSize: matchPc ? "2.55vw" : "4.7vh",
                      verticalAlign: "middle",
                      justifyContent: "center",
                      zIndex: 2,
                      marginTop: matchPc ? "9vh" : "6.5vh",
                      position: "absolute",
                    }}
                  />
                </span>{" "}
              </Grid>
            </>
          )
        ) : null}

        <animated.div style={animationop}>
          <Grid
            container
            className={
              typeTop
                ? darkmodeReducer
                  ? `optionsTop-background-dark `
                  : `optionsTop-background-light `
                : ""
            }
            onTouchStart={handleTouchStartOptions}
            onTouchMove={handleTouchMoveOptions}
            style={{
              zIndex: 1,
              padding: "0px",
              top: matchPc ? (typeTop ? "-2vh" : "0vh") : "0vh",
              position: "relative",
              margin: "0 auto",
              overflow: "hidden",
              left: "0px",
              height: typeTop ? (matchPc ? "24.4vh" : "20.5vh") : "auto",
              paddingBottom: "1.4vh",
            }}
          >
            <animated.div ref={optionsCollectImageRef} style={modalanimation}>
              <Grid
                item
                style={{
                  margin: "auto",
                  textAlign: "center",
                  position: "relative",
                  bottom: typeTop ? "-2.6vh" : "0.2em",
                  left: typeTop ? "-3px" : matchPc ? "-2px" : "0px",
                }}
              >
                <CircleIcon
                  onClick={() => {
                    //nextSlidePc();
                    OpenUploadModal(0);
                  }}
                  className="buttonshake"
                  style={{
                    fontSize: typeTop ? "1.7vw" : "1.3vw",
                    cursor: "pointer",
                    color: darkmodeReducer
                      ? typeTop
                        ? "rgba(200, 200, 200, 0.5)"
                        : "rgba(200, 200, 200, 0.1)"
                      : typeTop
                      ? "rgba(005, 005, 005, 0.4)"
                      : "rgba(005, 005, 005, 0.2)",
                    display: matchPc ? "block" : "none",
                  }}
                />
              </Grid>
              {optionsImages.map((im: any, i: any) => (
                <Grid key={i} item xs={12}>
                  {typeTop ? (
                    <Grid
                      item
                      xs={12}
                      style={{
                        margin: "auto",
                        textAlign: "center",
                        position: "relative",
                        top: "2.2vh",
                      }}
                    >
                      <Grid
                        item
                        xs={12}
                        style={{
                          paddingBottom: "10px",
                          fontSize: matchPc
                            ? "1.1vw"
                            : matchTablet
                            ? "2.08vh"
                            : "2vh",
                          fontWeight: "bolder",
                          fontFamily: "Arial, Helvetica, sans-serif",
                          visibility: ActiveSlide === i ? "visible" : "hidden",
                          filter: darkmodeReducer
                            ? "drop-shadow(1.2px 0.1px 1.92px rgba(255, 255, 255, 0.4))"
                            : "drop-shadow(1.2px 0.1px 1.92px rgba(41, 53, 70, 8.35))",
                          color: darkmodeReducer ? "#dddddd" : "#0b111b",
                        }}
                      >
                        {" "}
                        {optionsNameData[i]}
                      </Grid>
                    </Grid>
                  ) : null}
                  {i === circleIdentify || i === circleIdentify2 ? (
                    <>
                      {i === circleIdentify ? (
                        <>
                          {" "}
                          <div
                            onClick={() => {
                              clickOptions(i, optionsClickType, "upload");
                            }}
                            style={{
                              cursor: ActiveSlide === i ? "pointer" : "copy",
                              width: `${getSliderWidthNew}px`,
                              height: `${getSliderWidthNew}px`,
                              backgroundColor: darkmodeReducer
                                ? "rgba(010,010,010, 0.68)"
                                : "rgba(220,220,220, 0.7)",
                              borderRadius: "50%",
                              marginTop: "2.15vh",
                              textAlign: "center",
                              alignItems: "center",
                              display: "grid",
                              justifyContent: "center",
                              boxShadow: darkmodeReducer
                                ? ActiveSlide === i
                                  ? `0 0 3.4px ${ColorMemberReducer}`
                                  : typeTop
                                  ? "0 0 5.5px#aaaaaa"
                                  : ""
                                : ActiveSlide === i
                                ? `0 0 3.4px ${ColorMemberReducer}`
                                : typeTop
                                ? "0 0 5.45px#222222"
                                : ``,
                            }}
                          >
                            {" "}
                            <SettingsIcon
                              style={{
                                fontSize: matchPc ? "2vw" : "5vh",
                                opacity: ActiveSlide === i ? 0.5 : 1,
                                color: darkmodeReducer ? "#eeeeee" : "#222222",
                              }}
                              className="zuperkinginfo"
                            />
                          </div>
                        </>
                      ) : (
                        <>
                          {" "}

                          
 
      <input
      onChange={imageHandleChange}
     type="file"
     name="superImages"
     accept="image/*"
     multiple
     id="fileoo"
     style={{ visibility: "hidden",display:'none' }}
   />




                          <label htmlFor="fileoo">
                          <div
                            onClick={() => {
                              clickOptions(i, optionsClickType, "upload");
                              OpenUploadModal(1);
                            }}
                            style={{
                              cursor: ActiveSlide === i ? "pointer" : "copy",
                              width: `${getSliderWidthNew}px`,
                              height: `${getSliderWidthNew}px`,
                              backgroundColor: darkmodeReducer
                                ? "rgba(010,010,010, 0.68)"
                                : "rgba(220,220,220, 0.7)",
                              borderRadius: "50%",
                              marginTop: "2.15vh",
                              textAlign: "center",
                              alignItems: "center",
                              display: "grid",
                              justifyContent: "center",
                              boxShadow: darkmodeReducer
                                ? ActiveSlide === i
                                  ? `0 0 3.4px ${ColorMemberReducer}`
                                  : typeTop
                                  ? "0 0 5.5px#aaaaaa"
                                  : ""
                                : ActiveSlide === i
                                ? `0 0 3.4px ${ColorMemberReducer}`
                                : typeTop
                                ? "0 0 5.45px#222222"
                                : ``,
                            }}
                          >
                            {" "} 
                            <CircleIcon
                              style={{
                                fontSize: matchPc ? "1.65vw" : "4.5vh",
                                opacity: ActiveSlide === i ? 0.5 : 1,
                                color: darkmodeReducer
                                  ? ActiveSlide === i
                                    ? "red" 
                                    : "#eeeeee"
                                  : ActiveSlide === i
                                  ? "red"
                                  : "#222222",
                              }}
                              className="zuperkinginfo"
                            />
                            
                          </div>
                          </label>
                        </>
                      )}
                    </>
                  ) : (
                    <img
                      alt={` ${optionsNameData[i]}  option`}
                      onClick={() => {
                        clickOptions(i, optionsClickType, " ");
                      }}
                      style={{
                        cursor: ActiveSlide === i ? "pointer" : "copy",
                        width: `${getSliderWidthNew}px`,
                        height: `${getSliderWidthNew}px`,
                        borderRadius: "50%",
                        padding: "0px",
                        objectFit: "cover",
                        marginLeft: "2px",
                        marginTop: "14px",
                        boxShadow: darkmodeReducer
                          ? ActiveSlide === i
                            ? `0 0 3.4px ${ColorMemberReducer}`
                            : typeTop
                            ? "0 0 12.5px#aaaaaa"
                            : ""
                          : ActiveSlide === i
                          ? `0 0 3.4px ${ColorMemberReducer}`
                          : typeTop
                          ? `0 0 14.45px#222222`
                          : "",

                        marginBottom: "2.2px",
                      }}
                      src={`./images/options/${im}`}
                    />
                  )}

                  {typeTop ? null : (
                    <Grid
                      item
                      xs={12}
                      style={{
                        margin: "auto",
                        textAlign: "center",
                        position: "relative",
                        bottom:
                          i === circleIdentify || i === circleIdentify2
                            ? matchPc
                              ? "-0.5em"
                              : "-0.3em"
                            : "0.2em",
                      }}
                    >
                      <Grid
                        item
                        xs={12}
                        style={{
                          fontSize: matchPc
                            ? "0.95vw"
                            : matchTablet
                            ? "2vh"
                            : "1.8vh",
                          fontWeight: "bolder",
                          fontFamily: "Arial, Helvetica, sans-serif",
                          visibility: ActiveSlide === i ? "visible" : "hidden",
                          opacity: typeTop
                            ? 0
                            : optinstopshowingReducer
                            ? 0
                            : 1,
                          color: darkmodeReducer ? "#dddddd" : "#0b111b",
                        }}
                      >
                        {" "}
                        {optionsNameData[i]}
                      </Grid>
                    </Grid>
                  )}
                </Grid>
              ))}
            </animated.div>
          </Grid>
        </animated.div>
      </>
    </>
  );
}

export const MenuInner = React.memo(MenuInnerx);
