import React, { useRef, useState, useCallback } from "react";
import { Paper, Grid, Box } from "@material-ui/core";
import billboardPictureData from "../images/k.jpg";
import { matchPc, matchTablet } from "../DetectDevice";
import { useSelector, useDispatch } from "react-redux";
import "./profile.css";
import { DarkmodeToggleAction } from ".././GlobalActions";
import profilePictureData from "../images/profile/2602017071517530029820170521192154199Screenshot_2016-05-29-20-10-52_1.jpg";

function Profile() {
  ///
  ///
  ///
  /// GET DARKMODE FROM REDUX STORE
  interface RootStateGlobalReducer {
    GlobalReducer: {
      darkmode: boolean;
    };
  }
  const { darkmode } = useSelector((state: RootStateGlobalReducer) => ({
    ...state.GlobalReducer,
  }));

  const darkmodeReducer = darkmode;

  //////////////////////////////////BILLBOARD VARIABLES FOR DEVICE TYPES
  var widthh = matchPc ? "65.5vw" : "98vw";
  var topp = matchPc ? "2.8em" : matchTablet ? "3.5em" : "1.25em";
  var username = matchPc
    ? "usernamePc"
    : matchTablet
    ? "usernameTablet"
    : "usernameMobile";

  var widthName = matchPc ? "65.5vw" : "98vw";
  var topName = matchPc ? "8.8em" : matchTablet ? "11.3em" : "3.5em";
  var name = matchPc ? "namePc" : matchTablet ? "nameTablet" : "nameMobile";

  var widthConnect = matchPc ? "65.5vw" : "98vw";
  var bottomConnect = matchPc ? "9.5em" : matchTablet ? "11.3em" : "4.05em";
  var favclass = matchPc ? "favPc" : matchTablet ? "favTablet" : "favMobile";
  var fanclass = matchPc ? "fanPc" : matchTablet ? "fanTablet" : "fanMobile";

  var fontConnectText = matchPc ? "1.06vw" : matchTablet ? "2.5vw" : "1.73vh";
  var fontConnectnum = matchPc ? "1.75vw" : matchTablet ? "3.6vw" : "2.4vh";

  var widthProfilePic = matchPc ? "65.5vw" : matchTablet ? "60vw" : "58vw";
  var topProfilePic = matchPc ? "8.8em" : matchTablet ? "22.3em" : "6.2em";

  //////////////////////////////////BILLBOARD VARIABLES FOR DEVICE TYPES

  ///
  ///
  ///
  /// TOGGLEDARKMODE ON USERNAME CLICK
  var toggleDarkMode = false;
  const dispatch = useDispatch();
  const switchThemes = () => {
    if (darkmodeReducer) {
      toggleDarkMode = false;
    } else {
      toggleDarkMode = true;
    }
    dispatch(DarkmodeToggleAction());
    ////ACESSING LOCALSTORAGE
    localStorage.setItem("darkmode", toggleDarkMode.toString());
  };

  ///
  ///
  ///
  /// SET  DYNAMIC BILLBOARD BACKPAD HEIGHT
  const billboardActivePicRef = useRef<HTMLImageElement>(null);
  const [billboardDynamicHeight, setBillboardDynamicHeight] =
    useState<number>(0);
  const onBillboardimageload = useCallback((e: any) => {
    if (billboardActivePicRef && billboardActivePicRef.current) {
      const billimageHeight = billboardActivePicRef.current.clientHeight;
      setBillboardDynamicHeight(billimageHeight);
    }
  }, []);
  ///
  ///
  ///
  return (
    <Paper>
      <Grid container style={{ height: "100vh" }}>
        {/*///////////////////////////////////////////////////////////////////////////BACKPAD BILLBOARD LIGHTINING/DARKEN*/}
        <Grid
          container
          style={{
            position: "fixed",
            top: "0em",
            width: "100%",
            zIndex: 2,
          }}
        >
          <Grid
            item
            component={Box}
            display={{ xs: "none", md: "block" }}
            md={2}
          ></Grid>
          <Grid
            item
            xs={12}
            md={8}
            style={{
              borderBottomLeftRadius: "7px",
              borderBottomRightRadius: "7px",
              height: `${billboardDynamicHeight}px`,
              backgroundColor: darkmodeReducer
                ? "rgba(005, 005, 005, 0.045)"
                : "rgba(250, 250, 250, 0.09)",
            }}
          ></Grid>
        </Grid>
        {/*///////////////////////////////////////////////////////////////////////////BACKPAD BILLBOARD LIGHTINING/DARKEN*/}

        <Grid
          item
          component={Box}
          display={{ xs: "none", md: "block" }}
          md={2}
        ></Grid>
        <Grid item xs={12} md={8}>
          {/*///////////////////////////////////////////////////////////////////////////IMAGE*/}
          <img
            className={
              darkmodeReducer ? `turdarkbillboard` : ` turlightbillboard`
            }
            ref={billboardActivePicRef}
            onLoad={onBillboardimageload}
            style={{
              position: "relative",
              zIndex: 0,
              objectFit: "contain",
              width: "100%",
              borderRadius: "0px",
              borderBottomLeftRadius: matchPc ? "7px" : "0em",
              borderBottomRightRadius: matchPc ? "7px" : "0em",
              margin: "auto",
            }}
            src={billboardPictureData}
            alt="Superstarz Billboard "
          />{" "}
          {/*///////////////////////////////////////////////////////////////////////////IMAGE*/}
          {/*///////////////////////////////////////////////////////////////////////////USERNAME*/}
          <Grid
            item
            xs={12}
            style={{
              position: "absolute",
              width: widthh,
              height: "auto",
              top: topp,
              textAlign: "right",
              zIndex: 3,
            }}
          >
            <span
              onClick={switchThemes}
              className={
                darkmodeReducer
                  ? `fontfamilyArial ${username} turdark`
                  : `fontfamilyArial ${username} turlight`
              }
              style={{
                cursor: "pointer",
                color: darkmodeReducer ? "#ffffff" : "#000000",
                backgroundColor: darkmodeReducer
                  ? "rgba(005, 005, 005, 0.42)"
                  : "rgba(250, 250, 250, 0.75)",
              }}
            >
              Kendall
            </span>
          </Grid>
          {/*///////////////////////////////////////////////////////////////////////////USERNAME*/}
          {/*///////////////////////////////////////////////////////////////////////////FULLNAME OR QUOTES*/}
          <Grid
            item
            xs={12}
            style={{
              position: "absolute",
              width: widthName,
              height: "auto",
              top: topName,
              textAlign: "right",
              zIndex: 3,
            }}
          >
            <span
              style={{
                color: darkmodeReducer ? "#ffffff" : "#000000",
                backgroundColor: darkmodeReducer
                  ? "rgba(005, 005, 005, 0.42)"
                  : "rgba(250, 250, 250, 0.75)",
              }}
              className={
                darkmodeReducer
                  ? `fontfamilyArial ${name} turdark`
                  : `fontfamilyArial ${name} turlight`
              }
            >
              i love the summers so much
            </span>
          </Grid>
          {/*///////////////////////////////////////////////////////////////////////////FULLNAME OR QUOTES*/}
          {/*///////////////////////////////////////////////////////////////////////////FAVS*/}
          <Grid
            item
            xs={12}
            style={{
              position: "relative",
              width: widthConnect,
              height: "auto",
              bottom: bottomConnect,
              display: "flex",
              justifyContent: "flex-end",
              zIndex: 4,
            }}
          >
            <Grid item style={{ textAlign: "center" }}>
              {" "}
              <span
                style={{
                  fontWeight: "bolder",
                  fontSize: fontConnectText,
                  backgroundColor: darkmodeReducer
                    ? "rgba(005, 005, 005, 0.29)"
                    : "rgba(255, 255, 255, 0.35)",
                  padding: "1px  ",
                }}
                className={
                  darkmodeReducer
                    ? `fontfamilyArial zuperxyinfotext turdarkfavfan ${fanclass}  `
                    : `fontfamilyArial zuperxyinfotext turlight ${fanclass}  `
                }
              >
                FANS
              </span>
              <Grid xs={12} item style={{ height: "10px" }}></Grid>
              <span
                style={{
                  fontWeight: "bolder",
                  fontSize: fontConnectnum,

                  marginRight: "0.5vw",
                  backgroundColor: darkmodeReducer
                    ? "rgba(005, 005, 005, 0.2)"
                    : "rgba(255, 255, 255, 0.03)",
                }}
                className={`fontfamilyArial zuperxyinfo ${fanclass}  `}
              >
                147
              </span>{" "}
            </Grid>
            <Grid item style={{ textAlign: "center" }}>
              {" "}
              <span
                style={{
                  fontWeight: "bolder",
                  fontSize: fontConnectText,
                  backgroundColor: darkmodeReducer
                    ? "rgba(005, 005, 005, 0.29)"
                    : "rgba(255, 255, 255, 0.35)",

                  padding: "1px  ",
                }}
                className={
                  darkmodeReducer
                    ? `fontfamilyArial zuperxyinfotext turdarkfavfan ${favclass}  `
                    : `fontfamilyArial zuperxyinfotext turlight ${favclass}  `
                }
              >
                FAVORITES
              </span>
              <Grid xs={12} item style={{ height: "10px" }}></Grid>
              <span
                style={{
                  backgroundColor: darkmodeReducer
                    ? "rgba(005, 005, 005, 0.2)"
                    : "rgba(255, 255, 255, 0.03)",
                  fontWeight: "bolder",
                  fontSize: fontConnectnum,

                  marginRight: "0.5vw",
                  marginTop: "9.2px",
                }}
                className={`fontfamilyArial zuperxyinfo  ${favclass}`}
              >
                20
              </span>
            </Grid>
          </Grid>
          {/*///////////////////////////////////////////////////////////////////////////FAVS*/}
          {/*///////////////////////////////////////////////////////////////////////////USERNAME*/}
          <Grid
            item
            xs={12}
            style={{
              position: "absolute",
              width: widthProfilePic,
              height: "auto",
              marginLeft: "30.2vw",
              top: topProfilePic,

              zIndex: 3,
            }}
          >
            <img
              className={
                darkmodeReducer ? `turdarkbillboard` : ` turlightbillboard`
              }
              ref={billboardActivePicRef}
              onLoad={onBillboardimageload}
              style={{
                position: "relative",
                zIndex: 0,
                objectFit: "contain",
                width: "100%",
                borderRadius: "50%",
                margin: "auto",
              }}
              src={profilePictureData}
              alt="Superstarz Billboard "
            />{" "}
          </Grid>
          {/*///////////////////////////////////////////////////////////////////////////USERNAME*/}
        </Grid>
      </Grid>
    </Paper>
  );
}

export default Profile;
