import React, { useState, useEffect } from "react";

import { IconButton, InputAdornment, TextField } from "@material-ui/core";
import VisibilityOffIcon from "@material-ui/icons/VisibilityOff";
import VisibilityIcon from "@material-ui/icons/Visibility";
import { ItextfIeldSignup } from "./log-Interfaces";
import { DialogContent, Button, Paper, Grid } from "@material-ui/core";
import { RootStateOrAny, useSelector, useDispatch } from "react-redux";
import { PasswordCheck } from "./PasswordCheck";

function TextFieldSignupx({
  updateSignvalues,
  rawSignupValues,
  signupShowPassword,
  ShowSignupPasswordForaWhile,
  size,
  passwordType,
  emailType,
  withHolder,
  focus,
  setFocus,
  showFocusTextFieldByHidePadding,
  setShowFocusTextFieldByHidePadding,
}: ItextfIeldSignup): JSX.Element {
  const [checkSignupPasswordACTIVATE, setcheckSignupPasswordACTIVATE] =
    useState<boolean>(true);
  <PasswordCheck
    widthHolder={WidthHolder}
    checkSignupPassword={checkSignupPassword}
    setcheckSignupPassword={setcheckSignupPassword}
    checkSignupPasswordACTIVATE={checkSignupPasswordACTIVATE}
    setcheckSignupPasswordACTIVATE={setcheckSignupPasswordACTIVATE}
  />;
  var width = " ";
  var sizex: "small" | "medium" | undefined = undefined;
  var zIndex = 0;
  var zindexBackPlateP = 0;
  var displayBackPlateP = "none";
  var zindexU = 0;
  var zindexBackPlateU = 0;
  var displayBackPlateU = "none";
  var zindexE = 0;
  var zindexBackPlateE = 0;
  var displayBackPlateE = "none";

  switch (size) {
    case "small":
      sizex = "small";
      width = "100%";
      var transform = "scale(0.93)";
      var paddingBottomE = "65px";
      var paddingBottomP = "65px";
      var paddingBottomU = "75px";
      zIndex = 1;
      var font1 = "";
      var font2 = "";
      break;
    case "smallTablet":
      sizex = "small";
      width = "62%";
      var transform = "scale(1)";
      var paddingBottomE = "82px";
      var paddingBottomP = "65px";
      var paddingBottomU = "78px";
      zIndex = 1;
      var font1 = "2.6vh";
      var font2 = "2vh";
      break;
    default:
      var width = withHolder;
      var transform = "scale(1)";
      var paddingBottomE = "45px";
      var paddingBottomP = "27px";
      var paddingBottomU = "55px";
      zIndex = 1;
      var font1 = "2.7vh";
      var font2 = "1.9vh";
  }

  if (size == "small" || size === "smallTablet") {
    if (emailType) {
      if (focus) {
        zindexE = 15;
        zindexBackPlateE = 12;
        displayBackPlateE = "block";
      } else {
        zindexE = 0;
        zindexBackPlateE = 0;
        displayBackPlateE = "none";
      }
    } else if (passwordType) {
      if (focus) {
        zIndex = 15;
        zindexBackPlateP = 12;
        displayBackPlateP = "block";
      } else {
        zIndex = 1;
        zindexBackPlateP = 0;
        displayBackPlateP = "none";
      }
    } else {
      if (focus) {
        zindexU = 15;
        zindexBackPlateU = 12;
        displayBackPlateU = "block";
      } else {
        zindexU = 0;
        zindexBackPlateU = 0;
        displayBackPlateU = "none";
      }
    }
  }

  const focusTextfield = (a: number) => {
    if (size == "small" || size === "smallTablet") {
      if (a === 1) {
        setFocus(true);
        setShowFocusTextFieldByHidePadding(true);
      } else {
        setFocus(false);
        setShowFocusTextFieldByHidePadding(false);
      }
    }
  };

  ///
  ///
  ///
  /// GET DARKMODE FROM REDUX STORE
  interface RootStateGlobalReducer {
    GlobalReducer: {
      darkmode: boolean;
    };
  }
  const { darkmode } = useSelector((state: RootStateGlobalReducer) => ({
    ...state.GlobalReducer,
  }));

  const darkmodeReducer = darkmode;

  var TextFieldOpacity = "1";
  if (darkmodeReducer) {
    if (focus) {
      TextFieldOpacity = "1";
    } else {
      TextFieldOpacity = "0.8";
    }
  } else {
    TextFieldOpacity = "1";
  }

  return (
    <>
      {emailType ? (
        <>
          {" "}
          <DialogContent
            className={
              darkmodeReducer
                ? "mobileTextfield-backplate dontallowhighlighting  mobileTextfield-backplateColorDark"
                : "mobileTextfield-backplate dontallowhighlighting  mobileTextfield-backplateColorLight "
            }
            style={{ zIndex: zindexBackPlateE, display: displayBackPlateE }}
          ></DialogContent>
          <TextField
            onFocus={() => focusTextfield(1)}
            onBlur={() => focusTextfield(0)}
            size={sizex}
            inputProps={{ style: { fontSize: font1 } }}
            InputLabelProps={{ style: { fontSize: font2 } }}
            style={{
              transform: transform,
              width: width,
              paddingBottom: showFocusTextFieldByHidePadding
                ? "0px"
                : paddingBottomE,
              zIndex: zindexE,
              opacity: TextFieldOpacity,
            }}
            label="Email"
            type="email"
            onChange={updateSignvalues}
            name="inputedEmail"
            value={rawSignupValues.inputedEmail}
            variant="standard"
          />
        </>
      ) : passwordType ? (
        <>
          {" "}
          <DialogContent
            className={
              darkmodeReducer
                ? "mobileTextfield-backplate dontallowhighlighting  mobileTextfield-backplateColorDark"
                : "mobileTextfield-backplate dontallowhighlighting  mobileTextfield-backplateColorLight"
            }
            style={{ zIndex: zindexBackPlateP, display: displayBackPlateP }}
          ></DialogContent>
          <TextField
            onFocus={() => focusTextfield(1)}
            onBlur={() => focusTextfield(0)}
            size={sizex}
            inputProps={{ style: { fontSize: font1 } }}
            InputLabelProps={{ style: { fontSize: font2 } }}
            style={{
              transform: transform,
              width: width,
              paddingBottom: showFocusTextFieldByHidePadding
                ? "0px"
                : paddingBottomP,
              zIndex: zIndex,
              opacity: TextFieldOpacity,
            }}
            label="Password"
            type={signupShowPassword ? "text" : "password"}
            onChange={updateSignvalues}
            name="inputedPassword"
            value={rawSignupValues.inputedPassword}
            variant="standard"
            InputProps={{
              endAdornment: (
                <InputAdornment position="end">
                  <IconButton
                    onMouseDown={ShowSignupPasswordForaWhile}
                    aria-label="toggle password visibility"
                  >
                    {signupShowPassword ? (
                      <VisibilityIcon />
                    ) : (
                      <VisibilityOffIcon />
                    )}
                  </IconButton>
                </InputAdornment>
              ),
            }}
          />
        </>
      ) : (
        <>
          {" "}
          <DialogContent
            className={
              darkmodeReducer
                ? "mobileTextfield-backplate dontallowhighlighting  mobileTextfield-backplateColorDark"
                : "mobileTextfield-backplate dontallowhighlighting   mobileTextfield-backplateColorLight"
            }
            style={{ zIndex: zindexBackPlateU, display: displayBackPlateU }}
          ></DialogContent>
          <TextField
            onFocus={() => focusTextfield(1)}
            onBlur={() => focusTextfield(0)}
            size={sizex}
            inputProps={{ style: { fontSize: font1 } }}
            InputLabelProps={{ style: { fontSize: font2 } }}
            style={{
              transform: transform,
              width: width,
              paddingBottom: showFocusTextFieldByHidePadding
                ? "0px"
                : paddingBottomU,
              zIndex: zindexU,
              opacity: TextFieldOpacity,
            }}
            label="Username"
            type="text"
            onChange={updateSignvalues}
            name="inputedUsername"
            value={rawSignupValues.inputedUsername}
            variant="standard"
          />
        </>
      )}
    </>
  );
}

export const TextFieldSignup = React.memo(TextFieldSignupx);
