import React, {
    useRef,
    useEffect,
    useCallback,
    useState,
    useLayoutEffect,
} from "react";
import ControlPointDuplicateIcon from "@mui/icons-material/ControlPointDuplicate";
import { Grid, Switch, DialogContent } from "@material-ui/core";
import { matchPc, matchTablet, matchMobile } from "../DetectDevice";
import { useSpring, animated } from "react-spring";
import { OptionsSlider } from "../profile/OptionsSlider";
import { RootStateOrAny, useSelector, useDispatch } from "react-redux";
import Masonry from "@mui/lab/Masonry";
import Axios from "axios";
import CheckIcon from "@mui/icons-material/Check";
import date from "date-and-time";
import { UserInfoUpdatePROFILE } from "../log/actions/UserdataAction";
import { UserInfoUpdateBILLBOARD } from "../log/actions/UserdataAction";
import { usePalette } from "react-palette";
import { UpdateColorAction } from "../GlobalActions";
import EditTwoToneIcon from "@mui/icons-material/EditTwoTone";
import DoneOutlineIcon from '@mui/icons-material/DoneOutline';
import { UpdateUploadData } from "../GlobalActions";
import DoneIcon from '@mui/icons-material/Done';
import CheckCircleIcon from '@mui/icons-material/CheckCircle';

import CropIcon from '@mui/icons-material/Crop';
import Slider from "@material-ui/core/Slider";
import { TaskbarCrop } from "./TaskbarCrop";






function TaskbarSuperphotox({
    finalImage }: any): JSX.Element {
    const dispatch = useDispatch();


    const [CropImageHolder, setCropImageHolder] = useState<any>(null);



    const Draw = (type: any, ctx, w, h) => {

        if (type === "lift") {
            ctx.filter =
                "contrast(1.25) brightness(0.98) blur(0px) saturate(125%)";
        } else if (type === "floyd") {
            ctx.filter = "contrast(1.02) brightness(0.95) saturate(164%)";
        } else if (type === "jentle") {
            ctx.filter =
                "contrast(0.82) brightness(0.83) hue-rotate(20deg) saturate(150%) ";
        } else if (type === "mint") {
            ctx.filter = "contrast(1) brightness(1.05)  saturate(155%)  ";
        } else if (type === "nebula") {
            ctx.filter = "contrast(1.1) brightness(1.1)";
        } else if (type === "juice") {
            if (method === "thumb" && matchMobile) {
                ctx.filter =
                    "contrast(1.17) brightness(0.99) saturate(185%) blur(0px)";
            } else {
                ctx.filter =
                    "contrast(1.17) brightness(0.99) saturate(185%) blur(0.4px)";
            }
        } else if (type === "futurama") {
            ctx.filter = "contrast(1.3) brightness(0.94) saturate(145%) ";
        } else if (type === "zoidberg") {
            ctx.filter =
                "contrast(1.2) brightness(0.9) saturate(130%)  hue-rotate(1.4deg)";
        } else if (type === "tentacion") {
            ctx.filter = "contrast(1.2) brightness(1.1) saturate(137%)";
        } else if (type === "moonshine") {
            if (method === "thumb" && matchMobile) {
                ctx.filter =
                    "contrast(1.3) brightness(0.99) saturate(50%) blur(0.8px)";
            } else {
                if (matchMobile) {
                    ctx.filter =
                        "contrast(1) brightness(0.99) saturate(40%) blur(1.2px)";
                } else {
                    ctx.filter =
                        "contrast(1) brightness(0.99) saturate(40%) blur(2.8px)";
                }
            }
        } else if (type === "vintage") {
            ctx.filter = " contrast(1.1)  brightness(0.85)  saturate(25%)";
        } else {
        }

        ctx.drawImage(CropImageHolder, 0, 0, w, h);
    }

    useLayoutEffect(() => {
        const Newcropimage: any = new Image();
        Newcropimage.src = finalImage[0];
        Newcropimage.onload = function () {
            if (CropImageHolder === Newcropimage) {
            } else {

                //////////////
                setCropImageHolder(Newcropimage);
                ///GiveImageSpecificDimensions(Newcropimage);
            }


        }
    }, [finalImage[0]]);




    const cropCanvasRef: any = useRef(null);

    const canvasRefdummy: any = useRef(null);


    useLayoutEffect(() => {
        if (CropImageHolder && cropCanvasRef.current) {


            const ctx = cropCanvasRef.current.getContext("2d");



            const w = CropImageHolder.naturalWidth;
            const h = CropImageHolder.naturalHeight;

            cropCanvasRef.current.width = w;
            cropCanvasRef.current.height = h;



            ////////////////////////////////





            requestAnimationFrame(() => {

                Draw();


            });





        }
    }, [CropImageHolder, cropCanvasRef])





    function blend(
        background: any,
        foreground: any,
        width: any,
        height: any,
        transform: any
    ) {
        var bottom = background.getImageData(0, 0, width, height);
        var top = foreground.getImageData(0, 0, width, height);

        for (var i = 0, size = top.data.length; i < size; i += 4) {
            // red
            top.data[i + 0] = transform(bottom.data[i + 0], top.data[i + 0]);
            // green
            top.data[i + 1] = transform(bottom.data[i + 1], top.data[i + 1]);
            // blue
            top.data[i + 2] = transform(bottom.data[i + 2], top.data[i + 2]);
            // the fourth slot is alpha. We don't need that (so skip by 4)
        }

        return top;
    }

    function FilterGradient(width: any, height: any, type: string) {
        var ctx = canvasRefdummy.current.getContext("2d");
        canvasRefdummy.current.height = height;
        canvasRefdummy.current.width = width;

        if (type === "jentle") {
            var gradient = ctx.createLinearGradient(
                width / 73.6,
                height / 33.4,
                0,
                width / 2,
                height / 2,
                width * 0.6
            );
        } else if (type === "mint" || type === "floyd") {
            var gradient = ctx.createLinearGradient(
                width / 73.6,
                height / 33.4,
                0,
                width / 2,
                height / 2,
                width * 0.6
            );
        } else if (type === "tentacion") {
            var gradient = ctx.createLinearGradient(
                width / 2,
                height / 2,
                0,
                width / 2,
                height / 2,
                width * 0.6
            );
        } else if (type === "nebula") {
            var gradient = ctx.createLinearGradient(
                width / 2,
                height / 2,
                0,
                width / 2,
                height / 2,
                width * 0.6
            );
        } else if (type === "juice") {
            var gradient = ctx.createRadialGradient(
                width / 2,
                height / 2,
                0,
                width / 2,
                height / 2,
                width * 0.7
            );
        } else if (type === "moonshine") {
            var gradient = ctx.createRadialGradient(
                width / 2,
                height / 2,
                0,
                width / 2,
                height / 2,
                width * 0.7
            );
        } else if (type === "vintage") {
            var gradient = ctx.createRadialGradient(
                width / 2,
                height / 3,
                0,
                width / 2,
                height / 2,
                width * 0.66
            );
        } else if (type === "futurama" || type === "zoidberg") {
            var gradient = ctx.createLinearGradient(
                width / 73.6,
                height / 33.4,
                0,
                width / 2,
                height / 2,
                width * 0.6
            );
        } else {
        }

        if (type === "jentle") {
            gradient.addColorStop(0, "#2c1f1f");
            gradient.addColorStop(0.4, "#41243c");
            gradient.addColorStop(1, "#272122");
        } else if (type === "mint") {
            gradient.addColorStop(0.25, "#2c3431");
            gradient.addColorStop(0.5, "#26483e");
            gradient.addColorStop(0.75, "#232831");
            gradient.addColorStop(1, "#273131");
        } else if (type === "nebula") {
            gradient.addColorStop(0, "#3b1858");
            gradient.addColorStop(1, "#151569");
        } else if (type === "juice") {
            gradient.addColorStop(0, "#242424");
            gradient.addColorStop(1, "#313131");
        } else if (type === "moonshine") {
            gradient.addColorStop(0, "#0e0e14");
            gradient.addColorStop(1, "#17171f");
        } else if (type === "futurama") {
            gradient.addColorStop(0.25, "#5c384c");
            gradient.addColorStop(0.5, "#5c384c");
            gradient.addColorStop(0.75, "#3e452f");
            gradient.addColorStop(1, " #2e3c3f");
        } else if (type === "zoidberg") {
            gradient.addColorStop(0, "#244a82");
            gradient.addColorStop(1, "#4d3017");
        } else if (type === "tentacion") {
            gradient.addColorStop(0, "#293746");
            gradient.addColorStop(0.6, "#3a2922");
            gradient.addColorStop(1, "#3b355d");
        } else if (type === "vintage") {
            gradient.addColorStop(0, "#2f3340");
            gradient.addColorStop(1, "#3d4358");
        } else if (type === "floyd") {
            gradient.addColorStop(0, "#3b3939");
            gradient.addColorStop(1, "#191d1b");
        } else {
        }

        ctx.fillStyle = gradient;

        ctx.fillRect(0, 0, width, height);

        return ctx;
    }




    return (
        <>
            {finalImage.length > 0 ? <><Masonry
                columns={3}
                spacing={0}
                style={{
                    padding: "0px",
                    height: '88vh',
                    overflow: 'hidden'
                }}
            >


                {finalImage.map((picture: any, index: any) => (

                    <img key={index} style={{ cursor: 'pointer' }} src={picture} alt={`Image ${index}`} />

                ))}




            </Masonry ></> : null
            }


            <button style={{ marginLeft: '20vh', cursor: 'pointer' }}>Zoidberg</button>


            <canvas
                ref={cropCanvasRef}
                style={{
                    padding: "0px",
                    margin: "auto",
                    cursor: "pointer",
                    overflow: 'hidden',
                    position: 'fixed',
                    top: '2vh'
                }} />


            <canvas
                ref={canvasRefdummy}
                style={{
                    padding: "0px",
                    display: "none",
                    position: "fixed",
                    top: "-400%",
                    zIndex: 0,
                    visibility: "hidden",
                }}
            />

        </>
    );
}

export const TaskbarSuperphoto = React.memo(TaskbarSuperphotox);
