import React, {
  ChangeEvent,
  useRef,
  useState,
  useCallback,
  useEffect,
  useLayoutEffect,
} from "react";
import { matchPc, matchTablet, matchMobile } from "../DetectDevice";
import { TextField } from "@material-ui/core";
import { Grid, GridSize } from "@material-ui/core";
import MoreHorizIcon from "@material-ui/icons/MoreHoriz";
import { RootStateOrAny, useSelector, useDispatch } from "react-redux";
import { useSpring, animated } from "react-spring";
import Masonry from "@mui/lab/Masonry";
import CircleIcon from "@mui/icons-material/Circle";
import { SuperCrop } from "./SuperCrop";
import MusicNoteIcon from "@mui/icons-material/MusicNote";
import DeleteOutlineIcon from '@material-ui/icons/DeleteOutline';
import PhotoIcon from "@mui/icons-material/Photo";
import GifIcon from "@mui/icons-material/Gif";
import ChevronRightIcon from "@mui/icons-material/ChevronRight";
import Axios from "axios";
import CropIcon from "@mui/icons-material/Crop";
import CheckIcon from "@mui/icons-material/Check";
import { PreviewCanvasCropAll } from "./PreviewCanvasCropAll";
import { FilterModeArrow } from "./FilterModeArrow";
import { OptionsSlider } from "../profile/OptionsSlider";
import { convertHexToRGB } from "material-ui/utils/colorManipulator";
import InsertPhotoIcon from "@mui/icons-material/InsertPhoto";
import FormatColorTextIcon from "@mui/icons-material/FormatColorText";
import UndoIcon from "@mui/icons-material/Undo";
import LayersIcon from "@mui/icons-material/Layers";
import FormatSizeIcon from "@mui/icons-material/FormatSize";
import FontDownloadIcon from "@mui/icons-material/FontDownload";
import LightbulbIcon from "@mui/icons-material/Lightbulb";
import LightModeIcon from "@mui/icons-material/LightMode";
import TextFormatIcon from "@mui/icons-material/TextFormat";
import HighlightIcon from "@mui/icons-material/Highlight";
import Slider from "@material-ui/core/Slider";
import { HexColorPicker } from "react-colorful";
import ColorizeIcon from "@mui/icons-material/Colorize";
import EditIcon from "@mui/icons-material/Edit";
import PhotoSizeSelectLargeIcon from "@mui/icons-material/PhotoSizeSelectLarge";
import CropRotateIcon from "@mui/icons-material/CropRotate";
import CloseIcon from "@mui/icons-material/Close";
import RestoreIcon from "@mui/icons-material/Restore";
import SettingsBackupRestoreIcon from "@mui/icons-material/SettingsBackupRestore";
import date from "date-and-time";
import { UpdateNavFilterReducer } from "../GlobalActions";
import { UpdateNavCropReducer } from "../GlobalActions";
import { AudioEditor } from "./AudioEditor";


function Captionx({
  closeUploadModal,
  setstartTopicCap,
  selectedImage,
  finalImageData,
  finalImageDataSD,
  finalImageDataBASE64,
  cropInitialIn2,
  interactContent2,
  cropInitialIn,
  interactContent,
  radius1,
  radius2,
  interactContenttype2,
  interactContenttype,
  interactContentvideo2,
  interactContentvideo,
}: any): JSX.Element {
  const [matchTabletMobile, setmatchTabletMobile] = useState<boolean>(false);

  const [supeFilterLoadFadex, setsupeFilterLoadFadex] =
    useState<boolean>(false);


  const [backgroudAudio, setbackgroudAudio] =
    useState<boolean>(true);



  const { REACT_APP_SUPERSTARZ_URL } = process.env;

  const Value = {
    caption: "",
    topic: "",
  };


  const [captionvalues, setcaptionvalues] = useState(Value);

  var s3finaldata: any = [];

  var s3finaldataThumb: any = [];

  var s3finaldataAll: any = [];

  var s3finaldataINT1: any = [];

  var s3finaldataINT2: any = [];

  var s3finalvid: any = '';

  var s3finalvid2: any = '';

  ///
  ///
  ///DARKMODE FROM REDUX
  interface RootStateGlobalReducer {
    GlobalReducer: {
      darkmode: boolean;
    };
  }
  const { darkmode } = useSelector((state: RootStateGlobalReducer) => ({
    ...state.GlobalReducer,
  }));

  const darkmodeReducer = darkmode;

  var pcfont = 2.9;

  var mobilefont = 4.8;

  //
  ///
  ///GET OPTIONS SLIDER IMAGE WIDTH FROM MATERIAL UI GRID
  useEffect(() => {
    if (matchMobile || matchTablet) {
      setmatchTabletMobile(true);

    }
  }, []);




  useEffect(() => {

    console.log(cropInitialIn)

  }, [cropInitialIn]);


  ///
  ///
  /// SANITISE LOG IN FORM INPUT CLIENT SIDE
  const updatecaptiontop = useCallback(
    (e: any) => {
      const { name, value } = e.target;
      setcaptionvalues({ ...captionvalues, [name]: value });
    },
    [captionvalues]
  );

  var transform = "";
  var font1 = "";
  var font2 = "";
  var paddingbutU = "";

  var width = " ";
  var sizex: "small" | "medium" | undefined = undefined;
  ///
  ///
  ///
  if (matchPc) {
    sizex = "medium";
    width = "20%";
    transform = "scale(1)";
    font1 = "2.7vh";
    font2 = "2.1vh";
    paddingbutU = "70px";
  } else if (matchTablet) {
    sizex = "small";
    width = "62%";
    transform = "scale(1)";
    font1 = "2.6vh";
    font2 = "2vh";
    paddingbutU = "100px";
  } else {
    sizex = "small";
    width = "100%";
    transform = "scale(0.94)";
    font1 = "";
    font2 = "";
    paddingbutU = "80px";
  }

  ///
  ///
  ///
  /// INTERFACE/TYPES FOR SCREENHEIGHT AND DARKMODE
  interface RootUserdataReducer {
    UserdataReducer: {
      id: number;
    };
  }

  ///
  ///
  ///
  /// GET SCREENHEIGHT FROM REDUX STORE
  const { id } = useSelector((state: RootUserdataReducer) => ({
    ...state.UserdataReducer,
  }));

  const idReducer = id;

  const dispatch = useDispatch();

  const UpdatePostDatabaseStatus200 = (datak: any, b: any) => {

    console.log(datak);

    // alert(finalImageData.length)
    Axios.post(`${REACT_APP_SUPERSTARZ_URL}/post_upload_data`, {
      values: datak,
    })
      .then((response) => {
        console.log(response);
        setsupeFilterLoadFadex(false);
        if (response.data.message === "images uploaded") {

          setsupeFilterLoadFadex(false);
          closeUploadModal(2);

          window.location.reload();

        }
      })
      .catch(function (error) {
        setsupeFilterLoadFadex(false);
        console.log(error);
      });
  };



  const PutImageInS3WithURLVid = useCallback(
    (type: any, type2: any, h1: any, h2: any, v1: any, v2: any, base64: any) => {

      let urlx = "";
      var x: any = null;

      ///alert(h1);
      let urlxx = "";
      var xx: any = null;


      const calldatabaseVid = () => {

        var datallx = {
          topic: captionvalues.topic,
          caption: captionvalues.caption,
          id: idReducer,
          all: s3finaldataAll,
          I1x: cropInitialIn[0].x,
          I1y: cropInitialIn[0].y,
          I1bx: cropInitialIn2[0].x,
          I1by: cropInitialIn2[0].y,
          I2x: cropInitialIn[1].x,
          I2y: cropInitialIn[1].y,
          I2bx: cropInitialIn2[1].x,
          I2by: cropInitialIn2[1].y,
          I3x: cropInitialIn[2].x,
          I3y: cropInitialIn[2].y,
          I3bx: cropInitialIn2[2].x,
          I3by: cropInitialIn2[2].y,
          rad1: radius1,
          rad2: radius2,
          vid1: s3finalvid,
          vid2: s3finalvid2,
          interacttype1: type,
          interacttype2: type2,
        };


        setsupeFilterLoadFadex(true);
        UpdatePostDatabaseStatus200(datallx, base64);


      }



      const calltwo = (t: number) => {
        if (type2 === 1) {
          // alert('thumb');
          urlxx = h2;
          xx = v2;
          Axios.put(urlxx, xx)
            .then((response) => {

              setsupeFilterLoadFadex(false);
              if (response.status === 200) {
                setsupeFilterLoadFadex(true);
                let imagelinkx = urlxx.split("?")[0];

                s3finalvid2 = imagelinkx;


                if (t === 1) { calldatabaseVid(); }
                ///console.log(imagelinkx);
              }
            })
            .catch(function (error) {
              setsupeFilterLoadFadex(false);
              console.log(error);
            });

        } else {
          calldatabaseVid();

        }
      }



      if (type === 1) {
        // alert('thumb');
        urlx = h1;
        x = v1;
        Axios.put(urlx, x)
          .then((response) => {

            setsupeFilterLoadFadex(false);
            if (response.status === 200) {
              setsupeFilterLoadFadex(true);
              let imagelink = urlx.split("?")[0];

              s3finalvid = imagelink;

              calltwo(0);

              ///console.log(imagelink);
            }
          })
          .catch(function (error) {
            setsupeFilterLoadFadex(false);
            console.log(error);
          });
      } else {
        calltwo(1)
      }






    },
    [idReducer, s3finaldata, s3finaldataThumb, s3finalvid2, s3finalvid, s3finaldataAll, cropInitialIn, cropInitialIn2]
  );


  const GetVideoSecureURL = (a: any, b: any, type: any, type2: any, datall: any, base64: any) => {
    var countxx: any = 0;

    if (type === 1) {
      countxx = countxx + 1;
    }

    if (type2 === 1) {
      countxx = countxx + 1;
    }

    if (countxx > 0) {
      var kob = {
        count: countxx,
      };

      Axios.post(`${REACT_APP_SUPERSTARZ_URL}/get_signed_url_4upload_post_vid`, {
        values: kob,
      })
        .then((response: any) => {
          setsupeFilterLoadFadex(false);
          var holder = response.data.holder;
          setsupeFilterLoadFadex(true);
          /// alert('UploadingPost');
          console.log(holder);

          PutImageInS3WithURLVid(type, type2, holder[0].urlVid, holder[0].urlVid2, a, b, base64);

        })
        .catch(function (error) {
          setsupeFilterLoadFadex(false);
          console.log(error);
        });
    } else {

      setsupeFilterLoadFadex(true);
      UpdatePostDatabaseStatus200(datall, base64);
    }
  };



  ////
  ////
  ////
  const CallDatabase = useCallback((datall: any, base64: any, UpdatePostDatabaseStatus200: any) => {

    GetVideoSecureURL(interactContentvideo, interactContentvideo2, interactContenttype, interactContenttype2, datall, base64);

    ///setsupeFilterLoadFadex(true);
    //UpdatePostDatabaseStatus200(datall, base64);
  }, [interactContentvideo, interactContentvideo2, interactContenttype, interactContenttype2])

  const PutImageInS3Interaction = useCallback(
    (holder: any, datap: any, base64: any, allow: boolean, i: number) => {


      let urlx = "";
      var x: any = null;
      if (allow) {
        // alert('thumb');
        urlx = holder[i].urlinteraction1;
        x = interactContent;
      } else {
        //alert('hd');
        urlx = holder[i].urlinteraction2;
        console.log(urlx)
        x = interactContent2;
      }

      if (x[i]) {

        Axios.put(urlx, x[i])
          .then((response) => {
            console.log(response)
            setsupeFilterLoadFadex(false);
            if (response.status === 200) {
              setsupeFilterLoadFadex(true);
              if (allow) {
                let imagelink = urlx.split("?")[0];
                s3finaldataINT1[i] = imagelink;
                var datak = {
                  imagedata: s3finaldata[i],
                  imagedataThumb: s3finaldataThumb[i],
                  interact1: s3finaldataINT1[i],
                  interact2: s3finaldataINT2[i]
                };
                s3finaldataAll[i] = datak;

                if (i + 1 === finalImageData.length) {
                  var datall = {
                    topic: captionvalues.topic,
                    caption: captionvalues.caption,
                    id: idReducer,
                    all: s3finaldataAll,
                    I1x: cropInitialIn[0].x,
                    I1y: cropInitialIn[0].y,
                    I1bx: cropInitialIn2[0].x,
                    I1by: cropInitialIn2[0].y,
                    I2x: cropInitialIn[1].x,
                    I2y: cropInitialIn[1].y,
                    I2bx: cropInitialIn2[1].x,
                    I2by: cropInitialIn2[1].y,
                    I3x: cropInitialIn[2].x,
                    I3y: cropInitialIn[2].y,
                    I3bx: cropInitialIn2[2].x,
                    I3by: cropInitialIn2[2].y,
                    rad1: radius1,
                    rad2: radius2,
                    vid1: s3finalvid,
                    vid2: s3finalvid2,
                    interacttype1: 0,
                    interacttype2: 0,
                  };

                  CallDatabase(datall, base64, UpdatePostDatabaseStatus200);


                  ///alert('Processing');

                } else {

                  PutImageInS3Interaction(holder, datap, base64, false, i + 1);
                }
              } else {
                let imagelinkx = urlx.split("?")[0];
                s3finaldataINT2[i] = imagelinkx;

                PutImageInS3Interaction(holder, datap, base64, true, i);
              }
            }
          })
          .catch(function (error) {
            setsupeFilterLoadFadex(false);
            console.log(error);
          });
      } else {

        //////////////////////////////////
        ////////////////////////////////////
        setsupeFilterLoadFadex(true);
        if (allow) {
          let imagelink = null;
          s3finaldataINT1[i] = imagelink;
          var datak = {
            imagedata: s3finaldata[i],
            imagedataThumb: s3finaldataThumb[i],
            interact1: s3finaldataINT1[i],
            interact2: s3finaldataINT2[i]
          };
          s3finaldataAll[i] = datak;


          if (i + 1 === finalImageData.length) {
            var datall = {
              topic: captionvalues.topic,
              caption: captionvalues.caption,
              id: idReducer,
              all: s3finaldataAll,
              I1x: cropInitialIn[0].x,
              I1y: cropInitialIn[0].y,
              I1bx: cropInitialIn2[0].x,
              I1by: cropInitialIn2[0].y,
              I2x: cropInitialIn[1].x,
              I2y: cropInitialIn[1].y,
              I2bx: cropInitialIn2[1].x,
              I2by: cropInitialIn2[1].y,
              I3x: cropInitialIn[2].x,
              I3y: cropInitialIn[2].y,
              I3bx: cropInitialIn2[2].x,
              I3by: cropInitialIn2[2].y,
              rad1: radius1,
              rad2: radius2,
              vid1: s3finalvid,
              vid2: s3finalvid2,
              interacttype1: 0,
              interacttype2: 0,
            };

            CallDatabase(datall, base64, UpdatePostDatabaseStatus200);

          } else {

            PutImageInS3Interaction(holder, datap, base64, false, i + 1);
          }
        } else {
          let imagelinkx = null;
          s3finaldataINT2[i] = imagelinkx;

          PutImageInS3Interaction(holder, datap, base64, true, i);
        }
        ////////////////////////////////////
        /////////////////////////////////
      }
    },
    [idReducer, s3finaldata, s3finaldataINT1, s3finaldataINT2, interactContent, interactContent2, cropInitialIn, cropInitialIn2, s3finalvid, s3finalvid2]
  );


  const PutImageInS3WithURL = useCallback(
    (holder: any, a: any, b: any, base64: any, allow: boolean, i: number) => {


      let urlx = "";
      var x: any = null;
      if (allow) {
        // alert('thumb');
        urlx = holder[i].urlThumb;
        x = b;
      } else {
        //alert('hd');
        urlx = holder[i].urlHD;
        x = a;
      }
      Axios.put(urlx, x[i])
        .then((response) => {
          console.log(response);
          setsupeFilterLoadFadex(false);
          if (response.status === 200) {
            setsupeFilterLoadFadex(true);
            if (allow) {
              let imagelink = urlx.split("?")[0];
              s3finaldataThumb[i] = imagelink;
              var datak = {
                imagedata: s3finaldata[i],
                imagedataThumb: s3finaldataThumb[i],
              };
              s3finaldataAll[i] = datak;

              if (i + 1 === finalImageData.length) {
                var datap = {
                  topic: captionvalues.topic,
                  caption: captionvalues.caption,
                  id: idReducer,
                  all: s3finaldataAll,
                };

                ///console.log(s3finaldataAll);
                //setsupeFilterLoadFadex(true);
                //UpdatePostDatabaseStatus200(datap, base64);


                /// alert('UploadingInteraction');
                PutImageInS3Interaction(holder, datap, base64, false, 0);
              } else {
                PutImageInS3WithURL(holder, a, b, base64, false, i + 1);
              }
            } else {
              let imagelinkx = urlx.split("?")[0];
              s3finaldata[i] = imagelinkx;
              PutImageInS3WithURL(holder, a, b, base64, true, i);
            }
          }
        })
        .catch(function (error) {
          setsupeFilterLoadFadex(false);
          console.log(error);
        });
    },
    [idReducer, s3finaldata, s3finaldataThumb]
  );

  const GetSecureURL = (hdBlob: any, thumbBlob: any, hdBase64: any) => {
    var kob = {
      count: finalImageData.length,
    };

    Axios.post(`${REACT_APP_SUPERSTARZ_URL}/get_signed_url_4upload_post`, {
      values: kob,
    })
      .then((response) => {
        setsupeFilterLoadFadex(false);
        var holder = response.data.holder;
        setsupeFilterLoadFadex(true);
        /// alert('UploadingPost');
        PutImageInS3WithURL(holder, hdBlob, thumbBlob, hdBase64, false, 0);
      })
      .catch(function (error) {
        setsupeFilterLoadFadex(false);
        console.log(error);
      });
  };

  const UploadSuperData = useCallback(
    (hdBlob: any, thumbBlob: any, hdBase64: any) => {
      setsupeFilterLoadFadex(true);
      GetSecureURL(hdBlob, thumbBlob, hdBase64);
    },
    [finalImageData, captionvalues, closeUploadModal, idReducer]
  );


  const { PaperStyleLight, PaperStyleDark } = useSelector(
    (state: RootStateOrAny) => ({
      ...state.PaperReducerLightnDark,
    })
  );



  const fileInputRef = useRef<HTMLInputElement | null>(null);

  const [AudioUrl, setAudioUrl] = useState<any>(null);

  const [interactContentAudio, setinteractContentAudio] = useState<any>(null);

  const [interactContentAudiotype, setinteractContentAudiotype] = useState<any>(0);

  const [ShowAudio, setShowAudio] = useState<any>(false);

  const [Audioname, setAudioname] = useState('');

  /////
  const convertAndSetAudioUrl = (file: any) => {
    const reader = new FileReader();

    reader.onloadend = () => {
      // This is the data URL
      const audioDataUrl = reader.result;
      setAudioUrl(audioDataUrl);
      setShowAudio(true);

    };

    // Read the file as a Data URL
    reader.readAsDataURL(file);

  };

  ////
  const handleFileChangex = async (event: ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];

    // Clear the input value to allow the same file to be selected again
    if (event.target && event.target.value) {
      event.target.value = '';
    }

    if (file) {
      try {
        // Check if the selected file is an audio file
        if (file.type.startsWith('audio/')) {
          // Audio file handling logic

          // For example, setting the audio file to state variables
          // Assuming setAudioData is your state setter for audio data

          setAudioname(file.name);
          convertAndSetAudioUrl(file);

          // You might also handle the audio data URL or Blob here if needed
          // ...

        } else {
          // Handle non-audio file selection, possibly show an error message
          console.error('Selected file is not an audio file');
        }

      } catch (error) {
        console.error('Error processing the audio file:', error);
      }
    }
  };

  // Make sure to define or update your state setters like setAudioData
  // and other relevant states and logic as needed.




  return (
    <>
      {supeFilterLoadFadex ? (
        <>
          <Grid
            container
            style={{
              backgroundImage: darkmodeReducer
                ? "rgba(50,50,50,0.5)"
                : "rgba(250,250,250,0.5)",
              position: "fixed",
              top: "0px",
              width: "100%",
              height: "100%",
              zIndex: 10,
            }}
          ></Grid>{" "}
        </>
      ) : null}

      <Grid
        container
        className={
          darkmodeReducer ? "modal-containerDark" : "modal-containerLight"
        }
        style={{
          padding: "0px",
          height: "100%",
          zIndex: 1,
        }}
      > <Grid
        item
        xs={12}
        style={{
          padding: "0px",
        }}
      ></Grid>

        {ShowAudio ? <AudioEditor
          setAudioUrl={setAudioUrl}
          setAudioname={setAudioname}
          AudioUrl={AudioUrl}
          ShowAudio={ShowAudio}
          setShowAudio={setShowAudio}
          setinteractContentAudio={setinteractContentAudio}
          setinteractContentAudiotype={setinteractContentAudiotype}
        /> : null}





        <Grid
          item
          xs={6}
          style={{
            padding: "0px",
            backgroundColor: '#00ccff',
            height: '0px',
            marginTop: '3vh'
          }}
        >

          <TextField
            size={sizex}
            inputProps={{ style: { fontSize: font1 } }}
            InputLabelProps={{ style: { fontSize: font2 } }}
            onChange={updatecaptiontop}
            value={captionvalues.topic}
            style={{
              transform: transform,
              width: width,
              paddingBottom: "0px",
              position: "fixed",
              top: "10vh",
              left: "10vw",
              zIndex: 26,
            }}
            label="Share Your Thoughts"
            type="text"
            name="topic"
            variant="standard"
          />{" "}
          <TextField
            size={sizex}
            inputProps={{ style: { fontSize: font1 } }}
            InputLabelProps={{ style: { fontSize: font2 } }}
            onChange={updatecaptiontop}
            value={captionvalues.caption}
            style={{
              transform: transform,
              width: "60%",
              paddingBottom: "0px",
              position: "fixed",
              top: "45vh",
              left: "20vw",
              zIndex: 26,
              display: 'none'
            }}
            label="Share Your Thoughts"
            type="text"
            name="caption"
            variant="standard"
          />{" "}
          {finalImageData[0] ? < img
            src={URL.createObjectURL(finalImageData[0])}
            style={{
              width: "65%",
              height: "53vh",
              objectFit: 'cover',
              marginLeft: '10vw'
            }}
            alt="Image"
          /> : null}



        </Grid>



        <Grid
          item
          xs={6}
          style={{
            padding: "0px",
            height: '0vh',
            marginTop: '5vh',
            paddingLeft: '8vw',
          }}
        >




          <Grid
            item
            xs={12}
            style={{
              padding: "0px",
              height: '5vh',
              marginTop: '-5vh',
              display: interactContentAudiotype === 1 ? 'block' : 'none'
            }}
          >


            <CircleIcon
              onClick={
                () => {
                  setbackgroudAudio(!backgroudAudio);
                }
              }
              className={
                darkmodeReducer
                  ? "make-small-icons-clickable-lightCrop dontallowhighlighting zuperkingIcon "
                  : "make-small-icons-clickable-darkCrop dontallowhighlighting zuperkingIcon  "
              }
              style={{
                fontSize: matchTabletMobile ? `${mobilefont}vh` : `${pcfont - 1}vw`,
                marginRight: "5vw",
                color: backgroudAudio ? 'green' : 'red'
              }}

            />
            <span
              style={{
                position: 'relative', // Absolute position for the text
                top: '-10%', // Center vertically
                fontFamily: 'fantasy',
                fontSize: matchTabletMobile ? `${mobilefont}vh` : `1.2vw`,
              }}
            >
              {backgroudAudio ? 'Allow Audio Play During Interaction' : 'Stop Audio Play During Interaction'}
            </span>


          </Grid>


          <Grid
            item
            xs={12}
            style={{
              padding: "0px",
              height: '5vh',
              marginTop: '-14vh'

            }}
          >

            <input
              type="file"
              ref={fileInputRef}
              style={{ display: 'none' }}
              onChange={handleFileChangex}
            />
            <MusicNoteIcon
              onClick={
                () => {

                  if (interactContentAudiotype === 1) {

                    if (fileInputRef.current) {
                      fileInputRef.current.click();
                    }
                  } else {
                    if (fileInputRef.current) {
                      fileInputRef.current.click();
                    }

                  }
                }
              }
              className={
                darkmodeReducer
                  ? "make-small-icons-clickable-lightCrop dontallowhighlighting zuperkingIcon "
                  : "make-small-icons-clickable-darkCrop dontallowhighlighting zuperkingIcon  "
              }
              style={{
                color: "#ffffff",
                fontSize: matchTabletMobile ? `${mobilefont}vh` : `${pcfont - 1}vw`,
                marginRight: "5vw",
              }}
            />
            <span
              style={{
                position: 'relative', // Absolute position for the text
                top: '-10%', // Center vertically
                fontFamily: 'fantasy',
                fontSize: matchTabletMobile ? `${mobilefont}vh` : `1.3vw`,
              }}
            >
              {interactContentAudiotype === 1 ? Audioname : 'Add Audio'}
            </span>


          </Grid>



          <Grid container>
            <Grid
              item
              xs={12}
              style={{
                padding: "0px",
                position: 'relative',
                marginTop: '12vh',
                height: 'auto',

              }}
            >





              <Grid
                item
                xs={6}
                style={{
                  padding: "0px",

                  height: '0vh',
                  position: 'relative',

                }}
              >

                {
                  interactContenttype === 1 ?
                    <video
                      src={interactContentvideo ? URL.createObjectURL(interactContentvideo) : ''}   // Using the blob URL from interactContentvideo
                      style={{
                        width: "90%", // Set thevideo width to 100%
                        height: "auto",
                        display: "block", // Ensure proper rendering in some browsers
                        margin: "0 auto", // Center the video
                        cursor: 'pointer'
                      }}
                    // Add if you want video controls like play, pause, etc.
                    /> :
                    interactContent.length > 0 ? <img
                      src={URL.createObjectURL(interactContent[0])}
                      style={{
                        width: "90%",
                        height: "21vh",
                        objectFit: 'cover',

                      }}
                    /> : null
                }


                {interactContenttype2 === 1 ?
                  <video
                    src={interactContentvideo2 ? URL.createObjectURL(interactContentvideo2) : ''}   // Using the blob URL from interactContentvideo
                    style={{
                      width: "90%", // Set thevideo width to 100%
                      height: "auto",
                      display: "block", // Ensure proper rendering in some browsers
                      margin: "0 auto", // Center the video
                      cursor: 'pointer'
                    }}
                  // Add if you want video controls like play, pause, etc.
                  /> :
                  interactContent2.length > 0 ? <img
                    src={URL.createObjectURL(interactContent2[0])}

                    style={{
                      width: "90%",
                      height: "21vh",
                      objectFit: 'cover',
                    }}
                  /> : null
                }
              </Grid>


            </Grid>
          </Grid>




        </Grid>






        <Grid
          item
          xs={12}
          style={{
            padding: "0px",
          }}
        ></Grid>
        <Grid
          item
          xs={4}
          style={{
            margin: "auto",
            textAlign: "center",
            height: "0px",
            marginTop: '30vh'
          }}
        >
          <CheckIcon
            onClick={() => {
              //////postdata();
              UploadSuperData(
                finalImageData,
                finalImageDataSD,
                finalImageDataBASE64
              );
            }}
            className={
              darkmodeReducer
                ? "make-small-icons-clickable-lightCrop dontallowhighlighting zuperkingIcon "
                : "make-small-icons-clickable-darkCrop dontallowhighlighting zuperkingIcon  "
            }
            style={{
              color: "#ffffff",
              fontSize: matchTabletMobile ? `${mobilefont}vh` : `${pcfont}vw`,
              marginRight: "5vw",
            }}
          />
          <CloseIcon
            onClick={() => {
              setstartTopicCap(false);
            }}
            className={
              darkmodeReducer
                ? "make-small-icons-clickable-lightCrop dontallowhighlighting zuperkingIcon "
                : "make-small-icons-clickable-darkCrop dontallowhighlighting zuperkingIcon  "
            }
            style={{
              margin: "auto",
              color: "#ffffff",
              fontSize: matchTabletMobile ? `${mobilefont}vh` : `${pcfont}vw`,
              marginLeft: "5vw",
            }}
          />
        </Grid>





      </Grid >
    </>
  );
}

export const Caption = React.memo(Captionx);
