import React, { useRef, useState } from "react";
import { Grid } from "@material-ui/core";
import { matchPc, matchTablet } from "../DetectDevice";
import { useSpring, animated } from "react-spring";
import MoreHorizIcon from "@material-ui/icons/MoreHoriz";

import CircleIcon from "@mui/icons-material/Circle";
import { useSelector, useDispatch } from "react-redux";
import { UpdateOptionsTop } from ".././GlobalActions";
function OptionsSliderx({ getSliderWidth, typeTop, sethaltedTop }: any) {
  ///
  ///
  ///
  /// USE DISPATCH
  const dispatch = useDispatch();

  var getSliderWidthNew = matchPc
    ? getSliderWidth / 1.5
    : matchTablet
    ? getSliderWidth / 1.02
    : getSliderWidth / 0.77;

  var optionsClickType: number = matchPc ? 1 : 0;

  const optionsCollectImageRef = useRef<HTMLDivElement>(null);
  const optionsImages = ["1.jpg", "2.jpg", "3.jpg", "4.jpg", "5.jpg"];
  const [translate, setTranslate] = useState(0);
  const [stalestate] = useState(1);

  const [ActiveSlide, setActiveSlide] = useState(0);
  const [ShowHideNegativeValue, setShowHideNegativeValue] = useState("-");

  const [touchPosition, setTouchPosition] = useState(null);
  const handleTouchMoveTimer = useRef<ReturnType<typeof setTimeout> | null>(
    null
  );

  const [transitionFast, settransitionFast] = useState<boolean>(false);

  const prevJoltTimer = useRef<ReturnType<typeof setTimeout> | null>(null);
  const nextJoltTimer = useRef<ReturnType<typeof setTimeout> | null>(null);

  const [optionsShow, setoptionsShow] = useState<boolean>(true);

  ///
  ///
  ///
  ///DARKMODE FROM REDUX
  interface RootStateGlobalReducer {
    GlobalReducer: {
      darkmode: boolean;
    };
  }
  const { darkmode } = useSelector((state: RootStateGlobalReducer) => ({
    ...state.GlobalReducer,
  }));

  const darkmodeReducer = darkmode;

  ///
  ///
  ///
  ///OPTIONS SLIDER VARIABLES DISCRIBING ITEMS
  var optionsNameData = [
    "filter feeds",
    "albums",
    "playlist",
    "showroom",
    "top posts",
    "settings",
  ];

  ///
  ///
  ///
  ///CREATE OPTIONS SLIDER ANIMATION WITH  REACT SPRING

  const animationop = useSpring({
    config: {
      duration: 300,
    },

    visibility: typeTop
      ? "visible"
      : optinstopshowingReducer
      ? "hidden"
      : "visible",
  });
  s;

  ///
  ///
  ///
  ///CREATE OPTIONS SLIDER ANIMATION WITH  REACT SPRING
  const modalanimation = useSpring({
    config: {
      mass: 1,
      tension: 120,
      friction: 14,
    },
    transform: `translateX(${ShowHideNegativeValue}${translate}px)`,
    transition: transitionFast ? "0s" : "0.15s",
    height: "auto",
    display: "flex",
    width: `auto`,
    padding: "0px",
    margin: "auto",
    paddingLeft: typeTop ? (matchPc ? "0px" : "9px") : "0px",
  });

  ///
  ///
  ///
  /// ACTIVATES SLIDER ITEM TO BE VIEWED ON CLICK
  const clickOptions = (i: number, type: number) => {
    if (type === 1) {
      setTranslate(stalestate + i);
      setActiveSlide(i);
    } else {
      setTranslate(getSliderWidthNew * i + 1.5 * i);
      setActiveSlide(i);
    }
  };

  ///
  ///
  ///
  /// GET COLOR FROM REDUX STORE
  interface RootStateReducerColor {
    GlobalReducerColor: {
      color: string;
      colordark: string;
    };
  }
  const { color, colordark } = useSelector((state: RootStateReducerColor) => ({
    ...state.GlobalReducerColor,
  }));
  const colorReducer = color;
  const colorDarkReducer = colordark;

  ///
  ///
  ///
  ///PUSH OPTIONS SLIDE A LITTLE BIT ON LAST SLIDE ITEM
  const TouchJoltOnLastNEXT = () => {
    setShowHideNegativeValue("-");
    var i = ActiveSlide;
    var colorPaddingAllowance = 1.67 + i / 10;
    var getSliderWidthNewx = getSliderWidthNew / 2;
    setTranslate(getSliderWidthNew * i + getSliderWidthNewx);
    nextJoltTimer.current = setTimeout(function () {
      setTranslate(getSliderWidthNew * 0 + colorPaddingAllowance * 0);
      setActiveSlide((ActiveSlide) => 0);
    }, 200);
  };

  ///
  ///
  ///
  ///PUSH OPTIONS SLIDE A LITTLE BIT ON FIRST SLIDE ITEM
  const TouchJoltOnLastPREV = () => {
    setShowHideNegativeValue("");
    var i = ActiveSlide;
    var colorPaddingAllowance = 1.67 + i / 10;
    var ix = optionsImages.length - 1;
    var getSliderWidthNewx = getSliderWidthNew / 2;
    setTranslate(getSliderWidthNewx);
    prevJoltTimer.current = setTimeout(function () {
      setShowHideNegativeValue("-");
      setTranslate(getSliderWidthNew * ix + colorPaddingAllowance * ix);
      setActiveSlide((ActiveSlide) => ix);
    }, 200);
  };

  ///
  ///
  ///
  /// NEXT SLIDE  FOR PC
  const nextSlidePc = () => {
    if (typeTop) {
      closeoptionsslide();
    } else {
      var i = ActiveSlide + 1;
      setShowHideNegativeValue("-");
      ///set((state) => (state + 1) % slides.length)
      if (ActiveSlide === optionsImages.length - 1) {
        settransitionFast(true);
        setTranslate(0);
        setActiveSlide(0);
      } else {
        settransitionFast(false);
        setTranslate(stalestate + i);
        setActiveSlide(i);
      }
    }
  };

  ///
  ///
  ///
  /// NEXT SLIDE
  const nextSlide = () => {
    var i = ActiveSlide + 1;
    var colorPaddingAllowance = 1.67 + i / 10;
    ///set((state) => (state + 1) % slides.length)
    if (ActiveSlide === optionsImages.length - 1) {
      settransitionFast(true);
      TouchJoltOnLastNEXT();
    } else {
      if (nextJoltTimer.current) {
        clearTimeout(nextJoltTimer.current);
      }
      setShowHideNegativeValue("-");
      settransitionFast(false);
      setTranslate(getSliderWidthNew * i + colorPaddingAllowance * i);
      setActiveSlide((ActiveSlide) => ActiveSlide + 1);
    }
  };

  ///
  ///
  ///
  /// PREV SLIDE
  const prevSlide = () => {
    ///set((state) => (state + 1) % slides.length)
    var i = ActiveSlide - 1;
    var colorPaddingAllowance = 1.67 + i / 10;
    if (ActiveSlide === 0) {
      settransitionFast(true);
      if (typeTop) {
        closeoptionsslide();
      } else {
        TouchJoltOnLastPREV();
      }
    } else {
      if (prevJoltTimer.current) {
        clearTimeout(prevJoltTimer.current);
      }
      setShowHideNegativeValue("-");
      settransitionFast(false);
      setTranslate(getSliderWidthNew * i + colorPaddingAllowance * i);
      setActiveSlide((ActiveSlide) => ActiveSlide - 1);
    }
  };

  ///
  ///
  ///
  /// HANDLE TOUCH START EVENT
  const handleTouchStartOptions = (e: any) => {
    ////onMouseDown onMouseMove
    ////touchDown = e.clientX
    const touchDown = e.touches[0].clientX;
    setTouchPosition(touchDown);
  };

  ///
  ///
  ///
  /// HANDLE TOUCH MOVE EVENT
  const handleTouchMoveOptions = (e: any) => {
    if (handleTouchMoveTimer.current) {
      clearTimeout(handleTouchMoveTimer.current);
    }
    handleTouchMoveTimer.current = setTimeout(function () {
      const touchDown = touchPosition;

      if (touchDown === null) {
        return;
      }
      ////currentTouch = e.clientX
      const currentTouch = e.touches[0].clientX;
      const diff = touchDown - currentTouch;

      if (diff > 18) {
        nextSlide();
      } else if (diff < -18) {
        prevSlide();
      } else {
      }

      setTouchPosition(null);
      return false;
    }, 50);

    return false;
  };

  const closeoptionsslide = () => {
    sethaltedTop(false);
    dispatch(UpdateOptionsTop(false));
  };

  ///
  ///
  ///
  ///DARKMODE FROM REDUX
  interface RootoptinstopshowingReducer {
    OptionsTopShowReducer: {
      optinstopshowing: boolean;
    };
  }
  const { optinstopshowing } = useSelector(
    (state: RootoptinstopshowingReducer) => ({
      ...state.OptionsTopShowReducer,
    })
  );
  const optinstopshowingReducer = optinstopshowing;

  return (
    <>
      {optionsShow ? (
        <>
          {optinstopshowingReducer ? (
            <Grid container style={{ width: "10%", margin: "auto" }}>
              <span>
                <MoreHorizIcon
                  onClick={closeoptionsslide}
                  style={{
                    fontSize: "2.8vw",
                    verticalAlign: "middle",
                    justifyContent: "center",

                    zIndex: 2,
                    marginTop: "8vh",
                    position: "absolute",
                  }}
                />
              </span>{" "}
            </Grid>
          ) : null}

          <animated.div style={animationop}>
            <Grid
              container
              className={
                typeTop
                  ? darkmodeReducer
                    ? `optionsTop-background-dark `
                    : `optionsTop-background-light `
                  : ""
              }
              onTouchStart={handleTouchStartOptions}
              onTouchMove={handleTouchMoveOptions}
              style={{
                zIndex: 1,
                padding: "0px",

                top: matchPc ? (typeTop ? "-2vh" : "0vh") : "0vh",
                position: "relative",
                margin: "0 auto",
                overflow: "hidden",
                left: "0px",
                height: typeTop ? (matchPc ? "21vh" : "18.5vh") : "auto",
                paddingBottom: "1px",
              }}
            >
              <animated.div ref={optionsCollectImageRef} style={modalanimation}>
                <Grid
                  item
                  style={{
                    margin: "auto",
                    textAlign: "center",
                    position: "relative",
                    bottom: typeTop ? "-1.4em" : "0.2em",
                    left: "-4px",
                  }}
                >
                  <CircleIcon
                    onClick={() => {
                      nextSlidePc();
                    }}
                    className="buttonshake"
                    style={{
                      fontSize: "1.2vw",
                      cursor: "pointer",
                      color: darkmodeReducer
                        ? typeTop
                          ? "rgba(200, 200, 200, 0.5)"
                          : "rgba(200, 200, 200, 0.1)"
                        : typeTop
                        ? "rgba(005, 005, 005, 0.4)"
                        : "rgba(005, 005, 005, 0.2)",
                      display: matchPc ? "block" : "none",
                    }}
                  />
                </Grid>
                {optionsImages.map((im: any, i: any) => (
                  <Grid key={i} item xs={12}>
                    {typeTop ? (
                      <Grid
                        item
                        xs={12}
                        style={{
                          margin: "auto",
                          textAlign: "center",
                          position: "relative",
                          top: matchPc ? "0.8em" : "0.54em",
                        }}
                      >
                        <Grid
                          item
                          xs={12}
                          style={{
                            fontSize: matchPc ? "1.1vw" : "2vh",
                            fontWeight: "bolder",
                            fontFamily: "Arial, Helvetica, sans-serif",
                            visibility:
                              ActiveSlide === i ? "visible" : "hidden",

                            color: darkmodeReducer ? "#dddddd" : "#0b111b",
                          }}
                        >
                          {" "}
                          {optionsNameData[i]}
                        </Grid>
                      </Grid>
                    ) : null}

                    <img
                      alt={` ${optionsNameData[i]}  option`}
                      onClick={() => {
                        clickOptions(i, optionsClickType);
                      }}
                      style={{
                        cursor: ActiveSlide === i ? "pointer" : "alias",
                        width: `${getSliderWidthNew}px`,
                        height: `${getSliderWidthNew}px`,
                        borderRadius: "50%",
                        padding: "0px",
                        objectFit: "cover",
                        marginLeft: "2px",
                        marginTop: "14px",
                        boxShadow: darkmodeReducer
                          ? ActiveSlide === i
                            ? `0 0 5px ${colorDarkReducer}`
                            : ""
                          : ActiveSlide === i
                          ? `0 0 3.5px ${colorReducer}`
                          : "",

                        marginBottom: "2.2px",
                      }}
                      src={`./images/options/${im}`}
                    />
                    {typeTop ? null : (
                      <Grid
                        item
                        xs={12}
                        style={{
                          margin: "auto",
                          textAlign: "center",
                          position: "relative",
                          bottom: "0.2em",
                        }}
                      >
                        <Grid
                          item
                          xs={12}
                          style={{
                            fontSize: matchPc ? "1.1vw" : "2vh",
                            fontWeight: "bolder",
                            fontFamily: "Arial, Helvetica, sans-serif",
                            visibility:
                              ActiveSlide === i ? "visible" : "hidden",
                            opacity: typeTop
                              ? 0
                              : optinstopshowingReducer
                              ? 0
                              : 1,
                            color: darkmodeReducer ? "#dddddd" : "#0b111b",
                          }}
                        >
                          {" "}
                          {optionsNameData[i]}
                        </Grid>
                      </Grid>
                    )}
                  </Grid>
                ))}
              </animated.div>
            </Grid>
          </animated.div>
        </>
      ) : (
        <>
          <Grid container>
            <Grid
              item
              xs={9}
              style={{
                textAlign: "right",
                marginTop: matchPc ? "6.4vh" : matchTablet ? "8.8vh" : "8.3vh",
              }}
            >
              <span
                style={{
                  padding: "16px",
                  cursor: "pointer",
                }}
              >
                <CircleIcon
                  style={{
                    fontSize: matchPc
                      ? "1.2vw"
                      : matchTablet
                      ? "2.5vh"
                      : "2.3vh",
                    color: darkmodeReducer
                      ? "rgba(200, 200, 200, 0.1)"
                      : "rgba(005, 005, 005, 0.2)",
                  }}
                />
              </span>
            </Grid>
          </Grid>
        </>
      )}
    </>
  );
}

export const OptionsSlider = React.memo(OptionsSliderx);
